INSERT INTO `posts` (`id`, `user_id`, `category_id`, `name`, `detail`, `info`, `content`, `status`, `sort_by`, `slug`, `img`, `title`, `description`, `created_at`, `updated_at`) VALUES
(131, 1, 63, 'Tổng quan dự án', 'Khuôn viên mộ gia tộc với diện tích 200m2 trở lên, được xây dựng đáp ứng theo nhu cầu chôn cất chung của gia tộc. Với thiết kế cảnh quan đẳng cấp, khu gia tộc tại Công viên Nghĩa trang Thiên Đường sẽ trở thành ngôi nhà vĩnh cửu để tổ tiên ông bà đoàn tụ, cũng như là nơi con cháu lưu giữ những giá trị tinh hoa, phát huy phúc khí của gia tộc', NULL, '<h2><span style=\"font-size:20px\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Mỗi đất nước đều mang một sắc th&aacute;i văn h&oacute;a, phong tục tập qu&aacute;n ri&ecirc;ng biệt, nhưng tất cả mọi người sinh ra tr&ecirc;n thế gian n&agrave;y lại c&oacute; c&ugrave;ng điểm giống nhau đ&oacute; l&agrave; tinh thần Hiếu đạo. Chữ Hiếu từ xưa cho đến nay mang gi&aacute; trị vượt khỏi phạm tr&ugrave; kh&ocirc;ng gian v&agrave; thời gian m&agrave; ng&ocirc;n ngữ kh&oacute; diễn tả hết về gi&aacute; trị cao đẹp đ&oacute;. Hiếu đạo ch&iacute;nh l&agrave; bức th&ocirc;ng điệp gửi đến những người con h&atilde;y hướng về đấng sinh th&agrave;nh để niệm &acirc;n gi&aacute;o dưỡng thi&ecirc;ng li&ecirc;ng của cha v&agrave; mẹ. Theo Phật gi&aacute;o, con người kh&ocirc;ng chỉ c&oacute; kiếp sống hiện tại m&agrave; c&ograve;n c&oacute; kiếp sống vị lai, do đ&oacute; người con hiếu thảo cần phải chăm lo cho cha mẹ cả đời sống sau khi từ gi&atilde; c&otilde;i đời n&agrave;y. Người Phật tử hiếu đạo lu&ocirc;n ưu tư: Sau khi chết, cha mẹ sẽ đi về đ&acirc;u? L&agrave;m sao để gi&uacute;p cha mẹ c&oacute; được niềm an lạc hạnh ph&uacute;c trong đời sống hiện tại v&agrave; đời sống sau khi chết? &nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Những trăn trở, ưu tư n&agrave;y ch&iacute;nh l&agrave; nguồn th&ocirc;i th&uacute;c Chủ đầu tư phải t&igrave;m kiếm được lời giải đ&aacute;p, v&agrave; <a href=\"https://nghiatrangthienduong.com.vn/\">Dự &aacute;n&nbsp;C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</a>&nbsp;ra đời ch&iacute;nh l&agrave; c&acirc;u trả lời về một thế giới vĩnh hằng d&agrave;nh cho người đ&atilde; khuất, l&agrave; nơi t&ocirc;n vinh những gi&aacute; trị tốt đẹp của t&igrave;nh cảm gia đ&igrave;nh, nơi con ch&aacute;u tỏ l&ograve;ng biết ơn với tổ ti&ecirc;n, g&oacute;p phần x&acirc;y dựng những gi&aacute; trị đời sống tinh thần cho c&aacute;c thế hệ sau.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường l&agrave; c&ocirc;ng vi&ecirc;n nghĩa trang được x&acirc;y dựng theo m&ocirc; h&igrave;nh <a href=\"https://nghiatrangthienduong.com.vn/\">c&ocirc;ng vi&ecirc;n t&acirc;m linh</a> &ndash; sinh th&aacute;i tại x&atilde; Lang Qu&aacute;n, huyện Y&ecirc;n Sơn, tỉnh Tuy&ecirc;n Quang. C&ocirc;ng vi&ecirc;n c&oacute; địa thế phong thủy vượng sơn vượng hướng, ph&iacute;a sau l&agrave; n&uacute;i L&agrave; vững ch&atilde;i, dưới ch&acirc;n l&agrave; d&ograve;ng suối ngọc tự nhi&ecirc;n bao quanh, mang theo linh kh&iacute; đất trời về với Thi&ecirc;n Đường tạo n&ecirc;n hai hồ lớn. Thi&ecirc;n Đường tọa lạc ngay s&aacute;t cạnh trục đường quốc lộ v&agrave; trục đường cao tốc nối Tuy&ecirc;n Quang v&agrave; c&aacute;c tỉnh miền n&uacute;i ph&iacute;a Bắc. Qu&yacute; kh&aacute;ch c&oacute; thể dễ d&agrave;ng di chuyển đến Thi&ecirc;n Đường chỉ với hơn 75 ph&uacute;t từ H&agrave; Nội, 45 ph&uacute;t từ H&agrave; Giang, 35 ph&uacute;t từ Y&ecirc;n B&aacute;i v&agrave; 10 ph&uacute;t từ th&agrave;nh phố Tuy&ecirc;n Quang.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ngo&agrave;i ra, C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường c&ograve;n c&oacute; hệ thống tiện &iacute;ch to&agrave;n diện, từ c&aacute;c tiện &iacute;ch t&acirc;m linh đặc sắc như <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/den-trinh\">đền Tr&igrave;nh</a>, <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/chua-bao-an\">ch&ugrave;a B&aacute;o &Acirc;n</a>,<a href=\"https://nghiatrangthienduong.com.vn/canh-quan/cong-trinh-tuong-phat\"> quần thể tượng Phật</a>, <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/truc-than-dao\">Trục Thần Đạo</a>, đến những tiện &iacute;ch phụ trợ đa dạng như nh&agrave; hỏa t&aacute;ng ứng dụng c&ocirc;ng nghệ Ch&acirc;u &Acirc;u, c&aacute;c dịch vụ trọn g&oacute;i được c&aacute; nh&acirc;n h&oacute;a v&agrave; cung cấp theo nhu cầu của mỗi gia đ&igrave;nh. Bằng lễ nghĩa v&agrave; sự tận t&acirc;m phục vụ, Thi&ecirc;n Đường mong muốn mang đến sự an lạc vĩnh hằng v&agrave; sự an t&acirc;m, trọn vẹn đạo hiếu của con ch&aacute;u đối với c&aacute;c đấng sinh th&agrave;nh, để mỗi lần đến Thi&ecirc;n Đường, qu&yacute; kh&aacute;ch cảm nhận được sự b&igrave;nh y&ecirc;n, thanh thản trong t&acirc;m hồn.</span></p>\r\n\r\n<h2><span style=\"font-size:16px\">C&Ocirc;NG VI&Ecirc;N NGHĨA TRANG THI&Ecirc;N ĐƯỜNG</span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\">Vị tr&iacute;: X&atilde; Lang Qu&aacute;n, huyện Y&ecirc;n Sơn, tỉnh Tuy&ecirc;n Quang</span></li>\r\n	<li><span style=\"font-size:16px\">Quy m&ocirc; dự &aacute;n:&nbsp;25 (h&eacute;c-ta)</span></li>\r\n	<li><span style=\"font-size:16px\">Diện t&iacute;ch: Cảnh quan c&acirc;y xanh chiếm 65%, hạ tầng kỹ thuật v&agrave; c&aacute;c dịch vụ tiện &iacute;ch</span></li>\r\n	<li><span style=\"font-size:16px\">chiếm 28%, diện t&iacute;ch x&acirc;y dựng c&aacute;t t&aacute;ng (kh&ocirc;ng hung t&aacute;ng) chỉ chiếm 7%.</span></li>\r\n	<li><span style=\"font-size:16px\">Chủ đầu tư: C&ocirc;ng ty Cổ phần Đầu tư X&acirc;y dựng v&agrave; Thương mại An Ph&aacute;t Vinh</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/thiên đường 1.jpg\" /><em>&nbsp;Phối cảnh C&ocirc;ng vi&ecirc;n T&acirc;m linh Thi&ecirc;n Đường</em></span></p>', 'true', 'News', 'tong-quan-du-an', 'z4739939583777_ed37970f5389ec145ff423bd67dab831.jpg', NULL, NULL, '2023-10-29 21:21:11', '2023-12-20 04:51:21'),
(132, 1, 64, 'Khuôn viên Gia tộc', 'Khuôn viên mộ gia tộc với diện tích 200m2 trở lên, được xây dựng đáp ứng theo nhu cầu chôn cất chung của gia tộc. Với thiết kế cảnh quan đẳng cấp, khu gia tộc tại Công viên Nghĩa trang Thiên Đường sẽ trở thành ngôi nhà vĩnh cửu để tổ tiên ông bà đoàn tụ, cũng như là nơi con cháu lưu giữ những giá trị tinh hoa, phát huy phúc khí của gia tộc.', '<p>Chỉ từ: <strong>12 triệu đồng/m2</strong></p>\r\n\r\n<p>Diện t&iacute;ch khu&ocirc;n vi&ecirc;n:&nbsp;<strong>Từ 200m2 trở l&ecirc;n</strong></p>\r\n\r\n<p>Ph&aacute;p l&yacute; đầy đủ: <strong>Hợp đồng dịch vụ v&agrave; Cấp GCNQSD phần mộ</strong></p>', '<p><strong>Khu&ocirc;n vi&ecirc;n mộ gia tộc với diện t&iacute;ch 200m2 trở l&ecirc;n, được x&acirc;y dựng đ&aacute;p ứng theo nhu cầu ch&ocirc;n cất chung của gia tộc. Với thiết kế cảnh quan đẳng cấp, khu gia tộc tại <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường-Tuy&ecirc;n Quang</a> sẽ trở th&agrave;nh ng&ocirc;i nh&agrave; vĩnh cửu để tổ ti&ecirc;n &ocirc;ng b&agrave; đo&agrave;n tụ, cũng như l&agrave; nơi con ch&aacute;u lưu giữ những gi&aacute; trị tinh hoa, ph&aacute;t huy ph&uacute;c kh&iacute; của gia tộc.&nbsp;</strong></p>\r\n\r\n<h2><strong>Khu&ocirc;n vi&ecirc;n mộ gia tộc l&agrave; g&igrave;?&nbsp;</strong></h2>\r\n\r\n<p>Mộ gia tộc hay nghĩa trang gia đ&igrave;nh l&agrave; một khu đất ri&ecirc;ng, thuộc quyền sử dụng của cả gia tộc. Mộ gia tộc thường được nhận định l&agrave; v&ugrave;ng đất phong thuỷ tốt để ch&ocirc;n cất c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh. Một khu lăng mộ gia tộc&nbsp;thường l&agrave; nơi ch&ocirc;n cất từ 2 đến 4 đời th&agrave;nh vi&ecirc;n trong gia tộc&nbsp;(c&oacute; thể nhiều hơn).</p>\r\n\r\n<h2><strong>Khu&ocirc;n vi&ecirc;n mộ gia tộc tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</strong></h2>\r\n\r\n<p>Gia đ&igrave;nh v&agrave; gia tộc l&agrave; điểm tựa chắp c&aacute;nh cho ta bay cao bay xa, theo ta suốt cả cuộc đời, đến khi trăm tuổi l&aacute; lại rụng về cội. Hiện nay, c&oacute; kh&ocirc;ng &iacute;t gia đ&igrave;nh c&oacute; mong muốn t&acirc;m linh quy tụ mộ phần của c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh về một mối. X&acirc;y dựng khu mộ gia tộc khang trang, đầy đủ gi&uacute;p con ch&aacute;u thể hiện sự k&iacute;nh trọng của m&igrave;nh đến những người đ&atilde; khuất, đồng thời thể hiện sự bề thế của gia tộc m&igrave;nh</p>\r\n\r\n<p>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/san-pham/khuon-vien-gia-dinh\">khu&ocirc;n vi&ecirc;n gia đ&igrave;nh tại Thi&ecirc;n Đường</a></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"l\" /><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia tộc/gia tộc.png\" /><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><em><span style=\"font-family:&quot;san-serif&quot;,serif\"><span style=\"color:#1d1919\">Khu&ocirc;n vi&ecirc;n mộ gia tộc l&agrave; nơi ph&aacute;t huy ph&uacute;c kh&iacute; của d&ograve;ng họ</span></span></em></span></span></span><img alt=\"\" src=\"http://localhost/www/nov/public/uploads/photos/1/PC1.png\" /></p>\r\n\r\n<h3><strong><em>Diện t&iacute;ch</em></strong></h3>\r\n\r\n<p>Khu&ocirc;n vi&ecirc;n mộ gia tộc tại C&ocirc;ng vi&ecirc;n <a href=\"https://nghiatrangthienduong.com.vn/\">Nghĩa trang Thi&ecirc;n Đường </a>được x&acirc;y dựng đ&aacute;p ứng theo nhu cầu ch&ocirc;n cất chung của gia tộc, với diện t&iacute;ch ho&agrave;n hảo từ 100m2 trở l&ecirc;n, bao gồm: Từ đường, cảnh quan c&acirc;y xanh, khu mộ gia tộc.</p>\r\n\r\n<p>Trong đ&oacute;, Từ đường l&agrave; c&ocirc;ng tr&igrave;nh thờ c&uacute;ng t&acirc;m linh trang trọng nhất của khu&ocirc;n vi&ecirc;n Gia tộc. Đ&acirc;y l&agrave; nơi lưu giữ gia phả d&ograve;ng họ v&agrave; thực hiện c&aacute;c nghi lễ t&iacute;n ngưỡng. V&igrave; vậy, Từ đường l&agrave; nơi hội tụ linh kh&iacute; của to&agrave;n bộ khu&ocirc;n vi&ecirc;n Gia tộc.</p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia tộc/gia tộc 1.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia tộc/gia tộc 2.jpg\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia tộc/gia tộc 3.jpg\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia tộc/gia tộc 4.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><em><span style=\"font-family:&quot;san-serif&quot;,serif\"><span style=\"color:#1d1919\">Khu&ocirc;n vi&ecirc;n mộ gia tộc 60m2</span></span></em></span></span></span></p>\r\n\r\n<h3><strong><em>Phong thủy</em></strong></h3>\r\n\r\n<p>Phong thủy trong x&acirc;y dựng khu&ocirc;n vi&ecirc;n mộ gia tộc l&agrave; một yếu tố quan trọng được đặt l&ecirc;n h&agrave;ng đầu trong văn h&oacute;a truyền thống người Việt. Theo đ&oacute;, để đảm bảo phong thủy, khu&ocirc;n vi&ecirc;n mộ gia tộc tại C&ocirc;ng vi&ecirc;n Nghĩa trang <a href=\"https://nghiatrangthienduong.com.vn/\">Thi&ecirc;n Đường </a>được x&acirc;y dựng c&acirc;n bằng, h&agrave;i h&ograve;a với thi&ecirc;n nhi&ecirc;n nhằm thu h&uacute;t v&agrave; giữ lại năng lượng t&iacute;ch cực, đồng thời bảo vệ linh hồn của người đ&atilde; khuất v&agrave; mang lại may mắn, thịnh vượng cho gia đ&igrave;nh, d&ograve;ng tộc.</p>\r\n\r\n<p>Khu&ocirc;n vi&ecirc;n mộ gia tộc l&agrave; c&aacute;c l&ocirc; đất c&oacute; diện t&iacute;ch rộng, thường được quy hoạch tr&ecirc;n đỉnh đồi v&agrave; c&aacute;c khu vực c&oacute; chiều s&acirc;u của cơ đất ph&ugrave; hợp với khu&ocirc;n vi&ecirc;n rộng nhằm đ&aacute;p ứng cho c&aacute;c gia đ&igrave;nh c&oacute; nhu cầu quy tập h&agrave;i cốt của người th&acirc;n, d&ograve;ng họ.</p>\r\n\r\n<h3><strong><em>Thiết kế</em></strong></h3>\r\n\r\n<p>Để kh&aacute;ch h&agrave;ng dễ d&agrave;ng lựa chọn, khu&ocirc;n vi&ecirc;n mộ gia tộc được đội ngũ kỹ sư gi&agrave;u kinh nghiệm thiết kế tinh xảo gồm 4 mẫu. Mỗi mẫu mang đến vẻ đẹp v&agrave; phong c&aacute;ch kh&aacute;c nhau l&agrave;m h&agrave;i l&ograve;ng mọi nhu cầu của Q&uacute;y kh&aacute;ch</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia tộc/gia tộc 5.jpg\" /><em>Mẫu 1</em></p>\r\n\r\n<p style=\"text-align:center\"><em><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia tộc/gia tộc 6.png\" /></em></p>\r\n\r\n<p style=\"text-align:center\"><em>Mẫu 2</em></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia tộc/gia tộc 7.jpg\" /><em>Mẫu 3</em></p>\r\n\r\n<p style=\"text-align:center\"><em><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia tộc/gia tộc 8.jpg\" /></em></p>\r\n\r\n<p style=\"text-align:center\"><em>Mẫu 4</em></p>\r\n\r\n<h3><strong><em>Kh&ocirc;ng gian kiến tr&uacute;c</em></strong></h3>\r\n\r\n<p>Khu&ocirc;n vi&ecirc;n mộ gia tộc tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường sở hữu thiết kế đơn giản, hiện đại, mang đậm phong c&aacute;ch &Aacute; Đ&ocirc;ng. Nơi đ&acirc;y trở th&agrave;nh kh&ocirc;ng gian linh thi&ecirc;ng v&agrave; trang nghi&ecirc;m, nơi kiến tr&uacute;c v&agrave; thi&ecirc;n nhi&ecirc;n giao h&ograve;a một c&aacute;ch tinh tế, tạo n&ecirc;n bức tranh h&agrave;i h&ograve;a giữa thế giới của người sống v&agrave; người đ&atilde; khuất.</p>\r\n\r\n<h3><strong><em>Cảnh quan</em></strong></h3>\r\n\r\n<p>Khu&ocirc;n vi&ecirc;n cảnh quan khu gia tộc được phủ xanh bởi nhiều loại c&acirc;y cối tươi tốt như: T&ugrave;ng x&agrave;, t&ugrave;ng la h&aacute;n, nguyệt quế, chuỗi ngọc, mai vạn ph&uacute;c&hellip; Tất cả l&agrave;m n&ecirc;n khung cảnh xanh m&aacute;t, b&igrave;nh y&ecirc;n cho khu&ocirc;n vi&ecirc;n phần mộ.</p>\r\n\r\n<h3><strong><em>Vật liệu</em></strong></h3>\r\n\r\n<p>Đ&aacute; trắng B&igrave;nh Định, đ&aacute; trắng Xu&acirc;n Sơn, đ&aacute; x&aacute;m Gia Lai l&agrave; ba chất liệu đ&aacute; tự nhi&ecirc;n cao cấp được lựa chọn để thi c&ocirc;ng khu mộ gia tộc. Ưu điểm của c&aacute;c loại đ&aacute; n&agrave;y phải kể đến khả năng chịu m&agrave;i m&ograve;n, chịu nước v&agrave; va đập tốt. Ngo&agrave;i ra, c&aacute;c loại đ&aacute; n&agrave;y c&ograve;n gi&uacute;p l&agrave;m t&ocirc;n l&ecirc;n n&eacute;t sang trọng của khu mộ.</p>\r\n\r\n<h2><strong>V&igrave; sao n&ecirc;n chọn khu&ocirc;n vi&ecirc;n mộ gia tộc tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường?</strong></h2>\r\n\r\n<p>C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường được quy hoạch đồng bộ, ph&acirc;n l&ocirc; giữa c&aacute;c phần mộ tiện dụng, đồng thời c&oacute; diện t&iacute;ch rộng r&atilde;i, l&agrave; địa chỉ tin cậy để Q&uacute;y kh&aacute;ch mua đất x&acirc;y dựng mộ gia tộc cho d&ograve;ng họ.</p>\r\n\r\n<p>+ Ph&aacute;p l&yacute; minh bạch, sở hữu l&acirc;u d&agrave;i: To&agrave;n bộ&nbsp;khu&ocirc;n vi&ecirc;n mộ gia tộc&nbsp;tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường sở hữu quyền sử dụng đất l&acirc;u d&agrave;i, từng mộ phần đều được cấp giấy chứng nhận quyền sử dụng mộ phần.</p>\r\n\r\n<p>+ Tiện &iacute;ch đẳng cấp: Đến với C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường, Q&uacute;y kh&aacute;ch sẽ được tận hưởng trọn vẹn c&aacute;c tiện &iacute;ch đẳng cấp nhất như: B&atilde;i đỗ xe v&agrave; xe điện miễn ph&iacute;; Nh&agrave; h&agrave;ng đặt cỗ chay/mặn theo nhu cầu; Quầy h&agrave;ng b&aacute;n đồ v&agrave;ng m&atilde;, hương hoa cho kh&aacute;ch; Khu nghỉ ch&acirc;n, ăn uống ngo&agrave;i trời; Quầy nước lưu động; Khu vui chơi trẻ em; Ch&ograve;i nghỉ ch&acirc;n; Am h&oacute;a v&agrave;ng.</p>\r\n\r\n<p>+ Dịch vụ ho&agrave;n hảo: Mong muốn mang đến cho kh&aacute;ch h&agrave;ng những khoảnh khắc trải nghiệm ho&agrave;n hảo, đội ngũ nh&acirc;n sự tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường được đ&agrave;o tạo b&agrave;i bản, chuy&ecirc;n nghiệp với sự tận t&acirc;m v&agrave; tinh tế. Tại đ&acirc;y, Ch&uacute;ng t&ocirc;i đảm bảo cung cấp đầy đủ c&aacute;c dịch vụ hiện đại v&agrave; cao cấp bậc nhất Việt Nam như: Chăm s&oacute;c mộ phần v&agrave; cảnh quan; X&acirc;y dựng mộ phần; Tư vấn v&agrave; tổ chức tang lễ trọn g&oacute;i; Tư vấn phong thủy; C&uacute;ng giỗ online; Hỗ trợ ăn uống, lưu tr&uacute;; X&acirc;y dựng gia phả; Dịch vụ an ninh; Dịch vụ lập kế hoạch trước; Dịch vụ hỏa t&aacute;ng.</p>\r\n\r\n<p>+ Gi&aacute; th&agrave;nh hợp l&yacute;: Khu&ocirc;n vi&ecirc;n mộ gia tộc tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường c&oacute; mức gi&aacute; v&ocirc; c&ugrave;ng hợp l&yacute;.&nbsp;</p>\r\n\r\n<p>Xem th&ecirc;m:<a href=\"https://nghiatrangthienduong.com.vn/san-pham/khuon-vien-don\"> Khu&ocirc;n vi&ecirc;n đơn tại Thi&ecirc;n Đường</a></p>', 'true', 'News', 'khuon-vien-gia-toc', 'AC9F6D78-C86F-4EA6-9852-BEAB29C8E811_1_201_a.jpeg', NULL, NULL, '2023-11-08 23:58:58', '2023-12-22 04:04:36'),
(134, 1, 64, 'Khuôn viên Gia đình', 'Khuôn viên mộ gia đình tại Công viên Nghĩa trang Thiên Đường có diện tích từ 20-80m2 được thiết kế tinh xảo, là không gian yên bình, ấm cúng để con cháu tưởng nhớ đến thành tựu của ông cha cũng như lưu giữ bóng hình và những khoảnh khắc đẹp của người thân lúc sinh thời.', '<p style=\"text-align:start\">Chỉ từ: <strong>11 triệu đồng/m2</strong></p>\r\n\r\n<p>Diện t&iacute;ch khu&ocirc;n vi&ecirc;n: <strong>Từ 20m2</strong></p>\r\n\r\n<p>Ph&aacute;p l&yacute; đầy đủ: <strong>Hợp đồng dịch vụ v&agrave; Cấp GCNQSD phần mộ</strong></p>', '<p><strong><span style=\"font-size:16px\">Khu&ocirc;n vi&ecirc;n mộ gia đ&igrave;nh tại <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường-Tuy&ecirc;n Quang</a> c&oacute; diện t&iacute;ch từ 20-80m2 được thiết kế tinh xảo, l&agrave; kh&ocirc;ng gian y&ecirc;n b&igrave;nh, ấm c&uacute;ng để con ch&aacute;u tưởng nhớ đến th&agrave;nh tựu của &ocirc;ng cha cũng như lưu giữ b&oacute;ng h&igrave;nh v&agrave; những khoảnh khắc đẹp của người th&acirc;n l&uacute;c sinh thời.</span></strong></p>\r\n\r\n<h2><strong><span style=\"font-size:20px\">Khu&ocirc;n vi&ecirc;n mộ gia đ&igrave;nh l&agrave; g&igrave;?</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\">Khu mộ gia đ&igrave;nh được x&aacute;c định l&agrave;&nbsp;một khu đất ri&ecirc;ng, hợp phong thủy, được d&ugrave;ng để ch&ocirc;n cất những th&agrave;nh vi&ecirc;n trong c&ugrave;ng một gia đ&igrave;nh với khối lượng mộ vừa phải.</span></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>Khu&ocirc;n vi&ecirc;n mộ gia đ&igrave;nh tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Khu&ocirc;n vi&ecirc;n mộ gia đ&igrave;nh kh&ocirc;ng chỉ l&agrave; nơi thể hiện sự biết ơn s&acirc;u sắc của con ch&aacute;u với tổ ti&ecirc;n m&agrave; c&ograve;n l&agrave; chỗ dựa tinh thần vững chắc, l&agrave; nơi thỉnh cầu những điều may mắn với gia đ&igrave;nh. Việc x&acirc;y dựng mộ gia đ&igrave;nh vốn c&oacute; &yacute; nghĩa quan trọng n&ecirc;n phải được thực hiện một c&aacute;ch kỹ lưỡng v&agrave; chỉn chu.</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia đình/gia đình.jpg\" /></p>\r\n\r\n<p><span style=\"font-size:16px\">Khu mộ gia đ&igrave;nh tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường hội tụ những ưu điểm vượt trội.</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia đình/gia đình 1.jpg\" /><span style=\"font-size:16px\">Khu&ocirc;n vi&ecirc;n mộ gia đ&igrave;nh c&oacute; diện t&iacute;ch: 20, 30, 40, 60, 80m2</span></p>\r\n\r\n<h3><strong><span style=\"font-size:18px\">Diện t&iacute;ch</span></strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Khu&ocirc;n vi&ecirc;n mộ gia đ&igrave;nh tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường đa dạng về diện t&iacute;ch cho Q&uacute;y kh&aacute;ch dễ d&agrave;ng lựa chọn: 20, 30, 40, 60, 80m2. Bao gồm c&aacute;c hạng mục: Long đ&igrave;nh, cảnh quan c&acirc;y xanh, khu vực mộ gia đ&igrave;nh.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\">Trong đ&oacute;, khu vực Long đ&igrave;nh l&agrave; khu vực thờ tự chung của cả gia&nbsp;đ&igrave;nh, l&agrave; nơi hội tụ linh kh&iacute;, đại diện cho sự thịnh vượng, sung t&uacute;c, quyền thế của cả gia đ&igrave;nh.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia đình/gia đình 2.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia đình/gia đình 3.jpg\" />&nbsp;</span><img alt=\"\" src=\"gvhb\" /><em><span style=\"font-size:16px\">Khu&ocirc;n vi&ecirc;n mộ gia đ&igrave;nh 20m2</span></em></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia đình/gia đình 4.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia đình/gia đình 5.jpg\" /><em><span style=\"font-size:16px\">Khu&ocirc;n vi&ecirc;n mộ gia đ&igrave;nh 30m2</span></em></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia đình/gia đình 6.jpg\" /><em><span style=\"font-size:16px\">Khu&ocirc;n vi&ecirc;n mộ gia đ&igrave;nh 40m2</span></em></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia đình/gia đình 7.jpg\" /><em><span style=\"font-size:16px\">Khu&ocirc;n vi&ecirc;n mộ gia đ&igrave;nh 60m2</span></em></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia đình/gia đình 8.jpg\" /><em><span style=\"font-size:16px\">Khu&ocirc;n vi&ecirc;n mộ gia đ&igrave;nh 80m2</span></em></p>\r\n\r\n<h3><span style=\"font-size:16px\">Xem th&ecirc;m :<a href=\"https://nghiatrangthienduong.com.vn/san-pham/khuon-vien-gia-toc\"> mộ gia tộc tại C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường</a></span></h3>\r\n\r\n<h3><span style=\"font-size:18px\"><strong>Phong thủy</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Khu&ocirc;n vi&ecirc;n mộ gia đ&igrave;nh tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường được thiết kế hợp phong thủy, bố tr&iacute; c&aacute;c khu mộ theo d&atilde;y h&igrave;nh ruộng bậc thang. Điều n&agrave;y đảm bảo mộ phần ph&iacute;a trước kh&ocirc;ng bị che lấp, c&oacute; tầm nh&igrave;n tho&aacute;ng đ&atilde;ng, cảnh quan đặc sắc.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Đặc biệt, c&aacute;c khu mộ gia đ&igrave;nh đều được đặt ở nơi đất c&oacute; sinh kh&iacute; tốt, kh&ocirc;ng kh&iacute; trong l&agrave;nh, c&acirc;y cối xanh tươi, vị tr&iacute; đẹp, gi&uacute;p đem lại ph&uacute;c lộc đời đời cho con ch&aacute;u.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Khu&ocirc;n vi&ecirc;n mộ gia đ&igrave;nh được sắp xếp ph&ugrave; hợp với diện t&iacute;ch sử dụng, theo thứ tự ph&acirc;n bậc từ cao xuống thấp.</span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><strong>Thiết kế</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường, khu&ocirc;n vi&ecirc;n mộ gia đ&igrave;nh được đội ngũ kỹ sư gi&agrave;u kinh nghiệm thiết kế tinh xảo gồm 2 mẫu để Q&uacute;y kh&aacute;ch dễ d&agrave;ng lựa chọn. Mỗi mẫu thiết kế mang đến vẻ đẹp v&agrave; phong c&aacute;ch kh&aacute;c nhau, tạo n&ecirc;n một kh&ocirc;ng gian t&ocirc;n k&iacute;nh v&agrave; ấm c&uacute;ng, gi&uacute;p x&oacute;a nh&ograve;a khoảng c&aacute;ch giữa hai thế giới.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Khu&ocirc;n vi&ecirc;n mộ gia đ&igrave;nh gồm 2 mẫu thiết kế tinh tế, đẳng cấp</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia đình/gia đình 9.jpg\" /></span><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia đình/gia đình 10.jpg\" /></p>\r\n\r\n<h3><span style=\"font-size:18px\"><strong>Kh&ocirc;ng gian kiến tr&uacute;c</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">C&aacute;c mẫu mộ gia đ&igrave;nh tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường sở hữu thiết kế đơn giản, hiện đại, nhưng vẫn mang đậm phong c&aacute;ch &Aacute; Đ&ocirc;ng. Từng chi tiết nhỏ nhất đều được Chủ đầu tư chăm ch&uacute;t kỹ lưỡng. Bao bọc xung quanh phần mộ l&agrave; những b&atilde;i cỏ h&agrave;ng c&acirc;y lu&ocirc;n được cắt tỉa chăm ch&uacute;t để giữ được vẻ xanh tươi. Kết hợp với kh&ocirc;ng kh&iacute; trong l&agrave;nh tho&aacute;ng đ&atilde;ng c&agrave;ng l&agrave;m cho phần mộ th&ecirc;m phần y&ecirc;n b&igrave;nh.</span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><strong>Cảnh quan</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Để tăng sinh kh&iacute; cho khu mộ gia đ&igrave;nh, Chủ đầu tư lựa chọn c&aacute;c loại c&acirc;y: T&ugrave;ng la h&aacute;n, nguyệt quế, mai vạn ph&uacute;c&hellip; M&agrave;u xanh của cỏ c&acirc;y, hoa l&aacute; kh&ocirc;ng chỉ l&agrave;m đẹp cảnh quan khu mộ m&agrave; c&ograve;n tạo cảm gi&aacute;c b&igrave;nh y&ecirc;n cho c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh mỗi khi viếng thăm.</span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><strong>Vật liệu</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Đ&aacute; trắng B&igrave;nh Định, đ&aacute; trắng Xu&acirc;n Sơn, đ&aacute; x&aacute;m Gia Lai l&agrave; ba chất liệu đ&aacute; tự nhi&ecirc;n cao cấp được lựa chọn để thi c&ocirc;ng khu mộ gia đ&igrave;nh. Ưu điểm của c&aacute;c loại đ&aacute; n&agrave;y phải kể đến khả năng chịu m&agrave;i m&ograve;n, chịu nước v&agrave; va đập tốt. Ngo&agrave;i ra, c&aacute;c loại đ&aacute; n&agrave;y c&ograve;n l&agrave;m t&ocirc;n l&ecirc;n n&eacute;t sang trọng của khu mộ.</span></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>V&igrave; sao n&ecirc;n chọn khu&ocirc;n vi&ecirc;n mộ gia đ&igrave;nh tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường?</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường được quy hoạch đồng bộ, ph&acirc;n l&ocirc; giữa c&aacute;c phần mộ tiện dụng, đồng thời c&oacute; diện t&iacute;ch rộng r&atilde;i, l&agrave; địa chỉ tin cậy để Q&uacute;y kh&aacute;ch mua đất x&acirc;y dựng mộ gia đ&igrave;nh cho người th&acirc;n.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/phap-ly-so-huu\">Ph&aacute;p l&yacute; minh bạch, sở hữu l&acirc;u d&agrave;</a>i: To&agrave;n bộ khu mộ gia đ&igrave;nh tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường sở hữu quyền sử dụng đất l&acirc;u d&agrave;i, từng mộ phần đều được cấp giấy chứng nhận quyền sử dụng mộ phần.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ <a href=\"https://nghiatrangthienduong.com.vn/tien-ich\">Tiện &iacute;ch</a> đẳng cấp: Đến với C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường, Q&uacute;y kh&aacute;ch sẽ được tận hưởng trọn vẹn c&aacute;c tiện &iacute;ch đẳng cấp nhất: B&atilde;i đỗ xe v&agrave; xe điện miễn ph&iacute;; Nh&agrave; h&agrave;ng đặt cỗ chay/mặn theo nhu cầu; Quầy h&agrave;ng b&aacute;n đồ v&agrave;ng m&atilde;, hương hoa cho kh&aacute;ch; Khu nghỉ ch&acirc;n, ăn uống ngo&agrave;i trời; Quầy nước lưu động; Khu vui chơi trẻ em; Ch&ograve;i nghỉ ch&acirc;n; Am h&oacute;a v&agrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ <a href=\"https://nghiatrangthienduong.com.vn/dich-vu\">Dịch vụ</a> ho&agrave;n hảo: Mong muốn mang đến cho kh&aacute;ch h&agrave;ng những khoảnh khắc trải nghiệm ho&agrave;n hảo, đội ngũ nh&acirc;n sự tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường được đ&agrave;o tạo b&agrave;i bản, chuy&ecirc;n nghiệp với sự tận t&acirc;m v&agrave; tinh tế. Tại đ&acirc;y, Ch&uacute;ng t&ocirc;i đảm bảo cung cấp đầy đủ c&aacute;c dịch vụ hiện đại v&agrave; cao cấp bậc nhất Việt Nam như: Chăm s&oacute;c mộ phần v&agrave; cảnh quan; X&acirc;y dựng mộ phần; Tư vấn v&agrave; tổ chức tang lễ trọn g&oacute;i; Tư vấn phong thủy; C&uacute;ng giỗ online; Hỗ trợ ăn uống, lưu tr&uacute;; X&acirc;y dựng gia phả; Dịch vụ an ninh; Dịch vụ lập kế hoạch trước; Dịch vụ hỏa t&aacute;ng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ Gi&aacute; th&agrave;nh hợp l&yacute;: Khu&ocirc;n vi&ecirc;n mộ gia đ&igrave;nh tại <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường- Tuy&ecirc;n Quang&nbsp;</a>c&oacute; mức gi&aacute; v&ocirc; c&ugrave;ng hợp l&yacute;</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/\">XEM TH&Ecirc;M</a></span><a href=\"https://nghiatrangthienduong.com.vn/\"><span style=\"font-size:16px\">&nbsp;NHẬN TƯ VẤN</span></a></p>', 'true', 'News', 'khuon-vien-gia-dinh', 'FABEE558-9523-434B-8360-09F49D82001E_1_201_a.jpeg', 'Khuôn viên gia đình- sản phẩm của công viên nghĩa trang Thiên Đường', 'Khuôn viên mộ gia đình tại Công viên Nghĩa trang Thiên Đường có diện tích từ 20-80m2 được thiết kế tinh xảo, là không gian yên bình, ấm cúng để con cháu tưởng nhớ đến thành tựu của ông cha.', '2023-11-09 01:39:19', '2023-12-22 04:04:59'),
(135, 1, 64, 'Khuôn viên Đôi', 'Với diện tích 8-12m2, thiết kế hiện đại, tinh xảo, cùng khuôn viên mộ phần tràn ngập cây xanh, hoa đẹp, khu mộ đôi tại Công viên Nghĩa trang Thiên Đường là nơi an nghỉ vĩnh hằng cho ông bà, cha mẹ.', '<p>Chỉ từ:&nbsp;<strong>164 triệu đồng/ng&ocirc;i</strong></p>\r\n\r\n<p>K&iacute;ch thước: từ 8-12m2<br />\r\n<strong>B&agrave;n giao ho&agrave;n thiện to&agrave;n bộ</strong></p>\r\n\r\n<p>Ph&aacute;p l&yacute; đầy đủ: <strong>Hợp đồng dịch vụ v&agrave; Cấp GCNQSD phần mộ</strong></p>', '<p><strong><span style=\"font-size:16px\">Với diện t&iacute;ch 8-12m2, thiết kế hiện đại, tinh xảo, c&ugrave;ng khu&ocirc;n vi&ecirc;n mộ phần tr&agrave;n ngập c&acirc;y xanh, hoa đẹp, khu mộ đ&ocirc;i tại <a href=\"https://nghiatrangthienduong.com.vn/san-pham/khuon-vien-don\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường- Tuy&ecirc;n Quang&nbsp;</a>l&agrave; nơi an nghỉ vĩnh hằng cho &ocirc;ng b&agrave;, cha mẹ.&nbsp;</span></strong></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>Mộ đ&ocirc;i l&agrave; g&igrave;?</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Mộ đ&ocirc;i l&agrave; những khu&ocirc;n vi&ecirc;n mộ được thiết kế d&agrave;nh cho 2 người c&oacute; sự đồng nhất về k&iacute;ch thước, kiểu d&aacute;ng cho tới hoa văn trang tr&iacute;. V&igrave; vậy, mộ đ&ocirc;i thường được d&ugrave;ng cho &ocirc;ng b&agrave;, cha mẹ hoặc anh chị em sinh đ&ocirc;i đ&atilde; khuất.</span></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>Khu&ocirc;n vi&ecirc;n mộ đ&ocirc;i tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Khu&ocirc;n vi&ecirc;n mộ đ&ocirc;i tại <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường (Tuy&ecirc;n Quang)</a> l&agrave; địa chỉ tin cậy để con ch&aacute;u b&agrave;y tỏ l&ograve;ng th&agrave;nh k&iacute;nh, hiếu thảo với &ocirc;ng b&agrave;, cha mẹ. Đồng thời l&agrave; nơi để &ocirc;ng b&agrave;, cha mẹ lu&ocirc;n được ở cạnh nhau d&ugrave; l&agrave; khi c&ograve;n sống hay khi đ&atilde; về với thế giới b&ecirc;n kia. </span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên đôi/đôi.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m : <a href=\"https://nghiatrangthienduong.com.vn/san-pham/khuon-vien-gia-dinh\">Khu&ocirc;n vi&ecirc;n mộ gia đ&igrave;nh</a></span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><strong>Diện t&iacute;ch</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Khu&ocirc;n vi&ecirc;n mộ đ&ocirc;i tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường được ph&acirc;n l&ocirc; giữa c&aacute;c phần mộ tiện dụng với diện t&iacute;ch ho&agrave;n hảo từ 8-12m2, bao gồm: Khu&ocirc;n vi&ecirc;n phần mộ, đường giao th&ocirc;ng, h&agrave;ng r&agrave;o x&acirc;y xanh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên đôi/đôi 1.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Để đ&aacute;p ứng mọi nhu cầu của kh&aacute;ch h&agrave;ng, khu&ocirc;n vi&ecirc;n mộ đ&ocirc;i được chia th&agrave;nh 2 loại:</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ Mộ đ&ocirc;i ti&ecirc;u chuẩn với diện t&iacute;ch 4,42m2 (bao gồm: Khu&ocirc;n vi&ecirc;n phần mộ, đường giao th&ocirc;ng, h&agrave;ng r&agrave;o c&acirc;y xanh).</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ Mộ đ&ocirc;i cao cấp với diện t&iacute;ch 8,25m2 (bao gồm: Khu&ocirc;n vi&ecirc;n phần mộ, đường giao th&ocirc;ng, h&agrave;ng r&agrave;o c&acirc;y xanh).</span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><strong>Phong thủy</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Mộ đ&ocirc;i tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường được thiết kế chuẩn phong thủy với c&aacute;c số đo rơi v&agrave;o cung số đẹp theo thước đo phong thủy Lỗ Ban. C&aacute;c th&ocirc;ng số lu&ocirc;n l&agrave; số đỏ thuộc cung số đẹp, mang lại sự b&igrave;nh y&ecirc;n cho người đ&atilde; khuất v&agrave; c&aacute;t lợi cho con ch&aacute;u trong gia đ&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên đôi/đôi 2.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ngo&agrave;i ra, mộ đ&ocirc;i được thiết kế theo nhiều hướng kh&aacute;c nhau, c&oacute; view nội khu v&agrave; cảnh quan đẹp, đa dạng về m&agrave;u sắc để gia chủ dễ d&agrave;ng lựa chọn, ph&ugrave; hợp với phong thủy từng c&aacute; nh&acirc;n.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:18px\">Thiết kế</span></strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Để kh&aacute;ch h&agrave;ng dễ d&agrave;ng lựa chọn, khu&ocirc;n vi&ecirc;n mộ đ&ocirc;i được đội ngũ kỹ sư gi&agrave;u kinh nghiệm thiết kế tinh xảo gồm 2 mẫu. Mỗi mẫu mang đến vẻ đẹp v&agrave; phong c&aacute;ch kh&aacute;c nhau l&agrave;m h&agrave;i l&ograve;ng mọi nhu cầu của Q&uacute;y kh&aacute;ch.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Khu&ocirc;n vi&ecirc;n mộ đ&ocirc;i được thiết kế tinh xảo gồm 2 mẫu</span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên đôi/đôi 3.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><em>Mẫu 1</em></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><em><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên đôi/đôi 4.jpg\" />&nbsp;</em></span><span style=\"font-size:16px\"><em>Mẫu 2</em></span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><strong>Kh&ocirc;ng gian kiến tr&uacute;c</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">C&aacute;c mẫu mộ đ&ocirc;i tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường sở hữu thiết kế đơn giản, hiện đại, nhưng vẫn mang đậm phong c&aacute;ch &Aacute; Đ&ocirc;ng.</span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><strong>Cảnh quan</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Khu&ocirc;n vi&ecirc;n cảnh quan xung quanh khu mộ đ&ocirc;i được Chủ đầu tư chăm ch&uacute;t kỹ lưỡng. Mai vạn ph&uacute;c v&agrave; chuỗi ngọc l&agrave; hai loại c&acirc;y được lựa chọn để đem đến vẻ đẹp xanh m&aacute;t, b&igrave;nh y&ecirc;n cho khu&ocirc;n vi&ecirc;n phần mộ.</span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><strong>Vật liệu</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Đ&aacute; trắng B&igrave;nh Định v&agrave; đ&aacute; t&iacute;m Kh&aacute;nh H&ograve;a l&agrave; hai chất liệu đ&aacute; tự nhi&ecirc;n cao cấp được lựa chọn để thi c&ocirc;ng mộ đ&ocirc;i. Hai loại đ&aacute; n&agrave;y đều c&oacute; t&iacute;nh thẩm mỹ cao, khả năng chịu lực, chịu nhiệt tốt, đồng thời rất bền bỉ v&agrave; tho&aacute;ng kh&iacute;.</span></p>\r\n\r\n<h2><strong><span style=\"font-size:20px\">V&igrave; sao n&ecirc;n chọn khu&ocirc;n vi&ecirc;n mộ đ&ocirc;i tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường?</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</a> được quy hoạch đồng bộ, ph&acirc;n l&ocirc; giữa c&aacute;c phần mộ tiện dụng, đồng thời c&oacute; diện t&iacute;ch rộng r&atilde;i, l&agrave; địa chỉ tin cậy để Q&uacute;y kh&aacute;ch mua đất x&acirc;y dựng mộ đ&ocirc;i cho người th&acirc;n.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/phap-ly-so-huu\">Ph&aacute;p l&yacute; minh bạch, sở hữu l&acirc;u d&agrave;i</a>: To&agrave;n bộ khu mộ đ&ocirc;i tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường sở hữu quyền sử dụng đất l&acirc;u d&agrave;i, từng mộ phần đều được cấp giấy chứng nhận quyền sử dụng mộ phần.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ <a href=\"https://nghiatrangthienduong.com.vn/tien-ich\">Tiện &iacute;ch</a> đẳng cấp: Đến với C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường, Q&uacute;y kh&aacute;ch sẽ được tận hưởng trọn vẹn c&aacute;c tiện &iacute;ch đẳng cấp nhất như: B&atilde;i đỗ xe v&agrave; xe điện miễn ph&iacute;; Nh&agrave; h&agrave;ng đặt cỗ chay/mặn theo nhu cầu; Quầy h&agrave;ng b&aacute;n đồ v&agrave;ng m&atilde;, hương hoa cho kh&aacute;ch; Khu nghỉ ch&acirc;n, ăn uống ngo&agrave;i trời; Quầy nước lưu động; Khu vui chơi trẻ em; Ch&ograve;i nghỉ ch&acirc;n; Am h&oacute;a v&agrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ <a href=\"https://nghiatrangthienduong.com.vn/dich-vu\">Dịch vụ </a>ho&agrave;n hảo: Mong muốn mang đến cho kh&aacute;ch h&agrave;ng những khoảnh khắc trải nghiệm ho&agrave;n hảo, đội ngũ nh&acirc;n sự tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường được đ&agrave;o tạo b&agrave;i bản, chuy&ecirc;n nghiệp với sự tận t&acirc;m v&agrave; tinh tế. Tại đ&acirc;y, Ch&uacute;ng t&ocirc;i đảm bảo cung cấp đầy đủ c&aacute;c dịch vụ hiện đại v&agrave; cao cấp bậc nhất Việt Nam như: Chăm s&oacute;c mộ phần v&agrave; cảnh quan; X&acirc;y dựng mộ phần; Tư vấn v&agrave; tổ chức tang lễ trọn g&oacute;i; Tư vấn phong thủy; C&uacute;ng giỗ online; Hỗ trợ ăn uống, lưu tr&uacute;; X&acirc;y dựng gia phả; Dịch vụ an ninh; Dịch vụ lập kế hoạch trước; Dịch vụ hỏa t&aacute;ng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ Gi&aacute; th&agrave;nh hợp l&yacute;: Khu mộ đ&ocirc;i tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường c&oacute; mức gi&aacute; v&ocirc; c&ugrave;ng hợp l&yacute;. </span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m : <a href=\"https://nghiatrangthienduong.com.vn/san-pham/khuon-vien-don\">Mộ đơn tại Thi&ecirc;n Đường&nbsp;</a></span></p>', 'true', 'News', 'khuon-vien-doi', 'đôi.jpg', NULL, NULL, '2023-11-09 01:43:30', '2023-12-22 04:01:41');
INSERT INTO `posts` (`id`, `user_id`, `category_id`, `name`, `detail`, `info`, `content`, `status`, `sort_by`, `slug`, `img`, `title`, `description`, `created_at`, `updated_at`) VALUES
(136, 1, 65, 'Chùa Báo Ân', 'Tọa lạc tại vị trí trung cung, đắc địa nhất của Công viên Nghĩa trang Thiên Đường với lối kiến trúc mang đậm nét chùa cổ Bắc Bộ, không gian nơi đây luôn tôn nghiêm, thanh tịnh, và linh thiêng. Vẻ đẹp trầm mặc được tạo nên bởi sắc nâu trầm của ngôi chùa mang lại cảm giác an yên trong tâm thức', NULL, '<p><span style=\"font-size:16px\"><strong>Tọa lạc tại vị tr&iacute; trung cung, đắc địa nhất của&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</a>&nbsp;với lối kiến tr&uacute;c mang đậm n&eacute;t ch&ugrave;a cổ Bắc Bộ, kh&ocirc;ng gian nơi đ&acirc;y lu&ocirc;n t&ocirc;n nghi&ecirc;m, thanh tịnh, v&agrave; linh thi&ecirc;ng. Vẻ đẹp trầm mặc được tạo n&ecirc;n bởi sắc n&acirc;u trầm của ng&ocirc;i ch&ugrave;a mang lại cảm gi&aacute;c an y&ecirc;n trong t&acirc;m thức.</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"z4791581277892_05d8a0a71978a81115fc47d618c4c5df\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/chùa báo ân/chùa báo ân.png\" title=\"z4791581277892_05d8a0a71978a81115fc47d618c4c5df\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Phối cảnh ch&ugrave;a B&aacute;o &Acirc;n tại C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</span></em></p>\r\n\r\n<h2><span style=\"font-size:18px\"><strong>Vị tr&iacute; v&agrave; t&ecirc;n gọi</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Ch&ugrave;a c&oacute; t&ecirc;n chữ l&agrave; &ldquo;B&aacute;o &Acirc;n tự&ldquo;. B&aacute;o c&oacute; nghĩa l&agrave;: b&aacute;o đ&aacute;p, th&ocirc;ng b&aacute;o, biểu lộ. &Acirc;n c&oacute; nghĩa l&agrave;: nhớ lại, suy tư ho&agrave;i niệm. Vậy &ldquo;B&aacute;o &Acirc;n tự&rdquo; l&agrave; ng&ocirc;i ch&ugrave;a b&aacute;o đ&aacute;p c&ocirc;ng đức của Trời &ndash; Phật &ndash; Th&aacute;nh &ndash; Thần đ&atilde; gi&uacute;p con người được sống một cuộc sống tốt đẹp, b&igrave;nh an, hạnh ph&uacute;c v&agrave; th&agrave;nh c&ocirc;ng.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/chùa báo ân/chùa báo ân 1.jpg\" /></span><span style=\"font-size:16px\"><em>Ch&ugrave;a B&aacute;o &Acirc;n tại vị tr&iacute; trung t&acirc;m C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</em></span></p>\r\n\r\n<p><span style=\"font-size:16px\">C&ocirc;ng tr&igrave;nh nằm tr&ecirc;n trục thần đạo của dự &aacute;n với bố cục theo h&igrave;nh thức truyền thống kiểu nội C&ocirc;ng ngoại Quốc. Ngo&agrave;i c&ugrave;ng l&agrave; tam quan kiểu tứ trụ, tr&ecirc;n c&oacute; m&aacute;i, tiếp đến l&agrave; lầu chi&ecirc;ng, lầu trống. Sau khoảng s&acirc;n rộng l&agrave; t&ograve;a Tam bảo nối với nh&agrave; Tổ ph&iacute;a sau bằng hệ thống h&agrave;nh lang 2 b&ecirc;n tạo n&ecirc;n tổng thể ch&iacute;nh kiểu Nội c&ocirc;ng ngoại quốc.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">T&ograve;a Tam bảo mặt bằng h&igrave;nh chữ Đinh (丁) gồm 3 gian 2 ch&aacute;i, h&igrave;nh thức kiến tr&uacute;c kiểu hai tầng 8 m&aacute;i, g&oacute;c m&aacute;i c&oacute; đầu đao; nh&agrave; Tổ mặt bằng h&igrave;nh chữ nhất (一) gồm 5 gian 2 ch&aacute;i. Vật liệu sử dụng theo vật liệu truyền thống: kết cấu gỗ, tường gạch, m&aacute;i lợp ng&oacute;i mũi h&agrave;i, s&acirc;n l&aacute;t gạch b&aacute;t.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ch&ugrave;a B&aacute;o &Acirc;n được x&acirc;y dựng theo kiến tr&uacute;c của Ch&ugrave;a T&acirc;y Phương hay S&ugrave;ng Ph&uacute;c tự - ng&ocirc;i chùa gắn li&ecirc;̀n với quá trình truy&ecirc;̀n bá Ph&acirc;̣t giáo vào Vi&ecirc;̣t Nam.</span></p>\r\n\r\n<h2><span style=\"font-size:18px\"><strong>Tam quan</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Cổng Tam quan được x&acirc;y tr&ecirc;n nền kẻ đ&aacute; xanh Thanh H&oacute;a, cao 50cm, giật 3 cấp, gồm c&oacute; 4 cột đ&uacute;c bằng đ&aacute; nguy&ecirc;n khối, tr&ecirc;n đỉnh 2 cột&nbsp;ngo&agrave;i&nbsp;l&agrave; hai h&igrave;nh sư tử đ&aacute;, ph&iacute;a dưới c&oacute; đấu vu&ocirc;ng th&oacute;t đ&aacute;y. Dưới đấu vu&ocirc;ng l&agrave; một chỏm cong mui luyện đắp cờ nổi dạng &ldquo;dạ c&aacute;&rdquo; uốn cong ở đầu đao, sau đến lồng đ&egrave;n h&igrave;nh vu&ocirc;ng với những &ocirc; hộc, khắc ch&igrave;m s&acirc;u trong l&ograve;ng cột. Hai cột trụ lớn ở b&ecirc;n trong cao hơn, đỉnh trụ c&oacute; kết cấu dạng đu&ocirc;i phượng l&aacute; lật, vuốt ngược l&ecirc;n chụm v&agrave;o nhau, ph&iacute;a dưới trang tr&iacute; giống cột hai b&ecirc;n. Bốn mặt của to&agrave;n bộ c&aacute;c trụ được đ&uacute;c c&acirc;u đối chữ H&aacute;n một c&aacute;ch kh&eacute;o l&eacute;o, tinh xảo bởi đội thi c&ocirc;ng l&agrave;nh nghề, c&oacute; kinh nghiệm đ&uacute;c c&acirc;u đối cho c&aacute;c đền ch&ugrave;a từ Bắc v&agrave;o Nam.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"z4791581212507_ae3c23769e791f35e388f9a4efdc195b\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/chùa báo ân/chùa báo ân 2.jpg\" title=\"z4791581212507_ae3c23769e791f35e388f9a4efdc195b\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\">&nbsp;<em>Tam quan ch&ugrave;a B&aacute;o &Acirc;n - <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</a></em></span></p>\r\n\r\n<h2><span style=\"font-size:18px\"><strong>S&acirc;n ch&ugrave;a</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Từ Tam Quan v&agrave;o ch&ugrave;a l&agrave; đến s&acirc;n ch&ugrave;a. S&acirc;n ch&ugrave;a l&aacute;t gạch B&aacute;t Tr&agrave;ng nung gi&agrave; lửa, k&iacute;ch thước 24x24cm, c&oacute; m&agrave;u n&acirc;u xẩm, tạo cho kh&ocirc;ng gian của c&ocirc;ng tr&igrave;nh tăng phần cổ k&iacute;nh. Chủ đầu tư quyết định sử dụng gạch ng&oacute;i của Thương hiệu sản xuất gạch ng&oacute;i uy t&iacute;n nhất Việt Nam, c&oacute; nguồn gốc từ l&agrave;ng gốm cổ B&aacute;t Tr&agrave;ng. Gạch Ng&oacute;i B&aacute;t Tr&agrave;ng c&oacute; những đặc điểm rất ri&ecirc;ng biệt, kh&aacute;c hẳn với c&aacute;c loại gạch kh&aacute;c tr&ecirc;n thị trường bởi chất lượng, kiểu d&aacute;ng, v&agrave; m&agrave;u sắc, khi g&otilde; v&agrave;o nghe rất đanh (tiếng vang trong, đanh như tiếng chu&ocirc;ng), chất men đẹp lạ tinh xảo. Đặc biệt, gạch được nung trong l&ograve; đất 7 ng&agrave;y 7 đ&ecirc;m, sau đ&oacute; cho ra th&agrave;nh phẩm l&agrave; những vi&ecirc;n gạch c&oacute; m&agrave;u sắc đậm nhạt kh&aacute;c nhau (do l&ograve; đất nung c&oacute; nhiệt độ kh&ocirc;ng đều), l&agrave;m n&ecirc;n chất ri&ecirc;ng m&agrave; kh&ocirc;ng nơi n&agrave;o c&oacute; được.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"Picture8_1\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/chùa báo ân/chùa báo ân 3.png\" title=\"Picture8_1\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><em>S&acirc;n ch&ugrave;a B&aacute;o &Acirc;n</em></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"gach_bat_trang_tai_cong_vien_nghia_trang_thien_duong\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/chùa báo ân/chùa báo ân 4.jpg\" title=\"gach_bat_trang_tai_cong_vien_nghia_trang_thien_duong\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><em>Gạch B&aacute;t Tr&agrave;ng được sử dụng tại&nbsp;s&acirc;n ch&ugrave;a</em></span></p>\r\n\r\n<h2><span style=\"font-size:18px\"><strong>Khu ch&ugrave;a ch&iacute;nh</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Đ&acirc;y l&agrave; hạng mục trung t&acirc;m của quần thể ch&ugrave;a. Với kết cấu h&igrave;nh chữ C&ocirc;ng, c&ocirc;ng tr&igrave;nh bao gồm Gian Tam Bảo v&agrave; Gian Nh&agrave; Tổ. C&aacute;c t&ograve;a nh&agrave; tại khu vực n&agrave;y của ch&ugrave;a B&aacute;o &Acirc;n được x&acirc;y theo kiểu chồng di&ecirc;m hai tầng t&aacute;m m&aacute;i với tường x&acirc;y ho&agrave;n to&agrave;n bằng gạch B&aacute;t Tr&agrave;ng nung đ&oacute;, để trần kh&ocirc;ng ch&aacute;t, điểm xuyết bằng c&aacute;c cửa sổ &ocirc; vu&ocirc;ng.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"z4788922048636_376d0aa3d5c90f3b5918520ec6ecf49b\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/chùa báo ân/chuà báo ân 5.jpg\" title=\"z4788922048636_376d0aa3d5c90f3b5918520ec6ecf49b\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"z4113935536280_2a86facbf8d22fc368169f44c1d4d7d5\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/chùa báo ân/chùa báo ân 7.jpg\" title=\"z4113935536280_2a86facbf8d22fc368169f44c1d4d7d5\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><em>Mặt b&ecirc;n Ch&ugrave;a B&aacute;o &Acirc;n - C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</em></span></p>\r\n\r\n<p><span style=\"font-size:16px\">M&aacute;i c&aacute;c ng&ocirc;i ch&ugrave;a đều c&oacute; 2 lớp ng&oacute;i, trong đ&oacute; m&aacute;i tr&ecirc;n c&oacute; m&uacute;i in nổi h&igrave;nh l&aacute; đề, lớp dưới l&agrave; ng&oacute;i l&oacute;t h&igrave;nh vu&ocirc;ng sơn ngũ sắc. Xung quanh diềm m&aacute;i cả ba t&ograve;a đều được trạm trổ theo h&igrave;nh l&aacute; triện cuốn rất tinh tế. Tr&ecirc;n m&aacute;i c&ograve;n gắn nhiều linh vật bằng đất nung, c&aacute;c đầu đao trạm khắc với c&aacute;c họa tiết nổi như h&igrave;nh hoa l&aacute;, rồng phượng gi&agrave;u t&iacute;nh d&acirc;n tộc. Giữa c&aacute;c ch&ugrave;a với nhau được ph&acirc;n c&aacute;ch bằng c&aacute;c khoảng s&acirc;n trời tho&aacute;ng đ&atilde;ng.&nbsp;&nbsp;</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"z4113935546252_a32fd044a6bdaa90ebdf05493f7cd402\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/chùa báo ân/chùa báo ân 6.jpg\" title=\"z4113935546252_a32fd044a6bdaa90ebdf05493f7cd402\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Vật liệu gỗ trong ch&ugrave;a được chế t&aacute;c thủ c&ocirc;ng từ gỗ m&iacute;t nguy&ecirc;n khối, được để kh&ocirc; tự nhi&ecirc;n trong khoảng từ 8-12 th&aacute;ng. Theo quan niệm th&igrave; c&acirc;y m&iacute;t thường l&agrave; nơi tr&uacute; ngụ của thần linh, nh&agrave; trồng m&iacute;t sẽ được thần linh tr&uacute; ngụ, che chở. C&ograve;n ở Ấn Độ, c&acirc;y gỗ m&iacute;t l&agrave; loại c&acirc;y thi&ecirc;ng li&ecirc;ng của đất trời. C&aacute;c đồ c&uacute;ng l&agrave;m từ gỗ m&iacute;t thường mang &yacute; nghĩa tốt l&agrave;nh đến cho gia chủ. Ngo&agrave;i ra, x&eacute;t về mặt kinh tế, gỗ m&iacute;t nguy&ecirc;n khối cực k&igrave; bền bỉ với thời gian, tr&aacute;nh được t&igrave;nh trạng mối mọt. Chủ đầu tư cho biết d&ugrave; cho gi&aacute; th&agrave;nh c&oacute; cao hơn c&aacute;c loại gỗ kh&aacute;c th&igrave; vẫn rất xứng đ&aacute;ng để đầu tư, đảm bảo cho ng&ocirc;i ch&ugrave;a được trường tồn với thời gian.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"z4791581246647_0be3b274ff19ed888f567f5878775b9b\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/chùa báo ân/chùa báo ân 8.jpg\" title=\"z4791581246647_0be3b274ff19ed888f567f5878775b9b\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"z4791581270402_0e6e25c916cb5ed06c5f377ee0222493\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/chùa báo ân/chùa báo ân 9.jpg\" title=\"z4791581270402_0e6e25c916cb5ed06c5f377ee0222493\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><em>Nội thất b&ecirc;n trong ch&ugrave;a</em></span></p>\r\n\r\n<h2><span style=\"font-size:18px\"><strong>Cảnh quan</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Bước ch&acirc;n đến Ch&ugrave;a B&aacute;o &Acirc;n, qu&yacute; kh&aacute;ch sẽ bắt gặp C&acirc;y Bồ đề c&oacute; tuổi đời 300 năm được đ&aacute;nh tận gốc từ Miến Điện về Việt Nam. C&acirc;y bồ đề tượng trưng cho sự tỉnh thức, ch&acirc;n l&yacute; th&ocirc;ng suốt v&agrave; sự gi&aacute;c ngộ. Ngo&agrave;i ra, c&acirc;y c&ograve;n tượng trưng cho sự may mắn, sự tốt l&agrave;nh, trừ t&agrave; ma v&agrave; thanh tẩy những &ocirc; uế, những g&igrave; kh&ocirc;ng tốt của bản th&acirc;n.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ở s&acirc;n Tam Bảo l&agrave; hai c&acirc;y thị - lo&agrave;i c&acirc;y gắn b&oacute; với sự t&iacute;ch Tấm c&aacute;m m&agrave; ai trong ch&uacute;ng ta cũng được nghe kể từ tấm b&eacute;. Đ&acirc;y l&agrave; lo&agrave;i c&acirc;y &iacute;t rụng l&aacute;, tuổi đời cao, ra quả cho m&ugrave;i thơm m&aacute;t, gi&uacute;p cảnh quan khu&ocirc;n vi&ecirc;n ch&ugrave;a được thơm l&agrave;nh, sạch sẽ.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xung quanh khu&ocirc;n vi&ecirc;n l&agrave; h&agrave;ng c&acirc;y l&ecirc; trắng, lo&agrave;i hoa n&agrave;y kh&ocirc;ng chỉ đẹp m&agrave; c&ograve;n mang &yacute; nghĩa phong thủy đặc biệt, biểu trưng cho sự thanh khiết, b&igrave;nh y&ecirc;n v&agrave; ấm &aacute;p trong cuộc sống.</span></p>\r\n\r\n<h2><span style=\"font-size:18px\"><strong>Qu&aacute; tr&igrave;nh thi c&ocirc;ng Ch&ugrave;a B&aacute;o &Acirc;n</strong></span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"Picture13\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/chùa báo ân/chùa báo ân 10.png\" title=\"Picture13\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">M&oacute;ng ch&ugrave;a được l&agrave;m bằng b&ecirc; t&ocirc;ng ki&ecirc;n cố, với c&aacute;c trụ th&eacute;p được &eacute;p chắc chắn</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"z4117216640181_762b6317695e4eaf8606aff84f218ae4\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/chùa báo ân/chùa báo ân 11.jpg\" title=\"z4117216640181_762b6317695e4eaf8606aff84f218ae4\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline&nbsp;<strong>0934.524.445</strong> hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\">https://nghiatrangthienduong.com.vn/</a></span></p>', 'true', 'News', 'chua-bao-an', 'chùa báo ân.png', 'Chùa Báo Ân tại Thiên Đường', 'Tọa lạc tại vị trí trung cung, đắc địa nhất của Công viên Nghĩa trang Thiên Đường với lối kiến trúc mang đậm nét chùa cổ Bắc Bộ, không gian nơi đây luôn tôn nghiêm, thanh tịnh, và linh thiêng.', '2023-11-09 02:15:10', '2023-12-20 04:38:36'),
(137, 2, 63, 'Địa thế phong thủy', 'Nguyên tắc chọn đất sinh phần hợp phong thủy.', NULL, '<h2><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#676767\">P</span></span></span></span></span></span><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#676767\">hong</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#676767\"> thủy đất sinh phần</span></span></span></span></span></span></h2>\r\n\r\n<h3><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">Nguy&ecirc;n</span></span></span></strong><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\"> tắc chọn đất sinh phần hợp phong thủy </span></span></span></strong></span></span></span></h3>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">C&oacute; thể khả định rằng phong thủy l&agrave; một phần kh&ocirc;ng thể t&aacute;ch rời trong văn h&oacute;a &Aacute; Đ&ocirc;ng n&oacute;i chung v&agrave; văn h&oacute;a Việt Nam n&oacute;i ri&ecirc;ng. Đặc biệt đối với phong thủy &acirc;m trạch, việc chăm lo cho mồ mả của &ocirc;ng b&agrave; tổ ti&ecirc;n, người th&acirc;n qu&aacute; cố l&agrave; v&ocirc; c&ugrave;ng quan trọng bởi đ&acirc;y kh&ocirc;ng chỉ li&ecirc;n quan đến việc bảo vệ linh hồn của người đ&atilde; khuất m&agrave; c&ograve;n tạo ra m&ocirc;i trường t&acirc;m linh an l&agrave;nh cho gia đ&igrave;nh v&agrave; c&aacute;c thế hệ về sau.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">Đối với vấn đề n&agrave;y c&aacute;c chuy&ecirc;n gia sau khi nghi&ecirc;n cứu kỹ lưỡng đ&atilde; cho rằng c&oacute; 2 yếu tố quan trọng m&agrave; ch&uacute;ng ta cần quan t&acirc;m đến đ&oacute; l&agrave; : &ldquo; Sơn quản nh&acirc;n đinh&rdquo; v&agrave; &ldquo; Thủy quản t&agrave;i lộc&rdquo;, để giải th&iacute;ch cho điều n&agrave;y c&aacute;c chuy&ecirc;n gia đ&atilde; giải đ&aacute;p rằng khi con ch&aacute;u ch&ocirc;n cất tổ ti&ecirc;n ở những v&ugrave;ng đất tốt th&igrave; nhờ nguy&ecirc;n l&yacute; đồng thanh tương ứng- đồng kh&iacute; tương cầu, kh&iacute; mạch tốt đ&oacute; sẽ dẫn truyền đến thế hệ con ch&aacute;u c&oacute; c&ugrave;ng con ch&aacute;u c&oacute; c&ugrave;ng ADN, c&ugrave;ng tinh kh&iacute; với người đ&atilde; mất.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">Kinh nghiệm để lựa chọn đất sinh phần sao cho hợp phong thủy để người đ&atilde; mất ấm &aacute;p nơi ch&iacute;n suối, người sống được thịnh vượng. Thứ nhất n&ecirc;n t&igrave;m đến nghĩa trang c&oacute; yếu tố đầu ti&ecirc;n l&agrave; sơn thủy hữu t&igrave;nh, huyệt c&oacute; hướng đẹp, ph&ugrave; hợp với gia tộc. Thứ hai l&agrave; mộ phần nằm trong khu vực đảm bảo ổn định, kh&ocirc;ng giải tỏa hay di rời.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">Đ&atilde; c&oacute; nhiều dẫn chứng thực tế l&agrave; nhiều gia đ&igrave;nh ra quyết định trong t&iacute;ch tắc trong bối cảnh chạy đ&ocirc;n chạy đ&aacute;o để t&igrave;m một mảnh đất ph&ugrave; hợp với tất cả ti&ecirc;u ch&iacute; tr&ecirc;n th&igrave; rất kh&oacute; vẹn to&agrave;n nhất l&agrave; khi tang gia bối rối hoặc gia đ&igrave;nh, người th&acirc;n đang ốm nặng. Cho n&ecirc;n thực tế, việc lựa chọn mua đất sinh phần từ trước gi&uacute;p gia chủ kh&ocirc;ng bị &aacute;p lực về t&agrave;i ch&iacute;nh b&ecirc;n cạnh đ&oacute; c&ograve;n c&oacute; thể lựa chọn kỹ c&agrave;ng. V&igrave; vậy việc mua đất sinh phần trước kh&ocirc;ng c&oacute; g&igrave; l&agrave; xui xẻo nhưng nhiều người quan niệm m&agrave; ngược lại c&ograve;n mang gi&aacute; trị nh&acirc;n văn tốt đẹp, thể hiện đạo hiếu vẹn tr&ograve;n của con ch&aacute;u với cha mẹ, &ocirc;ng b&agrave;, tổ ti&ecirc;n. </span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">Với <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n nghĩa trang Thi&ecirc;n Đường</a> ch&uacute;ng t&ocirc;i đ&aacute;p ứng để điều kiện m&agrave; qu&yacute; kh&aacute;ch đang t&igrave;m kiếm. C&ocirc;ng vi&ecirc;n nghĩa trang của ch&uacute;ng t&ocirc;i được x&acirc;y dựng tr&ecirc;n mảnh đất c&aacute;ch mạng Tuy&ecirc;n Quang, với khu đất tọa sơn hướng thủy v&agrave; m&ocirc;i trường t&acirc;m linh ph&aacute;t triển. </span></span></span></span></span></span></p>\r\n\r\n<h3><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">Thế đất tọa sơn hướng thủy</span></span></span></strong></span></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường được đặt tại x&atilde; Lang Qu&aacute;n, huyện Y&ecirc;n Sơn, tỉnh Tuy&ecirc;n Quang. Nơi đ&acirc;y được bao bọc bởi d&atilde;y n&uacute;i L&agrave; &ndash; một d&atilde;y n&uacute;i nổi tiếng của qu&ecirc; hương c&aacute;ch mạng. D&atilde;y n&uacute;i n&agrave;y gồm c&aacute;c ngọn n&uacute;i l&ecirc;n xuống tr&ugrave;ng tr&ugrave;ng, uốn lượn c&aacute; nhảy chim bay, độ dốc thoải, địa thế đất lớn v&agrave; rất vững ch&atilde;i. Khi quan s&aacute;t, c&oacute; thể thấy rằng c&aacute;c v&ugrave;ng đất được ngọn n&uacute;i n&agrave;y bao quanh lu&ocirc;n c&oacute; một trường sinh lực mạnh mẽ v&agrave; thế tụ kh&iacute; rất r&otilde; r&agrave;ng.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">Từ d&atilde;y n&uacute;i L&agrave;, d&ograve;ng suối ngọc hiền h&ograve;a uốn lượn chảy xuống thung lũng, mang theo linh kh&iacute; đất trời về với Thi&ecirc;n Đường. Theo ph&acirc;n t&iacute;ch của chuy&ecirc;n gia phong thủy th&igrave; địa thế &quot;thủy bao&quot; với d&ograve;ng suối bao bọc xung quanh c&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường uốn lượn v&agrave;o b&ecirc;n trong l&ograve;ng từng quả đồi tương tự như nguồn t&agrave;i lộc đi v&agrave;o b&ecirc;n trong v&agrave; được lưu giữ, mang đến nhiều t&agrave;i lộc v&agrave; vận may.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">Cha &ocirc;ng ta lu&ocirc;n quan niệm nơi n&agrave;o c&oacute; n&uacute;i bao v&ograve;ng che chở, d&ograve;ng nước uốn cong l&agrave; nơi tụ kh&iacute; tốt để x&acirc;y dựng nh&agrave; cửa. Phong thủy quan niệm nơi c&oacute; thủy tụ sẽ tụ kh&iacute;, m&agrave; tụ kh&iacute; th&igrave; tụ t&agrave;i, gi&uacute;p cho gia chủ hưng vượng, sức khỏe an khang. Nếu được cả thế đất c&oacute; sơn thủy bao bọc th&igrave; lại c&agrave;ng l&agrave; điềm đại c&aacute;t đại lợi. Những cuộc đất qu&yacute; hiếm n&agrave;y l&agrave; nơi phong t&agrave;ng kh&iacute; tụ, đem lại t&agrave;i lộc thịnh vượng với dấu hiệu dễ d&agrave;ng nhận biết l&agrave; kh&ocirc;ng kh&iacute; tho&aacute;ng đ&atilde;ng, trong l&agrave;nh, cỏ c&acirc;y bốn m&ugrave;a tươi tốt.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/z4739939583777_ed37970f5389ec145ff423bd67dab831.jpg\" style=\"height:1440px; width:2560px\" /></span></span></span></span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">B&ecirc;n</span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\"> cạnh ưu điểm l&agrave; thế đất tọa sơn hướng thủy ở C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường thậm ch&iacute; c&ograve;n mang th&ecirc;m d&aacute;ng h&igrave;nh Linh Quy. </span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">Ở lĩnh vực&nbsp;</span></span></span></span><span style=\"color:black\"><a href=\"https://vi.wikipedia.org/wiki/T%C3%A2m_linh\" style=\"color:blue; text-decoration:underline\" title=\"Tâm linh\"><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">t&acirc;m linh</span></span></span></span></a></span><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">,&nbsp;</span></span></span></span><span style=\"color:black\"><a href=\"https://vi.wikipedia.org/wiki/Ng%C6%B0%E1%BB%9Di_Vi%E1%BB%87t\" style=\"color:blue; text-decoration:underline\" title=\"Người Việt\"><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">người Việt</span></span></span></span></a></span><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">&nbsp;coi Quy l&agrave; vật hợp bởi cả&nbsp;</span></span></span></span><span style=\"color:black\"><a href=\"https://vi.wikipedia.org/wiki/%C3%82m\" style=\"color:blue; text-decoration:underline\" title=\"Âm\"><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">&acirc;m</span></span></span></span></a></span><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">&nbsp;lẫn&nbsp;</span></span></span></span><span style=\"color:black\"><a href=\"https://vi.wikipedia.org/wiki/D%C6%B0%C6%A1ng\" style=\"color:blue; text-decoration:underline\" title=\"Dương\"><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">dương</span></span></span></span></a></span><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">: bụng phẳng tượng trưng cho&nbsp;</span></span></span></span><span style=\"color:black\"><a href=\"https://vi.wikipedia.org/wiki/%C4%90%E1%BA%A5t\" style=\"color:blue; text-decoration:underline\" title=\"Đất\"><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">đất</span></span></span></span></a></span><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">&nbsp;(&acirc;m), mai khum tượng trưng cho&nbsp;</span></span></span></span><span style=\"color:black\"><a href=\"https://vi.wikipedia.org/wiki/Tr%E1%BB%9Di\" style=\"color:blue; text-decoration:underline\" title=\"Trời\"><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">trời</span></span></span></span></a></span><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">&nbsp;(dương). H&igrave;nh tượng r&ugrave;a đội bia tượng trưng cho hạnh ph&uacute;c, ph&aacute;t triển v&agrave; thịnh</span></span></span></span><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\"> vượng. </span></span></span></span></span></span></span></p>\r\n\r\n<h3><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">D&aacute;ng h&igrave;nh Linh Quy</span></span></span></strong><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">: </span></span></span></strong></span></span></span></h3>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</span></span></span> <span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">nằm tr&ecirc;n khu đất c&oacute; d&aacute;ng h&igrave;nh LINH QUY biểu hiện sự th&ocirc;ng minh,mẫn c&aacute;n v&agrave; trường thọ. Dải đất n&agrave;y c&oacute; mạch chạy theo hướng T&acirc;y Bắc - Đ&ocirc;ng Nam. Thế đất n&agrave;y theo Phong Thủy l&agrave; Vượng sơn Vượng hướng, c&oacute; địa vận 160 năm, đem lại Ph&uacute;c Lộc đời đời cho&nbsp;con ch&aacute;u. Nơi đ&acirc;y c&oacute; nhiều c&ocirc;ng tr&igrave;nh kiến tr&uacute;c t&acirc;m linh, tiểu cảnh c&acirc;y xanh, hoa cỏ,... tạo n&ecirc;n sự hội tụ giao cảm h&agrave;i h&ograve;a giữa người trần thế v&agrave; người đ&atilde; khuất, đ&oacute;n nhận &quot;kh&iacute; thi&ecirc;ng&quot; đất trời.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/Picture1.jpg\" style=\"height:833px; width:1429px\" /></span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">Th&ecirc;m</span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\"> v&agrave;o một yếu tố để gi&uacute;p mọi người c&oacute; thể tin tưởng th&ecirc;m m&agrave; lựa chọn c&ocirc;ng vi&ecirc;n nghĩa trang Thi&ecirc;n Đường l&agrave;m nơi gửi gắm mộ phần của gia đ&igrave;nh nữa l&agrave; nơi đ&acirc;y nằm tr&ecirc;n v&ugrave;ng đất Tuy&ecirc;n Quang cực k&igrave; t&acirc;m linh, nơi đ&acirc;y gắn liền với rất nhiều sự t&iacute;ch linh thi&ecirc;ng v&agrave; đồng thời cũng l&agrave; qu&ecirc; hương của c&aacute;ch mạng</span></span></span></span></span></span></p>\r\n\r\n<h3><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">V&ugrave;ng đất linh thi&ecirc;ng</span></span></span></strong><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">: </span></span></span></strong></span></span></span></h3>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">Thư tịch cổ đ&atilde; ch&eacute;p về Tuy&ecirc;n Quang xưa: &ldquo;Đ&acirc;y l&agrave; xứ sơn kỳ thủy t&uacute;, vạn vật tốt tươi, mặt người tươi đẹp, ruộng nhiều l&uacute;a ch&iacute;n, đất nở đầy hoa, chim k&ecirc;u vượn h&oacute;t, thuốc qu&yacute; tiềm t&agrave;ng, sơn h&agrave;o v&ocirc; tận, kh&ocirc;ng thứ g&igrave; l&agrave; kh&ocirc;ng c&oacute;..., xem như nơi đ&acirc;y c&oacute; nguồn thi&ecirc;ng hội tụ, vui nước an d&acirc;n, hợp đức th&aacute;nh nh&acirc;n&rdquo;.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">Tuy&ecirc;n</span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\"> Quang đ&atilde; được coi l&agrave; v&ugrave;ng đất t&acirc;m linh từ những ng&agrave;y xưa, từ c&aacute;c truyền thuyết m&agrave; d&acirc;n gian tương truyền. </span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">Tr&ecirc;n địa b&agrave;n th&agrave;nh phố Tuy&ecirc;n Quang hiện c&oacute; 18 đền, ch&ugrave;a xưa nay được cho l&agrave; nổi tiếng linh thi&ecirc;ng trong đ&oacute; c&oacute; 12 ng&ocirc;i đền thờ Mẫu thần được x&acirc;y dựng c&aacute;ch đ&acirc;y v&agrave;i trăm năm. Cũng ch&iacute;nh bởi hệ thống c&aacute;c đền thờ mẫu kh&aacute; d&agrave;y m&agrave; Tuy&ecirc;n Quang c&ograve;n được coi như v&ugrave;ng đất mẫu.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\"><img alt=\"\" src=\"GHBJ\" /><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/z4899209475137_a4b75abf2f79a071f3c91e286cfa3133.jpg\" style=\"height:401px; width:600px\" /></span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:14.0pt\"><span style=\"color:#1d1919\">Nhắc đến xứ Tuy&ecirc;n chắc hẳn những người theo đạo Mẫu sẽ biết rất r&otilde; về <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/truyen-thuyet-mau-thoai-tuyen-quang\">truyền thuyết Mẫu Thoải. </a></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">Ngo&agrave;i</span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\"> những sự truyền thuyết về những thần th&aacute;nh của Tuy&ecirc;n Quang th&igrave; v&ugrave;ng đất n&agrave;y c&ograve;n c&oacute; th&ecirc;m danh xưng l&agrave; qu&ecirc; hương c&aacute;ch mạng. Sở dĩ được mọi người gọi với c&aacute;i t&ecirc;n như vậy l&agrave; do Tuy&ecirc;n Quang từ những ng&agrave;y kh&aacute;ng chiến đ&atilde; đem lại nhiều thắng lợi cho d&acirc;n tộc ta, thậm ch&iacute; khi đặt ch&acirc;n đến đ&acirc;y cụ Hồ v&agrave; những người l&iacute;nh bộ đội dường như được linh kh&iacute; của đất trời nơi đ&acirc;y che chở v&agrave; bảo vệ th&ecirc;m phần n&agrave;o. </span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:14.0pt\"><span style=\"color:#1d1919\">Để m&agrave; n&oacute;i tới th&igrave; kh&ocirc;ng thể n&agrave;o kh&ocirc;ng nhắc đến t&iacute;ch c&acirc;y đa T&acirc;n Tr&agrave;o. </span></span><strong><span style=\"font-size:14.0pt\"><span style=\"color:#222222\">C&acirc;y đa T&acirc;n Tr&agrave;o</span></span></strong><span style=\"font-size:14.0pt\"><span style=\"color:#222222\">&nbsp;c&ugrave;ng khu di t&iacute;ch lịch sử T&acirc;n Tr&agrave;o từ l&acirc;u đ&atilde; trở th&agrave;nh điểm đến hấp dẫn đối với du kh&aacute;ch nếu c&oacute; dịp gh&eacute; thăm Tuy&ecirc;n Quang. Nơi đ&acirc;y ch&iacute;nh l&agrave; nơi lưu giữ lại&nbsp;những sự kiện lịch sử h&agrave;o h&ugrave;ng của ng&agrave;y đầu th&agrave;nh lập nước Việt Nam D&acirc;n chủ Cộng ho&agrave;.</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:14.0pt\"><span style=\"color:#222222\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/Picture2.jpg\" style=\"height:250px; width:468px\" /></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:14.0pt\"><span style=\"color:#222222\">Trong suốt 9 năm chiến đấu trường kỳ l&aacute;n N&agrave; Lừa, c&acirc;y đa T&acirc;n Tr&agrave;o, đ&igrave;nh T&acirc;n Tr&agrave;o, đ&igrave;nh Hồng Th&aacute;i&hellip; l&agrave; nơi phục vụ c&aacute;ch mạng mang đậm dấu ấn lịch sử.</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:14.0pt\"><span style=\"color:#222222\">Gắn liền với mốc son lịch sử d&acirc;n tộc, dưới gốc đa T&acirc;n Tr&agrave;o chiều ng&agrave;y 16 th&aacute;ng 8 năm 1945, sau khi&nbsp;Đại tướng V&otilde; Nguyễn Gi&aacute;p đọc bản Qu&acirc;n lệnh số 1; qu&acirc;n đội Việt Nam Giải ph&oacute;ng đ&atilde; l&agrave;m lễ xuất qu&acirc;n tiến về giải ph&oacute;ng Thủ đ&ocirc; H&agrave; Nội trước sự chứng kiến của to&agrave;n thể đồng&nbsp;b&agrave;o&nbsp;c&aacute;c d&acirc;n tộc Việt Bắc c&ugrave;ng 60 đại biểu to&agrave;n quốc.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">Việc lựa chọn c&acirc;y đa l&agrave; nơi tập hợp mọi người để đọc bản Qu&acirc;n lệnh dường như cũng mang phần n&agrave;o yếu tố t&acirc;m linh, thể hiện sự vững ch&atilde;i trong qu&acirc;n đội Việt Nam v&agrave; sự l&acirc;u d&agrave;i cho thắng lợi.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">Qua những sự t&iacute;ch tr&ecirc;n c&agrave;ng minh chứng th&ecirc;m phần n&agrave;o sự linh thi&ecirc;ng của xứ Tuy&ecirc;n. Với ưu điểm vượt trội l&agrave; n</span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#1d1919\">ằm giữa miền đất mẫu thượng ng&agrave;n, C&ocirc;ng vi&ecirc;n Nghĩa trang <a href=\"https://nghiatrangthienduong.com.vn/\">Thi&ecirc;n Đường </a>được khu&ocirc;ng ph&ugrave;, bảo vệ, mang lại ph&uacute;c lộc mu&ocirc;n đời.</span></span></span> </span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>', 'true', 'News', 'dia-the-phong-thuy', '83_z4739939583777_ed37970f5389ec145ff423bd67dab831.jpg', 'Phong thủy đất sinh phần- tầm quan trọng', 'Có thể khả định rằng phong thủy là một phần không thể tách rời trong văn hóa Á Đông nói chung và văn hóa Việt Nam nói riêng.', '2023-11-30 16:02:41', '2023-12-06 08:57:29');
INSERT INTO `posts` (`id`, `user_id`, `category_id`, `name`, `detail`, `info`, `content`, `status`, `sort_by`, `slug`, `img`, `title`, `description`, `created_at`, `updated_at`) VALUES
(138, 2, 63, 'Kiến trúc cảnh quan', 'Tọa lạc tại Xã Lang Quán, huyện Yên Sơn, tỉnh Tuyên Quang, Công viên Nghĩa trang Thiên Đường với thiết kế đạt tiêu chuẩn hòa hợp các yếu tố cảnh quan và môi trường. Nơi đây sở hữu không gian trầm lắng, thanh tịnh, bình yên cho những người đã an nghỉ lẫn gia quyến đến viếng thăm.', NULL, '<h2><span style=\"font-size:20px\"><strong><span style=\"font-family:Times New Roman,Times,serif\">Kiến tr&uacute;c cảnh quan</span></strong></span></h2>\r\n\r\n<p><strong><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Tọa lạc tại X&atilde; Lang Qu&aacute;n, huyện Y&ecirc;n Sơn, tỉnh Tuy&ecirc;n Quang, <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</a> với thiết kế đạt ti&ecirc;u chuẩn h&ograve;a hợp c&aacute;c yếu tố cảnh quan v&agrave; m&ocirc;i trường. Nơi đ&acirc;y sở hữu kh&ocirc;ng gian trầm lắng, thanh tịnh, b&igrave;nh y&ecirc;n cho những người đ&atilde; an nghỉ lẫn gia quyến đến viếng thăm.</span></span></strong></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/Picture4.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường kh&ocirc;ng chỉ c&oacute; vị tr&iacute; phong thủy vượng sơn vượng hướng m&agrave; c&ograve;n được bao phủ bởi thi&ecirc;n nhi&ecirc;n trong l&agrave;nh, kh&ocirc;ng gian thanh tịnh, đất trời linh thi&ecirc;ng. M&ocirc;i trường v&agrave; cảnh quan thi&ecirc;n nhi&ecirc;n tại đ&acirc;y lu&ocirc;n được ch&uacute; trọng bảo tồn v&agrave; ph&aacute;t triển nhằm bảo đảm Thi&ecirc;n Đường lu&ocirc;n l&agrave; điểm đến sinh th&aacute;i - t&acirc;m linh y&ecirc;n b&igrave;nh, thanh tịnh.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">C&ocirc;ng vi&ecirc;n&nbsp;<a href=\"https://nghiatrangthienduong.com.vn\">Nghĩa trang&nbsp;Thi&ecirc;n Đường</a> sở hữu vẻ đẹp của kiến tr&uacute;c &Aacute; Đ&ocirc;ng h&agrave;i h&ograve;a c&ugrave;ng thi&ecirc;n nhi&ecirc;n. Cảnh quan c&acirc;y xanh chiếm 65%, c&ocirc;ng tr&igrave;nh hạ tầng kỹ thuật v&agrave; dịch vụ tiện &iacute;ch chi&ecirc;́m 28%, diện t&iacute;ch x&acirc;y dựng c&aacute;t t&aacute;ng (kh&ocirc;ng c&oacute; hung t&aacute;ng) chỉ chiếm 7%.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/z4899209375565_36cb4f76e8a4d97ff727969405e11d48.jpg\" /><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<em> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;M&agrave;u xanh mướt tại C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</em></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Tại c&ocirc;ng vi&ecirc;n, ch&uacute;ng t&ocirc;i ch&uacute; trọng đến việc trồng v&agrave; chăm s&oacute;c cỏ để đảm bảo đ&acirc;y sẽ l&agrave; một kh&ocirc;ng gian xanh mướt, y&ecirc;n b&igrave;nh. Sau khi khảo s&aacute;t nhiều nơi, chủ đầu tư quyết định sử dụng cỏ Mỹ để phủ xanh c&ocirc;ng vi&ecirc;n. Loại cỏ n&agrave;y được ưa chuộng trong thiết kế s&acirc;n vườn biệt thự nhờ h&igrave;nh d&aacute;ng đẹp, thướt tha c&ugrave;ng m&agrave;u l&aacute; xanh đậm v&ocirc; c&ugrave;ng bắt mắt. Cỏ Mỹ c&oacute; chiều cao trung b&igrave;nh từ 20 &ndash; 37cm. L&aacute; mọc thẳng từ gốc v&agrave; tạo h&igrave;nh v&ograve;m ph&iacute;a tr&ecirc;n, tỏa ra xung quanh nh&igrave;n cực kỳ đẹp v&agrave; thu h&uacute;t. Cỏ mọc th&agrave;nh từng cụm với những ch&ugrave;m l&aacute; m&agrave;u xanh đậm v&ocirc; c&ugrave;ng bắt mắt. D&ugrave; mọc rất nhiều nhưng lại rất mịn khi sờ v&agrave;o cảm gi&aacute;c như sờ v&agrave;o vải lụa. Đặc biệt đến m&ugrave;a H&egrave; khi hoa nở, những ch&ugrave;m hoa trắng xinh đẹp c&ugrave;ng sắc xanh đậm của l&aacute; gi&uacute;p cho cảnh quan th&ecirc;m sinh động, tạo cảm gi&aacute;c tươi m&aacute;t, gần gũi với thi&ecirc;n nhi&ecirc;n. Bước ch&acirc;n v&agrave;o c&ocirc;ng vi&ecirc;n m&agrave;u cỏ xanh mướt điểm xuyến l&agrave; những b&ocirc;ng hoa trắng muốt, kết hợp với m&agrave;u trong xanh của bầu trời đ&atilde; tạo ngay cho du kh&aacute;ch cảm gi&aacute;c thoải m&aacute;i, th&acirc;n thuộc v&agrave; b&igrave;nh y&ecirc;n khi tới đ&acirc;y. Thậm ch&iacute; c&ograve;n ph&ugrave; hợp với c&aacute;c hộ gia đ&igrave;nh dẫn con c&aacute;i đến thăm viếng &ocirc;ng b&agrave; tổ ti&ecirc;n v&igrave; độ mịn của cỏ ph&ugrave; hợp cho việc trẻ em chơi đ&ugrave;a tr&ecirc;n đ&oacute;.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/Picture5.jpg\" /></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<em> Cận cảnh cỏ Mỹ tại C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</em></span></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Ngo&agrave;i ra, <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang</a> Thi&ecirc;n Đường được bao phủ bởi 500 c&acirc;y l&ecirc; trắng, được đ&aacute;nh tận gốc từ những c&aacute;nh rừng gi&agrave; Điện Bi&ecirc;n. Vẻ đẹp của hoa l&ecirc; trắng c&oacute; một sức quyến rũ lạ kỳ, đủ để l&agrave;m bất cứ ai đắm say trước sự tinh khiết của ch&iacute;nh m&igrave;nh. C&ugrave;ng với sự quyến rũ v&agrave; nhẹ nh&agrave;ng vốn c&oacute;, đ&acirc;y được coi l&agrave; biểu tượng của sự y&ecirc;n tĩnh, thanh b&igrave;nh ở trong cuộc sống của con người, khiến cho bất k&igrave; du kh&aacute;ch n&agrave;o đến với C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường đều sẽ cảm thấy b&igrave;nh y&ecirc;n, thanh thản.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/thien_duong_19.05_4.jpg\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<em> &nbsp; &nbsp; H&agrave;ng l&ecirc; trắng được tr&ocirc;ng dọc theo đường đi&nbsp;</em></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/z4899209272065_366c51d97b847c8d726294b20fd58a73.jpg\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <em>Hoa l&ecirc; trắng khi nở&nbsp;</em></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường cũng được điểm xuyết bởi rất nhiều lo&agrave;i c&acirc;y cổ thụ, c&oacute; tuổi đời đến h&agrave;ng trăm năm, c&oacute; thể kể đến c&acirc;y muỗm với 200 năm tuổi đời, hay 2 c&acirc;y đại 300 năm tuổi được đ&aacute;nh từ Chăm Pa. Đ&acirc;y đều l&agrave; những giống c&acirc;y l&acirc;u năm, &iacute;t rụng l&aacute;, xanh mướt quanh năm, đảm bảo cho C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang <a href=\"https://nghiatrangthienduong.com.vn/\">Thi&ecirc;n Đường</a> lu&ocirc;n xanh m&aacute;t.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/z4899209224895_7c89f6a8c58eb5e4f8bc277d630503c1.jpg\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<em>C&acirc;y Muỗm 200 năm tuổi đời tại Thi&ecirc;n Đường</em></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Quả thật, C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường với&nbsp;dấu ấn kiến tr&uacute;c ri&ecirc;ng biệt kết hợp với kh&ocirc;ng gian xanh m&aacute;t&nbsp;sẽ kh&ocirc;ng khiến ai thất vọng khi gh&eacute; thăm nơi đ&acirc;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Xem th&ecirc;m <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/dia-the-phong-thuy\">: Địa thế phong thủy</a></span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>', 'true', 'News', 'kien-truc-canh-quan', 'Picture1.jpg', 'Kiến trúc cảnh quan tại công viên thiên đường - Tuyên Quang', 'Công viên Nghĩa trang Thiên Đường không chỉ có vị trí phong thủy vượng sơn vượng hướng mà còn được bao phủ bởi thiên nhiên trong lành, không gian thanh tịnh, đất trời linh thiêng', '2023-12-04 07:09:11', '2023-12-06 13:56:17'),
(139, 2, 64, 'Khuôn viên Đơn', 'Khuôn viên đơn tại Công viên Nghĩa trang Thiên Đường với diện tích 3.5m2 được thiết kế trang trọng, hài hòa, cùng cảnh sắc thiên nhiên tươi đẹp, không gian yên tĩnh, bình dị. Nơi đây sẽ là nơi xoa dịu trái tim, an nghỉ vĩnh hằng dành cho người thân đã khuất.', '<p>Chỉ từ: <strong>64&nbsp;triệu đồng/ng&ocirc;i</strong></p>\r\n\r\n<p>K&iacute;ch thước: 3,5m2<br />\r\n<strong>B&agrave;n giao ho&agrave;n thiện to&agrave;n bộ</strong></p>\r\n\r\n<p>Ph&aacute;p l&yacute; đầy đủ: <strong>Hợp đồng dịch vụ v&agrave; Cấp GCNQSD phần mộ</strong></p>', '<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"color:#1d1919\">Khu&ocirc;n vi&ecirc;n đơn tại <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</a> với diện t&iacute;ch 3,5m2 được thiết kế trang trọng, h&agrave;i h&ograve;a, c&ugrave;ng cảnh sắc thi&ecirc;n nhi&ecirc;n tươi đẹp, kh&ocirc;ng gian y&ecirc;n tĩnh, b&igrave;nh dị. Nơi đ&acirc;y sẽ l&agrave; nơi xoa dịu tr&aacute;i tim, an nghỉ vĩnh hằng d&agrave;nh cho người th&acirc;n đ&atilde; khuất.</span></strong></span></span></p>\r\n\r\n<h2><span style=\"font-size:18pt\"><span style=\"background-color:white\"><strong><strong><span style=\"font-size:16.5pt\"><span style=\"color:#1d1919\">Mộ đơn l&agrave; g&igrave;?</span></span></strong></strong></span></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\">Mộ đơn l&agrave; những khu&ocirc;n vi&ecirc;n mộ được quy hoạch theo từng khu vực nhất định, c&oacute; diện t&iacute;ch vừa phải nhằm đ&aacute;p ứng nhu cầu&nbsp;của c&aacute;c c&aacute; nh&acirc;n, gia đ&igrave;nh.</span></span></span></p>\r\n\r\n<h2><span style=\"font-size:18pt\"><span style=\"background-color:white\"><strong><strong><span style=\"font-size:16.5pt\"><span style=\"color:#1d1919\">Khu&ocirc;n vi&ecirc;n mộ đơn tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</span></span></strong></strong></span></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\">Với truyền thống &ldquo;uống nước nhớ nguồn&rdquo;, d&acirc;n tộc ta lu&ocirc;n coi trọng đạo hiếu thờ c&uacute;ng &ocirc;ng b&agrave;, tổ ti&ecirc;n. V&igrave; thế, khi người th&acirc;n nhắm mắt xu&ocirc;i tay, con ch&aacute;u đều mong muốn x&acirc;y &ldquo;mồ y&ecirc;n mả đẹp&rdquo;. Ng&ocirc;i mộ thể hiện l&ograve;ng hiểu thảo của con ch&aacute;u với gia ti&ecirc;n v&agrave; người th&acirc;n đ&atilde; khuất nhằm mang đến may mắn, t&agrave;i lộc cho c&aacute;c thế hệ sau.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\">Khu&ocirc;n vi&ecirc;n mộ đơn tại <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường</a> l&agrave; nơi an nghỉ ho&agrave;n hảo của người th&acirc;n nhờ đ&aacute;p ứng đầy đủ c&aacute;c ti&ecirc;u ch&iacute;:</span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên đơn/đơn.jpg\" /></span></span></span><span style=\"font-size:16px\"><em>Mộ đơn c&oacute; diện t&iacute;ch tối ưu 3,5m2</em></span></p>\r\n\r\n<h3><span style=\"font-size:13.5pt\"><span style=\"background-color:white\"><strong><em><span style=\"font-size:15.0pt\"><span style=\"color:#1d1919\">Diện t&iacute;ch</span></span></em></strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\">Khu&ocirc;n vi&ecirc;n đơn sở hữu diện t&iacute;ch tối ưu 3,5m2, bao gồm c&aacute;c hạng mục: Diện t&iacute;ch phần mộ, diện t&iacute;ch đường giao th&ocirc;ng v&agrave; h&agrave;ng r&agrave;o c&acirc;y xanh.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên đơn/đơn 1.jpg\" /></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><em><span style=\"color:#1d1919\">Xem th&ecirc;m:<a href=\"https://nghiatrangthienduong.com.vn/san-pham/khuon-vien-gia-dinh\"> Khu&ocirc;n vi&ecirc;n gia đ&igrave;nh&nbsp;</a></span></em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:13.5pt\"><span style=\"background-color:white\"><strong><em><span style=\"font-size:15.0pt\"><span style=\"color:#1d1919\">Phong thủy</span></span></em></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\">Khu&ocirc;n vi&ecirc;n mộ đơn tại C&ocirc;ng vi&ecirc;n Nghĩa trang <a href=\"https://nghiatrangthienduong.com.vn/\">Thi&ecirc;n Đường</a> được thiết kế hợp phong thủy, thể hiện r&otilde; ở c&aacute;c yếu tố: Hướng mộ, địa điểm đặt mộ, chất đất, khoảng c&aacute;ch giữa c&aacute;c mộ, cũng như bố tr&iacute; cảnh quan xung quanh để tạo sự c&acirc;n bằng sinh th&aacute;i.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên đơn/đơn 2.jpg\" /></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\">Đặc biệt, điểm nhấn của khu&ocirc;n vi&ecirc;n mộ đơn tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường ch&iacute;nh l&agrave; sự đa dạng về hướng, view nội khu v&agrave; cảnh quan đẹp nằm trong bố cục cảnh quan chung của to&agrave;n C&ocirc;ng vi&ecirc;n. Khu&ocirc;n vi&ecirc;n mộ đơn được sắp xếp tối ưu, ph&ugrave; hợp với diện t&iacute;ch sử dụng, đảm bảo phong thủy v&agrave; yếu tố thẩm mỹ.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên đơn/đơn 3.jpg\" /></span></span></span></p>\r\n\r\n<h3><span style=\"font-size:13.5pt\"><span style=\"background-color:white\"><strong><em><span style=\"font-size:15.0pt\"><span style=\"color:#1d1919\">Thiết kế</span></span></em></strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\">Dưới b&agrave;n tay kh&eacute;o l&eacute;o v&agrave; t&agrave;i hoa của đội ngũ kỹ sư gi&agrave;u kinh nghiệm, khu&ocirc;n vi&ecirc;n mộ đơn tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường được thiết kế tinh tế, tỉ mỉ tới từng chi tiết nhỏ nhất. Nơi đ&acirc;y kh&ocirc;ng chỉ l&agrave; nơi an nghỉ ho&agrave;n hảo cho người th&acirc;n m&agrave; c&ograve;n l&agrave; nơi để con ch&aacute;u lưu giữ những kỷ niệm, di sản của người đ&atilde; khuất.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên đơn/đơn 4.jpg\" /></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:13.5pt\"><span style=\"background-color:white\"><strong><em><span style=\"font-size:15.0pt\"><span style=\"color:#1d1919\">Kiến tr&uacute;c</span></span></em></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\">Kho&aacute;c l&ecirc;n m&igrave;nh vẻ đẹp y&ecirc;n b&igrave;nh v&agrave; t&ocirc;n nghi&ecirc;m, khu&ocirc;n vi&ecirc;n mộ đơn được thiết kế mang theo hơi thở của phong c&aacute;ch kiến tr&uacute;c &Aacute; Đ&ocirc;ng. Khu&ocirc;n vi&ecirc;n mộ phần được thiết kế h&agrave;i h&ograve;a với thi&ecirc;n nhi&ecirc;n tạo n&ecirc;n điểm nhấn tinh tế trong kh&ocirc;ng gian.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên đơn/đơn 5.jpg\" /></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\">Từng đường n&eacute;t tr&ecirc;n bề mặt khu mộ tỏa ra vẻ đẹp thanh nh&atilde; v&agrave; truyền thống, tạo n&ecirc;n kh&ocirc;ng gian tĩnh lặng v&agrave; y&ecirc;n b&igrave;nh.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên đơn/đơn 6.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><em><span style=\"color:#1d1919\">Khu&ocirc;n vi&ecirc;n mộ đơn tr&agrave;n ngập m&agrave;u sắc của cỏ c&acirc;y, hoa l&aacute;</span></em></span></span></p>\r\n\r\n<h3><span style=\"font-size:13.5pt\"><span style=\"background-color:white\"><strong><em><span style=\"font-size:15.0pt\"><span style=\"color:#1d1919\">Cảnh quan</span></span></em></strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\">Để tăng sinh kh&iacute; cho khu mộ đơn, Chủ đầu tư lựa chọn c&aacute;c loại c&acirc;y: T&ugrave;ng la h&aacute;n, nguyệt quế, mai vạn ph&uacute;c&hellip; M&agrave;u xanh của cỏ c&acirc;y, hoa l&aacute; kh&ocirc;ng chỉ l&agrave;m đẹp cảnh quan khu mộ m&agrave; c&ograve;n tạo cảm gi&aacute;c b&igrave;nh y&ecirc;n cho c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh mỗi khi viếng thăm.</span></span></span></p>\r\n\r\n<h3><span style=\"font-size:13.5pt\"><span style=\"background-color:white\"><strong><em><span style=\"font-size:15.0pt\"><span style=\"color:#1d1919\">Vật liệu</span></span></em></strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\">Đ&aacute; trắng B&igrave;nh Định, đ&aacute; trắng Xu&acirc;n Sơn, đ&aacute; x&aacute;m Gia Lai l&agrave; ba chất liệu đ&aacute; tự nhi&ecirc;n cao cấp được lựa chọn để thi c&ocirc;ng khu mộ đơn. Ưu điểm của c&aacute;c loại đ&aacute; n&agrave;y phải kể đến khả năng chịu m&agrave;i m&ograve;n, chịu nước v&agrave; va đập tốt. Ngo&agrave;i ra, c&aacute;c loại đ&aacute; n&agrave;y c&ograve;n l&agrave;m t&ocirc;n l&ecirc;n n&eacute;t sang trọng của khu mộ.</span></span></span></p>\r\n\r\n<h2><span style=\"font-size:18pt\"><span style=\"background-color:white\"><strong><strong><span style=\"font-size:16.5pt\"><span style=\"color:#1d1919\">V&igrave; sao n&ecirc;n chọn khu&ocirc;n vi&ecirc;n mộ đơn tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường?</span></span></strong></strong></span></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\"><a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang</a> Thi&ecirc;n Đường được quy hoạch đồng bộ, ph&acirc;n l&ocirc; giữa c&aacute;c phần mộ tiện dụng, đồng thời c&oacute; diện t&iacute;ch rộng r&atilde;i, l&agrave; địa chỉ tin cậy để Q&uacute;y kh&aacute;ch mua đất x&acirc;y dựng mộ gia đ&igrave;nh cho người th&acirc;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\">+ Ph&aacute;p l&yacute; minh bạch, sở hữu l&acirc;u d&agrave;i: To&agrave;n bộ khu mộ đơn tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường sở hữu quyền sử dụng đất l&acirc;u d&agrave;i, từng mộ phần đều được cấp giấy chứng nhận quyền sử dụng mộ phần.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\">+ <a href=\"https://nghiatrangthienduong.com.vn/tien-ich\">Tiện &iacute;ch</a> đẳng cấp: Đến với C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường, Q&uacute;y kh&aacute;ch sẽ được tận hưởng trọn vẹn c&aacute;c tiện &iacute;ch đẳng cấp nhất: B&atilde;i đỗ xe v&agrave; xe điện miễn ph&iacute;; Nh&agrave; h&agrave;ng đặt cỗ chay/mặn theo nhu cầu; Quầy h&agrave;ng b&aacute;n đồ v&agrave;ng m&atilde;, hương hoa cho kh&aacute;ch; Khu nghỉ ch&acirc;n, ăn uống ngo&agrave;i trời; Quầy nước lưu động; Khu vui chơi trẻ em; Ch&ograve;i nghỉ ch&acirc;n; Am h&oacute;a v&agrave;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\">+ <a href=\"https://nghiatrangthienduong.com.vn/dich-vu\">Dịch vụ</a> ho&agrave;n hảo: Mong muốn mang đến cho kh&aacute;ch h&agrave;ng những khoảnh khắc trải nghiệm ho&agrave;n hảo, đội ngũ nh&acirc;n sự tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường được đ&agrave;o tạo b&agrave;i bản, chuy&ecirc;n nghiệp với sự tận t&acirc;m v&agrave; tinh tế. Tại đ&acirc;y, Ch&uacute;ng t&ocirc;i đảm bảo cung cấp đầy đủ c&aacute;c dịch vụ hiện đại v&agrave; cao cấp bậc nhất Việt Nam như: Chăm s&oacute;c mộ phần v&agrave; cảnh quan; X&acirc;y dựng mộ phần; Tư vấn v&agrave; tổ chức tang lễ trọn g&oacute;i; Tư vấn phong thủy; C&uacute;ng giỗ online; Hỗ trợ ăn uống, lưu tr&uacute;; X&acirc;y dựng gia phả; Dịch vụ an ninh; Dịch vụ lập kế hoạch trước; Dịch vụ hỏa t&aacute;ng.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#1d1919\">+ Gi&aacute; th&agrave;nh hợp l&yacute;: Khu mộ đơn tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường c&oacute; mức gi&aacute; v&ocirc; c&ugrave;ng hợp l&yacute;. </span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>', 'true', 'News', 'khuon-vien-don', 'Mo don 4.jpg', 'Khuôn viên đơn- sản phẩm của công viên nghĩa trang Thiên Đường', 'Khuôn viên đơn tại Công viên Nghĩa trang Thiên Đường với diện tích 3,5m2 được thiết kế trang trọng, hài hòa, cùng cảnh sắc thiên nhiên tươi đẹp, không gian yên tĩnh, bình dị.', '2023-12-05 11:16:18', '2023-12-22 06:56:22'),
(141, 2, 64, 'Khuôn viên Tròn', 'Khuôn viên tròn tại Công viên Nghĩa trang Thiên Đường được thiết kế gồm những khuôn viên đơn sắp xếp hài hòa cạnh nhau tạo nên không gian đoàn tụ ấm cúng cho gia đình, họ tộc.', '<p>Chỉ từ:&nbsp;<strong>68 TRIỆU</strong></p>\r\n\r\n<p>K&iacute;ch thước: từ 3,5m2 được x&acirc;y dựng theo nhu cầu ch&ocirc;n cất c&aacute; nh&acirc;n</p>\r\n\r\n<p>Đ&atilde; đưa v&agrave;o sử dụng</p>\r\n\r\n<p>Ph&aacute;p l&yacute; r&otilde; r&agrave;ng</p>', '<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-family:&quot;san-serif&quot;,serif\"><span style=\"color:#1d1919\">Khu&ocirc;n vi&ecirc;n tr&ograve;n tại <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường </a>được thiết kế gồm những khu&ocirc;n vi&ecirc;n đơn sắp xếp h&agrave;i h&ograve;a cạnh nhau tạo n&ecirc;n kh&ocirc;ng gian đo&agrave;n tụ ấm c&uacute;ng cho gia đ&igrave;nh, họ tộc. Đ&acirc;y l&agrave; nơi thể hiện l&ograve;ng hiếu thảo của con ch&aacute;u với gia ti&ecirc;n v&agrave; người th&acirc;n đ&atilde; khuất.</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;san-serif&quot;,serif\"><span style=\"color:#1d1919\">Với truyền thống &ldquo;uống nước nhớ nguồn&rdquo;, d&acirc;n tộc ta lu&ocirc;n coi trọng đạo hiếu thờ c&uacute;ng &ocirc;ng b&agrave;, tổ ti&ecirc;n. V&igrave; thế, khi người th&acirc;n nhắm mắt xu&ocirc;i tay, con ch&aacute;u đều mong muốn x&acirc;y &ldquo;mồ y&ecirc;n mả đẹp&rdquo;. Khu&ocirc;n vi&ecirc;n tr&ograve;n tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường l&agrave; nơi con ch&aacute;u thể hiện l&ograve;ng hiếu thảo với gia ti&ecirc;n v&agrave; người đ&atilde; khuất nhằm mang đến may mắn, t&agrave;i lộc cho c&aacute;c thế hệ sau.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;san-serif&quot;,serif\"><span style=\"color:#1d1919\">Khu&ocirc;n vi&ecirc;n tr&ograve;n ph&ugrave; hợp với những gia đ&igrave;nh d&ograve;ng tộc muốn được ch&ocirc;n cất gần nhau nhưng lại kh&ocirc;ng muốn lựa chọn <a href=\"https://nghiatrangthienduong.com.vn/san-pham/khuon-vien-gia-dinh\">khu&ocirc;n vi&ecirc;n gia đ&igrave;nh</a> hoặc <a href=\"https://nghiatrangthienduong.com.vn/san-pham/khuon-vien-gia-toc\">gia tộc.</a></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;san-serif&quot;,serif\"><span style=\"color:#1d1919\">Với sự kết hợp h&agrave;i h&ograve;a giữa những v&ograve;m c&acirc;y bao xung quanh khu&ocirc;n vi&ecirc;n v&agrave; thiết kế độc đ&aacute;o, tinh xảo đ&atilde; tạo n&ecirc;n một phần mộ vừa đẹp mắt lại cho ta cảm gi&aacute;c thoải m&aacute;i, ấm c&uacute;ng v&agrave; đủ đầy như vầng trăng tr&ograve;n.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;san-serif&quot;,serif\"><span style=\"color:#1d1919\">Kh&ocirc;ng ngoại lệ khu&ocirc;n vi&ecirc;n tr&ograve;n cũng được chủ đầu tư lựa chọn những loại c&acirc;y đem lại m&ugrave;i hương v&agrave; cảm gi&aacute;c dễ chịu như t&ugrave;ng la h&aacute;n, nguyệt quế, mai vạn ph&uacute;c&hellip; v&agrave; được x&acirc;y dựng bằng đ&aacute; trắng B&igrave;nh Định loại đ&aacute; c&oacute; t&iacute;nh chất bền bỉ, chịu lực tốt.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;san-serif&quot;,serif\"><span style=\"color:#1d1919\">Một số h&igrave;nh ảnh khu&ocirc;n vi&ecirc;n tr&ograve;n:</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;san-serif&quot;,serif\"><span style=\"color:#1d1919\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/khuôn viên tròn/tròn.jpg\" /></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;san-serif&quot;,serif\"><span style=\"color:#1d1919\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/khuôn viên tròn/tròn q.jpg\" /></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;san-serif&quot;,serif\"><span style=\"color:#1d1919\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/khuôn viên tròn/tròn 2.jpg\" /></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">Xem th&ecirc;m:&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/san-pham/khuon-vien-doi\">Khu&ocirc;n vi&ecirc;n mộ đ&ocirc;i</a></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:12pt\"><span style=\"background-color:white\">&nbsp;</span></span><span style=\"font-size:18pt\"><span style=\"background-color:white\"><strong><strong><span style=\"font-size:16.5pt\"><span style=\"color:#1d1919\">V&igrave; sao n&ecirc;n chọn khu&ocirc;n vi&ecirc;n mộ tr&ograve;n</span></span></strong><strong><span style=\"font-size:16.5pt\"><span style=\"color:#1d1919\">&nbsp;tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường?</span></span></strong></strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;san-serif&quot;,serif\"><span style=\"color:#1d1919\"><a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang</a> Thi&ecirc;n Đường được quy hoạch đồng bộ, ph&acirc;n l&ocirc; giữa c&aacute;c phần mộ tiện dụng, đồng thời c&oacute; diện t&iacute;ch rộng r&atilde;i, l&agrave; địa chỉ tin cậy để Q&uacute;y kh&aacute;ch mua đất x&acirc;y dựng mộ gia đ&igrave;nh cho người th&acirc;n.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;san-serif&quot;,serif\"><span style=\"color:#1d1919\">+ Ph&aacute;p l&yacute; minh bạch, sở hữu l&acirc;u d&agrave;i: To&agrave;n bộ khu mộ tr&ograve;n tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường sở hữu quyền sử dụng đất l&acirc;u d&agrave;i, từng mộ phần đều được cấp giấy chứng nhận quyền sử dụng mộ phần.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;san-serif&quot;,serif\"><span style=\"color:#1d1919\">+<a href=\"https://nghiatrangthienduong.com.vn/tien-ich\"> Tiện &iacute;ch</a> đẳng cấp: Đến với C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường, Q&uacute;y kh&aacute;ch sẽ được tận hưởng trọn vẹn c&aacute;c tiện &iacute;ch đẳng cấp nhất: B&atilde;i đỗ xe v&agrave; xe điện miễn ph&iacute;; Nh&agrave; h&agrave;ng đặt cỗ chay/mặn theo nhu cầu; Quầy h&agrave;ng b&aacute;n đồ v&agrave;ng m&atilde;, hương hoa cho kh&aacute;ch; Khu nghỉ ch&acirc;n, ăn uống ngo&agrave;i trời; Quầy nước lưu động; Khu vui chơi trẻ em; Ch&ograve;i nghỉ ch&acirc;n; Am h&oacute;a v&agrave;ng.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;san-serif&quot;,serif\"><span style=\"color:#1d1919\">+ <a href=\"https://nghiatrangthienduong.com.vn/dich-vu\">Dịch vụ </a>ho&agrave;n hảo: Mong muốn mang đến cho kh&aacute;ch h&agrave;ng những khoảnh khắc trải nghiệm ho&agrave;n hảo, đội ngũ nh&acirc;n sự tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường được đ&agrave;o tạo b&agrave;i bản, chuy&ecirc;n nghiệp với sự tận t&acirc;m v&agrave; tinh tế. Tại đ&acirc;y, Ch&uacute;ng t&ocirc;i đảm bảo cung cấp đầy đủ c&aacute;c dịch vụ hiện đại v&agrave; cao cấp bậc nhất Việt Nam như: Chăm s&oacute;c mộ phần v&agrave; cảnh quan; X&acirc;y dựng mộ phần; Tư vấn v&agrave; tổ chức tang lễ trọn g&oacute;i; Tư vấn phong thủy; C&uacute;ng giỗ online; Hỗ trợ ăn uống, lưu tr&uacute;; X&acirc;y dựng gia phả; Dịch vụ an ninh; Dịch vụ lập kế hoạch trước; Dịch vụ hỏa t&aacute;ng.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-family:&quot;san-serif&quot;,serif\"><span style=\"color:#1d1919\">+ Gi&aacute; th&agrave;nh hợp l&yacute;: Khu mộ tr&ograve;n tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường c&oacute; mức gi&aacute; v&ocirc; c&ugrave;ng hợp l&yacute;. </span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>', 'true', 'News', 'khuon-vien-tron', 'tròn.jpg', 'Khuôn viên tròn- sản phẩm của công viên nghĩa trang Thiên Đường', 'Khuôn viên tròn tại Công viên Nghĩa trang Thiên Đường được thiết kế gồm những khuôn viên đơn sắp xếp hài hòa cạnh nhau tạo nên không gian đoàn tụ ấm cúng cho gia đình, họ tộc.', '2023-12-05 11:42:00', '2023-12-19 09:11:45'),
(149, 2, 68, 'Truyền thuyết mẫu Thoải- Tuyên Quang', 'Truyền thuyết về Mẫu Thoải (hay Mẫu Thủy) gắn liền với truyền thuyết về hai di tích Đền Thượng và Đền Hạ ở Thành Tuyên.', NULL, '<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"color:#1d1919\">Mẫu Thoải - B&agrave; mẹ cai quản v&ugrave;ng s&ocirc;ng nước theo quan niệm của d&acirc;n gian. Cũng ch&iacute;nh v&igrave; thế m&agrave; h&agrave;ng năm, c&oacute; h&agrave;ng vạn người con tr&ecirc;n khắp mọi miền đất nước hướng về xứ Tuy&ecirc;n. Đ&ocirc;ng nhất l&agrave; v&agrave;o m&ugrave;a xu&acirc;n, những người theo t&iacute;n ngưỡng đạo Mẫu, những người hay l&ecirc;nh đ&ecirc;nh tr&ecirc;n s&oacute;ng nước v&agrave; cả kh&aacute;ch du lịch đổ về c&aacute;c đền Tuy&ecirc;n Quang như c&oacute; một lời hẹn từ trong t&acirc;m.</span></span></strong></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:14.0pt\"><span style=\"color:#1d1919\">Truyền thuyết về Mẫu Thoải (hay Mẫu Thủy) gắn liền với truyền thuyết về hai di t&iacute;ch Đền Thượng v&agrave; Đền Hạ ở Th&agrave;nh Tuy&ecirc;n. Người xưa kể rằng, đời trước c&oacute; hai c&ocirc;ng ch&uacute;a l&agrave; Ngọc L&acirc;n (tức Mai Hoa c&ocirc;ng ch&uacute;a) v&agrave; Phương Dung c&ocirc;ng ch&uacute;a (tức Quỳnh Hoa c&ocirc;ng ch&uacute;a) theo xa gi&aacute; đi xem x&eacute;t địa phương đỗ thuyền ở bờ s&ocirc;ng. Đến đ&ecirc;m nổi cơn mưa gi&oacute;, hai c&ocirc;ng ch&uacute;a vụt bay l&ecirc;n trời, người ta cho l&agrave; linh dị, lập đền để thờ. Nơi con thuyền đỗ b&ecirc;n bờ s&ocirc;ng xưa l&agrave; Đền Thượng, nay thuộc x&oacute;m 14, x&atilde; Tr&agrave;ng Đ&agrave; (TP Tuy&ecirc;n Quang). Theo quan niệm của d&acirc;n gian, hai vị c&ocirc;ng ch&uacute;a ch&iacute;nh l&agrave; hiện th&acirc;n của con g&aacute;i Long Vương.&nbsp;</span></span></span></span></span><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> </span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:14.0pt\"><span style=\"color:#1d1919\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/Picture3.jpg\" style=\"height:976px; width:976px\" /></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><em><span style=\"font-family:&quot;san-serif&quot;,serif\"><span style=\"color:#1d1919\">Truyền thuyết về Mẫu Thoải gắn liền với truyền thuyết về hai di t&iacute;ch Đền Thượng v&agrave; Đền Hạ ở Th&agrave;nh Tuy&ecirc;n</span></span></em></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:14.0pt\"><span style=\"color:#1d1919\">Tuy nhi&ecirc;n bằng v&agrave;o hệ thống tượng thờ v&agrave; h&agrave;nh tr&igrave;nh lễ rước cho thấy khi t&iacute;n ngưỡng thờ Mẫu ph&aacute;t triển, d&acirc;n gian đ&atilde; đưa Mẫu Thượng Thi&ecirc;n v&agrave; Mẫu Thoải c&ugrave;ng Ngọc Ho&agrave;ng, Long Vương, Quan Ho&agrave;ng v&agrave;o phối thờ. Ch&iacute;nh v&igrave; vậy, hai c&ocirc;ng ch&uacute;a thờ tại đền Thượng đền Hạ, đền Ỷ La được Mẫu h&oacute;a trở th&agrave;nh những vị Mẫu thần. Theo quan điểm của một số nh&agrave; nghi&ecirc;n cứu về văn h&oacute;a, sở dĩ Đền Thượng thờ Ngọc L&acirc;n c&ocirc;ng ch&uacute;a v&agrave; Đền Hạ thờ Phương Dung c&ocirc;ng ch&uacute;a được t&ocirc;n l&agrave; Mẫu Thoải kh&ocirc;ng chỉ v&igrave; những l&yacute; do tr&ecirc;n m&agrave; hơn cả l&agrave; c&oacute; sắc phong của nh&agrave; nước phong kiến xưa. Người d&acirc;n xứ Bắc theo đ&oacute; m&agrave; thờ phụng.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:14.0pt\"><span style=\"color:#1d1919\">Ngo&agrave;i hai ng&ocirc;i đền Thượng v&agrave; đền Hạ, ở th&agrave;nh phố Tuy&ecirc;n Quang c&ograve;n một ng&ocirc;i đền kh&aacute;c thờ mẫu Thoải. Ấy l&agrave; đền Ỷ La. Tương truyền v&agrave;o thời vua nh&agrave; Nguyễn, nghe tin c&oacute; giặc loạn đảng tr&agrave;n v&agrave;o tỉnh lị Tuy&ecirc;n Quang, người d&acirc;n mang long ngai thờ Th&aacute;nh mẫu từ đền Hạ chạy v&agrave;o th&ocirc;n Gốc Đa x&atilde; Ỷ La. Họ vừa kịp giấu pho tượng v&agrave;o rừng c&acirc;y th&igrave; qu&acirc;n giặc tới, nhưng ch&uacute;ng kh&ocirc;ng ph&aacute;t hiện ra. S&aacute;ng h&ocirc;m sau, thay v&agrave;o chỗ bức tượng l&agrave; một đống mối đ&ugrave;n lớn, d&acirc;n l&agrave;ng cho l&agrave; điềm b&aacute;o ứng. Giặc tan, họ c&ugrave;ng nhau x&acirc;y một ng&ocirc;i đền mới thờ Th&aacute;nh Mẫu ngay tr&ecirc;n mảnh đất đ&oacute;.&nbsp;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:14.0pt\"><span style=\"color:#1d1919\">H&agrave;ng năm, xu&acirc;n thu nhị kỳ v&agrave;o trung tuần th&aacute;ng 2 v&agrave; th&aacute;ng 7 (&acirc;m lịch), lễ rước Kiệu Mẫu bắt đầu từ Đền Mẫu Ỷ La ra Đền Hạ, rồi tiếp đến lễ rước Kiệu Mẫu từ Đền Thượng qua s&ocirc;ng về Đền Hạ để c&ugrave;ng hợp tế. Nghi thức uy nghi, c&oacute; đầy đủ gi&agrave; trẻ g&aacute;i trai v&agrave; kh&aacute;ch thập phương tham dự. Người rước Kiệu Mẫu phải l&agrave; những nam thanh, nữ t&uacute; xứ Tuy&ecirc;n. K&egrave;m theo lễ rước l&agrave; m&uacute;a l&acirc;n, kết hợp d&agrave;n nhạc với lời ca. Những năm Đền được vua ban cấp sắc phong, nh&acirc;n d&acirc;n tổ chức lễ đ&oacute;n nhận long trọng, đ&ocirc;ng vui. Rước Mẫu l&agrave; một sinh hoạt lễ hội lớn nhất từ xa xưa k&eacute;o d&agrave;i đến thời gian đầu kh&aacute;ng chiến chống thực d&acirc;n Ph&aacute;p mới chấm dứt.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#1d1919; font-family:Times New Roman,serif\"><span style=\"font-size:18.6667px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/dia-the-phong-thuy\">Địa thế phong thủy&nbsp;</a></span></span></p>', 'true', 'News', 'truyen-thuyet-mau-thoai-tuyen-quang', '00_BVCG.jpg', 'Truyền thuyết mẫu Thoải- Tuyên Quang', 'Mẫu Thoải - Bà mẹ cai quản vùng sông nước theo quan niệm của dân gian. Cũng chính vì thế mà hàng năm, có hàng vạn người con trên khắp mọi miền đất nước hướng về xứ Tuyên.', '2023-12-06 08:56:11', '2023-12-09 03:44:28'),
(150, 2, 63, 'Tiện ích - Dịch vụ', 'Công viên Nghĩa trang Thiên Đường cung cấp hệ thống tiện ích đẳng cấp và dịch vụ chu đáo, tận tâm, đáp ứng mọi nhu cầu của Khách hàng.', NULL, '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường cung cấp&nbsp;hệ thống tiện &iacute;ch đẳng cấp v&agrave; dịch vụ chu đ&aacute;o, tận t&acirc;m, đ&aacute;p ứng mọi nhu cầu của Kh&aacute;ch h&agrave;ng.</strong></span></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:20px\"><strong>Tiện &iacute;ch đẳng cấp</strong></span></h2>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">B&atilde;i đỗ xe v&agrave; xe điện miễn ph&iacute;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Nh&agrave; h&agrave;ng đặt cỗ chay/mặn theo nhu cầu</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Quầy h&agrave;ng b&aacute;n đồ v&agrave;ng m&atilde;, hương hoa cho kh&aacute;ch</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Khu nghỉ ch&acirc;n, ăn uống ngo&agrave;i trời</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Quầy nước lưu động</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Khu vui chơi trẻ em</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Ch&ograve;i nghỉ ch&acirc;n</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Am h&oacute;a v&agrave;ng</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/7.jpg\" style=\"height:394px; width:394px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>Văn ph&ograve;ng tiếp đ&oacute;n</strong>: L&agrave; một c&ocirc;ng tr&igrave;nh phức hợp bao gồm văn ph&ograve;ng Ban Quản l&yacute; Dự &aacute;n, ph&ograve;ng Y tế, ph&ograve;ng Kinh doanh v&agrave; Chăm s&oacute;c kh&aacute;ch h&agrave;ng.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>Nh&agrave; dịch vụ</strong>: Hệ thống nh&agrave; dịch vụ cung cấp dịch vụ ăn uống v&agrave; nghỉ ngơi d&agrave;nh cho kh&aacute;ch thăm viếng v&agrave; tang lễ.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>Khu vui chơi trẻ em</strong>: Kh&ocirc;ng gian vui chơi h&ograve;a hợp với thi&ecirc;n nhi&ecirc;n khiến mỗi chuyến đi thăm người th&acirc;n sẽ l&agrave; chuyến đi d&atilde; ngoại hướng về cội nguồn v&agrave; lưu giữ gi&aacute; trị gia đ&igrave;nh.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>Ch&ograve;i nghỉ ch&acirc;n</strong>: Ch&ograve;i nghỉ ch&acirc;n l&agrave; c&ocirc;ng tr&igrave;nh mang t&iacute;nh thẩm mỹ cao với vẻ đẹp kết hợp h&agrave;i ho&agrave; giữa kiến tr&uacute;c, vật liệu gỗ v&agrave; đ&aacute; tự nhi&ecirc;n. L&agrave; nơi gia đ&igrave;nh nghỉ ngơi, thư gi&atilde;n, ho&agrave; m&igrave;nh với kh&ocirc;ng gian thi&ecirc;n nhi&ecirc;n tại Thi&ecirc;n Đường.</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Lu&ocirc;n đề cao sự tinh tế trong từng đường n&eacute;t đậm chất &Aacute; Đ&ocirc;ng, c&aacute;c c&ocirc;ng tr&igrave;nh tiện &iacute;ch tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường kh&ocirc;ng chỉ đẹp về tổng thể m&agrave; c&ograve;n tập trung v&agrave;o sự tiện lợi d&agrave;nh cho kh&aacute;ch h&agrave;ng ở mọi l&uacute;c mọi nơi.</span></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:20px\"><strong>Dịch vụ chu đ&aacute;o tận t&acirc;m:</strong></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Mong muốn mang đến cho kh&aacute;ch h&agrave;ng những khoảnh khắc trải nghiệm ho&agrave;n hảo, đội ngũ nh&acirc;n sự tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường được đ&agrave;o tạo b&agrave;i bản, chuy&ecirc;n nghiệp với sự tận t&acirc;m v&agrave; tinh tế. Tại đ&acirc;y, Ch&uacute;ng t&ocirc;i đảm bảo cung cấp đầy đủ c&aacute;c dịch vụ hiện đại v&agrave; cao cấp bậc nhất Việt Nam.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/0.jpg\" style=\"height:341px; width:342px\" /></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Chăm s&oacute;c mộ phần v&agrave; cảnh quan</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">X&acirc;y dựng mộ phần</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Tư vấn v&agrave; tổ chức tang lễ trọn g&oacute;i</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Tư vấn phong thủy</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">C&uacute;ng giỗ online</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Hỗ trợ ăn uống, lưu tr&uacute;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">X&acirc;y dựng gia phả</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Dịch vụ an ninh</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Dịch vụ lập kế hoạch trước</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Dịch vụ hỏa t&aacute;ng</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/phap-ly-so-huu\">Ph&aacute;p l&yacute;- sở hữu</a></span></p>', 'true', 'News', 'tien-ich-dich-vu', '7.jpg', 'Dịch vụ- tiện ích của Công viên Nghĩa trang Thiên Đường', 'Công viên Nghĩa trang Thiên Đường cung cấp hệ thống tiện ích đẳng cấp và dịch vụ chu đáo, tận tâm, đáp ứng mọi nhu cầu của Khách hàng.', '2023-12-06 09:09:22', '2023-12-09 03:44:12');
INSERT INTO `posts` (`id`, `user_id`, `category_id`, `name`, `detail`, `info`, `content`, `status`, `sort_by`, `slug`, `img`, `title`, `description`, `created_at`, `updated_at`) VALUES
(151, 2, 63, 'Pháp lý- sở hữu', 'Công viên nghĩa trang Thiên Đường với pháp lý Dự án đầy đủ, minh bạch là yếu tố then chốt để khách hàng tin tưởng lựa chọn.', NULL, '<p><span style=\"font-size:16px\"><strong><a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n nghĩa trang Thi&ecirc;n Đường </a>với ph&aacute;p l&yacute; Dự &aacute;n đầy đủ, minh bạch l&agrave; yếu tố then chốt để kh&aacute;ch h&agrave;ng tin tưởng lựa chọn.</strong></span></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>Dự &aacute;n C&ocirc;ng vi&ecirc;n ra đời dựa tr&ecirc;n hệ thống văn bản ph&aacute;p l&yacute; sau:</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">+ Về chủ trương đầu tư: Quyết định của UBND tỉnh Tuy&ecirc;n Quang số 188/QĐ-UBND ng&agrave;y 20/7/2018.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ Về quy hoạch chi tiết: Quyết định của UBND huyện Y&ecirc;n Sơn số 275/QĐ-UBND ng&agrave;y 28/12/2018 v&agrave; Quyết định số 329/QĐ-UBND ng&agrave;y 19/9/2019.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ Về đ&aacute;nh gi&aacute; t&aacute;c động m&ocirc;i trường: Quyết định của UBND tỉnh Tuy&ecirc;n Quang số 200/QĐ-UBND ng&agrave;y 12/03/2020.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ Về giao đất, cho thu&ecirc; đất: 05 Quyết định của UBND tỉnh Tuy&ecirc;n Quang (Quyết định số 683/QĐ-UBND ng&agrave;y 30/12/2020, Quyết định số 15/QĐ-UBND ng&agrave;y 21/01/2021, Quyết định số 32/QĐ-UBND ng&agrave;y 02/02/2021, Quyết định số 138/QĐ-UBND ng&agrave;y 13/04/2021, Quyết định số 188/QĐ-UBND ng&agrave;y 13/04/2022)</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ Về Giấy ph&eacute;p x&acirc;y dựng: 06 Giấy ph&eacute;p x&acirc;y dựng của UBND huyện Y&ecirc;n Sơn (GPXD số 11/GPXD ng&agrave;y 31/12/2020, GPXD số 02/GPXD ng&agrave;y 25/01/2021; GPXD số 05/GPXD ng&agrave;y 02/02/2021; GPXD số 12/GPXD ng&agrave;y 06/05/2021; GPXD số 08/GPXD ng&agrave;y 18/04/2022; GPXD số 11/GPXD ng&agrave;y 27/05/2022).</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/00.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>Vậy khi mua đất tại<a href=\"https://nghiatrangthienduong.com.vn/\"> Thi&ecirc;n Đường</a>, kh&aacute;ch h&agrave;ng sẽ nhận được những chứng từ g&igrave;?</strong></span></h2>\r\n\r\n<h3><span style=\"font-size:18px\"><strong>Trước hết, Ch&iacute;nh phủ đ&atilde; ban h&agrave;nh những điều của luật đất đai như sau:</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Theo quy định tại Điều 10 Nghị định số 43/2014/NĐ-CP của Ch&iacute;nh phủ quy định chi tiết thi h&agrave;nh một số điều của Luật Đất đai 2013:</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ldquo;Điều 19. C&aacute;c trường hợp kh&ocirc;ng cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px\">Tổ chức, cộng đồng d&acirc;n cư được Nh&agrave; nước giao đất để quản l&yacute; thuộc c&aacute;c trường hợp quy định tại Điều 8 của Luật Đất đai.</span></li>\r\n	<li><span style=\"font-size:16px\">Người đang quản l&yacute;, sử dụng đất n&ocirc;ng nghiệp thuộc quỹ đất c&ocirc;ng &iacute;ch của x&atilde;, phường, thị trấn.</span></li>\r\n	<li><span style=\"font-size:16px\">Người thu&ecirc;, thu&ecirc; lại đất của người sử dụng đất, trừ trường hợp thu&ecirc;, thu&ecirc; lại đất của nh&agrave; đầu tư x&acirc;y dựng, kinh doanh kết cấu hạ tầng trong khu c&ocirc;ng nghiệp, cụm c&ocirc;ng nghiệp, khu chế xuất, khu c&ocirc;ng nghệ cao, khu kinh tế.</span></li>\r\n	<li><span style=\"font-size:16px\">Người nhận kho&aacute;n đất trong c&aacute;c n&ocirc;ng trường, l&acirc;m trường, doanh nghiệp n&ocirc;ng, l&acirc;m nghiệp, ban quản l&yacute; rừng ph&ograve;ng hộ, ban quản l&yacute; rừng đặc dụng.</span></li>\r\n	<li><span style=\"font-size:16px\">Người đang sử dụng đất kh&ocirc;ng đủ điều kiện cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất.</span></li>\r\n	<li><span style=\"font-size:16px\">Người sử dụng đất c&oacute; đủ điều kiện cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất nhưng đ&atilde; c&oacute; th&ocirc;ng b&aacute;o hoặc quyết định thu hồi đất của cơ quan nh&agrave; nước c&oacute; thẩm quyền.</span></li>\r\n	<li><span style=\"font-size:16px\">Tổ chức, Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; được Nh&agrave; nước giao đất kh&ocirc;ng thu tiền sử dụng đất để sử dụng v&agrave;o mục đ&iacute;ch x&acirc;y dựng c&ocirc;ng tr&igrave;nh c&ocirc;ng cộng gồm đường giao th&ocirc;ng, c&ocirc;ng tr&igrave;nh dẫn nước, dẫn xăng, dầu, kh&iacute;; đường d&acirc;y truyền tải điện, truyền dẫn th&ocirc;ng tin; khu vui chơi giải tr&iacute; ngo&agrave;i trời; nghĩa trang, nghĩa địa kh&ocirc;ng nhằm mục đ&iacute;ch kinh doanh&rdquo;</span></li>\r\n</ol>\r\n\r\n<h3><span style=\"font-size:18px\"><strong>Vậy Điều lệ do nh&agrave; nước ban h&agrave;nh c&oacute; t&aacute;c dụng g&igrave; đối với quyền sử dụng đất ?</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">- Người đang sử dụng đất v&agrave; trước đ&acirc;y kh&ocirc;ng đủ điều kiện cấp Giấy chứng nhận quyền sử dụng đất, nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất. Quyền sở hữu nh&agrave; ở</span></p>\r\n\r\n<p><span style=\"font-size:16px\">- Người đang hoặc đ&atilde; sử dụng đất c&oacute; đủ điều kiện cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất nhưng khi nhận được th&ocirc;ng b&aacute;o hoặc quyết định thu hồi đất của cơ quan nh&agrave; nước c&oacute; thẩm quyền.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">- Tổ chức, Ủy ban nh&acirc;n d&acirc;n cấp x&atilde; khi được Nh&agrave; nước giao đất kh&ocirc;ng thu tiền sử dụng đất để x&acirc;y dựng c&ocirc;ng tr&igrave;nh c&ocirc;ng cộng gồm: đường giao th&ocirc;ng, đường ống dẫn nước, xăng dầu, đường d&acirc;y tải điện kh&iacute;, đường d&acirc;y truyền tải th&ocirc;ng tin; khu vui chơi giải tr&iacute; ngo&agrave;i trời; Nghĩa trang, nghĩa địa kh&ocirc;ng nhằm mục đ&iacute;ch kinh doanh. Như vậy, đất l&agrave;m nghĩa trang, nghĩa địa thuộc diện đất c&ocirc;ng &iacute;ch được Nh&agrave; nước giao kh&ocirc;ng thu tiền sử dụng đất n&ecirc;n sẽ kh&ocirc;ng được cấp Giấy chứng nhận quyền sử dụng đất.</span></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>Vậy kh&aacute;ch h&agrave;ng sẽ nhận được những giấy tờ g&igrave; khi mua đất sinh phần tại <a href=\"https://nghiatrangthienduong.com.vn/\">Thi&ecirc;n Đường?</a></strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Kh&aacute;ch h&agrave;ng sẽ được Chủ đầu tư cấp Giấy chứng nhận quyền sử dụng khu&ocirc;n vi&ecirc;n phần mộ với thời hạn sử dụng ổn định l&acirc;u d&agrave;i (vĩnh viễn) theo đ&uacute;ng quy định tại Khoản 10 Điều 125 Luật đất đai 2013 v&agrave; tại c&aacute;c Quyết định giao đất của Dự &aacute;n.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m : <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/tien-ich-dich-vu\">Tiện &iacute;ch-dịch vụ&nbsp;</a></span></p>', 'true', 'News', 'phap-ly-so-huu', '58_z4739939583777_ed37970f5389ec145ff423bd67dab831.jpg', 'Pháp lý- sở hữu tại Công viên nghĩa trang Thiên Đường', 'Công viên nghĩa trang Thiên Đường với pháp lý Dự án đầy đủ, minh bạch là yếu tố then chốt để khách hàng tin tưởng lựa chọn.', '2023-12-06 09:19:27', '2023-12-09 03:44:00'),
(152, 2, 65, 'Đền Trình', 'Đền Trình là điểm dừng chân đầu tiên khi quý khách tới thăm quần thể tâm linh Công viên Nghĩa trang Thiên Đường.', NULL, '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>Đền Tr&igrave;nh l&agrave; điểm dừng ch&acirc;n đầu ti&ecirc;n khi qu&yacute; kh&aacute;ch tới thăm quần thể t&acirc;m linh&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</a><a href=\"https://nghiatrangthienduong.com.vn/\">.</a> Gọi l&agrave; Đền Tr&igrave;nh v&igrave; trước khi đến thăm khu mộ của người th&acirc;n, ai qua đ&acirc;y cũng k&iacute;nh cẩn v&agrave;o l&agrave;m lễ với &yacute; nghĩa l&agrave; một sự tr&igrave;nh b&aacute;o trước về việc viếng thăm, cầu cho chuyến đi n&agrave;y được may mắn, người đến thăm được an y&ecirc;n, thanh thản trong suốt qu&atilde;ng thời gian dừng lại nơi đ&acirc;y.</strong><br />\r\n<img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/đền trình/đền trình.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><em>Phối cảnh đền Tr&igrave;nh tại C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</em></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Đền Tr&igrave;nh trong phối cảnh ph&ecirc; duyệt hiện l&ecirc;n l&agrave; một ng&ocirc;i đền mang đậm bản sắc của một ng&ocirc;i đền Bắc Bộ xưa. T&ocirc;ng m&agrave;u n&acirc;u ấm của m&aacute;i ng&oacute;i, khung cửa khiến cho người viếng thăm c&oacute; cảm gi&aacute;c y&ecirc;n b&igrave;nh như t&igrave;m lại được k&iacute; ức của những ng&agrave;y c&ograve;n thơ b&eacute;. Chủ đầu tư t&acirc;m niệm rằng, nơi đ&acirc;y l&agrave; <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n nghĩa trang</a> cho người Việt. Người ta c&oacute; thể du nhập rồi Việt ho&aacute; một loại văn h&oacute;a n&agrave;o đ&oacute; chứ kh&ocirc;ng bao giờ v&agrave; kh&ocirc;ng thể du nhập quy hoạch một xứ lạ v&agrave;o kiến tr&uacute;c t&acirc;m linh của ta được. Vậy th&igrave; chắc chắn c&aacute;i vỏ vật thể đ&oacute; &ndash; ng&ocirc;i Đền &ndash; phải h&agrave;m chứa hồn qu&ecirc;, bản sắc của người Việt.</span></p>\r\n\r\n<h2><span style=\"font-size:18px\"><strong>C&ocirc;ng tr&igrave;nh Đền Tr&igrave;nh khi ho&agrave;n th&agrave;nh trong thực tế</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/đền trình/đền trình 1.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><em>Đền Tr&igrave;nh thực tế tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</em></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Đội ngũ thiết kế của chủ đầu tư đ&atilde; phải d&agrave;nh ra 100 giờ để đi khảo s&aacute;t c&aacute;c đền cổ ở c&aacute;c tỉnh th&agrave;nh Th&aacute;i B&igrave;nh, Bắc Ninh, Bắc Giang&hellip; với mong muốn được hiểu r&otilde; về kiến tr&uacute;c Bắc Bộ xưa. Từng chi tiết hoa văn, m&aacute;i ng&oacute;i, c&acirc;u đối được ghi lại tỉ mỉ nhằm phục vụ qu&aacute; tr&igrave;nh chốt phương &aacute;n thiết kế v&agrave; phối cảnh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Đền Tr&igrave;nh sau khi x&acirc;y dựng đảm bảo 100% giống với thiết kế phối cảnh, bao gồm từ hoa văn cho đến m&agrave;u sắc. Ng&ocirc;i đền nằm tr&ecirc;n một khu đất cao, rộng, nh&igrave;n ra c&aacute;nh đồng l&uacute;a. Với diện t&iacute;ch 60m2, đền Tr&igrave;nh tại C&ocirc;ng vi&ecirc;n <a href=\"https://nghiatrangthienduong.com.vn/\">Nghĩa trang Thi&ecirc;n Đường</a> được chia l&agrave;m 3 gian, x&acirc;y kiểu tường hồi b&iacute;t đốc, ch&iacute;nh giữa đắp m&acirc;y, hai m&aacute;i kiểu chồng di&ecirc;m, ph&iacute;a trước trung tế c&oacute; lư hương đ&aacute;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/chua-bao-an\">Ch&ugrave;a B&aacute;o &Acirc;n</a></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/đền trình/đền trình 2.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><em>Mặt b&ecirc;n của Đền Tr&igrave;nh</em></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><em>- Về kiến tr&uacute;c v&agrave; hoa văn</em>: Đền Tr&igrave;nh m&ocirc; phỏng nguy&ecirc;n mẫu Kiến tr&uacute;c v&agrave; hoa văn của Quốc Tử Gi&aacute;m - Biểu tượng trường tồn tinh hoa văn h&oacute;a Việt. Lối kiến tr&uacute;c phương Đ&ocirc;ng với sự ảnh hưởng đậm đặc của Nho &ndash; Phật gi&aacute;o thể hiện r&otilde; n&eacute;t trong từng chi tiết của kh&ocirc;ng gian. Từ chất liệu (chủ yếu l&agrave; gỗ lim, gạch nung v&agrave; ng&oacute;i mũi h&agrave;i&hellip;) cho đến những lớp ng&oacute;i, hoa văn đều được trang tr&iacute; một c&aacute;ch tỉ mỉ v&agrave; sang trọng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><em>- Về gạch ng&oacute;i</em>:&nbsp;Chủ đầu tư quyết định sử dụng gạch ng&oacute;i của Thương hiệu sản xuất gạch ng&oacute;i uy t&iacute;n nhất Việt Nam, c&oacute; nguồn gốc từ l&agrave;ng gốm cổ B&aacute;t Tr&agrave;ng. Gạch Ng&oacute;i B&aacute;t Tr&agrave;ng c&oacute; những đặc điểm rất ri&ecirc;ng biệt, kh&aacute;c hẳn với c&aacute;c loại gạch kh&aacute;c tr&ecirc;n thị trường bởi chất lượng, kiểu d&aacute;ng, v&agrave; m&agrave;u sắc, khi g&otilde; v&agrave;o nghe rất đanh (tiếng vang trong, đanh như tiếng chu&ocirc;ng), chất men đẹp lạ tinh xảo. Đặc biệt, gạch được nung trong l&ograve; đất 7 ng&agrave;y, sau đ&oacute; cho ra th&agrave;nh phẩm l&agrave; những vi&ecirc;n gạch c&oacute; m&agrave;u sắc đậm nhạt kh&aacute;c nhau (do l&ograve; đất nung c&oacute; nhiệt độ kh&ocirc;ng đều), l&agrave;m n&ecirc;n chất ri&ecirc;ng m&agrave; kh&ocirc;ng nơi n&agrave;o c&oacute; được.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/đền trình/đền trình 3.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><em>Gạch B&aacute;t Tr&agrave;ng với m&agrave;u sắc từng &ocirc; kh&aacute;c nhau</em></span></p>\r\n\r\n<p><span style=\"font-size:16px\">- <em>Về đồ gỗ</em>:&nbsp;Được&nbsp;sử dụng&nbsp;từ gỗ nguy&ecirc;n khối nhập khẩu trực tiếp từ Malaysia.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Gỗ nguy&ecirc;n khối l&agrave; những mẫu nội thất gỗ được l&agrave;m từ gỗ tự nhi&ecirc;n 100%. Ch&uacute;ng sẽ được lấy từ những c&acirc;y gỗ to, c&oacute; k&iacute;ch thước lớn v&agrave; kh&ocirc;ng bị gh&eacute;p giữa c&aacute;c mảnh gỗ nhỏ lại với nhau. Đấy ch&iacute;nh l&agrave; yếu tố tạo n&ecirc;n sự độc đ&aacute;o cho sản phẩm. Cũng bởi sự qu&yacute; hiếm v&agrave; đ&ograve;i hỏi nhiều yếu tố như vậy n&ecirc;n gỗ nguy&ecirc;n khối c&oacute; gi&aacute; th&agrave;nh rất cao v&agrave; kh&ocirc;ng phải bất k&igrave; c&acirc;y gỗ n&agrave;o cũng c&oacute; thể l&agrave;m được. Một tấm gỗ nguy&ecirc;n khối đẹp cần c&oacute; đầy đủ những yếu tố như: m&agrave;u sắc tự nhi&ecirc;n, k&iacute;ch thước lớn, đường v&acirc;n r&otilde; n&eacute;t, bề mặt phẳng phiu, c&oacute; t&iacute;nh thẩm mỹ cao.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">- <em>Về cảnh quan xung quanh</em>:&nbsp;Được trang tr&iacute; bằng rất nhiều c&acirc;y xanh, cụ thể, 2 b&ecirc;n Đền Tr&igrave;nh l&agrave; 2 c&acirc;y Đại c&oacute; tuổi đời 300 tuổi được Chủ đầu tư cất c&ocirc;ng sang đất nước Chăm Pa (L&agrave;o) v&agrave; vận chuyển về Việt Nam. B&ecirc;n cạnh đ&oacute; l&agrave; điểm xuyết l&ecirc; trắng được đ&aacute;nh tận gốc trong những c&aacute;nh rừng gi&agrave; tại Điện Bi&ecirc;n, vận chuyển v&agrave; hạ thổ trong ng&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline&nbsp;0934.524.445 hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\">https://nghiatrangthienduong.com.vn/</a>.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m : <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/kien-truc-canh-quan\">Kiến tr&uacute;c cảnh quan của C&ocirc;ng vi&ecirc;n nghĩa trang Thi&ecirc;n Đường</a></span></p>', 'true', 'News', 'den-trinh', 'đền trình 1.jpg', 'Đền Trình tại Công viên Nghĩa Trang Thiên Đường', 'Đền Trình là điểm dừng chân đầu tiên khi quý khách tới thăm quần thể tâm linh Công viên Nghĩa trang Thiên Đường. Gọi là Đền Trình vì trước khi đến thăm khu mộ của người thân, ai qua đây cũng kính cẩn vào làm lễ với ý nghĩa là một sự trình báo.', '2023-12-06 13:48:18', '2023-12-20 04:23:05'),
(155, 2, 70, 'Dịch vụ Tư vấn & Tổ chức tang lễ trọn gói', 'Để hành trình của người đã khuất được trọn vẹn và san sẻ phần nào nỗi đau với gia quyến trên hành trình tiễn đưa người thân về cõi vĩnh hằng, Công viên Nghĩa trang Thiên Đường đã và đang hợp tác với các đơn vị cung cấp dịch vụ Tang lễ trọn gói.', NULL, '<p><span style=\"font-size:16px\"><strong>Để h&agrave;nh tr&igrave;nh của người đ&atilde; khuất được trọn vẹn v&agrave; san sẻ phần n&agrave;o nỗi đau với gia quyến tr&ecirc;n h&agrave;nh tr&igrave;nh tiễn đưa người th&acirc;n về c&otilde;i vĩnh hằng,&nbsp;<a href=\"https://congvienthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>&nbsp;đ&atilde; v&agrave; đang hợp t&aacute;c với c&aacute;c đơn vị cung cấp dịch vụ Tang lễ trọn g&oacute;i, chuy&ecirc;n nghiệp với mức chi ph&iacute; hợp l&yacute; v&agrave; dịch vụ ho&agrave;n chỉnh. Ch&uacute;ng t&ocirc;i cam kết mang đến dịch vụ vượt trội v&agrave; l&agrave; người bạn đồng h&agrave;nh chia sẻ mọi nỗi trăn trở c&ugrave;ng Qu&yacute; gia đ&igrave;nh.</strong></span></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>Dịch vụ tang lễ trọn g&oacute;i tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</strong></span></h2>\r\n\r\n<h3><span style=\"font-size:18px\"><strong>Dịch vụ tang lễ trọn g&oacute;i tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường bao gồm:</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">- Tư vấn về thủ tục, nghi thức tổ chức lễ tang, cung cấp c&aacute;c dịch vụ v&agrave; phục vụ tổ chức lễ tang tại Nh&agrave; lễ tang v&agrave; gia đ&igrave;nh tang chủ.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">- Tổ chức c&aacute;c dịch vụ hỏa t&aacute;ng, c&aacute;t t&aacute;ng tại&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</a></span></p>\r\n\r\n<p><span style=\"font-size:16px\">- Phục vụ vận tải chở thi h&agrave;i, đưa gia đ&igrave;nh v&agrave; th&acirc;n nh&acirc;n đi&nbsp;hỏa t&aacute;ng&nbsp;v&agrave; c&aacute;c hoạt động dịch vụ vận tải th&ocirc;ng thường kh&aacute;c đ&aacute;p ứng y&ecirc;u cầu của nh&acirc;n d&acirc;n.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">- X&acirc;y dựng c&aacute;c c&ocirc;ng tr&igrave;nh t&acirc;m linh tại nghĩa trang, x&acirc;y cất, lắp gh&eacute;p c&aacute;c phần mộ tại nghĩa trang.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">- Thực hiện c&aacute;c hợp đồng thi c&ocirc;ng di chuyển c&aacute;c phần mộ từ c&aacute;c dự &aacute;n x&acirc;y dựng ph&aacute;t triển đ&ocirc; thị của Th&agrave;nh Phố v&agrave; c&aacute;c nghĩa trang phải di dời.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">- Sản xuất, kinh doanh dịch vụ c&aacute;c sản phẩm h&agrave;ng h&oacute;a phục vụ việc tang như quan t&agrave;i, tiểu qu&aacute;ch, b&igrave;nh qu&aacute;ch đựng tro cốt gốm sứ, s&agrave;nh, đồ kh&acirc;m liệm, đồ t&ugrave;y t&aacute;ng, đồ thờ c&uacute;ng... từ th&ocirc;ng thường đến cao cấp.</span></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>Những lợi &iacute;ch khi sử dụng dịch vụ tang lễ trọn g&oacute;i</strong></span></h2>\r\n\r\n<h3><span style=\"font-size:18px\"><strong>Đảm bảo đầy đủ, kh&ocirc;ng bỏ lỡ bất kỳ bước n&agrave;o trong quy tr&igrave;nh tổ chức tang lễ</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Hiểu được ho&agrave;n cảnh &ldquo;tang gia bối rối&rdquo;, c&ugrave;ng với mong muốn được đồng h&agrave;nh với gia quyến tr&ecirc;n h&agrave;nh tr&igrave;nh tiễn đưa người th&acirc;n về c&otilde;i vĩnh hằng thật chỉn chu, dịch vụ Tang lễ tại&nbsp;<a href=\"https://congvienthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</a><a href=\"https://nghiatrangthienduong.com.vn/\">&nbsp;</a>bắt đầu từ việc tư vấn chi tiết, tận t&acirc;m v&agrave; tổ chức lễ tang trọn g&oacute;i theo đ&uacute;ng thủ tục v&agrave; nghi lễ t&ocirc;n gi&aacute;o, ph&ugrave; hợp y&ecirc;u cầu của mỗi gia đ&igrave;nh</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp;<img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/32.png\" /></span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><strong>Gia quyến c&oacute; th&ecirc;m thời gian ở cạnh người đ&atilde; khuất</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Thay v&igrave; phải tất bật ngược xu&ocirc;i chuẩn bị c&aacute;c c&ocirc;ng việc như: đ&oacute;n rước Thầy c&uacute;ng, chọn &aacute;o quan, dịch vụ ma chay, chuẩn bị đồ tang lễ hay mua đất nghĩa trang,&hellip; Gia đ&igrave;nh c&oacute; thể b&igrave;nh t&acirc;m d&agrave;nh khoảng thời gian qu&yacute; b&aacute;u c&ograve;n lại để ở b&ecirc;n linh cữu người đ&atilde; khuất, những việc c&ograve;n lại đ&atilde; c&oacute; dịch vụ tang lễ trọn g&oacute;i tại C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường chịu tr&aacute;ch nhiệm.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/33.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Hơn nữa, c&aacute;c dịch vụ tang lễ tại nh&agrave; ng&agrave;y nay cũng linh hoạt, đa dạng. Gia đ&igrave;nh cần g&igrave; &ndash; thu&ecirc; nấy. Ch&uacute;ng t&ocirc;i sẽ kết hợp với c&aacute;c đơn vị tổ chức tang lễ chuẩn bị chu đ&aacute;o c&aacute;c thủ tục theo đ&uacute;ng t&iacute;n ngưỡng t&ocirc;n gi&aacute;o, từ đ&oacute; gia quyến c&oacute; thể an t&acirc;m v&agrave; c&oacute; th&ecirc;m những gi&acirc;y ph&uacute;t trọn vẹn b&ecirc;n cạnh người th&acirc;n y&ecirc;u.&nbsp;</span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><strong>Dịch vụ tang lễ tổ chức mang t&iacute;nh chuy&ecirc;n nghiệp cao</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Việc tổ chức một đ&aacute;m tang theo phong tục tập qu&aacute;n, thuần phong, mỹ tục, đầy đủ mọi thứ kh&ocirc;ng phải l&agrave; việc đơn giản của nhiều gia đ&igrave;nh. Bởi trong l&uacute;c đau buồn, gia đ&igrave;nh kh&ocirc;ng thể tr&aacute;nh khỏi những thiếu s&oacute;t.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Trọn g&oacute;i Mộ phần v&agrave; dịch vụ mai t&aacute;ng sẽ gi&uacute;p gia quyến chuẩn bị mọi thứ một c&aacute;ch đủ đầy v&agrave; lễ tang diễn ra mang t&iacute;nh chuy&ecirc;n nghiệp cao. Kh&ocirc;ng những thế, g&oacute;i dịch vụ n&agrave;y hướng đến phong c&aacute;ch trang tr&iacute; nhẹ nh&agrave;ng nhưng vẫn to&aacute;t l&ecirc;n sự sang trọng, hiện đại v&agrave; cũng sẽ thuận mắt hơn đối với người tham dự.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp;<img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/34.jpg\" /></span></p>\r\n\r\n<h3><span style=\"font-size:18px\"><strong>Xử l&yacute; c&aacute;c t&igrave;nh huống bất ngờ trong tang lễ</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Thiếu s&oacute;t v&agrave; bối rối l&agrave; những điều thường thấy trong qu&aacute; tr&igrave;nh tổ chức tang lễ, v&igrave; thế những sự cố kh&ocirc;ng mong muốn cũng sẽ xảy ra m&agrave; gia quyến kh&ocirc;ng để t&acirc;m đến hết được.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/35.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Tại&nbsp;<a href=\"https://congvienthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n </a><a href=\"https://nghiatrangthienduong.com.vn/\">Đường-Tuy&ecirc;n Quang</a>, ch&uacute;ng t&ocirc;i sẽ c&oacute; đội ngũ nh&acirc;n vi&ecirc;n hỗ trợ v&agrave; t&uacute;c trực c&ugrave;ng với gia quyến, giải quyết mọi sự cố m&agrave; gia đ&igrave;nh chưa bao giờ gặp phải. Đ&oacute; cũng ch&iacute;nh l&agrave; một ưu điểm m&agrave; rất nhiều gia đ&igrave;nh lựa chọn đến với dịch vụ n&agrave;y.</span></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>V&igrave; sao n&ecirc;n lựa chọn dịch vụ tang lễ trọn g&oacute;i tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường?</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">- Gi&aacute; cả minh bạch: Cam kết kh&ocirc;ng ph&aacute;t sinh th&ecirc;m bất kỳ chi ph&iacute; n&agrave;o.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">- Trọn g&oacute;i&nbsp;tổ chức tang lễ chu to&agrave;n: Dịch vụ tang lễ chu đ&aacute;o, sang trọng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">- Phụng sự chuy&ecirc;n nghiệp, tận t&acirc;m: Đội ngũ nh&acirc;n vi&ecirc;n nhiều năm kinh nghiệm, t&acirc;m huyết, chu đ&aacute;o, được đ&agrave;o tạo b&agrave;i bản, chuy&ecirc;n nghiệp v&agrave; tận t&acirc;m, hỗ trợ gia quyến trong suốt&nbsp;qu&aacute; tr&igrave;nh diễn ra tang lễ, đ&aacute;p ứng đầy đủ c&aacute;c&nbsp;nghi lễ t&ocirc;n gi&aacute;o, sẵn s&agrave;ng phục vụ qu&yacute; kh&aacute;ch h&agrave;ng 24/7, kh&ocirc;ng ngại thời tiết hay khoảng c&aacute;ch địa l&yacute;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/dich-vu/dich-vu-xay-dung-mo-phan\">Dịch vụ x&acirc;y dựng mộ phần</a></span></p>', 'true', 'News', 'dich-vu-tu-van-to-chuc-tang-le-tron-goi', '20_33.jpg', 'Dịch vụ Tư vấn & Tổ chức tang lễ trọn gói', 'Để hành trình của người đã khuất được trọn vẹn và san sẻ phần nào nỗi đau với gia quyến trên hành trình tiễn đưa người thân về cõi vĩnh hằng, Công viên Nghĩa trang Thiên Đường đã và đang hợp tác với các đơn vị cung cấp dịch vụ Tang lễ trọn gói.', '2023-12-07 09:46:21', '2023-12-09 05:03:00'),
(156, 2, 70, 'Dịch vụ Xây dựng mộ phần', 'Công viên Nghĩa trang Thiên Đường-Tuyên Quang cung cấp dịch vụ xây dựng lăng mộ theo yêu cầu của Quý khách hàng. Dịch vụ xây dựng mộ phần tại Thiên Đường được đội ngũ kiến trúc sư lành nghề, với nhiều năm kinh nghiệm đảm nhiệm, đảm bảo đem đến cho Quý khách một công trình tâm linh chỉn chu, hoàn hảo nhất.', NULL, '<p><span style=\"font-size:16px\"><strong>Trong văn h&oacute;a d&acirc;n gian của người Việt, x&acirc;y dựng lăng mộ cho người đ&atilde; khuất lu&ocirc;n được coi trọng v&agrave; b&agrave;n bạc kĩ lưỡng bởi đ&acirc;y ch&iacute;nh l&agrave; c&aacute;ch con ch&aacute;u đời sau b&agrave;y tỏ l&ograve;ng th&agrave;nh k&iacute;nh, biết ơn với &ocirc;ng b&agrave; tổ ti&ecirc;n, cũng như thể hiện đạo l&yacute; &quot;Uống nước nhớ nguồn&quot; của người Việt ta từ xưa tới nay.</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>Nắm bắt được mong muốn n&agrave;y,&nbsp;<a href=\"https://congvienthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</a><a href=\"https://nghiatrangthienduong.com.vn/\">-Tuy&ecirc;n Quang&nbsp;</a>cung cấp dịch vụ x&acirc;y dựng lăng mộ theo y&ecirc;u cầu của Qu&yacute; kh&aacute;ch h&agrave;ng.&nbsp;Dịch vụ x&acirc;y dựng mộ phần tại Thi&ecirc;n Đường&nbsp;được đội ngũ&nbsp;kiến tr&uacute;c sư&nbsp;l&agrave;nh nghề, với nhiều năm kinh nghiệm đảm nhiệm, đảm bảo đem đến cho&nbsp;Qu&yacute; kh&aacute;ch&nbsp;một c&ocirc;ng tr&igrave;nh t&acirc;m linh&nbsp;chỉn&nbsp;chu, ho&agrave;n hảo nhất.</strong></span></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>N&eacute;t ri&ecirc;ng trong thiết kế x&acirc;y dựng mộ phần tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">C&aacute;c&nbsp;mẫu thiết kế khu&ocirc;n vi&ecirc;n mộ phần tại C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường đều mang n&eacute;t sang trọng v&agrave;&nbsp;đẹp mắt, pha trộn những&nbsp;m&agrave;u sắc văn h&oacute;a vừa cổ k&iacute;nh, vừa hiện đại, mang đến cho kh&aacute;ch h&agrave;ng cảm gi&aacute;c an y&ecirc;n, bề thế mỗi khi về thăm.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Đặc biệt, c&aacute;c khu mộ sẽ được sắp xếp v&agrave; thiết kế theo kiến tr&uacute;c&nbsp;lăng mộ&nbsp;vua ch&uacute;a thời xưa. C&aacute;c c&ocirc;ng tr&igrave;nh tại <a href=\"https://nghiatrangthienduong.com.vn/\">Thi&ecirc;n Đường</a> l&agrave; sự pha lẫn giữa n&eacute;t vững ch&atilde;i nhưng vẫn cuốn h&uacute;t. Kh&ocirc;ng chỉ l&agrave; những bức tường th&ocirc; r&aacute;p, c&aacute;c khu mộ sẽ được điểm xuyết c&aacute;c chi tiết rồng phượng, những bức tranh khắc h&igrave;nh&nbsp;tinh xảo, mềm mại, những n&eacute;t chữ bay nhảy mềm mại uốn quanh tr&ecirc;n những tấm bia mộ lớn, hay những loại&nbsp;h&igrave;nh d&aacute;ng hợp phong thủy, điểm xuyết&nbsp;một v&agrave;i chi tiết của nền văn h&oacute;a ngoại nhập.</span></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>Ưu điểm khi sử dụng dịch vụ x&acirc;y dựng mộ phần trọn g&oacute;i tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Tiết kiệm thời gian: Qu&yacute; kh&aacute;ch vừa quản l&yacute; được kế hoạch x&acirc;y dựng cho khu&ocirc;n vi&ecirc;n mộ phần, vừa c&oacute; thời gian d&agrave;nh cho c&ocirc;ng việc, m&agrave; kh&ocirc;ng cần bận t&acirc;m đến vật tư, nh&acirc;n c&ocirc;ng, quản l&yacute;.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Tiết kiệm chi ph&iacute;:&nbsp;Khi Qu&yacute; kh&aacute;ch trực tiếp x&acirc;y dựng, qu&yacute; kh&aacute;ch phải mất c&ocirc;ng lựa chọn nh&agrave; thầu, c&ocirc;ng nh&acirc;n, nguy&ecirc;n vật liệu, tổ chức triển khai thực hiện c&ocirc;ng việc, c&aacute;c việc ph&aacute;t sinh kh&aacute;c như: thời tiết, điện, nước, m&aacute;y m&oacute;c, thiết bị... c&ugrave;ng nhiều việc kh&ocirc;ng t&ecirc;n kh&aacute;c.&nbsp;B&ecirc;n cạnh đ&oacute;,&nbsp;sự phối hợp thiếu đồng nhất, mệnh lệnh kh&ocirc;ng r&otilde; r&agrave;ng giữa c&aacute;c tổ thợ kh&aacute;c nhau dẫn đến c&ocirc;ng việc chồng ch&eacute;o, kh&acirc;u trước kh&ocirc;ng khớp với kh&acirc;u sau khiến chi ph&iacute; c&ocirc;ng tr&igrave;nh bị đội l&ecirc;n g&acirc;y l&atilde;ng ph&iacute;. C&ograve;n ch&uacute;ng t&ocirc;i l&agrave; đơn vị đồng nhất về m&aacute;y m&oacute;c, phương tiện, con người, l&agrave;m việc bằng hiệu lệnh, hiệu suất, đ&uacute;ng theo tinh thần phục vụ kh&aacute;ch h&agrave;ng. Đảm bảo chất lượng c&ocirc;ng tr&igrave;nh, đảm bảo kỹ thuật, mỹ thuật, tiết kiệm chi ph&iacute;. Kh&ocirc;ng những vậy ch&uacute;ng t&ocirc;i c&ograve;n duy tu, bảo tr&igrave; h&agrave;ng năm với g&oacute;i dịch vụ chăm s&oacute;c mộ phần trọn đời. N&ecirc;n Qu&yacute; kh&aacute;ch thực sự y&ecirc;n t&acirc;m khi lựa chọn ch&uacute;ng t&ocirc;i l&agrave; nh&agrave; thầu x&acirc;y dựng.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Hiệu quả kinh tế: Với đội ngũ kỹ sư, kiến tr&uacute;c sư gi&agrave;u kinh nghiệm, đội ngũ thợ giỏi l&agrave;nh nghề c&oacute; tinh thần tr&aacute;ch nhiệm cao v&agrave; t&acirc;m huyết với nghề. Ch&uacute;ng t&ocirc;i tự tin sẽ đem đến dịch vụ x&acirc;y dựng cho khu&ocirc;n vi&ecirc;n mộ phần của người th&acirc;n gia đ&igrave;nh Qu&yacute; kh&aacute;ch đẹp, trang trọng như mong muốn m&agrave; vẫn đảm bảo hiệu quả kinh tế.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Hợp phong thủy:&nbsp;Tại C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường, khu&ocirc;n vi&ecirc;n mộ phần của Qu&yacute; kh&aacute;ch đều được lựa chọn nằm ở vị tr&iacute; long mạch, phong thủy tốt, c&oacute; hệ thống xử l&yacute; chất thải, nước thải theo c&ocirc;ng nghệ mới, ti&ecirc;n tiến n&ecirc;n kh&ocirc;ng c&oacute; trường hợp bị nước đọng.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">C&oacute; hệ thống nước, kh&iacute; thải:&nbsp;Dự &aacute;n của c&ocirc;ng vi&ecirc;n nằm xa khu d&acirc;n cư, với một nửa diện t&iacute;ch cho kiến tr&uacute;c v&agrave; cảnh quan, hệ thống c&acirc;y xanh phủ khắp c&ocirc;ng vi&ecirc;n nhưng c&aacute;ch xa c&aacute;c huyệt mộ tr&aacute;nh t&igrave;nh trạng rễ c&acirc;y đ&acirc;m v&agrave;o h&agrave;i cốt.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">C&ocirc;ng Vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường&nbsp;c&oacute; nhiều g&oacute;i dịch vụ, nhiều mẫu mộ&nbsp;kh&aacute;c nhau ph&ugrave; hợp với mọi tầng lớp, t&ocirc;n gi&aacute;o kh&aacute;c nhau trong&nbsp;x&atilde; hội.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/dich-vu/dich-vu-cham-soc-mo-phan-canh-quan\">Dịch vụ Chăm s&oacute;c mộ phần &amp; cảnh quan</a></span></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>Một số mẫu thiết kế khu&ocirc;n vi&ecirc;n mộ tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</strong></span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/001.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><em><a href=\"https://nghiatrangthienduong.com.vn/san-pham/khuon-vien-gia-toc\">Khu&ocirc;n vi&ecirc;n gia tộc</a> tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</em></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/29.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><em><a href=\"https://nghiatrangthienduong.com.vn/san-pham/khuon-vien-gia-dinh\">Khu&ocirc;n vi&ecirc;n gia đ&igrave;nh</a>&nbsp;tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</em></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/19.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><em><a href=\"https://nghiatrangthienduong.com.vn/san-pham/khuon-vien-doi\">Khu&ocirc;n vi&ecirc;n đ&ocirc;i</a> tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</em></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/002.jpg\" /><em><a href=\"https://nghiatrangthienduong.com.vn/san-pham/khuon-vien-don\">Khu&ocirc;n vi&ecirc;n đơn</a> tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</em></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/san-pham\">Sản phẩm của Thi&ecirc;n Đường&nbsp;</a></span></p>\r\n\r\n<p>&nbsp;</p>', 'true', 'News', 'dich-vu-xay-dung-mo-phan', '86_002.jpg', 'Dịch vụ xây dựng mộ phần', ' Dịch vụ xây dựng mộ phần tại Thiên Đường được đội ngũ kiến trúc sư lành nghề, với nhiều năm kinh nghiệm đảm nhiệm, đảm bảo đem đến cho Quý khách một công trình tâm linh chỉn chu, hoàn hảo nhất.', '2023-12-07 09:51:08', '2023-12-09 05:02:53'),
(158, 2, 70, 'Dịch vụ Chăm sóc mộ phần & Cảnh quan', 'Công viên Nghĩa trang Thiên Đường-Tuyên Quang cung cấp dịch vụ chăm sóc mộ phần và cảnh quan một cách chu đáo, tận tâm và chuyên nghiệp để người đã khuất được an lạc, người còn sống được an tâm.', NULL, '<p><span style=\"font-size:16px\"><strong>K&iacute;nh trọng, phụng dưỡng Cha Mẹ khi c&ograve;n sống l&agrave; việc l&agrave;m v&ocirc; c&ugrave;ng quan trọng, nhưng việc chăm s&oacute;c, hương kh&oacute;i cho phần mộ khi Cha Mẹ khuất n&uacute;i cũng cần thiết kh&ocirc;ng k&eacute;m. Việc l&agrave;m n&agrave;y kh&ocirc;ng những gi&uacute;p đấng sinh th&agrave;nh được y&ecirc;n nghỉ b&igrave;nh an m&agrave; c&ograve;n gi&uacute;p những người con được thanh thản v&igrave; đ&atilde; l&agrave;m trọn chữ Hiếu.</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>Hiểu được điều n&agrave;y, <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường-Tuy&ecirc;n Quang&nbsp;</a>cung cấp dịch vụ chăm s&oacute;c&nbsp;mộ phần v&agrave; cảnh quan&nbsp;một c&aacute;ch chu đ&aacute;o, tận t&acirc;m v&agrave; chuy&ecirc;n nghiệp để người đ&atilde; khuất được an lạc, người c&ograve;n sống được an t&acirc;m.</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Hầu hết ở c&aacute;c nghĩa trang, dịch vụ chăm s&oacute;c v&agrave; bảo vệ&nbsp;c&aacute;c phần mộ chưa thực sự chu đ&aacute;o, con ch&aacute;u mỗi lần đi viếng thăm mới c&oacute; cơ hội thắp hương, dọn cỏ, chăm s&oacute;c lại&nbsp;phần mộ của tổ ti&ecirc;n. Điều n&agrave;y chắc hẳn l&agrave; một nỗi trăn trở lớn với kh&ocirc;ng &iacute;t gia đ&igrave;nh. Tuy nhi&ecirc;n, tại <a href=\"https://nghiatrangthienduong.com.vn/\">Thi&ecirc;n Đường,</a> ch&uacute;ng t&ocirc;i cung cấp dịch vụ chăm s&oacute;c mộ phần v&agrave; cảnh quan để&nbsp;đảm bảo c&aacute;c phần mộ sẽ được&nbsp;chăm s&oacute;c cẩn thận, chu đ&aacute;o, kh&ocirc;ng c&ograve;n hiu quạnh, lẻ loi,&nbsp;m&agrave; lu&ocirc;n ấm &aacute;p bởi hương v&agrave; hoa.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường l&agrave; c&ocirc;ng vi&ecirc;n&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">Nghĩa trang</a>&nbsp;được x&acirc;y dựng theo m&ocirc; h&igrave;nh sinh th&aacute;i &ndash; du lịch t&acirc;m linh tại Miền Bắc &ndash; Việt Nam. Với quy m&ocirc; rộng&nbsp;lớn&nbsp;v&agrave; c&aacute;c dịch vụ cao cấp, đội ngũ nh&acirc;n vi&ecirc;n được đ&agrave;o tạo b&agrave;i bản, hiểu biết s&acirc;u về phong thủy, t&acirc;m linh, ch&uacute;ng t&ocirc;i cung cấp dịch vụ chăm s&oacute;c mộ phần v&agrave; cảnh quan trọn g&oacute;i, gi&uacute;p cho những người con ở xa kh&ocirc;ng thể tự tay chăm s&oacute;c mộ phần của tổ ti&ecirc;n&nbsp;an l&ograve;ng v&agrave; y&ecirc;n t&acirc;m c&ocirc;ng t&aacute;c bởi mộ phần những người th&acirc;n y&ecirc;u của qu&yacute; kh&aacute;ch sẽ lu&ocirc;n được chăm s&oacute;c v&agrave; vệ sinh cẩn thận.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/.1.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/san-pham\">c&aacute;c sản phẩm của Thi&ecirc;n Đường&nbsp;</a></span></p>\r\n\r\n<h2><span style=\"font-size:16px\"><strong>DỊCH VỤ CHĂM S&Oacute;C MỘ PHẦN &amp; CẢNH QUAN TRỌN G&Oacute;I BAO GỒM:</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">+ Thắp nhang v&agrave;o ng&agrave;y m&ugrave;ng 1 v&agrave; rằm</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ Vệ sinh mộ phần thường xuy&ecirc;n</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ Cắt cỏ, chăm s&oacute;c cảnh quan</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ Tr&ugrave;ng tu, bảo quản mộ phần</span></p>\r\n\r\n<h2><span style=\"font-size:16px\"><strong>KHI SỬ DỤNG DỊCH VỤ CHĂM S&Oacute;C MỘ PHẦN &amp; CẢNH QUAN TRỌN G&Oacute;I, QU&Yacute; KH&Aacute;CH SẼ ĐƯỢC:</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">+ An t&acirc;m, y&ecirc;n l&ograve;ng: Khi mộ phần của gia quyến lu&ocirc;n được chăm s&oacute;c v&agrave; vệ sinh cẩn thận. Bất kỳ sự cố n&agrave;o xảy ra, lu&ocirc;n c&oacute; ch&uacute;ng t&ocirc;i hỗ trợ tr&ugrave;ng tu, bảo dưỡng kịp thời.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ Phước b&aacute;u: Mộ phần của gia quyến Qu&yacute; kh&aacute;ch kh&ocirc;ng bị lạnh lẽo, u &aacute;m như c&aacute;c nơi kh&aacute;c v&igrave; lu&ocirc;n được nghe kinh kệ ng&agrave;y 2 thời, thắp nhang mỗi ng&agrave;y 2 lần.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/dich-vu/dich-vu-lap-ke-hoach-truoc\">Dịch vụ Lập kế hoạch trước</a></span></p>', 'true', 'News', 'dich-vu-cham-soc-mo-phan-canh-quan', '13_.1.jpg', 'Dịch vụ Chăm sóc mộ phần & Cảnh quan', 'Công viên Nghĩa trang Thiên Đường-Tuyên Quang cung cấp dịch vụ chăm sóc mộ phần và cảnh quan một cách chu đáo, tận tâm và chuyên nghiệp để người đã khuất được an lạc, người còn sống được an tâm.', '2023-12-07 14:04:28', '2023-12-09 05:02:47'),
(160, 2, 70, 'Dịch vụ Lập kế hoạch trước', 'Chúng ta chuẩn bị cho hầu hết mọi thứ trong cuộc sống. Nhưng dường như chúng ta quên rằng cuộc sống là hữu hạn, rằng bạn có thể ra đi bất cứ lúc nào. Vậy tại sao chúng ta lại bỏ qua nó mà không coi đó là một sự kiện cần LẬP KẾ HOẠCH TRƯỚC?', NULL, '<p><span style=\"font-size:16px\">Ch&uacute;ng ta chuẩn bị cho hầu hết mọi thứ trong cuộc sống. Nhưng dường như ch&uacute;ng ta qu&ecirc;n rằng cuộc sống l&agrave; hữu hạn, rằng bạn c&oacute; thể ra đi bất cứ l&uacute;c n&agrave;o. Vậy tại sao ch&uacute;ng ta lại bỏ qua n&oacute; m&agrave; kh&ocirc;ng coi đ&oacute; l&agrave; một sự kiện cần LẬP KẾ HOẠCH TRƯỚC?</span></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>Lập kế hoạch trước l&agrave; g&igrave;?</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">N&oacute;i một c&aacute;ch đơn giản, lập kế hoạch trước (đ&ocirc;i khi c&ograve;n được gọi l&agrave; &ldquo;sắp xếp trước&rdquo;, &ldquo;lập kế hoạch sắp xếp trước&rdquo; hoặc &ldquo;lập kế hoạch trước nhu cầu&rdquo;) c&oacute; nghĩa l&agrave; sắp xếp trước cho lễ tang của bạn trước khi c&oacute; nhu cầu về c&aacute;c dịch vụ n&agrave;y, như một bảo hiểm tang lễ hoặc hợp đồng tang lễ cần thiết trước. Kh&aacute;i niệm n&agrave;y c&oacute; vẻ mới lạ v&agrave; thậm ch&iacute; l&agrave; thật kh&oacute; hiểu. Tuy nhi&ecirc;n, lập kế hoạch trước thực ra kh&ocirc;ng phải l&agrave; điều g&igrave; qu&aacute; mới mẻ.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/111.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m : <a href=\"https://nghiatrangthienduong.com.vn/tien-ich-dich-vu/dich-vu-xay-dung-mo-phan\">Dịch vụ x&acirc;y dựng mộ phần</a></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/san-pham\">C&aacute;c sản phẩm của Thi&ecirc;n Đường</a></span></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>Lợi &iacute;ch khi lựa chọn dịch vụ lập kế hoạch trước tại<a href=\"https://nghiatrangthienduong.com.vn/\"> C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường-Tuy&ecirc;n Quang</a></strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Kh&ocirc;ng ph&acirc;n biệt địa vị của bạn trong gia đ&igrave;nh &ndash; d&ugrave; bạn l&agrave; chồng hay vợ; cha hoặc mẹ; hoặc thậm ch&iacute; l&agrave; con trai hay con g&aacute;i &ndash; sự ra đi của bạn sẽ l&agrave; một sự kiện duy nhất t&aacute;c động đến gia đ&igrave;nh v&agrave; những người th&acirc;n y&ecirc;u của bạn nhiều nhất, cả về t&igrave;nh cảm v&agrave; t&agrave;i ch&iacute;nh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Lập kế hoạch trước sẽ đem lại cho bạn v&agrave; cho gia đ&igrave;nh rất nhiều lợi &iacute;ch:</span></p>\r\n\r\n<h3><span style=\"font-size:16px\"><strong>Cho bạn</strong></span></h3>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\">Bạn tự chủ chi ph&iacute; trong khoản t&agrave;i ch&iacute;nh m&agrave; bạn đ&atilde; chuẩn bị.</span></li>\r\n	<li><span style=\"font-size:16px\">Bạn được lựa chọn buổi lễ của m&igrave;nh.</span></li>\r\n	<li><span style=\"font-size:16px\">Bạn c&oacute; đủ thời gian để chuẩn bị kỹ lưỡng.</span></li>\r\n</ul>\r\n\r\n<h3><span style=\"font-size:16px\"><strong>Cho gia đ&igrave;nh</strong></span></h3>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\">Gia đ&igrave;nh bạn sẽ biết phải li&ecirc;n hệ với ai khi tang gia bối rối.</span></li>\r\n	<li><span style=\"font-size:16px\">Gia đ&igrave;nh bạn sẽ kh&ocirc;ng bị &aacute;p lực t&agrave;i ch&iacute;nh khi việc kh&ocirc;ng may xảy ra.</span></li>\r\n	<li><span style=\"font-size:16px\">Gia đ&igrave;nh bạn c&oacute; thể tập trung v&agrave;o ghi nhớ kỷ niệm v&agrave; h&agrave;n gắn nỗi đau.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/dich-vu/dich-vu-xay-dung-gia-pha\">Dịch vụ x&acirc;y dựng gia phả</a></span></p>', 'true', 'News', 'dich-vu-lap-ke-hoach-truoc', '92_111.jpg', 'Dịch vụ lập kế hoạch trước', 'Chúng ta chuẩn bị cho hầu hết mọi thứ trong cuộc sống. Nhưng dường như chúng ta quên rằng cuộc sống là hữu hạn, rằng bạn có thể ra đi bất cứ lúc nào. Vậy tại sao chúng ta lại bỏ qua nó mà không coi đó là một sự kiện cần LẬP KẾ HOẠCH TRƯỚC?', '2023-12-08 07:50:04', '2023-12-09 05:02:41');
INSERT INTO `posts` (`id`, `user_id`, `category_id`, `name`, `detail`, `info`, `content`, `status`, `sort_by`, `slug`, `img`, `title`, `description`, `created_at`, `updated_at`) VALUES
(162, 2, 70, 'Dịch vụ Xây dựng gia phả', '“Hiếu đạo gia tộc, lộc khởi muôn đời” là điều mà bất cứ ai trong mỗi chúng ta cũng muốn hướng đến, vì lẽ đó tại Công viên Nghĩa trang Thiên Đường- Tuyên Quang, chúng tôi xây dựng gia phả để tạo ra nơi lưu giữ, kết nối và tôn vinh những giá trị truyền thống của các dòng tộc.', NULL, '<p><span style=\"font-size:16px\"><strong>&ldquo;Hiếu đạo gia tộc, lộc khởi mu&ocirc;n đời&rdquo; l&agrave; điều m&agrave; bất cứ ai trong mỗi ch&uacute;ng ta cũng muốn hướng đến, v&igrave; lẽ đ&oacute; tại <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường- Tuy&ecirc;n Quang</a>, ch&uacute;ng t&ocirc;i x&acirc;y dựng gia phả để tạo ra nơi lưu giữ, kết nối v&agrave; t&ocirc;n vinh những gi&aacute; trị truyền thống của c&aacute;c d&ograve;ng tộc.</strong></span></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>Gia Phả l&agrave; g&igrave;?</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Gia phả hay gia phổ l&agrave; bản ghi ch&eacute;p t&ecirc;n họ, tuổi t&aacute;c, ng&agrave;y giỗ, vai tr&ograve; v&agrave; c&ocirc;ng đức của cha mẹ, &ocirc;ng b&agrave;, ti&ecirc;n tổ v&agrave; mộ phần của một gia đ&igrave;nh lớn hay một d&ograve;ng họ.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Gia phả c&oacute; thể được coi như một bản sử k&yacute; của một gia đ&igrave;nh hay một d&ograve;ng họ. Gia phả c&oacute; khi gọi l&agrave; Phổ k&yacute;, c&oacute; khi l&agrave; Phổ truyền. C&aacute;c nh&agrave; T&ocirc;ng thất (d&ograve;ng d&otilde;i vua quan), c&oacute; khi gọi gia phả của vương triều m&igrave;nh hay gia tộc m&igrave;nh bằng từ ngữ tr&acirc;n trọng hơn: Ngọc phả, Thế phả&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/000.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tien-ich-dich-vu/dich-vu-tu-van-to-chuc-tang-le-tron-goi\">Dịch vụ tổ chức tang lễ trọn g&oacute;i</a></span></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>&Yacute; nghĩa của việc x&acirc;y dựng gia phả&nbsp;</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Gia phả l&agrave;&nbsp;nơi lưu lại những t&agrave;i liệu về d&ograve;ng họ, gia đ&igrave;nh. Từ nguồn gốc, h&igrave;nh ảnh &ocirc;ng b&agrave; tổ ti&ecirc;n đến những cống hiến, th&agrave;nh tựu của c&aacute;c con c&aacute;c ch&aacute;u hiện tại&hellip; C&oacute; thể n&oacute;i gia phả ch&iacute;nh l&agrave; một kho dữ liệu v&ocirc; hạn lưu giữ m&atilde;i m&atilde;i cho con ch&aacute;u mu&ocirc;n đời.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">X&acirc;y dựng gia phả c&ograve;n l&agrave; c&aacute;ch&nbsp;lưu giữ v&agrave; truyền tải những th&ocirc;ng điệp tốt đẹp hiệu quả nhất đến thế hệ sau&nbsp;m&agrave; kh&ocirc;ng sợ bị hư hỏng, ngắt qu&atilde;ng như những văn bản bằng giấy. Hơn nữa, đ&acirc;y cũng l&agrave; c&aacute;ch để giới trẻ tiếp cận với nguồn cội một c&aacute;ch dễ d&agrave;ng v&agrave; tự nguyện nhất.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ngo&agrave;i ra, gia phả mang lại nhiều &yacute; nghĩa kh&aacute;c: gi&uacute;p c&aacute;c thế hệ con ch&aacute;u lu&ocirc;n biết sống đ&ugrave;m bọc, gi&uacute;p đỡ nhau, &ldquo;l&aacute; l&agrave;nh đ&ugrave;m l&aacute; r&aacute;ch&rdquo;, khơi gợi niềm tự h&agrave;o d&ograve;ng tộc. Đồng thời c&ograve;n l&agrave; cầu nối khắp mọi nơi của c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh, d&ograve;ng họ.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/dia-the-phong-thuy\">Địa thế phong thủy tại Thi&ecirc;n Đường</a></span></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>Dịch vụ x&acirc;y dựng gia phả tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">C&ocirc;ng vi&ecirc;n Nghĩa trang <a href=\"https://nghiatrangthienduong.com.vn/\">Thi&ecirc;n Đường</a> cung cấp dịch vụ tạo lập c&acirc;y gia phả d&ograve;ng họ, lưu trữ phần mộ cho gia tộc được nghi&ecirc;n cứu c&ocirc;ng phu dưới sự tư vấn của c&aacute;c chuy&ecirc;n gia về C&ocirc;ng nghệ Th&ocirc;ng tin nhằm bảo tồn v&agrave; t&ocirc;n vinh những gi&aacute; trị truyền thống của d&acirc;n tộc.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Giao diện gia phả được b&agrave;i tr&iacute; đẹp mắt với c&aacute;c chức năng ch&iacute;nh được tối ưu h&oacute;a tốt nhất để n&acirc;ng cao hiệu quả việc sử dụng.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Đặc biệt l&agrave; c&acirc;y gia phả, đ&acirc;y l&agrave; chức năng đ&atilde; được nghi&ecirc;n cứu v&agrave; cải tiến nhằm đ&aacute;p ứng y&ecirc;u cầu của việc cập nhật c&acirc;y phả đồ từ bất kỳ th&agrave;nh vi&ecirc;n n&agrave;o, chi họ n&agrave;o trong d&ograve;ng tộc v&agrave; truy t&igrave;m ngược c&aacute;c th&agrave;nh vi&ecirc;n trong d&ograve;ng tộc qua đ&oacute; chắp nối c&aacute;c d&ograve;ng họ lại để t&igrave;m ra gốc g&aacute;c, thủy tổ.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: Dịch vụ An ninh&nbsp;</span></p>', 'true', 'News', 'dich-vu-xay-dung-gia-pha', '57_000.jpg', 'Dịch vụ xây dựng gia phả', '“Hiếu đạo gia tộc, lộc khởi muôn đời” là điều mà bất cứ ai trong mỗi chúng ta cũng muốn hướng đến, vì lẽ đó tại Thiên Đường, chúng tôi xây dựng gia phả để tạo ra nơi lưu giữ và tôn vinh những giá trị truyền thống của các dòng tộc.', '2023-12-08 08:20:56', '2023-12-09 05:02:35'),
(164, 2, 70, 'Dịch vụ An ninh', 'Canh giữ bảo vệ cho giấc ngủ ngàn thu của những người đã khuất là một trong những nhiệm vụ mà Công Viên Nghĩa trang Thiên Đường ưu tiên hàng đầu. Chúng tôi thấu hiểu rằng gia quyến sẽ cảm thấy an tâm, yên lòng khi biết được rằng mộ phần của những người thân đang được ngày đem canh gác, bảo vệ, chăm sóc.', NULL, '<p><span style=\"font-size:16px\"><strong>Canh giữ bảo vệ cho giấc ngủ ng&agrave;n thu của những người đ&atilde; khuất l&agrave; một trong những nhiệm vụ m&agrave;&nbsp;<a href=\"https://congvienthienduong.com.vn/\">C&ocirc;ng Vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</a>&nbsp;ưu ti&ecirc;n h&agrave;ng đầu. Ch&uacute;ng t&ocirc;i thấu hiểu rằng gia quyến sẽ cảm thấy an t&acirc;m, y&ecirc;n l&ograve;ng khi biết được rằng mộ phần của những người th&acirc;n đang được ng&agrave;y đem canh g&aacute;c, bảo vệ, chăm s&oacute;c.</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/lll.jpg\" /></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\">Đội ngũ bảo vệ tại C&ocirc;ng vi&ecirc;n <a href=\"https://nghiatrangthienduong.com.vn/\">Nghĩa trang Thi&ecirc;n Đường</a> được đ&agrave;o tạo b&agrave;i bản, chuy&ecirc;n nghiệp v&agrave; tham gia lớp bồi dưỡng nghiệp vụ do c&aacute;c chuy&ecirc;n gia của những c&ocirc;ng ty bảo vệ h&agrave;ng đầu đ&agrave;o tạo. Do vậy,&nbsp;họ&nbsp;lu&ocirc;n nhận thức được tr&aacute;ch nhiệm của m&igrave;nh tại đ&acirc;y l&agrave; một nhiệm vụ cao cả, linh thi&ecirc;ng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/kien-truc-canh-quan\">Kiến tr&uacute;c cảnh quan của Thi&ecirc;n Đường</a></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ngo&agrave;i ra, tại C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n đường, ch&uacute;ng t&ocirc;i cung cấp dịch vụ an ninh bằng camera 24/7 c&oacute; độ ph&acirc;n giải cao, t&iacute;ch hợp cảm biến nhiệt. Cứ mỗi 6m sẽ được gắn camera an ninh để chắc chắn rằng tất cả khu&ocirc;n vi&ecirc;n đều được ghi lại. Đặc biệt, mỗi camera sẽ được gắn với một mật khẩu ri&ecirc;ng, v&agrave; mật khẩu n&agrave;y sẽ được cấp cho qu&yacute; kh&aacute;ch h&agrave;ng c&oacute; phần mộ người th&acirc;n nằm trong phạm vi ghi h&igrave;nh của camera đ&oacute;. D&ugrave; cho đang ở nơi đ&acirc;u, qu&yacute; kh&aacute;ch h&agrave;ng c&oacute; thể truy cập v&agrave; quan s&aacute;t phần mộ của người th&acirc;n từng ng&agrave;y từng giờ.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/003.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/\">Thi&ecirc;n Đường </a>kh&ocirc;ng chỉ lu&ocirc;n lắng nghe v&agrave; thấu hiểu từng mong muốn, nhu cầu của kh&aacute;ch h&agrave;ng v&agrave; đ&aacute;p ứng tối đa nhu cầu đ&oacute; của qu&yacute; kh&aacute;ch m&agrave; đ&acirc;y c&ograve;n l&agrave; địa chỉ tịn cậy trong việc bảo vệ an ninh theo y&ecirc;u cầu của qu&yacute; kh&aacute;ch suốt 24/7. Hơn thế nữa, c&aacute;c nh&acirc;n vi&ecirc;n của Thi&ecirc;n Đường lu&ocirc;n đảm bảo tuyệt đối t&iacute;nh b&iacute; mật về th&ocirc;ng tin của qu&yacute; kh&aacute;ch h&agrave;ng, qu&yacute; doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/dich-vu/dich-vu-ho-tro-an-uong-luu-tru\">Dịch vụ hỗ trợ ăn uống, lưu tr&uacute;</a></span></p>', 'true', 'News', 'dich-vu-an-ninh', '91_lll.jpg', 'Dịch vụ An ninh', 'Chúng tôi thấu hiểu rằng gia quyến sẽ cảm thấy an tâm, yên lòng khi biết được rằng mộ phần của những người thân đang được ngày đem canh gác, bảo vệ, chăm sóc.', '2023-12-08 09:35:14', '2023-12-09 05:02:28'),
(166, 2, 70, 'Dịch vụ Hỗ trợ ăn uống, lưu trú', 'Để đảm bảo sức khỏe và thời gian nghỉ ngơi thoải mải, cũng như dành thời gian nghiên cứu sản phẩm, dịch vụ, khuôn viên đất của khách hàng, Công viên Nghĩa trang Thiên Đường-TQ cung cấp dịch vụ ăn uống và lưu trú với thực đơn phong phú, hội tụ đặc sản đặc sắc của vùng đất Tuyên Quang.', NULL, '<p><span style=\"font-size:16px\"><strong>Để đảm bảo sức khỏe v&agrave; thời gian nghỉ ngơi thoải mải, cũng như d&agrave;nh thời gian nghi&ecirc;n cứu sản phẩm, dịch vụ, khu&ocirc;n vi&ecirc;n đất của kh&aacute;ch h&agrave;ng, <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường-TQ </a>cung cấp dịch vụ ăn uống v&agrave; lưu tr&uacute; với thực đơn phong ph&uacute;, hội tụ đặc sản đặc sắc của v&ugrave;ng đất Tuy&ecirc;n Quang.</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Chuyến tham quan, thăm viếng người th&acirc;n của Q&uacute;y kh&aacute;ch tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường sẽ trở n&ecirc;n trọn vẹn v&agrave; thư th&aacute;i hơn khi c&oacute; dịch vụ ăn uống v&agrave; lưu tr&uacute;. C&ograve;n g&igrave; tuyệt vời hơn khi Q&uacute;y kh&aacute;ch vừa thưởng thức c&aacute;c m&oacute;n đặc sản của v&ugrave;ng đất Tuy&ecirc;n Quang, vừa c&oacute; thời gian nghỉ ngơi, giao lưu tr&ograve; chuyện với những kh&aacute;ch h&agrave;ng kh&aacute;c, với những con người của nhiều v&ugrave;ng miền tr&ecirc;n cả nước.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/004.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><em>Khu ăn uống v&agrave; lưu tr&uacute; cho kh&aacute;ch h&agrave;ng</em></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Đối với mỗi nh&acirc;n vi&ecirc;n của<a href=\"https://nghiatrangthienduong.com.vn/\"> Thi&ecirc;n Đường,</a> được phục vụ v&agrave; đ&oacute;n tiếp Q&uacute;y kh&aacute;ch h&agrave;ng l&agrave; niềm tự h&agrave;o của ch&uacute;ng t&ocirc;i. Sự h&agrave;i l&ograve;ng, hoan hỉ của kh&aacute;ch h&agrave;ng khi lựa chọn sản phẩm v&agrave; dịch vụ tại Thi&ecirc;n Đường l&agrave; động lực để ch&uacute;ng t&ocirc;i th&ecirc;m nỗ lực, cố gắng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Với tất cả tấm l&ograve;ng của m&igrave;nh, <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang </a>Thi&ecirc;n Đường xin tr&acirc;n trọng cảm ơn Q&uacute;y kh&aacute;ch h&agrave;ng đ&atilde;, đang v&agrave; c&oacute; &yacute; định gửi trọn niềm tin tới sản phẩm v&agrave; dịch vụ m&agrave; ch&uacute;ng t&ocirc;i cung cấp.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tien-ich-dich-vu/dich-vu-tu-van-phong-thuy\">dịch vụ tư vấn phong thủy&nbsp;</a></span></p>', 'true', 'News', 'dich-vu-ho-tro-an-uong-luu-tru', '23_004.jpg', 'Dịch vụ Hỗ trợ ăn uống, lưu trú', 'Công viên Nghĩa trang Thiên Đường cung cấp dịch vụ ăn uống và lưu trú với thực đơn phong phú, hội tụ đặc sản đặc sắc của vùng đất Tuyên Quang.', '2023-12-09 02:49:31', '2023-12-09 05:02:20'),
(168, 2, 70, 'Dịch vụ Tư vấn phong thủy', 'Trong văn hóa của người Việt về “Mồ yên mả đẹp” hay như quan điểm về “Nhất mộ, nhì phong, tam bát tự” vô cùng quan trọng trong đời sống tín ngưỡng tâm linh cũng như những mong muốn về gia đạo trên dưới đều thuận hòa, an vui. Phong thủy âm trạch tốt có thể ảnh hưởng đến vận mệnh và sự hưng thịnh của gia đình, họ tộc.', NULL, '<p><strong><span style=\"font-size:16px\">Trong văn h&oacute;a của người Việt về &ldquo;Mồ y&ecirc;n mả đẹp&rdquo; hay như quan điểm về &ldquo;Nhất mộ, nh&igrave; phong, tam b&aacute;t tự&rdquo; v&ocirc; c&ugrave;ng quan trọng trong đời sống t&iacute;n ngưỡng t&acirc;m linh cũng như những mong muốn về gia đạo tr&ecirc;n dưới đều thuận h&ograve;a, an vui. Phong thủy &acirc;m trạch tốt c&oacute; thể ảnh hưởng đến vận mệnh v&agrave; sự hưng thịnh của gia đ&igrave;nh, họ tộc.</span></strong></p>\r\n\r\n<p><span style=\"font-size:16px\">Từ xa xưa, c&aacute;c vị vua ch&uacute;a đ&atilde; c&oacute; quan niệm &quot;Thứ nhất dương cơ, thứ hai &acirc;m phần &ndash; Việc x&acirc;y cất quan trọng sau cung điện l&agrave; lăng mộ&quot;, hay &ldquo;Tức vị trị lăng &ndash; nghĩa l&agrave; ngay khi tại vị, vua đ&atilde; cho tiến h&agrave;nh khảo s&aacute;t địa thế v&agrave; x&acirc;y dựng lăng tẩm cho m&igrave;nh&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Với c&aacute;c bậc đại sư phong thủy như Tả Ao hay c&aacute;c vị quan lại, c&aacute;c gia tộc lớn đều t&igrave;m kiếm miền đất c&oacute; phong thủy tốt l&agrave; nơi an t&aacute;ng, lưu giữ truyền thống gia đ&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/dia-the-phong-thuy\">Địa thế phong thủy tại Thi&ecirc;n Đường</a></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/01.jpg\" style=\"height:624px; width:624px\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Phong thủy &acirc;m trạch ảnh hưởng lớn đến vận mệnh v&agrave; sự hưng thịnh của gia đ&igrave;nh, họ tộc</span></em></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: Phong thủy &acirc;m tr&aacute;ch mộ phần cải t&aacute;ng</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Khi lựa chọn dịch vụ Tư vấn phong thủy tại <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</a>, kh&aacute;ch h&agrave;ng sẽ được:</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ Tư vấn phong thủy &acirc;m phần trực tiếp</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ Chọn hướng cụ thể ph&ugrave; hợp với trạch đất cũng như th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh (thường l&agrave; con trai trưởng)</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ Chọn vị tr&iacute; đẹp v&agrave; tư vấn kiến tr&uacute;c phong thủy to&agrave;n bộ &acirc;m trạch ph&ugrave; hợp với c&ocirc;ng năng, văn h&oacute;a của từng v&ugrave;ng miền</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ Tư vấn ng&agrave;y tiến h&agrave;nh x&acirc;y dựng, tu tạo sửa chữa</span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ Tư vấn chi tiết th&ecirc;m về c&aacute;c quy tr&igrave;nh nghi thức nghi lễ cần thiết</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Hiện nay, quan điểm &quot;t&aacute;ng tiền ấm hậu&quot; của người xưa vẫn được lưu truyền, việc chọn đất sinh phần kh&ocirc;ng chỉ l&agrave; h&agrave;nh động thể hiện Hiếu Đạo với &ocirc;ng b&agrave;, cha mẹ m&agrave; c&ograve;n l&agrave; c&aacute;ch đem lại ph&uacute;c phần đời đời cho hậu bối mai sau.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m:<a href=\"https://nghiatrangthienduong.com.vn/tien-ich-dich-vu/dich-vu-hoa-tang\"> Dịch vụ hỏa t&aacute;ng&nbsp;</a></span></p>', 'true', 'News', 'dich-vu-tu-van-phong-thuy', '22_01.jpg', 'Dịch vụ tư vấn phong thủy', 'Phong thủy âm trạch tốt có thể ảnh hưởng đến vận mệnh và sự hưng thịnh của gia đình, họ tộc.', '2023-12-09 02:58:30', '2023-12-09 05:02:12'),
(170, 2, 70, 'Dịch vụ Hỏa táng', 'Dịch vụ hỏa táng tại Công viên Nghĩa trang Thiên Đường-Tuyên Quang sử dụng công nghệ TABO hiện đại của Thụy Điển, đảm bảo khí thải không màu, không mùi và không ô nhiễm.', NULL, '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Dịch vụ hỏa t&aacute;ng tại<a href=\"http://congvienthienduong.com.vn\"> C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường-Tuy&ecirc;n Quang </a>sử dụng c&ocirc;ng nghệ TABO hiện đại của Thụy Điển, đảm bảo kh&iacute; thải kh&ocirc;ng m&agrave;u, kh&ocirc;ng m&ugrave;i v&agrave; kh&ocirc;ng &ocirc; nhiễm.</strong></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Dịch vụ hỏa t&aacute;ng&nbsp;</strong>đ&atilde; v&agrave; đang dần trở th&agrave;nh xu hướng an t&aacute;ng văn minh, văn minh, hiện đại v&agrave; l&agrave; h&igrave;nh thức được nhiều gia đ&igrave;nh Việt lựa chọn. Hiện nay, dịch vụ hỏa t&aacute;ng nguy&ecirc;n xương đang được nhiều gia đ&igrave;nh quan t&acirc;m v&agrave; lựa chọn bởi t&iacute;nh văn minh, sạch sẽ v&agrave; chi ph&iacute; ph&ugrave; hợp với điều kiện kinh tế của nhiều gia đ&igrave;nh.</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Những điều cần biết về dịch vụ hỏa t&aacute;ng</strong></span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đầu ti&ecirc;n, ch&uacute;ng ta c&ugrave;ng t&igrave;m hiểu h&igrave;nh thức hỏa t&aacute;ng l&agrave; như thế n&agrave;o. Sau đ&oacute;, ch&uacute;ng ta đi s&acirc;u v&agrave;o ph&acirc;n t&iacute;ch ưu điểm cũng như quy tr&igrave;nh của dịch vụ hỏa t&aacute;ng.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Hỏa t&aacute;ng l&agrave; g&igrave;?</strong></span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Hỏa t&aacute;ng (hỏa thi&ecirc;u, thi&ecirc;u) l&agrave; h&igrave;nh thức d&ugrave;ng lửa để thi&ecirc;u x&aacute;c người đ&atilde; mất th&agrave;nh tro. Sau đ&oacute; d&ugrave;ng b&igrave;nh hoặc hũ để chứa đựng tro cốt đ&oacute; v&agrave; trao lại cho th&acirc;n nh&acirc;n.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/02.jpg\" /><em>Nh&agrave; hỏa t&aacute;ng tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</em></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Ưu điểm</strong></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">H&igrave;nh thức hỏa t&aacute;ng c&oacute; những ưu điểm sau:</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">+ Tiện di chuyển v&agrave; cất giữ: thể t&iacute;ch chiếc b&igrave;nh tro cốt kh&ocirc;ng lớn, th&acirc;n nh&acirc;n người qu&aacute; cố c&oacute; thể dễ d&agrave;ng di chuyển đi bất cứ đ&acirc;u, để bất cứ nơi n&agrave;o ph&ugrave; hợp.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">+ Tiết kiệm diện t&iacute;ch đất mai t&aacute;ng: sau khi hỏa thi&ecirc;u, th&acirc;n x&aacute;c người mất chỉ c&ograve;n lại một nắm tro cất v&agrave;o b&igrave;nh. V&igrave; vậy, diện t&iacute;ch đất an t&aacute;ng sẽ kh&ocirc;ng bị tốn qu&aacute; nhiều đất.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">+ Chi ph&iacute; hỏa t&aacute;ng kh&ocirc;ng cao bằng những h&igrave;nh thức kh&aacute;c: hiện nay, tổng chi ph&iacute; cho một lần hỏa t&aacute;ng trung b&igrave;nh khoảng 10-12 triệu đồng. So với việc mua đất, mua quan t&agrave;i, x&acirc;y mộ th&igrave; chi ph&iacute; hỏa t&aacute;ng thấp hơn nhiều lần.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">+ An to&agrave;n vệ sinh: hỏa t&aacute;ng với nhiệt độ cao ti&ecirc;u diệt to&agrave;n bộ vi khuẩn hoặc bệnh truyền nhiễm. Hơn nữa, phần tro cốt c&ograve;n lại tuyệt đối vệ sinh, kh&ocirc;ng thể g&acirc;y ra bất kỳ nguy cơ &ocirc; nhiễm n&agrave;o.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">+ Kh&ocirc;ng c&ograve;n phiền phức với việc cải t&aacute;ng, bốc mộ: khi gặp trường hợp bắt buộc phải di dời, việc vận chuyển hũ cốt dễ d&agrave;ng hơn so với cải t&aacute;ng hoặc bốc mộ.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">+ Thuận tiện bảo vệ, chăm s&oacute;c hũ cốt: nếu th&acirc;n nh&acirc;n chọn c&aacute;ch cất giữ b&igrave;nh tro cốt, họ c&oacute; thể dễ d&agrave;ng vệ sinh, chăm s&oacute;c v&agrave; bảo vệ n&oacute; bất cứ l&uacute;c n&agrave;o. Hũ tro sẽ kh&ocirc;ng c&oacute; khả năng bị mọc cỏ dại, bị đ&agrave;o trộm&hellip;</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Quy tr&igrave;nh hoả t&aacute;ng</strong></span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Hỏa t&aacute;ng diễn ra trong khoảng 3-4 tiếng đồng hồ trong nhiệt độ 1200-1400 độ C. Để đảm bảo kh&ocirc;ng bị nhầm lẫn danh t&iacute;nh, c&aacute;c trung t&acirc;m hỏa t&aacute;ng c&oacute; hệ thống d&aacute;n nh&atilde;n cẩn thận.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Ngo&agrave;i ra, phần tro cốt sau hỏa thi&ecirc;u cũng được đưa v&agrave;o hệ thống xử l&yacute; th&agrave;nh tro mịn trước khi bỏ v&agrave;o hủ v&agrave; giao lại cho th&acirc;n nh&acirc;n. Quy tr&igrave;nh hỏa t&aacute;ng đảm bảo chuẩn, hợp vệ sinh v&agrave; ch&iacute;nh x&aacute;c.</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Dịch vụ hỏa t&aacute;ng tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</strong></span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"http://congvienthienduong.com.vn\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường </a>triển khai dịch vụ hỏa t&aacute;ng với quy tr&igrave;nh v&agrave; thiết bị ti&ecirc;n tiến được nhập khẩu từ Thụy Điển. Kỹ thuật hỏa t&aacute;ng c&oacute; ưu điểm vượt trội l&agrave; hỏa t&aacute;ng kh&ocirc;ng kh&oacute;i, kh&ocirc;ng m&ugrave;i v&agrave; bảo vệ m&ocirc;i trường.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Hơn nữa, thời gian hỏa t&aacute;ng ở Thi&ecirc;n Đường nhanh, địa điểm hỏa t&aacute;ng sạch sẽ, sang trọng v&agrave; trang nghi&ecirc;m. Điều n&agrave;y sẽ l&agrave;m người ở lại được an ủi phần n&agrave;o nỗi đau mất người th&acirc;n của m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Xem th&ecirc;m: dịch vụ tư vấn online</span></p>', 'true', 'News', 'dich-vu-hoa-tang', '53_02.jpg', NULL, NULL, '2023-12-09 03:36:39', '2023-12-09 05:02:06'),
(171, 2, 68, 'Công viên nghĩa trang Thiên đường - Đất sinh phần đẹp đáng đầu tư', 'Hiện nay, trước tình trạng quỹ đất nghĩa trang ngày càng eo hẹp, việc mua sẵn đất sinh phần trở thành một ưu tiên đối với nhiều người dân. Khi lựa chọn đất nghĩa trang, đa phần mọi người đều mong muốn chọn được lô đất có phong thủy tốt, giao thông thuận tiện để tiện bề thăm viếng, chăm sóc.', NULL, '<p><strong><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/TMB_PA1.png\" /></span></strong></p>\r\n\r\n<p><strong><span style=\"font-size:16px\">Hiện nay, trước t&igrave;nh trạng quỹ đất nghĩa trang ng&agrave;y c&agrave;ng eo hẹp, việc mua sẵn đất sinh phần trở th&agrave;nh một ưu ti&ecirc;n đối với nhiều người d&acirc;n. Khi lựa chọn đất nghĩa trang, đa phần mọi người đều mong muốn chọn được l&ocirc; đất c&oacute; phong thủy tốt, giao th&ocirc;ng thuận tiện để tiện bề thăm viếng, chăm s&oacute;c.</span></strong></p>\r\n\r\n<p><span style=\"font-size:16px\">Th&ocirc;ng thường, khi chọn mua đất sinh phần, c&aacute;c gia đ&igrave;nh thường lựa chọn c&aacute;c dự &aacute;n uy t&iacute;n với ph&aacute;p l&yacute; minh bạch, r&otilde; r&agrave;ng để tr&aacute;nh viễn cảnh di dời sau n&agrave;y. Ngo&agrave;i ra, trong bối cảnh x&atilde; hội ng&agrave;y c&agrave;ng ph&aacute;t triển, con người dần trở n&ecirc;n bận rộn, nhu cầu t&igrave;m hoa vi&ecirc;n nghĩa trang c&oacute; dịch vụ tổ chức an t&aacute;ng, chăm s&oacute;c mộ phần trọn g&oacute;i l&agrave; nhu cầu thiết thực, gi&uacute;p giảm bớt nỗi lo cho gia quyến.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Hiểu được nhu cầu đ&oacute;, <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n nghĩa trang Thi&ecirc;n Đường</a> ra đời kh&ocirc;ng chỉ đ&aacute;p ứng y&ecirc;u cầu l&agrave; một dự &aacute;n uy t&iacute;n, được ph&ecirc; duyệt r&otilde; r&agrave;ng trong quy hoạch m&agrave; c&ograve;n mang gi&aacute; trị hướng về nguồn cội, đảm bảo ph&uacute;c kh&iacute; l&acirc;u d&agrave;i, c&oacute; vị tr&iacute; đ&aacute;p ứng được y&ecirc;u cầu t&igrave;m kiếm đất sinh phần đẹp của người d&acirc;n thủ đ&ocirc; v&agrave; c&aacute;c tỉnh l&acirc;n cận.</span></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>Dự &aacute;n mang t&iacute;nh bền vững</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">C&ocirc;ng vi&ecirc;n nghĩa trang <a href=\"https://nghiatrangthienduong.com.vn/\">Thi&ecirc;n Đường</a> được ch&iacute;nh quyền địa phương quy hoạch để đảm bảo yếu tố chắc chắn về mặt ph&aacute;p l&yacute; v&agrave; kh&ocirc;ng bị x&aacute;o trộn theo thời gian.&nbsp;Dự &aacute;n nằm trong quy hoạch tổng thể ph&aacute;t triển kinh tế - x&atilde; hội tỉnh Tuy&ecirc;n Quang đến năm 2020 v&agrave; tầm nh&igrave;n năm 2025 đ&atilde; được Thủ tướng Ch&iacute;nh phủ ph&ecirc; duyệt.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ngo&agrave;i ra, với kinh nghiệm l&acirc;u năm trong lĩnh vực x&acirc;y dựng v&agrave; sự am hiểu về văn h&oacute;a t&acirc;m linh của người Việt, C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường được chăm ch&uacute;t tỉ mẩn từ kh&acirc;u thiết kế, lựa chọn vật liệu đến kh&acirc;u thi c&ocirc;ng. &nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Đặc biệt nhất, khu vực an t&aacute;ng&nbsp;tại Thi&ecirc;n Đường được triển khai tr&ecirc;n 6 quả đồi bao quanh l&agrave; d&ograve;ng suối tự nhi&ecirc;n tu&acirc;n theo quy luật ngũ h&agrave;nh: Kim, mộc, thủy, hỏa, thổ; thế đất mang <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/dia-the-phong-thuy\">d&aacute;ng h&igrave;nh Linh Quy</a>, vượng sơn vượng hướng. Hệ thống hồ nước chảy quanh c&ocirc;ng vi&ecirc;n đem lại nhiều hanh th&ocirc;ng, thuận lợi m&aacute;t mẻ bốn m&ugrave;a trợ kh&iacute;, ph&ugrave; dương. Nơi đ&acirc;y c&oacute; cấu tạo địa chất h&agrave;ng trăm triệu năm, to&agrave;n bộ đất an t&aacute;ng được kiến tạo tr&ecirc;n v&ugrave;ng&nbsp;đất liền thổ&nbsp;&ndash; loại&nbsp;đất&nbsp;&quot;sạch&quot;, &quot;mới&quot;, rất tốt cho việc an t&aacute;ng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/1-goc-cong-vien-thien-duong.png\" style=\"height:468px; width:624px\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Một g&oacute;c C&ocirc;ng vi&ecirc;n t&acirc;m linh Thi&ecirc;n Đường.</span></em></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>Vị tr&iacute;&nbsp;thuận lợi</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">C&ocirc;ng vi&ecirc;n nghĩa trang Thi&ecirc;n đường c&oacute; tổng diện t&iacute;ch 24,5 ha, tọa lạc tại x&atilde; Lang Qu&aacute;n, huyện Y&ecirc;n Sơn, tỉnh Tuy&ecirc;n Quang, chỉ c&aacute;ch n&uacute;t giao tuyến đường cao tốc Tuy&ecirc;n Quang &ndash; H&agrave; Giang 400m.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/Giao thông 1920x980.png\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Từ H&agrave; Nội đến Thi&ecirc;n Đường chỉ mất hơn 75 ph&uacute;t di chuyển</span></em></p>\r\n\r\n<p><span style=\"font-size:16px\">Dự kiến khi tuyến cao tốc H&agrave; Nội &ndash; Tuy&ecirc;n Quang ho&agrave;n thiện, việc di chuyển từ H&agrave; Nội tới c&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường chỉ mất hơn 75 ph&uacute;t đi xe.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Khoảng c&aacute;ch n&agrave;y sẽ tạo điều kiện thuận tiện cho con ch&aacute;u sống ở thủ đ&ocirc; v&agrave; c&aacute;c tỉnh l&acirc;n cận dễ d&agrave;ng di chuyển đến chăm nom thăm viếng mộ phần người th&acirc;n của m&igrave;nh.</span></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>C&ocirc;ng vi&ecirc;n t&acirc;m linh hiện đại v&agrave; cao cấp bậc nhất Việt Nam&nbsp;</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường được đầu tư cơ sở hạ tầng đồng bộ ngay từ đầu, mang đến sự chỉnh chu, văn minh, tr&aacute;nh hiện tượng đ&agrave;o xới bất tiện trong qu&aacute; tr&igrave;nh vận h&agrave;nh&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n nghĩa trang</a>. Hệ thống Thi&ecirc;n Đường được &aacute;p dụng c&ocirc;ng nghệ, m&aacute;y m&oacute;c ti&ecirc;n tiến v&agrave;o việc quản l&yacute; v&agrave; thi c&ocirc;ng x&acirc;y dựng, hệ thống h&oacute;a to&agrave;n bộ bản vẽ, cơ điện kết cấu, c&ocirc;ng tr&igrave;nh ngầm&nbsp;l&ecirc;n m&ocirc; h&igrave;nh 3D, hỗ trợ c&ocirc;ng t&aacute;c quản l&yacute; vận h&agrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/hoang_hon_tai_thien_duong.jpg\" style=\"height:956px; width:1276px\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Một g&oacute;c ho&agrave;ng h&ocirc;n tại C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường</span></em></p>\r\n\r\n<p><span style=\"font-size:16px\">Tr&acirc;n trọng truyền thống văn h&oacute;a hướng về nguồn cội, khu gia đ&igrave;nh, gia tộc tại C&ocirc;ng vi&ecirc;n t&acirc;m linh Thi&ecirc;n Đường d&agrave;nh diện t&iacute;ch 24m2 trở l&ecirc;n với thiết kế cảnh quan đặc cấp sẽ trở th&agrave;nh ng&ocirc;i nh&agrave; vĩnh cửu để tổ ti&ecirc;n, &ocirc;ng b&agrave; đo&agrave;n tụ. B&ecirc;n cạnh đ&oacute;, Thi&ecirc;n Đường c&ograve;n cung cấp đa dạng c&aacute;c khu&ocirc;n vi&ecirc;n phần mộ đơn, phần mộ đ&ocirc;i để đ&aacute;p ứng mọi nhu cầu của kh&aacute;ch h&agrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Với sự am hiểu về văn h&oacute;a t&acirc;m linh của người Việt c&ugrave;ng đội ngũ quản l&yacute; v&agrave; nh&acirc;n vi&ecirc;n vận h&agrave;nh chuy&ecirc;n nghiệp, C&ocirc;ng vi&ecirc;n nghĩa trang Thi&ecirc;n Đường c&ograve;n d&agrave;nh nhiều kh&ocirc;ng gian l&agrave;m khu&ocirc;n vi&ecirc;n tổ chức c&aacute;c hoạt động, sự kiện cũng như nghi thức t&acirc;m linh&nbsp;như lễ cầu si&ecirc;u, lễ an t&aacute;ng, c&uacute;ng giỗ h&agrave;ng năm, cung cấp hoa, nhang, đ&egrave;n h&agrave;ng ng&agrave;y.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\">Để giới thiệu dự &aacute;n đến qu&yacute; kh&aacute;ch h&agrave;ng, Văn ph&ograve;ng c&ocirc;ng ty đ&atilde; bắt đầu đ&oacute;n kh&aacute;ch đến tham quan. Bằng sự nỗ lực tận t&acirc;m của m&igrave;nh, chủ đầu tư hy vọng sẽ đ&aacute;p ứng tốt nhất sự kỳ vọng của qu&yacute; kh&aacute;ch h&agrave;ng v&agrave; đang nỗ lực để x&acirc;y dựng <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n t&acirc;m linh </a>Thi&ecirc;n Đường kh&ocirc;ng chỉ l&agrave; nơi an nghỉ tuyệt vời nhất m&agrave; c&ograve;n th&agrave;nh nơi mỗi người t&igrave;m về cội nguồi, tham quan, chi&ecirc;m b&aacute;i đền, ch&ugrave;a, h&ograve;a m&igrave;nh v&agrave;o thi&ecirc;n nhi&ecirc;n để tĩnh t&acirc;m, giải tỏa mệt mỏi trong cuộc sống bộn bề&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Để đặt lịch tham quan v&agrave; tư vấn về sản phẩm, li&ecirc;n hệ số điện thoại 091.935.1155 để được phục vụ.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Website:&nbsp;<a href=\"https://congvienthienduong.com.vn/\" target=\"_blank\" title=\"https://congvienthienduong.com.vn/\">https://nghiatrangthienduong.com.vn/</a></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Th&ocirc;ng tin dự &aacute;n: C&ocirc;ng vi&ecirc;n nghĩa trang Thi&ecirc;n Đường tại x&atilde; Lang Qu&aacute;n, huyện Y&ecirc;n Sơn, tỉnh Tuy&ecirc;n Quang.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Theo&nbsp;<a href=\"https://cafef.vn/cong-vien-nghia-trang-thien-duong-dat-sinh-phan-dep-dang-dau-tu-188230517162502941.chn\">C&ocirc;ng vi&ecirc;n nghĩa trang Thi&ecirc;n đường - Đất sinh phần đẹp đ&aacute;ng đầu tư (cafef.vn)</a>&nbsp;</span></p>', 'true', 'News', 'cong-vien-nghia-trang-thien-duong-dat-sinh-phan-dep-dang-dau-tu', 'Giao thông 1920x980.png', 'Công viên nghĩa trang Thiên đường - Đất sinh phần đẹp đáng đầu tư', 'Khi lựa chọn đất nghĩa trang, đa phần mọi người đều mong muốn chọn được lô đất có phong thủy tốt, giao thông thuận tiện để tiện bề thăm viếng, chăm sóc.', '2023-12-10 06:28:49', '2023-12-10 06:28:49'),
(172, 2, 68, 'Nghĩa trang: truyền thống và xu hướng hiện đại', 'Không còn là những nghĩa trang ngổn ngang mộ phần ít được chăm sóc, bảo dưỡng, ngày nay công viên tâm linh đang trở thành nơi được ví như \"khu nghỉ dưỡng của những người đã khuất\" đúng theo quan niệm \"trần sao âm vậy\" của người Á Đông.', NULL, '<p><strong>(D&acirc;n tr&iacute;) - Kh&ocirc;ng c&ograve;n l&agrave; những nghĩa trang ngổn ngang mộ phần &iacute;t được chăm s&oacute;c, bảo dưỡng, ng&agrave;y nay c&ocirc;ng vi&ecirc;n t&acirc;m linh đang trở th&agrave;nh nơi được v&iacute; như &quot;khu nghỉ dưỡng của những người đ&atilde; khuất&quot; đ&uacute;ng theo quan niệm &quot;trần sao &acirc;m vậy&quot; của người &Aacute; Đ&ocirc;ng.</strong></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>Xu hướng quy hoạch hiện đại, thiết kế cao cấp&nbsp;&nbsp;&nbsp;</strong></span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</h2>\r\n\r\n<p>Nghĩa trang truyền thống được h&igrave;nh th&agrave;nh v&agrave; tồn tại từ rất l&acirc;u đời. Đ&oacute; l&agrave; những khu nghĩa trang tập trung của mỗi địa phương hoặc c&aacute;c khu mộ phần tự ph&aacute;t biệt lập nơi ruộng đồng vắng vẻ. Ng&agrave;y nay, tốc độ đ&ocirc; thị h&oacute;a nhanh khiến nhiều nghĩa trang truyền thống buộc phải di dời để nhường chỗ cho khu d&acirc;n cư, đường x&aacute; v&agrave; nh&agrave; m&aacute;y.</p>\r\n\r\n<p>Thậm ch&iacute;, nếu kh&oacute; di dời, c&aacute;c nghĩa trang c&ograve;n &quot;bất đắc dĩ&quot; sở hữu mặt tiền đường lớn, cạnh khu d&acirc;n cư đ&ocirc;ng đ&uacute;c, giao th&ocirc;ng ồn &agrave;o, bụi bặm. Điều n&agrave;y phần n&agrave;o ảnh hưởng đến sự an tĩnh của người đ&atilde; mất.</p>\r\n\r\n<p>X&eacute;t về mặt kiến tr&uacute;c th&igrave; một số nghĩa trang truyền thống thiếu t&iacute;nh quy hoạch bởi việc ch&ocirc;n theo hướng n&agrave;o, kiến tr&uacute;c ra sao sẽ phụ thuộc v&agrave;o thẩm mỹ v&agrave; quan niệm phong thủy của từng gia đ&igrave;nh. Hơn hết việc ch&ocirc;n cất, bốc mộ sau một thời gian cũng dẫn đến t&igrave;nh trạng mộ an t&aacute;ng v&agrave; mộ cải t&aacute;ng xen lẫn nhau. Thiết kế của từng mộ phần sau cải t&aacute;ng cũng kh&ocirc;ng đồng nhất k&iacute;ch cỡ, m&agrave;u sắc dẫn đến t&igrave;nh trạng lộn xộn v&agrave; tốn nhiều quỹ đất.</p>\r\n\r\n<p>Khắc phục những yếu điểm của nghĩa trang truyền thống, c&aacute;c dự &aacute;n c&ocirc;ng vi&ecirc;n t&acirc;m linh ra đời đ&atilde; trở th&agrave;nh xu hướng đầu tư đất sinh phần mới với nhiều cải tiến đột ph&aacute;. Theo đ&oacute;, c&ocirc;ng vi&ecirc;n t&acirc;m linh sở hữu ưu điểm vượt trội như c&oacute; quy m&ocirc; rộng lớn, c&oacute; vị tr&iacute; xa khu d&acirc;n cư, kết nối giao th&ocirc;ng thuận tiện, quy hoạch khu&ocirc;n vi&ecirc;n v&agrave; khu mộ phần đồng bộ hợp phong thủy. Đồng thời, c&ocirc;ng vi&ecirc;n t&acirc;m linh hiện nay đều c&oacute; quy hoạch, ph&aacute;p l&yacute; n&ecirc;n người mua sẽ tr&aacute;nh được viễn cảnh phải di dời mộ phần sau n&agrave;y.</p>\r\n\r\n<p>Nhằm kết hợp h&agrave;i h&ograve;a cho hoạt động thăm viếng v&agrave; thưởng ngoạn, v&atilde;n cảnh, c&aacute;c c&ocirc;ng vi&ecirc;n t&acirc;m linh được đầu tư nhiều cảnh quan thi&ecirc;n nhi&ecirc;n với mật độ x&acirc;y dựng thấp. Phần lớn diện t&iacute;ch được quy hoạch cho c&aacute;c c&ocirc;ng tr&igrave;nh t&acirc;m linh, c&acirc;y xanh, mặt nước v&agrave; tiện &iacute;ch.</p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>Tiện &iacute;ch v&agrave; c&aacute;c dịch vụ chăm s&oacute;c mộ phần</strong></span></h2>\r\n\r\n<p>Kh&ocirc;ng giới hạn ở việc chọn đất sinh phần c&oacute; vị tr&iacute; đẹp, thiết kế tinh tế, quy hoạch đồng bộ m&agrave; c&aacute;c yếu tố về tiện &iacute;ch, dịch vụ mai t&aacute;ng v&agrave; chăm s&oacute;c mộ phần cũng được người mua rất quan t&acirc;m.</p>\r\n\r\n<p>Ở nghĩa trang truyền thống chỉ những dịp lễ, tết c&aacute;c mộ phần mới được chăm s&oacute;c, bởi người th&acirc;n của người mất kh&ocirc;ng c&oacute; thời gian đến thăm mộ thường xuy&ecirc;n, dẫn đến t&igrave;nh trạng cỏ dại mọc chen lối đi. Nhiều khu nghĩa trang kh&ocirc;ng c&oacute; quản trang n&ecirc;n c&aacute;c gia đ&igrave;nh sau khi bốc mộ, cải t&aacute;ng kh&ocirc;ng thu dọn chất thải, kh&ocirc;ng lấp k&iacute;n mộ cũ g&acirc;y &ocirc; nhiễm m&ocirc;i trường, tạo cảm gi&aacute;c nặng nề u &aacute;m.</p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/p1.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>C&ocirc;ng vi&ecirc;n nghĩa trang - tư duy đạo hiếu mới trong đời sống t&acirc;m linh người Việt.</em></p>\r\n\r\n<p>Ngược lại, <a href=\"https://nghiatrangthienduong.com.vn/\">c&ocirc;ng vi&ecirc;n t&acirc;m linh</a> đều c&oacute; đơn vị quản l&yacute; chuy&ecirc;n nghiệp, đội ngũ chăm s&oacute;c mộ phần mỗi ng&agrave;y v&agrave; an ninh 24/7. Cỏ c&acirc;y được l&agrave;m sạch, hoa được chăm s&oacute;c để đảm bảo một m&ocirc;i trường y&ecirc;n b&igrave;nh, kh&ocirc;ng gian trong l&agrave;nh thanh tịnh cho cả người sống v&agrave; người đ&atilde; mất. Một số c&ocirc;ng vi&ecirc;n c&ograve;n mạnh tay đầu tư v&agrave;o c&aacute;c c&ocirc;ng tr&igrave;nh t&acirc;m linh v&atilde;n cảnh như vườn hoa, c&acirc;y xanh, hồ nước, ch&ugrave;a, đền, tượng Phật. Hệ thống tiện &iacute;ch của một nghĩa trang hiện đại, sang trọng với xe điện miễn ph&iacute;, nh&agrave; h&agrave;ng cỗ chay, trung t&acirc;m lưu tr&uacute;, khu vui chơi trẻ em, ch&ograve;i nghỉ ch&acirc;n&hellip;</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, c&ocirc;ng vi&ecirc;n nghĩa trang c&ograve;n cung cấp c&aacute;c dịch vụ đ&aacute;p ứng đầy đủ nhu cầu của kh&aacute;ch h&agrave;ng như: dịch vụ thiết kế v&agrave; x&acirc;y dựng mộ phần, dịch vụ mai t&aacute;ng, hỏa t&aacute;ng; chăm s&oacute;c, c&uacute;ng giỗ; tư vấn phong thủy; tổ chức sự kiện t&acirc;m linh như: cầu si&ecirc;u, cầu an&hellip;</p>\r\n\r\n<p>Với nhiều cải tiến vượt trội so với nghĩa trang truyền thống, v&agrave;i năm trở lại đ&acirc;y, c&ocirc;ng vi&ecirc;n t&acirc;m linh được ch&uacute; trọng đầu tư v&agrave; hiện diện tại nhiều tỉnh th&agrave;nh lớn tr&ecirc;n cả nước. C&oacute; thể kể đến một số dự &aacute;n ti&ecirc;u biểu như c&ocirc;ng vi&ecirc;n nghĩa trang Vĩnh Hằng (H&agrave; Nội), Lạc Hồng Vi&ecirc;n (H&ograve;a B&igrave;nh), Thi&ecirc;n đức Vĩnh Hằng Vi&ecirc;n (Ph&uacute; Thọ), Vĩnh Hằng Long Th&agrave;nh (Đồng Nai), Sala Garden (Đồng Nai), Hương An Vi&ecirc;n (Huế), Hoa vi&ecirc;n nghĩa trang B&igrave;nh Dương&hellip;</p>\r\n\r\n<p>Mới đ&acirc;y, tại Tuy&ecirc;n Quang cũng xuất hiện dự &aacute;n c&ocirc;ng vi&ecirc;n <a href=\"https://nghiatrangthienduong.com.vn/\">Nghĩa trang Thi&ecirc;n đường</a> do C&ocirc;ng ty cổ phần Đầu tư X&acirc;y dựng v&agrave; Thương mại An Ph&aacute;t Vinh l&agrave;m chủ đầu tư. Dự &aacute;n c&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n đường Tuy&ecirc;n Quang được quy hoạch v&agrave; x&acirc;y dựng theo ti&ecirc;u ch&iacute; bảo đảm m&ocirc; h&igrave;nh cảnh quan, bảo tồn sinh th&aacute;i v&agrave; c&oacute; gi&aacute; trị kiến tr&uacute;c với mật độ cảnh quan c&acirc;y xanh tới 65%.</p>\r\n\r\n<p>Đồng thời, c&ocirc;ng vi&ecirc;n Nghĩa trang <a href=\"https://nghiatrangthienduong.com.vn/\">Thi&ecirc;n đường</a> cũng cung cấp c&aacute;c tiện &iacute;ch dịch vụ đầy đủ từ tổ chức an t&aacute;ng, x&acirc;y dựng mộ phần cho đến việc phụng sự thờ tự&hellip; Thậm ch&iacute;, tại đ&acirc;y c&oacute; th&ecirc;m c&aacute;c dịch vụ mới như lập v&agrave; lưu giữ gia phả trực tuyến&hellip; Với kết nối chỉ c&ograve;n 75 ph&uacute;t di chuyển bằng cao tốc H&agrave; Nội - Tuy&ecirc;n Quang - H&agrave; Giang, dự b&aacute;o nơi đ&acirc;y sẽ trở th&agrave;nh t&acirc;m điểm đầu tư đất sinh phần mới khu vực ph&iacute;a Bắc, h&uacute;t d&ograve;ng kh&aacute;ch H&agrave; Nội v&agrave; c&aacute;c tỉnh l&acirc;n cận Tuy&ecirc;n Quang.</p>\r\n\r\n<p>C&oacute; thể thấy, sự nở rộ của xu hướng <a href=\"https://dantri.com.vn/bat-dong-san/nghia-trang-truyen-thong-va-xu-huong-hien-dai-20231205161243536.htm\">c&ocirc;ng vi&ecirc;n t&acirc;m lin</a>h thời gian gần đ&acirc;y kh&ocirc;ng chỉ mang lại &yacute; nghĩa về một x&atilde; hội văn minh tiến bộ m&agrave; c&ograve;n h&igrave;nh th&agrave;nh n&eacute;t văn h&oacute;a mới trong truyền thống hiếu đạo ng&agrave;n đời của người Việt.</p>\r\n\r\n<p>Theo :&nbsp;<a href=\"https://dantri.com.vn/bat-dong-san/nghia-trang-truyen-thong-va-xu-huong-hien-dai-20231205161243536.htm\">Nghĩa trang: truyền thống v&agrave; xu hướng hiện đại | B&aacute;o D&acirc;n tr&iacute; (dantri.com.vn)</a></p>\r\n\r\n<p>Xem th&ecirc;m : <a href=\"https://nghiatrangthienduong.com.vn/san-pham\">c&aacute;c sản phẩm tại Thi&ecirc;n Đường</a></p>', 'true', 'News', 'nghia-trang-truyen-thong-va-xu-huong-hien-dai', 'p1.jpg', 'Nghĩa trang: truyền thống và xu hướng hiện đại', 'Không còn là những nghĩa trang ngổn ngang mộ phần ít được chăm sóc, bảo dưỡng, ngày nay công viên tâm linh đang trở thành nơi được ví như \"khu nghỉ dưỡng của những người đã khuất\" đúng theo quan niệm \"trần sao âm vậy\" của người Á Đông.', '2023-12-10 06:57:05', '2023-12-10 06:57:05');
INSERT INTO `posts` (`id`, `user_id`, `category_id`, `name`, `detail`, `info`, `content`, `status`, `sort_by`, `slug`, `img`, `title`, `description`, `created_at`, `updated_at`) VALUES
(173, 2, 68, 'Kiếm tiền tỷ từ đầu tư đất nghĩa trang', '​​​​​​​Trong khi bất động sản nhà ở ảm đạm, chi phí đầu tư lớn thì bất động sản nghĩa trang (đất sinh phần) thu hút nhiều nhà đầu tư nhờ số vốn ít ỏi chỉ vài trăm triệu đồng.', NULL, '<p><strong><span style=\"font-size:16px\">Trong khi bất động sản nh&agrave; ở ảm đạm, chi ph&iacute; đầu tư lớn th&igrave; bất động sản nghĩa trang (đất sinh phần) thu h&uacute;t nhiều nh&agrave; đầu tư nhờ số vốn &iacute;t ỏi chỉ v&agrave;i trăm triệu đồng.</span></strong></p>\r\n\r\n<p><span style=\"font-size:16px\">C&aacute;c nghĩa trang phần lớn nằm quanh đ&ocirc; thị lớn như H&agrave; Nội, TP.HCM v&agrave; thường chỉ tr&ecirc;n dưới 1 giờ chạy xe, giao th&ocirc;ng thuận lợi.</span></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>Mua một, l&atilde;i ba, bốn lần</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Bất động sản <a href=\"https://nghiatrangthienduong.com.vn/\">nghĩa trang</a> được gọi dưới nhiều t&ecirc;n gọi kh&aacute;c nhau như: Đất sinh phần, bất động sản an t&aacute;ng, bất động sản t&acirc;m linh...Trong bối cảnh tốc độ đ&ocirc; thị h&oacute;a nhanh, quỹ đất nghĩa trang địa phương ng&agrave;y c&agrave;ng thu hẹp, c&aacute;c dự &aacute;n bất động sản nghĩa trang được đầu tư ồ ạt. Thậm ch&iacute;, nhiều người kiếm được bạc tỷ từ việc mua đi b&aacute;n lại mộ phần.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Chị Nguyễn Thị Chi (ở H&agrave; Nội) c&oacute; &ocirc;ng b&agrave; ngoại mai t&aacute;ng ở nghĩa trang Y&ecirc;n Kỳ, Ba V&igrave;, H&agrave; Nội. Năm 2010, khi đi thăm mộ, chị t&igrave;nh cờ gh&eacute; nghĩa trang tư nh&acirc;n Vĩnh Hằng. C&aacute;ch nghĩa trang Y&ecirc;n Kỳ kh&ocirc;ng xa nhưng nghĩa trang Vĩnh Hằng được quy hoạch gọn g&agrave;ng, b&agrave;i bản.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Khu&ocirc;n vi&ecirc;n mộ được thiết kế kh&aacute; đa dạng. C&oacute; những lăng mộ nguy nga, tr&aacute;ng lệ với họa tiết khắc đ&aacute; ch&igrave;m nổi hoặc nhiều c&acirc;y xanh, hoa cỏ; Cũng c&oacute; cả m&ocirc; h&igrave;nh nh&agrave; vườn truyền thống với c&acirc;y đa, s&acirc;n đ&igrave;nh. V&igrave; vậy, chị quyết định mua hơn 30m2 đất ở Vĩnh Hằng l&agrave;m nơi y&ecirc;n nghỉ cho bố mẹ v&agrave; vợ chồng m&igrave;nh sau n&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Đến nay, số đất chưa sử dụng tới bởi người th&acirc;n của chị đều c&ograve;n khỏe mạnh. Do vậy, chị Chi quyết định chuyển nhượng cho chủ mới v&agrave; thu được khoản tiền kh&aacute; lớn ngo&agrave;i dự t&iacute;nh: &quot;Chủ phần mộ ngay cạnh đ&atilde; sử dụng hết đất, muốn mua th&ecirc;m nhưng chủ đầu tư kh&ocirc;ng c&ograve;n. Thấy t&ocirc;i chưa sử dụng n&ecirc;n &ocirc;ng đặt vấn đề mua để gộp th&agrave;nh một cho tiện bề thăm nom. T&ocirc;i kh&ocirc;ng c&oacute; &yacute; định b&aacute;n nhưng &ocirc;ng năn nỉ qu&aacute;. L&uacute;c t&ocirc;i mua khoảng 5 triệu đồng/m2, nay b&aacute;n lại 18 triệu đồng/m2&quot;, chị Chi n&oacute;i.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">C&ocirc;ng vi&ecirc;n Tưởng niệm Thi&ecirc;n Đức Vĩnh Hằng (c&ograve;n gọi l&agrave; nghĩa trang Thi&ecirc;n Đức) cũng l&agrave; nơi c&oacute; nhiều người mua đi b&aacute;n lại. Anh Ho&agrave;ng Long, một nh&acirc;n vi&ecirc;n chăm s&oacute;c kh&aacute;ch h&agrave;ng ở đ&acirc;y cho biết, mỗi năm c&oacute; khoảng 2-3 kh&aacute;ch h&agrave;ng của anh chuyển nhượng lại đất sinh phần do chưa sử dụng tới. Việc chuyển nhượng được thực hiện như với bất động sản, phải trả ph&iacute; c&ocirc;ng chứng, nộp thuế c&aacute; nh&acirc;n (2%).</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Chị Nguyễn Thị Phương (một nh&agrave; đầu tư ở H&agrave; Nội) cho biết, khoảng năm 2015 trở về trước, kh&aacute;i niệm bất động sản nghĩa trang kh&aacute; lạ lẫm, thậm ch&iacute; khiến nhiều nh&agrave; đầu tư e ngại. Nhưng chị cảm nhận được xu hướng dịch chuyển của văn h&oacute;a an t&aacute;ng, sự thu hẹp dần c&aacute;c nghĩa trang ở khu d&acirc;n cư n&ecirc;n chọn đ&acirc;y l&agrave; k&ecirc;nh đầu tư.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Chị mua đất ở nhiều nghĩa trang quanh H&agrave; Nội. Nhưng sản phẩm chị nhắc nhiều l&agrave; mảnh đất tận Long Th&agrave;nh, Đồng Nai.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Một lần đi lễ, chị c&ugrave;ng nh&oacute;m bạn gh&eacute; nghĩa trang Sala Gaden (Long Th&agrave;nh, Đồng Nai). Trước sự tĩnh lặng ở đ&acirc;y, chị đ&atilde; xuống tiền 3 tỷ đồng mua khu hơn 200m2, gần một ng&ocirc;i ch&ugrave;a. Đến nay, c&oacute; người trả hơn 5 tỷ đồng nhưng chị chưa b&aacute;n.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&quot;T&ocirc;i thuộc nh&oacute;m nh&agrave; đầu tư xuống tiền đầu ti&ecirc;n ở đ&acirc;y. Đ&atilde; c&oacute; người trả gi&aacute; gần gấp đ&ocirc;i tiền đầu tư nhưng t&ocirc;i vẫn để đ&oacute;. Khu đ&oacute; an y&ecirc;n, c&oacute; tiền cũng kh&oacute; t&igrave;m&quot;, chị Phương cho hay.</span></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>L&atilde;i lớn nếu vượng phong thủy</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Anh Nguyễn Văn Tuấn, tư vấn dự &aacute;n Sala Gaden, cho biết h&agrave;ng b&aacute;n của chủ đầu tư đ&atilde; hết từ năm 2017. Hiện nay, đơn vị b&aacute;n h&agrave;ng của chủ đầu tư đang hỗ trợ b&aacute;n h&agrave;ng chuyển nhượng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Theo anh Tuấn, tỷ suất lợi nhuận khi đầu tư tại nghĩa trang khoảng 9-13%/năm. Những l&ocirc; gia tộc 200m2, vị tr&iacute; đắc địa gần ch&ugrave;a mua khoảng 3 tỷ đồng/l&ocirc; đều đ&atilde; tăng l&ecirc;n 5-7 tỷ đồng/l&ocirc;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Một nh&acirc;n vi&ecirc;n tư vấn dự &aacute;n nghĩa trang Thi&ecirc;n Đức cũng cho biết khoảng 2010, gi&aacute; b&aacute;n chỉ khoảng 3 triệu/m2. Đến năm 2020, gi&aacute; tăng l&ecirc;n khoảng 20 triệu/m2, hiện nay l&agrave; 30 triệu/m2. Gi&aacute; đất sinh phần ở nghĩa trang Vĩnh Hằng cũng tăng tương tự.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&Ocirc;ng Nguyễn Quốc Huy, Gi&aacute;m đốc C&ocirc;ng ty Nh&agrave; Ở Ngay, cho biết c&oacute; những l&ocirc; bất động sản an t&aacute;ng tăng gi&aacute; v&agrave;i ba lần bởi yếu tố phong thủy. L&ocirc; đất đẹp, vượng phong thủy kh&ocirc;ng căn cứ gi&aacute; theo thị trường, giao dịch phụ thuộc v&agrave;o &yacute; ch&iacute; b&ecirc;n b&aacute;n v&agrave; b&ecirc;n mua.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/shares/thien_duong_19.05_4.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/\">Nghĩa trang Thi&ecirc;n Đường (Tuy&ecirc;n Quang)</a> đang x&acirc;y dựng.</span></em></p>\r\n\r\n<p><span style=\"font-size:16px\">&Ocirc;ng Huy nhận định, bất động sản an t&aacute;ng sẽ l&agrave; một xu hướng trong thời gian tới, bởi lượng cầu lớn, cả chục ngh&igrave;n trường hợp hạ huyệt mỗi năm. Trong khi đ&oacute;, nguồn cung phục vụ nhu cầu n&agrave;y thiếu, c&aacute;c nghĩa trang gần H&agrave; Nội như: Văn Điển, Thanh Thước, Y&ecirc;n Kỳ, Vĩnh Hằng đ&atilde; hết h&agrave;ng, chỉ c&ograve;n lại đất chuyển nhượng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&quot;Sản phẩm n&agrave;y cũng đang thu h&uacute;t sự quan t&acirc;m của nhiều nh&agrave; đầu tư bởi vừa t&uacute;i tiền. Nếu đầu tư nh&agrave; ở hiện nay tối thiểu phải c&oacute; vốn tầm 2 tỷ, th&igrave; bất động sản an t&aacute;ng chỉ cần v&agrave;i chục đến v&agrave;i trăm triệu t&ugrave;y theo nhu cầu đầu tư. Tỷ suất sinh lợi kh&ocirc;ng thu k&eacute;m bất động sản kh&aacute;c, quan trọng nhất l&agrave; nhu cầu thực&quot;, &ocirc;ng Huy n&oacute;i.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Hiện nay, đất nghĩa trang được nhiều địa phương ch&uacute; trọng đưa v&agrave;o quy hoạch để thu h&uacute;t đầu tư, chẳng hạn như: C&ocirc;ng vi&ecirc;n nghĩa trang Vĩnh Hằng giai đoạn 2 (Ba V&igrave;, H&agrave; Nội), <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n nghĩa trang Tuy&ecirc;n Quang (nghĩa trang Thi&ecirc;n Đường)</a>, huyện Y&ecirc;n Sơn, tỉnh Tuy&ecirc;n Quang; Nghĩa trang An Lạc, Quảng Ninh; Lạc Hồng Vi&ecirc;n, H&ograve;a B&igrave;nh...</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Trao đổi với B&aacute;o Giao th&ocirc;ng, &ocirc;ng Nguyễn Mạnh Thản, chủ nghĩa trang Vĩnh Hằng ở Ba V&igrave; cho rằng, nhu cầu về chỗ an t&aacute;ng kh&ocirc;ng ngừng tăng l&ecirc;n. V&igrave; thế, chắc chắn bất động sản nghĩa trang sẽ c&ograve;n s&ocirc;i động hơn nữa thời gian tới.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">C&aacute;c dự &aacute;n đều kh&ocirc;ng qu&aacute; xa trung t&acirc;m</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang Tuy&ecirc;n Quang (nghĩa trang Thi&ecirc;n Đường)</a>, huyện Y&ecirc;n Sơn, tỉnh Tuy&ecirc;n Quang. Dự &aacute;n diện t&iacute;ch 24ha, c&aacute;ch H&agrave; Nội khoảng 75km. Sau khi ho&agrave;n th&agrave;nh tuyến cao tốc H&agrave; Nội - H&agrave; Giang, thời gian di chuyển từ H&agrave; Nội đến dự &aacute;n khoảng 1 giờ. Hiện, dự &aacute;n đang triển khai, chưa c&oacute; gi&aacute; b&aacute;n.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m:&nbsp;</span><a href=\"https://www.baogiaothong.vn/kiem-tien-ty-tu-dau-tu-dat-nghia-trang-192231129005410448.htm\">Kiếm tiền tỷ từ đầu tư đất nghĩa trang (baogiaothong.vn)</a></p>', 'true', 'News', 'kiem-tien-ty-tu-dau-tu-dat-nghia-trang', 'Picture4.jpg', 'Kiếm tiền tỷ từ đầu tư đất nghĩa trang', '​​​​​​​Trong khi bất động sản nhà ở ảm đạm, chi phí đầu tư lớn thì bất động sản nghĩa trang (đất sinh phần) thu hút nhiều nhà đầu tư nhờ số vốn ít ỏi chỉ vài trăm triệu đồng.', '2023-12-10 07:31:42', '2023-12-10 07:31:42'),
(175, 2, 68, 'Lễ tang truyền thống tại Việt Nam và các vấn đề cần lưu ý', 'Tang lễ hay gọi là Đám tang, Đám ma là một buổi lễ chính thức dành cho cái chết của một người hoặc đôi khi là một sinh vật sống nào đó đã chết. Hầu hết các tôn giáo đều có phong cách, phong tục, quy tắc riêng của họ về lễ tang.', NULL, '<h2><span style=\"font-size:16px\">Quy Tr&igrave;nh Tổ Chức Tang Lễ Truyền Thống&nbsp; tại Việt Nam</span></h2>\r\n\r\n<div>\r\n<p><span style=\"font-size:16px\">&nbsp;Tang lễ hay gọi l&agrave; Đ&aacute;m tang, Đ&aacute;m ma l&agrave; một buổi lễ ch&iacute;nh thức d&agrave;nh cho c&aacute;i chết của một người hoặc đ&ocirc;i khi l&agrave; một sinh vật sống n&agrave;o đ&oacute; đ&atilde; chết. Hầu hết c&aacute;c t&ocirc;n gi&aacute;o đều c&oacute; phong c&aacute;ch, phong tục, quy tắc ri&ecirc;ng của họ về lễ tang. V&agrave; thường được tổ chức trong ng&ocirc;i nh&agrave; của người chết, nh&agrave; tang lễ, một nh&agrave; thờ, một ng&ocirc;i ch&ugrave;a hay một nơi c&ocirc;ng cộng. Tang lễ lu&ocirc;n l&agrave; sự kh&oacute; khăn của mọi gia đ&igrave;nh, v&igrave; trong l&uacute;c tang gia bối rối sẽ kh&oacute; c&oacute; thể tổ chức được một tang lễ ho&agrave;n chỉnh, chu đ&aacute;o.&nbsp;Thấu hiểu được điều đ&oacute; <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng Vi&ecirc;n Nghĩa Trang Thi&ecirc;n Đường-Tuy&ecirc;n Quang</a> xin được giới thiệu cho qu&yacute; vị c&aacute;c thủ tục tang lễ truyền thống nhằm tạo sự thuận lợi cho gia quyến khi gia đ&igrave;nh gặp hữu sự.</span></p>\r\n\r\n<h2><span style=\"font-size:16px\">C&aacute;c bước tổ chức lễ tang tại nh&agrave; :</span></h2>\r\n\r\n<h3><span style=\"font-size:16px\">1. Chuẩn bị tổ chức tang lễ </span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Sau khi người th&acirc;n đ&atilde; mất con ch&aacute;u sẽ phải tắm gội sạch sẽ cho người mất bằng nước l&aacute; thơm hoặc rượu sau đ&oacute; thay bộ quần &aacute;o mới đ&atilde; được chuẩn bị từ trước. Sau đ&oacute; buộc hai ng&oacute;n ch&acirc;n c&aacute;i của người đ&atilde; mất lại với nhau, hai tay để l&ecirc;n bụng. Tang chủ thực hiện x&uacute;c một &iacute;t gạo v&agrave; một đồng tiền tra v&agrave;o miệng từ b&ecirc;n phải sang b&ecirc;n tr&aacute;i v&agrave; ch&iacute;nh giữa, xong b&oacute;p mồm lại v&agrave; phủ mặt như cũ. Khi l&agrave;m tang chủ quỳ kh&oacute;c v&agrave; n&oacute;i &ldquo; nay xin phạn h&agrave;n, phục duy h&acirc;m nạp&rdquo;</span></p>\r\n</div>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp;L&uacute;c n&agrave;y cứ để người chết nằm đấy bu&ocirc;ng m&agrave;n v&agrave; b&aacute;o cho họ h&agrave;ng xa gần biết v&agrave; xem giờ để l&agrave;m lễ kh&acirc;m niệm. Đặc biệt l&agrave; khi người gi&agrave; đang hấp hối th&igrave; con ch&aacute;u kh&ocirc;ng được kh&oacute;c tr&aacute;nh để nước mắt rơi v&agrave;o thi h&agrave;i v&igrave; như vật người chết sẽ ra đi kh&ocirc;ng thanh thản.&nbsp;&nbsp;&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m:<a href=\"https://nghiatrangthienduong.com.vn/san-pham\"> C&aacute;c sản phẩm của Thi&ecirc;n Đường</a></span></p>\r\n\r\n<h3><span style=\"font-size:16px\">2. Lập b&agrave;n thờ vong&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></h3>\r\n\r\n<div>\r\n<p><span style=\"font-size:16px\">Trước l&uacute;c chờ để đợi giờ đẹp kh&acirc;m liệm người ta thường phải lập b&agrave;n thờ vong. B&agrave;n thờ vong thường c&oacute; nải chuối, bưởi, hoa quả theo m&ugrave;a, ảnh b&agrave;i vị người đ&atilde; mất v&agrave; thường được kết hoa rất cầu k&igrave; để tỏ l&ograve;ng thương tiếc đến người đ&atilde; khuất.</span></p>\r\n</div>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp;<img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/42.jpg\" style=\"height:1400px; width:2000px\" /></span></p>\r\n\r\n<h3><span style=\"font-size:16px\">3. Kh&acirc;m liệm&nbsp;</span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Sau khi k&egrave;n trống được nổi một hồi d&agrave;i th&igrave; người ta bắt đầu tiến h&agrave;nh kh&acirc;m liệm. Phong tục kh&ocirc;ng thể thiếu l&agrave; bỏ một bộ chắn v&agrave;o quan t&agrave;i để khử tr&ugrave;ng v&agrave; để che chở cho người mất. Đối với người mất mắc bệnh th&igrave; người ta sẽ cho ch&egrave; v&agrave;o quan t&agrave;i để h&uacute;t ẩm v&agrave; khử m&ugrave;i hoặc d&ugrave;ng đ&aacute; kh&ocirc; CO2.</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/43.jpg\" style=\"height:1400px; width:2000px\" /></p>\r\n\r\n<h3><span style=\"font-size:16px\">4. Nhập quan</span></h3>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp;Nhập quan l&agrave; đưa thi h&agrave;i v&agrave;o quan t&agrave;i, thầy c&uacute;ng thắp hương khấn v&aacute;i rồi l&agrave;m thủ tục ph&aacute;t mộc d&ugrave;ng dao chặt v&agrave;o bốn g&oacute;c của quan t&agrave;i nhằm đuổi bọn ma quỷ v&agrave; mộc tinh. Con ch&aacute;u mặc tang phục đứng hai b&ecirc;n, họ h&agrave;ng từ từ n&acirc;ng nhẹ thi h&agrave;i đặt v&agrave;o quan t&agrave;i.</span></p>\r\n\r\n<h3><span style=\"font-size:16px\">5. Gọi hồn</span></h3>\r\n\r\n<div>\r\n<p><span style=\"font-size:16px\">Thầy c&uacute;ng l&agrave;m lễ gọi hồ thường th&igrave; cầm &aacute;o người chết ra s&acirc;n hoặc ngo&agrave;i đường quay về bốn hướng Đ&ocirc;ng, T&acirc;y, Nam, Bắc đ&agrave;n &ocirc;ng th&igrave; gọi &ldquo;ba hồn bẩy v&iacute;a&rdquo;, đ&agrave;n b&agrave; th&igrave; gọi &ldquo;ba hồn ch&iacute;n v&iacute;a&rdquo; về nhập quan. Xong bỏ &aacute;o người chết v&agrave;o quan t&agrave;i coi như họ đ&atilde; về nhập quan. Người ta quan niệm rằng khi người chết đi th&igrave; hồn v&iacute;a sẽ đi lang thang khắp kh&ocirc;ng trung n&ecirc;n phải l&agrave;m lễ n&agrave;y v&agrave; khấn để tr&igrave;nh b&aacute;o l&ecirc;n thi&ecirc;n đ&igrave;nh l&agrave; trần gian c&oacute; người quy ti&ecirc;n để ghi v&agrave;o sổ thi&ecirc;n t&agrave;o. </span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/dia-the-phong-thuy\">Địa thế phong thủy tại Thi&ecirc;n Đường</a></span></p>\r\n\r\n<h3><span style=\"font-size:16px\">6. Lễ ph&aacute;t tang</span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Chủ lễ l&agrave;m lễ ph&aacute;t tang, khăn tang, mũ mấn được chuẩn bị đủ với số con ch&aacute;u đặt v&agrave;o một chiếc m&acirc;m tr&ecirc;n hương &aacute;n. Trong l&uacute;c l&agrave;m lễ th&igrave; con ch&aacute;u phải chắp tay quỳ khấn ở dưới, sau khi lễ xong th&igrave; con trưởng sẽ ph&aacute;t khăn, &aacute;o cho mọi người, khăn tang của người vắng mặt th&igrave; để lại tr&ecirc;n m&acirc;m.</span></p>\r\n</div>\r\n\r\n<div>\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/44.jpg\" style=\"height:1400px; width:2000px\" /></p>\r\n\r\n<p><span style=\"font-size:16px\">C&aacute;ch &nbsp;thức để tang cũng c&oacute; quy định r&otilde; r&agrave;ng: Tang cha mẹ th&igrave; thắt khăn sổ mối, hai dải khăn b&ecirc;n c&oacute; người c&ograve;n sống v&agrave; bằng nhau nếu đ&atilde; mất hết; vợ để tang chồng cũng ch&iacute;t khăn sổ mối, một dải d&agrave;i, một dải ngắn nhưng chồng để tang vợ th&igrave; chỉ quấn v&ograve;ng tr&ograve;n quanh đầu, ch&aacute;u quấn khăn trắng quang đầu th&agrave;nh v&ograve;ng tr&ograve;n, chắt th&igrave; khăn v&agrave;ng v&agrave; ch&uacute;t quấn khăn đỏ. Trong suốt thời gian đ&aacute;m tang, lu&ocirc;n c&oacute; con ch&aacute;u t&uacute;c trực cạnh quanh t&agrave;i hờ kh&oacute;c.</span></p>\r\n\r\n<h3><span style=\"font-size:16px\">7. Ph&uacute;ng viếng&nbsp;&nbsp;</span></h3>\r\n</div>\r\n\r\n<div>\r\n<p><span style=\"font-size:16px\">Sau khi ph&aacute;t tang họ h&agrave;ng th&acirc;n thiết đến ph&uacute;ng trước l&uacute;c n&agrave;y c&aacute;c con lu&ocirc;n t&uacute;c trực đứng cạnh b&agrave;n thờ vong để đ&aacute;p nghĩa.</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/45.jpg\" style=\"height:1400px; width:2000px\" /></p>\r\n\r\n<h3><span style=\"font-size:16px\">8. Tế vong</span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Buổi tối khi ph&uacute;ng viếng đ&atilde; v&atilde;n kh&aacute;ch, phường hiếu l&agrave;m lễ tế vong. Người nh&agrave; chuẩn bị m&acirc;m cơm rượu thịt đầy đủ để d&acirc;ng l&ecirc;n b&agrave;n thờ vong.</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/46.jpg\" style=\"height:1400px; width:2000px\" /></p>\r\n\r\n<h3><span style=\"font-size:16px\">9. Quay cữu</span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Đ&uacute;ng 12 giờ đ&ecirc;m người ta l&agrave;m lễ quay cữu, quan t&agrave;i được quay theo chiều ngang của ng&ocirc;i nh&agrave; đầu quay về ph&iacute;a b&agrave;n thờ ch&acirc;n quay ra ngo&agrave;i cửa</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/47.jpg\" style=\"height:1400px; width:2000px\" /></p>\r\n</div>\r\n\r\n<div>\r\n<h3><span style=\"font-size:16px\">10.Tế cơm</span></h3>\r\n\r\n<p><span style=\"font-size:16px\">S&aacute;ng h&ocirc;m sau người nh&agrave; chuẩn bị b&aacute;t cơm, quả trứng, một đĩa muối, một ch&eacute;n nước l&atilde; lần lượt d&acirc;ng từng thứ l&ecirc;n b&agrave;n thờ vong. </span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/48.jpg\" style=\"height:1400px; width:2000px\" /></span></p>\r\n\r\n<h3><span style=\"font-size:16px\">11. Cất đ&aacute;m</span></h3>\r\n</div>\r\n\r\n<div>\r\n<p><span style=\"font-size:16px\">Đến giờ đuqa tang chủ lễ đọc điếu văn sau đ&oacute; d&ugrave;ng đinh đ&oacute;ng nắp v&aacute;n thi&ecirc;n lại quan t&agrave;i được mọi người h&agrave;ng x&oacute;m đưa l&ecirc;n xa tang. L&uacute;c n&agrave;y con trưởng c&oacute; lời cảm ơn cơ quan, h&agrave;ng x&oacute;m đ&atilde; c&oacute; mặt chia buồn c&ugrave;ng gia đ&igrave;nh.</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/49.jpg\" style=\"height:1400px; width:2000px\" /></p>\r\n\r\n<p><span style=\"font-size:16px\">Suốt qu&aacute; tr&igrave;nh đưa người mất ra nghĩa trang cờ, trướng đi trước linh cữu đi sau, con c&aacute;i đi sau linh cữu k&egrave;n trống được đ&aacute;nh li&ecirc;n hồi v&agrave; vừa đi vừa rải v&agrave;ng m&atilde; tiền lẻ từ nh&agrave; đến nghĩa trang.</span></p>\r\n\r\n<h3><span style=\"font-size:16px\">12. Hạ huyệt hoặc hỏa thi&ecirc;u</span></h3>\r\n</div>\r\n\r\n<div>\r\n<p><span style=\"font-size:16px\">Huyệt được chuẩn bị từ chiều h&ocirc;m trước, khi quan t&agrave;i cho xuống huyệt th&igrave; con trai sẽ lấp miếng đất đầu ti&ecirc;n, sau đ&oacute; c&aacute;c con ch&aacute;u mỗi người lấp một miếng thể hiện t&igrave;nh cảm, &yacute; nghĩa đắp mộ cho cha mẹ. Ở tr&ecirc;n c&oacute; c&aacute;c đội tụng kinh, xong xu&ocirc;i th&igrave; ra về tuyệt đối kh&ocirc;ng đi theo đường l&uacute;c đi v&agrave; con c&aacute;i kh&ocirc;ng được kh&oacute;c nữa v&igrave; như vậy l&agrave; hồn người mất sẽ kh&oacute; si&ecirc;u tho&aacute;t.</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/50.jpg\" style=\"height:1400px; width:2000px\" /></p>\r\n\r\n<h3><span style=\"font-size:16px\">13. Rước người th&acirc;n về nh&agrave; thờ</span></h3>\r\n</div>\r\n\r\n<div>\r\n<p><span style=\"font-size:16px\">Sau khi an t&aacute;ng xong ảnh của người mất được rước về nh&agrave; để thờ tr&ecirc;n b&agrave;n thờ vong, b&agrave;n thờ lu&ocirc;n c&oacute; hương kh&oacute;i, đ&egrave;n nhang h&agrave;ng ng&agrave;y gia chủ ăn g&igrave; th&igrave; c&uacute;ng thứ đấy.</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/51.jpg\" style=\"height:1400px; width:2000px\" /></p>\r\n\r\n<h3><span style=\"font-size:16px\">14.Nghi thức sau đ&aacute;m tang</span></h3>\r\n</div>\r\n\r\n<div>\r\n<ul>\r\n	<li><span style=\"font-size:16px\">C&uacute;ng tuần </span></li>\r\n	<li><span style=\"font-size:16px\">C&uacute;ng 49 ng&agrave;y.</span></li>\r\n	<li><span style=\"font-size:16px\">C&uacute;ng 100 ng&agrave;y.</span></li>\r\n</ul>\r\n\r\n<h3><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cong-vien-nghia-trang-thien-duong-dat-sinh-phan-dep-dang-dau-tu\">Đất sinh phần đẹp</a></span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">15.Những điều ki&ecirc;ng kị khi tổ chức tang lễ m&agrave; bạn cần biết</span></h3>\r\n\r\n<p><span style=\"font-size:16px\">&middot;&nbsp; N&ecirc;n mặc trang phục đen hoặc trắng khi đi đ&aacute;m tang. Khi tham dự 1 đ&aacute;m tang đưa tiễn người đ&atilde; khuất, tuyệt đối kh&ocirc;ng n&ecirc;n mặc những bộ trang phục l&ograve;e loẹt, kiểu d&aacute;ng lố lăng. Tốt nhất l&agrave; n&ecirc;n lựa chọn quần &aacute;o m&agrave;u đen trắng, trang điểm đơn giản v&agrave; kh&ocirc;ng n&oacute;i cười ầm ĩ.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&middot;&nbsp; Kh&ocirc;ng để ch&oacute;, m&egrave;o nhảy qua x&aacute;c người chết</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&middot;&nbsp; Kh&ocirc;ng để nước mắt rơi xuống khi kh&acirc;m liệm</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&middot;&nbsp; Đi chậm r&atilde;i khi khi&ecirc;ng linh cữu</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&middot;&nbsp; Ki&ecirc;ng lấy vợ, lấy chồng khi đang để tang cha mẹ</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&middot;&nbsp; Khi ch&ocirc;n cất kh&ocirc;ng mặc đồ của người tham gia ch&ocirc;n cất cho người đ&atilde; mất v&igrave; như thế người mất sẽ mang đi 1 phần của người đ&oacute;. Ngo&agrave;i ra, cũng kh&ocirc;ng n&ecirc;n sử dụng đồ của người đ&atilde; khuất như: giường, quần &aacute;o, gi&agrave;y d&eacute;p&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&middot;&nbsp; Ki&ecirc;ng để &aacute;nh s&aacute;ng mặt trời soi trực tiếp khi cải t&aacute;ng</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&middot;&nbsp; Quan t&agrave;i kh&ocirc;ng d&ugrave;ng gỗ c&acirc;y liễu</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&middot;&nbsp; Lựa chọn nơi để ch&ocirc;n cất, để nhận được sự ph&ugrave; hộ của tổ ti&ecirc;n, gia đ&igrave;nh c&oacute; người mất cần tr&aacute;nh những nơi hạ t&aacute;ng như:&nbsp; Nơi c&oacute; tảng đ&aacute; lớn; Đỉnh n&uacute;i vắng vẻ, c&ocirc; độc; Đồi n&uacute;i c&oacute; địa h&igrave;nh hỗn loạn; Nơi ẩm ướt hay địa h&igrave;nh kh&ocirc;ng ổn định&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Tảivềđểđọcth&ecirc;mtại:&nbsp;<a href=\"https://docs.google.com/forms/d/e/1FAIpQLSf0tA5PNk3jVYT0uxECJncltP3XB8_AOTpy8sDu7bOKo0KWrA/viewform?usp=sharing\">https://docs.google.com/forms/d/e/1FAIpQLSf0tA5PNk3jVYT0uxECJncltP3XB8_AOTpy8sDu7bOKo0KWrA/viewform?usp=sharing&nbsp;</a></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/\">Nghĩa Trang Thi&ecirc;n Đường</a></span></p>\r\n\r\n<p>&nbsp;</p>\r\n</div>', 'true', 'News', 'le-tang-truyen-thong-tai-viet-nam-va-cac-van-de-can-luu-y', '43.jpg', 'Lễ tang truyền thống tại Việt Nam và các vấn đề cần lưu ý', 'Tang lễ hay gọi là Đám tang, Đám ma là một buổi lễ chính thức dành cho cái chết của một người hoặc đôi khi là một sinh vật sống nào đó đã chết.', '2023-12-15 09:30:29', '2023-12-15 12:09:53'),
(177, 2, 70, 'Dịch vụ Cúng giỗ Online', 'Công viên Nghĩa trang Thiên Đường mang đến nhiều dịch vụ vượt trội để hành trình của người đã khuất được trọn vẹn và chia sẻ phần nào áp lực của những người con ở xa đang lo lắng không chu toàn hương khói cho tổ tiên, ông bà, cha mẹ. Trong đó, cúng giỗ online là một trong những dịch vụ được Khách hàng quan tâm hàng đầu bởi quá trình thực hiện dễ dàng, nhanh chóng mà mang lại hiệu quả cao.', NULL, '<p><a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>&nbsp;mang đến nhiều dịch vụ vượt trội để h&agrave;nh tr&igrave;nh của người đ&atilde; khuất được trọn vẹn v&agrave; chia sẻ phần n&agrave;o &aacute;p lực của những người con ở xa đang lo lắng kh&ocirc;ng chu to&agrave;n hương kh&oacute;i cho tổ ti&ecirc;n, &ocirc;ng b&agrave;, cha mẹ. Trong đ&oacute;, c&uacute;ng giỗ online l&agrave; một trong những dịch vụ được Kh&aacute;ch h&agrave;ng quan t&acirc;m h&agrave;ng đầu bởi qu&aacute; tr&igrave;nh thực hiện dễ d&agrave;ng, nhanh ch&oacute;ng m&agrave; mang lại hiệu quả cao.</p>\r\n\r\n<h2>C&Uacute;NG GIỖ ONLINE</h2>\r\n\r\n<p>C&ocirc;ng vi&ecirc;n Nghĩa trang&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">Thi&ecirc;n Đường&nbsp;</a>cung cấp g&oacute;i dịch vụ c&uacute;ng giỗ online với nguyện vọng thay mặt gia đ&igrave;nh chuẩn bị chu đ&aacute;o, tươm tất v&agrave; c&uacute;ng giỗ đ&uacute;ng nghi thức m&agrave; gia đ&igrave;nh Qu&yacute; vị y&ecirc;u cầu.</p>\r\n\r\n<p>&nbsp;<img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/55.jpg\" style=\"height:1500px; width:2000px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em>Một m&acirc;m lễ c&uacute;ng tại C&ocirc;ng Vi&ecirc;n&nbsp;T&acirc;m Linh Thi&ecirc;n Đường</em></p>\r\n\r\n<p>Để sử dụng dịch vụ C&uacute;ng giỗ online, Qu&yacute; vị chỉ cần thực hiện v&agrave;i thao t&aacute;c đơn giản: Ghi r&otilde; th&ocirc;ng tin địa chỉ mộ phần -&gt; Lựa chọn nghi thức c&uacute;ng giỗ gia đ&igrave;nh mong muốn -&gt; &nbsp;Chọn g&oacute;i dịch vụ -&gt; Thanh to&aacute;n.</p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/56.jpg\" style=\"height:1500px; width:2000px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em>Một m&acirc;m lễ c&uacute;ng tại C&ocirc;ng Vi&ecirc;n&nbsp;T&acirc;m Linh Thi&ecirc;n Đường</em></p>\r\n\r\n<p>Khi dịch vụ c&uacute;ng giỗ online ra đời, Ch&uacute;ng t&ocirc;i mong muốn gi&uacute;p Qu&yacute; vị lu&ocirc;n l&agrave;m tr&ograve;n bổn phận con ch&aacute;u m&agrave; kh&ocirc;ng bị vướng bận về mặt địa l&yacute; hay thời gian; gi&uacute;p những người th&acirc;n trong gia đ&igrave;nh thấy y&ecirc;n t&acirc;m hơn khi đang ở xa, khi đi c&ocirc;ng t&aacute;c vẫn c&oacute; thể hướng về cội nguồn, thắp hương cho &ocirc;ng b&agrave; tổ ti&ecirc;n. Một tiện &iacute;ch nhỏ nhưng gi&aacute; trị tinh thần v&ocirc; c&ugrave;ng to lớn, kh&ocirc;ng g&igrave; c&oacute; thể đo đếm được.</p>\r\n\r\n<p>Xem th&ecirc;m:<a href=\"https://nghiatrangthienduong.com.vn/dich-vu/dich-vu-an-ninh\"> Dịch vụ an ninh</a></p>\r\n\r\n<p>&nbsp;<img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/57.jpg\" style=\"height:1430px; width:2000px\" />Gi&uacute;p Qu&yacute; vị vẹn tr&ograve;n đạo hiếu với tổ ti&ecirc;n, mang hưng vinh cho gia tộc, l&agrave; nơi để người sống sống một cuộc sống ấm áp, nghĩa hiếu và yên bình, nơi để người đ&atilde; khuất được y&ecirc;n nghỉ ng&agrave;n thu - đ&oacute; l&agrave; gi&aacute; trị kh&aacute;c biệt m&agrave; <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n T&acirc;m linh Thi&ecirc;n Đường</a> mang đến.</p>\r\n\r\n<p>Xem th&ecirc;m : <a href=\"https://nghiatrangthienduong.com.vn/dich-vu/dich-vu-xay-dung-mo-phan\">Dịch vụ x&acirc;y dựng mộ phần</a></p>\r\n\r\n<p>&nbsp;</p>', 'true', 'News', 'dich-vu-cung-gio-online', '55.jpg', 'Dịch vụ Cúng giỗ Online', 'Cúng giỗ online là một trong những dịch vụ được Khách hàng quan tâm hàng đầu bởi quá trình thực hiện dễ dàng, nhanh chóng mà mang lại hiệu quả cao.', '2023-12-18 15:53:10', '2023-12-18 15:53:10'),
(180, 2, 71, 'Bảo vệ 24/7', 'Đội ngũ bảo vệ tại Công viên Nghĩa trang Thiên Đường được đào tạo bài bản, chuyên nghiệp và tham gia lớp bồi dưỡng nghiệp vụ do các chuyên gia của những công ty bảo vệ hàng đầu đào tạo.', NULL, '<p><span style=\"font-size:16px\"><strong>Đội ngũ bảo vệ tại <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</a> được đ&agrave;o tạo b&agrave;i bản, chuy&ecirc;n nghiệp v&agrave; tham gia lớp bồi dưỡng nghiệp vụ do c&aacute;c chuy&ecirc;n gia của những c&ocirc;ng ty bảo vệ h&agrave;ng đầu đ&agrave;o tạo. Do vậy,&nbsp;họ&nbsp;lu&ocirc;n nhận thức được tr&aacute;ch nhiệm của m&igrave;nh tại đ&acirc;y l&agrave; một nhiệm vụ cao cả, linh thi&ecirc;ng.</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Thi&ecirc;n Đường kh&ocirc;ng chỉ lu&ocirc;n lắng nghe v&agrave; thấu hiểu từng mong muốn, nhu cầu của kh&aacute;ch h&agrave;ng v&agrave; đ&aacute;p ứng tối đa nhu cầu đ&oacute; của qu&yacute; kh&aacute;ch m&agrave; đ&acirc;y c&ograve;n l&agrave; địa chỉ tịn cậy trong việc bảo vệ an ninh theo y&ecirc;u cầu của qu&yacute; kh&aacute;ch suốt 24/7. Hơn thế nữa, c&aacute;c nh&acirc;n vi&ecirc;n của <a href=\"https://nghiatrangthienduong.com.vn/\">Thi&ecirc;n Đường</a> lu&ocirc;n đảm bảo tuyệt đối t&iacute;nh b&iacute; mật về th&ocirc;ng tin của qu&yacute; kh&aacute;ch h&agrave;ng, qu&yacute; doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/dịch vụ- tiện ích/lll.jpg\" style=\"height:1500px; width:2000px\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tien-ich/tham-quan-cong-vien-mien-phi\">Tham quan c&ocirc;ng vi&ecirc;n miễn ph&iacute;</a></span></p>', 'true', 'News', 'bao-ve-247', 'lll.jpg', 'Bảo vệ 24/7', 'Đội ngũ bảo vệ tại Công viên Nghĩa trang Thiên Đường được đào tạo bài bản, chuyên nghiệp và tham gia lớp bồi dưỡng nghiệp vụ do các chuyên gia của những công ty bảo vệ hàng đầu đào tạo.', '2023-12-19 07:43:29', '2023-12-19 07:43:29'),
(181, 2, 71, 'Chụp ảnh lưu niệm miễn phí', 'Để giúp Qúy khách lưu lại những khoảnh khắc đẹp trong chuyến tham quan đầy ý nghĩa, Công viên Nghĩa trang Thiên Đường tổ chức dịch vụ chụp ảnh lưu niệm miễn phí.', NULL, '<p><strong>Để gi&uacute;p Q&uacute;y kh&aacute;ch lưu lại những khoảnh khắc đẹp trong chuyến tham quan đầy &yacute; nghĩa, C&ocirc;ng vi&ecirc;n <a href=\"https://nghiatrangthienduong.com.vn/\">Nghĩa trang Thi&ecirc;n Đường</a> tổ chức dịch vụ chụp ảnh lưu niệm miễn ph&iacute;.</strong></p>\r\n\r\n<p>V&agrave;o thứ 7 v&agrave; chủ nhật h&agrave;ng tuần, C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường tổ chức chương tr&igrave;nh tham quan miễn ph&iacute; d&agrave;nh cho Q&uacute;y kh&aacute;ch c&oacute; nhu cầu thăm viếng mộ người th&acirc;n hoặc v&atilde;n cảnh, thưởng ngoạn những c&ocirc;ng tr&igrave;nh t&acirc;m linh đặc sắc tại đ&acirc;y.</p>\r\n\r\n<p>Khi tới tham quan tại<a href=\"https://nghiatrangthienduong.com.vn/\"> C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</a>, Q&uacute;y kh&aacute;ch sẽ được chụp ảnh lưu niệm miễn ph&iacute; để lưu lại những khoảnh khắc đẹp trong chuyến đi đầy &yacute; nghĩa n&agrave;y.</p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/dịch vụ- tiện ích/523333.jpg\" /></p>\r\n\r\n<p>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/kien-truc-canh-quan\">Kiến tr&uacute;c cảnh quan tại Thi&ecirc;n Đường</a></p>\r\n\r\n<p>Mục đ&iacute;ch dịch vụ chụp ảnh miễn ph&iacute; tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường:</p>\r\n\r\n<p>&nbsp;- Gi&uacute;p kh&aacute;ch h&agrave;ng lưu lại những h&igrave;nh ảnh đẹp trong chuyến tham quan tại c&ocirc;ng vi&ecirc;n nơi c&oacute; cảnh đẹp tựa chốn Bồng lai ti&ecirc;n cảnh.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/dịch vụ- tiện ích/tiện ích.jpg\" /></p>\r\n\r\n<p>- Gi&uacute;p kh&aacute;ch h&agrave;ng lưu giữ những kỷ niệm c&ugrave;ng gia đ&igrave;nh trong những lần thăm viếng.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/dịch vụ- tiện ích/tiện ích 1.jpg\" /></p>\r\n\r\n<p>- Gi&uacute;p kh&aacute;ch h&agrave;ng trong những l&uacute;c bối rối, tang gia. Nh&acirc;n vi&ecirc;n Thi&ecirc;n Đường sẽ hỗ trợ kh&aacute;ch h&agrave;ng chụp lại ảnh sự kiện của gia đ&igrave;nh.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/dịch vụ- tiện ích/tiện ích 2.jpg\" /></p>\r\n\r\n<p>- Lan tỏa những gi&aacute; trị tốt đẹp của&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường</a>&nbsp;th&ocirc;ng qua Đạo Hiếu Nghĩa Vẹn Tr&ograve;n của mỗi gia đ&igrave;nh Việt Nam.&nbsp;</p>\r\n\r\n<p>Xem th&ecirc;m:<a href=\"https://nghiatrangthienduong.com.vn/tien-ich/cac-nghi-le-tam-linh-thuong-nien\"> C&aacute;c nghi lễ t&acirc;m linh thường ni&ecirc;n</a></p>', 'true', 'News', 'chup-anh-luu-niem-mien-phi', '523333.jpg', 'Chụp ảnh lưu niệm miễn phí', '​​​​​​​Để giúp Qúy khách lưu lại những khoảnh khắc đẹp trong chuyến tham quan đầy ý nghĩa, Công viên Nghĩa trang Thiên Đường tổ chức dịch vụ chụp ảnh lưu niệm miễn phí.', '2023-12-19 08:06:44', '2023-12-19 08:53:18'),
(182, 2, 71, 'Tham quan công viên miễn phí', 'Đến với Thiên Đường không chỉ có không gian xanh mướt, quần thể công trình tâm linh độc đáo, chúng tôi luôn nỗ lực thực hiện “lời hứa với khách hàng” bằng những hành động thiết thực “phục vụ bằng cả con tim và sự tinh tế”.', NULL, '<p><strong>Đến với <a href=\"https://nghiatrangthienduong.com.vn/\">Thi&ecirc;n Đường</a> kh&ocirc;ng chỉ c&oacute; kh&ocirc;ng gian xanh mướt, quần thể c&ocirc;ng tr&igrave;nh t&acirc;m linh độc đ&aacute;o, ch&uacute;ng t&ocirc;i lu&ocirc;n nỗ lực thực hiện &ldquo;lời hứa với kh&aacute;ch h&agrave;ng&rdquo; bằng những h&agrave;nh động thiết thực &ldquo;phục vụ bằng cả con tim v&agrave; sự tinh tế&rdquo;.</strong></p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/dịch vụ- tiện ích/52.jpg\" /></p>\r\n\r\n<p><em>Xe đ&oacute;n kh&aacute;ch tại Văn ph&ograve;ng H&agrave; Nội</em></p>\r\n\r\n<p>Được x&acirc;y dựng tại x&atilde; Lang Qu&aacute;n, huyện Y&ecirc;n Sơn, tỉnh Tuy&ecirc;n Quang, nằm ngay s&aacute;t trục đường Quốc Lộ 2 v&agrave; tuyến đường cao tốc trọng điểm quốc gia: H&agrave; Nội &ndash; Tuy&ecirc;n Quang, chỉ với 75 ph&uacute;t đi xe, qu&yacute; kh&aacute;ch h&agrave;ng đ&atilde; đến với <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường.</a></p>\r\n\r\n<p>Để đ&aacute;p ứng nhu cầu đi lại v&agrave; tham quan dự &aacute;n. C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường tổ chức chương tr&igrave;nh tham quan ho&agrave;n to&agrave;n miễn ph&iacute; v&agrave;o thứ 7 v&agrave; chủ nhật h&agrave;ng tuần.</p>\r\n\r\n<p>Lịch tr&igrave;nh cụ thể</p>\r\n\r\n<p>+ 7h00: Xe xuất ph&aacute;t tại Văn Ph&ograve;ng Số 164 Khuất Duy Tiến &ndash; Thanh Xu&acirc;n &ndash; H&agrave; Nội</p>\r\n\r\n<p>+ Loại xe sử dụng: 16 &ndash; 29 &ndash; 35 &ndash; 45 chỗ &ndash; Đời mới &ndash; Hiện đại.</p>\r\n\r\n<p>+ Đội ngũ l&aacute;i xe: Nhiệt t&igrave;nh &ndash; Cẩn thận &ndash; Lịch sự &ndash; Chuy&ecirc;n nghiệp</p>\r\n\r\n<p>+ C&oacute; chỗ để xe m&aacute;y &ndash; &ocirc; t&ocirc; cho kh&aacute;ch tại Văn ph&ograve;ng.</p>\r\n\r\n<p>+ 8h30: Xe đến dự &aacute;n &ndash; C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường &ndash; Lễ t&acirc;n đ&oacute;n tiếp kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>+ 8h40: Đo&agrave;n nghỉ giải lao 20 ph&uacute;t &ndash; C&oacute; nh&acirc;n vi&ecirc;n phục vụ nước uống &ndash; Khăn lạnh.</p>\r\n\r\n<p>+ 9h: Được nghe giới thiệu tổng thể dự &aacute;n qua video giới thiệu &ndash; Sau đ&oacute; đưa Kh&aacute;ch thăm quan bằng xe &ocirc; t&ocirc; điện t&igrave;m hiểu dự &aacute;n. &ndash; Kh&aacute;ch h&agrave;ng được ph&aacute;t t&agrave;i liệu li&ecirc;n quan đến dự &aacute;n.</p>\r\n\r\n<p>+ 10h30: Nh&acirc;n vi&ecirc;n Kinh doanh &ndash; Xin th&ocirc;ng tin v&agrave; ghi ch&eacute;p lại cẩn thận nhu cầu, nguyện vọng của kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>Tham gia: Ghi Lưu B&uacute;t &ndash; Cảm nghĩ &ndash; &Yacute; kiến đ&oacute;ng g&oacute;p tại Sổ v&agrave;ng Thi&ecirc;n Đường.</p>\r\n\r\n<p>+ 11h00: Kh&aacute;ch h&agrave;ng thưởng thức bữa trưa :X&ocirc;i nếp nương, đặc sản g&agrave; đồi, miến g&agrave;, hoa quả&hellip; hoặc tr&agrave; xanh Tuy&ecirc;n Quang, nước chanh lạnh, hỗ trợ &ocirc; che nắng, n&oacute;n l&aacute;, ủng đi mưa, nh&acirc;n vi&ecirc;n y tế v.v&hellip;</p>\r\n\r\n<p>+ 12h30 : Xe khởi h&agrave;nh từ C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường về H&agrave; Nội.</p>\r\n\r\n<p>+ 14h: Xe về đến VP C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường tại H&agrave; Nội &ndash; Kết th&uacute;c chuyến đi</p>\r\n\r\n<p>(Nước uống,&hellip;được phục vụ suốt h&agrave;nh tr&igrave;nh &ndash; tất cả c&aacute;c dịch vụ đều được Miễn ph&iacute;).</p>\r\n\r\n<p>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tien-ich/chup-anh-luu-niem-mien-phi\">Chụp ảnh lưu niệm miễn ph&iacute;&nbsp;</a></p>', 'true', 'News', 'tham-quan-cong-vien-mien-phi', '67_52.jpg', 'Tham quan công viên miễn phí', 'Đến với Thiên Đường không chỉ có không gian xanh mướt, quần thể công trình tâm linh độc đáo, chúng tôi luôn nỗ lực thực hiện “lời hứa với khách hàng” bằng những hành động thiết thực “phục vụ bằng cả con tim và sự tinh tế”.', '2023-12-19 08:08:57', '2023-12-19 08:08:57'),
(183, 2, 71, 'Các nghi lễ tâm linh thường niên', 'Công viên Nghĩa trang Thiên Đường thường niên tổ chức các nghi lễ tâm linh phù hợp văn hóa, tín ngưỡng người Việt.', NULL, '<p><strong><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</a> thường ni&ecirc;n tổ chức c&aacute;c nghi lễ t&acirc;m linh ph&ugrave; hợp văn h&oacute;a, t&iacute;n ngưỡng người Việt.</span></strong></p>\r\n\r\n<p><span style=\"font-size:16px\">Quan niệm của người Việt cho rằng, c&aacute;c nghi lễ t&acirc;m linh l&agrave; dịp mọi người c&oacute; thể b&agrave;y tỏ l&ograve;ng th&agrave;nh k&iacute;nh với &ocirc;ng b&agrave; tổ ti&ecirc;n, li&ecirc;n kết người c&ograve;n sống với người đ&atilde; khuất theo triết l&yacute; nh&acirc;n - quả lu&acirc;n hồi của nh&agrave; Phật. V&agrave;o thời điểm n&agrave;y, nhiều người Việt đến Nghĩa trang C&ocirc;ng vi&ecirc;n <a href=\"https://nghiatrangthienduong.com.vn/\">Thi&ecirc;n Đường</a> để thực hiện c&aacute;c nghi lễ t&acirc;m linh cho người đ&atilde; khuất.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">C&aacute;c nghi lễ t&acirc;m linh thường ni&ecirc;n bao gồm: Tết Nguy&ecirc;n Đ&aacute;n, Tết Nguy&ecirc;n Ti&ecirc;u, Tết H&agrave;n Thực, Giỗ tổ H&ugrave;ng Vương, Tết Thanh Minh, Lễ Phật Đản, Tết Đoan Ngọ, Tết Vu Lan, Tết Trung Thu, Tết &Ocirc;ng C&ocirc;ng &Ocirc;ng T&aacute;o.</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/dịch vụ- tiện ích/tiện ích 3.jpg\" /></p>\r\n\r\n<p><span style=\"font-size:16px\">Tại C&ocirc;ng vi&ecirc;n <a href=\"https://nghiatrangthienduong.com.vn/\">Nghĩa trang Thi&ecirc;n Đường</a>, ngo&agrave;i thực hiện c&aacute;c nghi lễ t&acirc;m linh, Q&uacute;y kh&aacute;ch c&oacute; thể dạo chơi, ngắm cảnh. Đ&acirc;y l&agrave; nơi c&oacute; kh&ocirc;ng gian quy m&ocirc;, đẹp mắt với trăm hoa đua nở sắc m&agrave;u. Kh&aacute;ch đến tham quan sẽ cảm nhận sự nhẹ nh&agrave;ng, binh an bởi tiếng chu&ocirc;ng ch&ugrave;a ng&acirc;n vọng, hay tiếng kinh thoang thoảng trong gi&oacute; chiều bảng lảng.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/dịch vụ- tiện ích/tiện ích 4.jpg\" /></p>\r\n\r\n<p>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tien-ich-dich-vu\">Dịch vụ- tiện &iacute;ch tại Thi&ecirc;n Đường</a></p>', 'true', 'News', 'cac-nghi-le-tam-linh-thuong-nien', '60.jpg', 'Các nghi lễ tâm linh thường niên', 'Công viên Nghĩa trang Thiên Đường thường niên tổ chức các nghi lễ tâm linh phù hợp văn hóa, tín ngưỡng người Việt.', '2023-12-19 08:18:25', '2023-12-19 08:55:07'),
(186, 2, 65, 'Tượng Đại Thế Chí Bồ Tát', 'Nằm ở vị trí trung tâm đồi Thiên Lộc, tượng Phật Đại Thế Chí Bồ Tát có chiều cao 19m, tay cầm hoa sen xanh, cổ đeo ngọc anh lạc. Ngài là biểu tượng cho tinh thần Đại Trí, dùng ánh sáng trí tuệ để giác ngộ chúng sinh.', NULL, '<p><strong><span style=\"font-size:16px\">Nằm ở vị tr&iacute; trung t&acirc;m đồi Thi&ecirc;n Lộc, tượng Phật Đại Thế Ch&iacute; Bồ T&aacute;t c&oacute; chiều cao 19m, tay cầm hoa sen xanh, cổ đeo ngọc anh lạc. Ng&agrave;i l&agrave; biểu tượng cho tinh thần Đại Tr&iacute;, d&ugrave;ng &aacute;nh s&aacute;ng tr&iacute; tuệ để gi&aacute;c ngộ ch&uacute;ng sinh.</span></strong></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>Đại Thế Ch&iacute; Bồ t&aacute;t</strong>&nbsp;l&agrave; một trong những vị Bồ t&aacute;t l&acirc;u đời v&agrave; quyền lực. Ng&agrave;i l&agrave; thị giả của Đức Phật A Di Đ&agrave;, đứng b&ecirc;n phải của Đức Phật, tay cầm hoa sen xanh, cổ đeo ngọc anh lạc. Bồ t&aacute;t Đại Thế Ch&iacute; c&ugrave;ng&nbsp;Qu&aacute;n Thế &Acirc;m Bồ t&aacute;t&nbsp;v&agrave;&nbsp;Đức Phật A Di Đ&agrave;&nbsp;thường được gọi l&agrave;&nbsp;T&acirc;y Phương Tam Th&aacute;nh&nbsp;của thế giới Cực Lạc ở phương T&acirc;y.</span></p>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>Đại Thế Ch&iacute; Bồ T&aacute;t l&agrave; ai?</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Đại Thế Ch&iacute; Bồ T&aacute;t c&ograve;n c&oacute; t&ecirc;n gọi kh&aacute;c l&agrave; V&ocirc; Bi&ecirc;n Quang Bồ t&aacute;t, Đắc Đại Thế Bồ t&aacute;t, Linh C&aacute;t Bồ t&aacute;t, Đại Tinh Tấn Bồ t&aacute;t v&agrave; thường được gọi tắt l&agrave; Thế Ch&iacute;. Ng&agrave;i c&oacute; hạnh nguyện đại h&ugrave;ng đại lực đại từ bi, hạnh nguyện n&agrave;y để điều phục, tiếp độ những ch&uacute;ng sanh cang cường trong Ta b&agrave; thế giới. Theo kinh Qu&aacute;n V&ocirc; Lượng Thọ th&igrave; Đại Thế Ch&iacute; được hiểu l&agrave; vị Bồ t&aacute;t d&ugrave;ng &aacute;nh s&aacute;ng tr&iacute; tuệ để soi khắp mười phương, gi&uacute;p ch&uacute;ng sanh trong ba đường &aacute;c được giải tho&aacute;t, đồng thời c&oacute; được năng lực v&ocirc; thượng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Trong thuở xa xưa, ở thời V&ocirc; Lượng Đức Tựu An Lạc, Đức Phật l&uacute;c bấy giờ c&oacute; hiệu l&agrave; Kim Quang Sư Tử Du Hỷ, Ng&agrave;i thị hiện để độ h&oacute;a ch&uacute;ng sanh. Trong nước thời ấy c&oacute; vị vua hiệu l&agrave; Oai Đức, trị v&igrave; d&acirc;n ch&uacute;ng bằng ch&aacute;nh ph&aacute;p n&ecirc;n được gọi l&agrave; Ph&aacute;p Vương, &ocirc;ng rất k&iacute;nh thờ Đức Phật. Một h&ocirc;m, khi đang ngồi tọa thiền Tam muội, khi xuất định vua nh&igrave;n thấy hai hoa sen mọc ở hai b&ecirc;n, trong mỗi hoa sen l&agrave; một vị đồng tử. Nh&agrave; vua c&ugrave;ng hai vị đồng tử đến chỗ Đức Phật nghe ph&aacute;p, l&uacute;c ấy vua Oai Đức ch&iacute;nh l&agrave; tiền th&acirc;n của Đức Phật Th&iacute;ch Ca M&acirc;u Ni, c&ograve;n hai vị đồng từ l&agrave;&nbsp;Qu&aacute;n Thế &Acirc;m Bồ t&aacute;t&nbsp;v&agrave; Đại Thế Ch&iacute; Bồ t&aacute;t.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Đại Thế Ch&iacute; Bồ T&aacute;t biểu tượng cho tinh thần Đại Tr&iacute;</span></em></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/tuong-phat-di-lac\">Tượng Phật Di Lặc tại Thi&ecirc;n Đường</a></span></p>\r\n\r\n<h2><span style=\"font-size:16px\"><strong>&Yacute; nghĩa tượng Đại Thế Ch&iacute; Bồ T&aacute;t tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Với chiều cao 19m, tượng Đại Thế Ch&iacute; Bồ T&aacute;t nằm ở vị tr&iacute; trung t&acirc;m đồi Thi&ecirc;n Lộc của C&ocirc;ng vi&ecirc;n <a href=\"https://nghiatrangthienduong.com.vn/\">Nghĩa trang Thi&ecirc;n Đường</a>. Tr&ecirc;n tay ng&agrave;i kh&ocirc;ng cầm ph&aacute;p bảo hay t&ugrave;y kh&iacute; b&ecirc;n ngo&agrave;i m&agrave; l&agrave; một b&ocirc;ng hoa sen xanh. Hoa sen xanh tượng trưng cho sự thanh tịnh, tức l&agrave; đoạn đức. Ng&agrave;i biểu thị cho tinh thần Đại Tr&iacute;, c&ograve;n Qu&aacute;n Thế &Acirc;m Bồ T&aacute;t biểu thị cho tinh thần Đại Bi. Con người hướng theo T&acirc;m Phật lu&ocirc;n cần c&oacute; t&acirc;m từ bi v&agrave; tr&iacute; tuệ để th&ocirc;ng tuệ vạn vật.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Muốn cứu vớt ch&uacute;ng sanh về c&otilde;i Tịnh độ, trước phải dạy họ dứt sạch phiền n&atilde;o &ocirc; uế. V&igrave; thế,&nbsp;&yacute; nghĩa tượng Đại Thế Ch&iacute; Bồ T&aacute;t tại C&ocirc;ng vi&ecirc;n Nghĩa trang <a href=\"https://nghiatrangthienduong.com.vn/\">Thi&ecirc;n Đường </a>với vầng h&agrave;o&nbsp;quang như&nbsp;&aacute;nh s&aacute;ng tr&iacute; tuệ chiếu soi cho ch&uacute;ng sinh thấy r&otilde; những &ocirc; nhiễm của m&igrave;nh, đồng thời cũng gi&uacute;p cho họ c&oacute; sức mạnh đoạn trừ những nhiễm &ocirc; n&agrave;y, để c&oacute; thể đưa họ về c&otilde;i Tịnh độ.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&Yacute; nghĩa s&acirc;u xa của tượng&nbsp;Bồ T&aacute;t Đại Thế Ch&iacute;, l&agrave; hướng con người đến điều thiện,&nbsp;nhắc nhở bản th&acirc;n, đoạn trừ c&aacute;i &aacute;c, qua đ&oacute; m&agrave; mọi tai ương đều được h&oacute;a giải.&nbsp;Đồng thời con người lu&ocirc;n phải d&ugrave;ng tr&iacute; tuệ, sự&nbsp;s&aacute;ng suốt khi nh&igrave;n nhận vấn đề, tr&aacute;nh để &ocirc; uế, t&agrave; niệm che khuất sự việc.&nbsp;Đạt được sức mạnh v&ocirc; bi&ecirc;n mới&nbsp;hướng tới th&acirc;n t&acirc;m an lạc.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline&nbsp;0934.524.445 hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\">https://nghiatrangthienduong.com.vn/</a>.</span></p>', 'true', 'News', 'tuong-dai-the-chi-bo-tat', 'tâm linh.jpg', 'Tượng Đại Thế Chí Bồ Tát', 'Nằm ở vị trí trung tâm đồi Thiên Lộc, tượng Phật Đại Thế Chí Bồ Tát có chiều cao 19m, tay cầm hoa sen xanh, cổ đeo ngọc anh lạc. Ngài là biểu tượng cho tinh thần Đại Trí, dùng ánh sáng trí tuệ để giác ngộ chúng sinh.', '2023-12-20 02:48:39', '2023-12-20 02:58:21');
INSERT INTO `posts` (`id`, `user_id`, `category_id`, `name`, `detail`, `info`, `content`, `status`, `sort_by`, `slug`, `img`, `title`, `description`, `created_at`, `updated_at`) VALUES
(187, 2, 65, 'Tượng Phật Di Lặc', 'Nằm ở vị trí trung tâm đồi Thiên Phúc, tượng Phật Di Lặc có chiều cao 13m. Tượng được tạc theo hình tướng Bố Đại hòa thượng, mang thân hình mập mạp, hiền hòa, tự tại, miệng tươi cười. Với thần sắc rạng rỡ, Ngài là biểu tượng cho niềm hạnh phúc, an vui.', NULL, '<p><span style=\"font-size:16px\"><strong>Nằm ở vị tr&iacute; trung t&acirc;m đồi Thi&ecirc;n Ph&uacute;c, tượng Phật Di Lặc c&oacute; chiều cao 13m. Tượng được tạc theo h&igrave;nh tướng Bố Đại h&ograve;a thượng, mang th&acirc;n h&igrave;nh mập mạp, hiền h&ograve;a, tự tại, miệng tươi cười. Với thần sắc rạng rỡ, Ng&agrave;i l&agrave; biểu tượng cho niềm hạnh ph&uacute;c, an vui.</strong></span></p>\r\n\r\n<h2><strong><span style=\"font-size:16px\">Phật Di Lặc l&agrave; ai?</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\">Đức Phật Di Lặc (Maitreya trong tiếng Phạn, Metteyya trong tiếng Pali) l&agrave; một vị bồ t&aacute;t hạ ph&agrave;m tr&ecirc;n trần gian, Ng&agrave;i đạt được gi&aacute;c ngộ vi&ecirc;n m&atilde;n v&agrave; giảng dạy gi&aacute;o ph&aacute;p cho ch&uacute;ng sinh khi gi&aacute;o l&yacute; Phật gi&aacute;o bị l&atilde;ng qu&ecirc;n. Trong một số kinh của Đạo Phật, chẳng hạn như Kinh A Di Đ&agrave; v&agrave; Kinh Ph&aacute;p Hoa, Ng&agrave;i c&ograve;n được gọi l&agrave; Bồ T&aacute;t Ajita.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Theo kinh Phật gi&aacute;o, Đức Di Lặc trở th&agrave;nh người kế vị của Đức Phật lịch sử Th&iacute;ch Ca M&acirc;u Ni, người s&aacute;ng lập Phật gi&aacute;o. Lời ti&ecirc;n tri về sự xuất hiện của Di Lặc được t&igrave;m thấy trong kinh điển của tất cả c&aacute;c trường ph&aacute;i Phật gi&aacute;o v&agrave; được hầu hết c&aacute;c Phật tử chấp nhận như l&agrave; dấu hiệu của một sự kiện c&oacute; thật trong tương lai. Kinh n&oacute;i: &ldquo;Bấy giờ Đức Phật bảo Di Lặc Bồ T&aacute;t rằng: Di Lặc, nay con tin ph&aacute;p V&ocirc; thượng Ch&aacute;nh đẳng Ch&aacute;nh gi&aacute;c m&agrave; con đ&atilde; t&iacute;ch tập trải qua v&ocirc; lượng kiếp. V&agrave;o ng&agrave;y tận thế, sau khi Đức Phật diệt độ, con n&ecirc;n d&ugrave;ng thần th&ocirc;ng của m&igrave;nh để truyền b&aacute; rộng r&atilde;i kinh điển như vậy ở c&otilde;i Di&ecirc;m Ph&ugrave; Đề, để kh&ocirc;ng bị ph&acirc;n ly&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh 1.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span style=\"font-size:16px\">&Yacute; nghĩa tượng Phật Di Lặc tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Tượng Phật Di Lặc tại <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</a> được x&acirc;y dựng bằng b&ecirc; t&ocirc;ng cốt th&eacute;p cao 13m, nằm ở vị tr&iacute; trung t&acirc;m đồi Thi&ecirc;n Ph&uacute;c. Tượng được tạc theo h&igrave;nh tướng Bố Đại h&ograve;a thượng, mang th&acirc;n h&igrave;nh mập mạp, hiền h&ograve;a, tự tại, miệng tươi cười&hellip; Đức Phật Di Lặc l&agrave; tượng trưng tuyệt đối của niềm vui v&agrave; hạnh ph&uacute;c. Đ&oacute; l&agrave; l&yacute; do d&acirc;n gian gọi Ng&agrave;i l&agrave; &ldquo;Phật Cười&rdquo;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Theo truyền thuyết, niềm vui lớn nhất của Phật Di Lặc l&agrave; biến những buồn phiền, giận dữ, &aacute;p lực hay căng thẳng của con người th&agrave;nh hạnh ph&uacute;c. Người ta tin rằng nụ cười nội t&acirc;m của Phật Di Lặc mạnh tới mức n&oacute; lu&ocirc;n tỏa s&aacute;ng tr&ecirc;n khu&ocirc;n mặt hiền từ v&agrave; Ng&agrave;i tới đ&acirc;u th&igrave; ở đ&oacute; sẽ c&oacute; hạnh ph&uacute;c.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Tượng được thực hiện bởi đi&ecirc;u khắc gia Hồ Văn Đen - nghệ nh&acirc;n đ&atilde; tạc n&ecirc;n những tượng Phật kỷ lục quốc gia như tượng Phật Di Lặc tr&ecirc;n n&uacute;i Cấm &ndash; Tịnh Bi&ecirc;n, tượng Phật B&agrave; Quan &Acirc;m &ndash; ch&ugrave;a Linh Ứng, B&atilde;i Bụt, Đ&agrave; Nẵng&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về C&ocirc;ng vi&ecirc;n <a href=\"https://nghiatrangthienduong.com.vn/\">Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline&nbsp;</span><strong>0934.524.445</strong><span style=\"font-size:16px\"> hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\">https://nghiatrangthienduong.com.vn/</a>.</span></p>\r\n\r\n<p><em><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/tuong-quan-the-am-bo-tat\">Tượng Qu&aacute;n Thế &Acirc;m Bồ T&aacute;t</a></span></em></p>\r\n\r\n<p>&nbsp;</p>', 'true', 'News', 'tuong-phat-di-lac', 'tâm linh 1.jpg', 'Tượng Phật Di Lặc', 'Nằm ở vị trí trung tâm đồi Thiên Phúc, tượng Phật Di Lặc có chiều cao 13m. Tượng được tạc theo hình tướng Bố Đại hòa thượng, mang thân hình mập mạp, hiền hòa, tự tại, miệng tươi cười. Với thần sắc rạng rỡ, Ngài là biểu tượng cho niềm hạnh phúc, an vui.', '2023-12-20 02:57:19', '2024-01-04 08:42:51'),
(188, 2, 65, 'Tượng Quán Thế Âm Bồ Tát', 'Tại Thiên Đường - Công viên tâm linh cao cấp hàng đầu Việt Nam, quần thể Đại Tượng Phật được coi là điểm nhấn quan trọng nhất. Trong số đó, không thể không kể đến tượng Quán Thế Âm Bồ Tát.', NULL, '<p><strong><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường-Tuy&ecirc;n Quang</a> được x&acirc;y dựng theo m&ocirc; h&igrave;nh c&ocirc;ng vi&ecirc;n&nbsp;t&acirc;m linh&nbsp;chất lượng cao. C&ocirc;ng vi&ecirc;n được đặt tại x&atilde; Lang Qu&aacute;n, huyện Y&ecirc;n Sơn, tỉnh Tuy&ecirc;n Quang. Khởi c&ocirc;ng từ năm 2021, đến nay C&ocirc;ng vi&ecirc;n đ&atilde; ho&agrave;n th&agrave;nh đến tr&ecirc;n 90% tiến độ dự &aacute;n v&agrave; đ&atilde; đ&oacute;n tiếp kh&aacute;ch tham quan từ cuối năm 2022.</span></strong></p>\r\n\r\n<p><span style=\"font-size:16px\">Tại Thi&ecirc;n Đường -&nbsp;C&ocirc;ng vi&ecirc;n t&acirc;m linh&nbsp;cao cấp h&agrave;ng đầu Việt Nam, <a href=\"https://nghiatrangthienduong.com.vn/canh-quan\">quần thể Đại Tượng Phật</a> được coi l&agrave; điểm nhấn quan trọng nhất. Trong số đ&oacute;, kh&ocirc;ng thể kh&ocirc;ng kể đến tượng Qu&aacute;n Thế &Acirc;m Bồ T&aacute;t.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>Qu&aacute;n Thế &Acirc;m Bồ T&aacute;t</strong> l&agrave; hiện th&acirc;n của sự từ bi, cứu khổ, cứu nạn. H&igrave;nh tượng của Ng&agrave;i rất đỗi th&acirc;n thuộc với người d&acirc;n Việt, bởi vậy hầu như c&aacute;c ng&ocirc;i ch&ugrave;a ở Việt Nam đều thờ t&ocirc;n tượng Ng&agrave;i. V&agrave; tại quần thể<a href=\"https://nghiatrangthienduong.com.vn/\">&nbsp;C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>, t&ocirc;n tượng của Qu&aacute;n Thế &Acirc;m Bồ T&aacute;t l&agrave; một trong những bức tượng nổi tiếng nhất.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh 2.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Tượng Qu&aacute;n Thế &Acirc;m Bồ T&aacute;t tại C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường</span></em></p>\r\n\r\n<p><span style=\"font-size:16px\">C&aacute;ch trung t&acirc;m th&agrave;nh phố về khoảng 9km, Tượng Phật Qu&aacute;n Thế &Acirc;m, tọa lạc tr&ecirc;n đồi Thi&ecirc;n Đức, thuộc quần thể <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang</a>&nbsp;Thi&ecirc;n Đường, l&agrave; một trong những bức tượng nổi tiếng tại Tuy&ecirc;n Quang.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/dia-the-phong-thuy\">Địa thế phong thủy&nbsp;tại Thi&ecirc;n Đường</a></span></p>\r\n\r\n<h2><span style=\"font-size:16px\">Điểm nổi bật trong thiết kế tượng Qu&aacute;n Thế &Acirc;m Bồ T&aacute;t tại C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường</span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Tượng Qu&aacute;n Thế &Acirc;m Bồ T&aacute;t tại đ&acirc;y được đ&uacute;c từ b&ecirc; t&ocirc;ng cốt th&eacute;p, cao 19m, trọng lượng gần 100 tấn, được c&aacute;c nghệ nh&acirc;n đi&ecirc;u khắc k&igrave; c&ocirc;ng trong khoảng 8 th&aacute;ng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Đứng từ xa ngước nh&igrave;n l&ecirc;n sẽ thấy h&igrave;nh ảnh Bồ T&aacute;t Qu&aacute;n&nbsp;Thế &Acirc;m nổi bật tr&ecirc;n đ&agrave;i sen, ph&iacute;a sau l&agrave; khung cảnh trời đất bao la, n&uacute;i rừng h&ugrave;ng vĩ, tay Ng&agrave;i đang rưới nước Cam Lồ với gương mặt hiền từ, to&aacute;t l&ecirc;n t&igrave;nh y&ecirc;u thương ch&uacute;ng sinh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh 3.png\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Tượng Qu&aacute;n Thế &Acirc;m được đ&uacute;c hai mặt quay về hướng Đ&ocirc;ng - T&acirc;y</span></em></p>\r\n\r\n<p><span style=\"font-size:16px\">C&aacute;c đường n&eacute;t tr&ecirc;n t&ocirc;n tượng rất đẹp, mềm mại, t&agrave; &aacute;o nh&igrave;n sắc n&eacute;t m&agrave; rất thần th&aacute;i. Bức tượng m&agrave;u trắng với vị thế nghi&ecirc;m trang nổi bật giữa nền trời xanh thẳm, tạo th&agrave;nh một điểm nhấn hết sức thu h&uacute;t m&agrave; kh&ocirc;ng k&eacute;m phần thanh tịnh tại <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường.</a></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Theo Phật gi&aacute;o, Mẹ Quan&nbsp;Thế &Acirc;m Bồ T&aacute;t l&agrave; biểu tượng của sự tu luyện khổ hạnh v&agrave; l&ograve;ng từ bi. Mẹ Quan Thế &Acirc;m c&oacute; thể thấy được hết những khổ đau, cơ cực trong biển m&ecirc; của con người nơi nh&acirc;n gian. V&igrave; thấy v&agrave; thấu hiểu hết những khổ đau đ&oacute; n&ecirc;n Mẹ Quan Thế &Acirc;m sẽ gi&uacute;p đỡ mọi người vượt qua những đau khổ để t&igrave;m được nguồn vui, niềm tin trong cuộc sống.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Mẹ Quan Thế &Acirc;m Bồ T&aacute;t c&ograve;n c&oacute; &yacute; nghĩa răn dạy mọi người sống hướng thiện hơn. Gương mặt ph&uacute;c hậu của Mẹ Quan Thế &Acirc;m mỗi khi nh&igrave;n l&ecirc;n sẽ gi&uacute;p mọi người lu&ocirc;n nhắc nhở bản th&acirc;n kh&ocirc;ng l&agrave;m những việc xấu, kh&ocirc;ng l&agrave;m những việc c&oacute; lỗi với t&acirc;m can.</span></p>\r\n\r\n<h2><span style=\"font-size:16px\">Nguồn gốc của danh xưng Quan &Acirc;m</span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Theo 1 truyền thuyết Phật Gi&aacute;o, những ai khi đ&atilde; tu th&agrave;nh ch&aacute;nh quả sẽ đạt được tới cảnh giới ngũ gi&aacute;c đồng quy, tức 5 gi&aacute;c quan h&ograve;a l&agrave;m một, gi&uacute;p họ c&oacute; thể d&ugrave;ng tai để &ldquo;thấy&rdquo;, mắt để &ldquo;nghe&rdquo; v&agrave; lưỡi để &ldquo;ngửi&rdquo;. Khi đ&oacute;, họ sẽ được trao cho danh xưng l&agrave; Quan &acirc;m, Quan Thế &Acirc;m hay Qu&aacute;n Thế &acirc;m.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ch&iacute;nh v&igrave; thế, Quan &Acirc;m Bồ T&aacute;t mang h&igrave;nh ảnh của 1 vị Đại sĩ lu&ocirc;n c&oacute; thể &ldquo;nh&igrave;n&rdquo;, &ldquo;cảm&rdquo; v&agrave; &ldquo;nghe&rdquo; được những tiếng ai o&aacute;n, khổ đau thầm k&iacute;n nhất của ch&uacute;ng sinh, từ đ&oacute; gi&uacute;p họ tai qua nạn khỏi, vượt ng&agrave;n thử th&aacute;ch bằng sự thần th&ocirc;ng, quảng đại của m&igrave;nh.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh 4.png\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Tượng Qu&aacute;n Thế &Acirc;m Bồ T&aacute;t tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</span></em></p>\r\n\r\n<h2><span style=\"font-size:16px\">&Yacute; nghĩa của Tượng Mẹ Quan Thế &Acirc;m Bồ T&aacute;t đứng tr&ecirc;n đ&agrave;i sen</span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Từ xưa đến nay, trong gi&aacute;o l&yacute; nh&agrave; Phật, hoa sen lu&ocirc;n l&agrave; h&igrave;nh ảnh biểu trưng cho sự trong sạch, thuần khiết v&agrave; hồi sinh. H&igrave;nh ảnh Bồ T&aacute;t đứng tr&ecirc;n đ&agrave;i sen l&agrave; biểu tượng của Phật ph&aacute;p vĩnh hằng v&agrave; m&agrave;u nhiệm.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường, Bồ T&aacute;t Qu&aacute;n Thế &Acirc;m được tạc đứng tr&ecirc;n đ&agrave;i sen, một tay cầm theo b&igrave;nh cam lồ, tay c&ograve;n lại bắt ấn để phổ độ ch&uacute;ng sinh. Tương truyền, b&igrave;nh cam lồ chứa nước ngọt trong l&agrave;nh, nước trong b&igrave;nh tưới v&agrave;o mu&ocirc;n vật th&igrave; tất cả đều được gi&aacute;c ngộ, th&acirc;n thể trong sạch, tươi mới, gi&uacute;p cho con người ở c&otilde;i tạm hay c&otilde;i &acirc;m cũng đều tho&aacute;t khỏi lu&acirc;n hồi, đau khổ.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline&nbsp;</span><strong>0934.524.445</strong><span style=\"font-size:16px\"> hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\">https://nghiatrangthienduong.com.vn/</a>.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/tuong-phat-thich-ca\">Tượng Phật Th&iacute;ch Ca</a></span></p>', 'true', 'News', 'tuong-quan-the-am-bo-tat', 'tâm linh 2.jpg', 'Tượng Quán Thế Âm Bồ Tá', 'Tại Thiên Đường - Công viên tâm linh cao cấp hàng đầu Việt Nam, quần thể Đại Tượng Phật được coi là điểm nhấn quan trọng nhất. Trong số đó, không thể không kể đến tượng Quán Thế Âm Bồ Tát.', '2023-12-20 03:07:37', '2023-12-20 04:02:29'),
(189, 2, 65, 'Tượng Phật Thích Ca', 'Tượng Phật Thích Ca tọa lạc tại vị trí đắc địa nhất trong khuôn viên của Công viên Nghĩa trang Thiên Đường, hai bên trục thần đạo là các pho tượng tôn giả.', NULL, '<p><strong><span style=\"font-size:16px\">Tượng Phật Th&iacute;ch Ca được x&acirc;y dựng tại đồi Thi&ecirc;n An,&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường.</a> Tượng được kh&aacute;nh th&agrave;nh v&agrave;o ng&agrave;y 2/9/2022 (tức 7/8 &acirc;m lịch) tại x&atilde; Lang Qu&aacute;n, huyện Y&ecirc;n Sơn, tỉnh Tuy&ecirc;n Quang. Tượng tạc thế Phật ngồi tr&ecirc;n đ&agrave;i sen, cao 23m, mặt từ bi, mắt nh&igrave;n xuống, miệng mỉm cười. Tượng tọa lạc tại vị tr&iacute; đắc địa nhất trong khu&ocirc;n vi&ecirc;n của C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường, hai b&ecirc;n trục thần đạo l&agrave; c&aacute;c pho tượng t&ocirc;n giả.</span></strong></p>\r\n\r\n<h2><strong><span style=\"font-size:16px\">&Yacute; nghĩa h&igrave;nh ảnh Phật Th&iacute;ch Ca ngồi tr&ecirc;n đ&agrave;i sen</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\">Hoa sen được v&iacute; như c&aacute;i t&acirc;m thanh tịnh kh&ocirc;ng &ocirc; nhiễm, đức hạnh, sự vi&ecirc;n m&atilde;n. Sự ph&aacute;t triển của b&ocirc;ng sen c&ograve;n được v&iacute; với qu&aacute; tr&igrave;nh tu tập t&acirc;m thức của con người: c&acirc;y sen mọc dưới b&ugrave;n l&agrave; c&aacute;i t&acirc;m bị che lấp v&igrave; những ưu phiền: sinh l&atilde;o bệnh tử của đời người; c&acirc;y vươn l&ecirc;n trong nước, được v&iacute; cho qu&aacute; tr&igrave;nh tu tập, thanh tịnh h&oacute;a; hoa sen nở rộ tr&ecirc;n mặt nước thể hiện cho c&aacute;i t&acirc;m đ&atilde; gi&aacute;c ngộ, thức tỉnh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Trước khi trở th&agrave;nh Phật, người sống trong nhung lụa, c&oacute; thể n&oacute;i l&agrave; nhiễm bụi bẩn của cuộc đời cũng như mầm sen c&ograve;n ở trong b&ugrave;n đất tăm tối. Đến khi Ng&agrave;i xuất gia tu th&agrave;nh ch&iacute;nh quả nghĩa l&agrave; hoa sen đ&atilde; vươn m&igrave;nh nở rộ khỏi b&ugrave;n đất.&nbsp;Hoa sen c&ograve;n tượng trưng cho sự b&igrave;nh đẳng, qua&nbsp;&yacute; nghĩa hoa sen trong Phật gi&aacute;o&nbsp;cũng gửi gắm một th&ocirc;ng điệp: bất cứ ai cũng c&oacute; thể thức tỉnh, tu h&agrave;nh để tho&aacute;t khỏi ngũ dục của cuộc đời nếu c&oacute; &yacute; ch&iacute;, sự quyết t&acirc;m.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh 5.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Tượng Phật Th&iacute;ch Ca tại C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</span></em></p>\r\n\r\n<h2><strong><span style=\"font-size:16px\">Qu&aacute; tr&igrave;nh thi c&ocirc;ng</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\">Tượng được khởi c&ocirc;ng v&agrave;o th&aacute;ng 11/11/2021, đến th&aacute;ng 8/2022 ho&agrave;n th&agrave;nh, sau khoảng gần 300 ng&agrave;y cần mẫn thi c&ocirc;ng. Tượng được thực hiện bởi đi&ecirc;u khắc gia Hồ Văn Đen, nghệ nh&acirc;n đ&atilde; tạc l&ecirc;n những tượng Phật kỷ lục quốc gia như tượng Phật Di Lặc tr&ecirc;n n&uacute;i Cấm &ndash; Tịnh Bi&ecirc;n, tượng Phật B&agrave; Quan &Acirc;m &ndash; ch&ugrave;a Linh Ứng, B&atilde;i Bụt, Đ&agrave; Nẵng,&hellip; C&ocirc;ng tr&igrave;nh tượng Th&iacute;ch Ca cao 23m, tương đương nh&agrave; cao 9 tầng v&agrave; l&agrave; một trong những tượng phật lớn tại miền Bắc. Tượng được đi&ecirc;u khắc v&ocirc; c&ugrave;ng k&igrave; c&ocirc;ng với 4 lớp b&ecirc; t&ocirc;ng v&agrave; lưới đan xen lẫn nhau, đảm bảo sự vững chắc cho bức tượng trường tồn với thời gian.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">C&ugrave;ng với c&aacute;c c&ocirc;ng tr&igrave;nh kiến tr&uacute;c t&acirc;m linh kh&aacute;c như: Nh&agrave; tang lễ, Đ&agrave;i h&oacute;a th&acirc;n sử dụng c&ocirc;ng nghệ hiện đại Ch&acirc;u &Acirc;u, <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/den-trinh\">Đền tr&igrave;nh</a>, <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/tuong-quan-the-am-bo-tat\">tượng Phật Quan Thế &Acirc;m</a>, <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/tuong-phat-di-lac\">tượng Phật Di Lặc</a>, <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/chua-bao-an\">ch&ugrave;a B&aacute;o &Acirc;n</a>, trục Thần đạo với&nbsp;18 vị La H&aacute;n th&igrave; hệ sinh th&aacute;i đa dạng v&agrave; c&aacute;c tiện &iacute;ch đạt chuẩn 5 sao đ&atilde; tạo n&ecirc;n C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường, m&ocirc; h&igrave;nh c&ocirc;ng vi&ecirc;n nghĩa trang hiện đại xanh, sạch, đẹp v&agrave; b&igrave;nh y&ecirc;n cho t&acirc;m hồn giữa hai thế giới t&acirc;m linh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh 6.png\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Qu&aacute; tr&igrave;nh k&igrave; c&ocirc;ng x&acirc;y dựng Tượng Phật Th&iacute;ch Ca</span></em></p>\r\n\r\n<p><span style=\"font-size:16px\">C&ocirc;ng tr&igrave;nh Tượng Phật Th&iacute;ch Ca được kh&aacute;nh th&agrave;nh đ&atilde; trở th&agrave;nh một điểm nhấn nổi bật của C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường. &Aacute;nh mắt, khu&ocirc;n mặt từ bi của Ng&agrave;i từ tr&ecirc;n cao tr&ocirc;ng ra bốn phương, t&aacute;m hướng như thấu suốt những nỗi l&ograve;ng của ch&uacute;ng sinh. Đứng dưới ch&acirc;n tượng Phật, bỗng thấy l&ograve;ng m&igrave;nh thanh thản, b&igrave;nh y&ecirc;n, như được chở che, ph&ugrave; hộ, chắp tay th&agrave;nh t&acirc;m khấn nguyện: &ldquo;c&uacute;i xin cửa từ bi rộng mở đ&oacute;n cửu huyền thất tổ si&ecirc;u sinh, độ mu&ocirc;n lo&agrave;i ti&ecirc;u tan nghiệp chướng, bước l&ecirc;n bờ gi&aacute;c c&otilde;i T&acirc;y Phương&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Người xưa quan niệm rằng, nơi địa thế bằng phẳng, rộng r&atilde;i, c&oacute; d&ograve;ng nước chảy cuốn quanh, kề gần, miền đất đ&oacute; nơi sẽ t&iacute;ch tụ được nhiều sinh kh&iacute;, miền đất đ&oacute; c&oacute; long mạch, tức l&agrave; nơi c&aacute;t địa hay ph&uacute;c địa. Cũng theo quan niệm của truyền thống Phương Đ&ocirc;ng, con số 6 ch&iacute;nh l&agrave; con số biểu hiện cho ph&uacute;c lộc tr&agrave;n đầy. Thi&ecirc;n Đường &ndash; v&ugrave;ng đất với 6 ngọn đồi v&acirc;y quanh hồ nước tự nhi&ecirc;n trong xanh bốn m&ugrave;a tạo n&ecirc;n cảnh sắc tuyệt diệu với sự giao h&ograve;a trời &ndash; đất &ndash; kh&iacute; &ndash; nước. Đ&acirc;y ch&iacute;nh l&agrave; nơi đ&oacute;n nhận kh&iacute; thi&ecirc;ng trời đất để tạo h&agrave;o quang cho cuộc sống nơi trần thế v&agrave; cả khi đ&atilde; trở về với c&otilde;i vĩnh hằng, đồng thời cũng l&agrave; nơi t&ocirc;n vinh l&ograve;ng hiếu nghĩa của mỗi người, mỗi nh&agrave; với gia ti&ecirc;n, &ocirc;ng cha, người th&acirc;n của m&igrave;nh đ&atilde; về nơi ch&iacute;n suối. </span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh 7.png\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline</span>&nbsp;<strong>0934.524.445 </strong><span style=\"font-size:16px\">hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\">https://nghiatrangthienduong.com.vn/</a>.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m:&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/canh-quan/tuong-dia-tang-vuong-bo-tat\">Tượng Địa Tạng Vương Bồ T&aacute;t</a></span><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', 'true', 'News', 'tuong-phat-thich-ca', 'tâm linh 5.jpg', 'Tượng Phật Thích Ca', 'Tượng Phật Thích Ca tọa lạc tại vị trí đắc địa nhất trong khuôn viên của Công viên Nghĩa trang Thiên Đường, hai bên trục thần đạo là các pho tượng tôn giả.', '2023-12-20 04:01:37', '2023-12-20 04:10:49'),
(190, 2, 65, 'Tượng Địa Tạng Vương Bồ Tát', 'Tượng Địa Tạng Vương Bồ Tát được xây dựng tại trung tâm đồi Thiên Tâm trong khuôn viên Công viên Nghĩa trang Thiên Đường với chiều cao 19m, được thiết kế tinh xảo, đứng giữa trời xanh như đang phổ độ, bảo vệ các vong linh đang yên nghỉ tại đây. ', NULL, '<p><strong><span style=\"font-size:16px\">Tượng Địa Tạng Vương Bồ T&aacute;t được x&acirc;y dựng&nbsp;tại trung t&acirc;m&nbsp;đồi Thi&ecirc;n T&acirc;m&nbsp;trong khu&ocirc;n vi&ecirc;n <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</a> với chiều cao 19m, được thiết kế tinh xảo, đứng giữa trời xanh như đang phổ độ, bảo vệ c&aacute;c vong linh đang y&ecirc;n nghỉ tại đ&acirc;y.&nbsp;</span></strong></p>\r\n\r\n<p><strong><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh 8.jpg\" /></span></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Tượng Bồ T&aacute;t Địa Tạng Vương tại C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</span></em></p>\r\n\r\n<h2><span style=\"font-size:16px\">Bồ t&aacute;t Địa Tạng l&agrave; ai?</span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Bồ t&aacute;t Địa Tạng tục danh t&ecirc;n l&agrave; Kim Kiều Gi&aacute;c (Kim Kyo-gak), sinh v&agrave;o thế kỷ thứ VII, năm 696 TL, tại nước T&acirc;n La (Silla), hiện nay l&agrave; H&aacute;n Th&agrave;nh, thuộc Nam H&agrave;n. Bồ t&aacute;t Địa Tang l&agrave; một trong s&aacute;u vị Bồ t&aacute;t của Phật gi&aacute;o Đại thừa. Năm vị c&ograve;n lại l&agrave; Qu&aacute;n Thế &Acirc;m Bồ t&aacute;t, Đại Thế Ch&iacute; Bồ t&aacute;t, Văn Th&ugrave; Sư Lợi Bồ t&aacute;t, Phổ Hiền Bồ t&aacute;t v&agrave; Di Lặc Bồ t&aacute;t.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp;<img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh 9.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Tượng Địa Tạng Vương Bồ T&aacute;t trong &aacute;nh ho&agrave;ng h&ocirc;n&nbsp;</span></em></p>\r\n\r\n<h2><span style=\"font-size:16px\">&Yacute; nghĩa tượng Địa Tạng Vương Bồ T&aacute;t</span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Bồ t&aacute;t Địa Tạng l&agrave; một vị Bồ t&aacute;t đại từ đại bi thệ nguyện độ thế rất rộng lớn. Ng&agrave;i l&agrave; gi&aacute;o chủ của c&otilde;i U Minh. Địa l&agrave; đất. Tạng l&agrave; tr&ugrave;m chứa. Bồ t&aacute;t c&oacute; lập đại nguyện tế độ tất cả ch&uacute;ng sinh cũng như đất l&agrave; nơi nương tựa cho mu&ocirc;n sự mu&ocirc;n vật n&ecirc;n gọi l&agrave; Địa. Cũng c&oacute; người l&yacute; giải nghĩa của chữ &ldquo;Địa&rdquo; nương theo Kinh Địa Tạng c&oacute; viết: &ldquo;U Minh Gi&aacute;o Chủ Bổn T&ocirc;n Địa Tạng Bồ t&aacute;t Ma Ha T&aacute;t&rdquo;. V&igrave; c&aacute;ch sử dụng từ ngữ trong kinh điển hết sức s&aacute;t nghĩa v&agrave; r&otilde; r&agrave;ng n&ecirc;n &ldquo;Bổn&rdquo; l&agrave; Bổn t&acirc;m, &ldquo;T&ocirc;n&rdquo; l&agrave; t&ocirc;n qu&yacute;, &ldquo;Địa&rdquo; l&agrave; t&acirc;m địa, &ldquo;Tạng&rdquo; l&agrave; Như Lai tạng. Như vậy, chỉ c&oacute; Bổn t&acirc;m mới l&agrave; t&ocirc;n qu&yacute; nhất, đ&oacute; l&agrave; kho Như Lai tạng t&acirc;m địa. Chỉ c&oacute; Bổn t&acirc;m mới l&agrave;m chủ được c&otilde;i u minh, tức l&agrave; l&agrave;m chủ c&otilde;i địa ngục tham &ndash; s&acirc;n &ndash; si của ch&iacute;nh m&igrave;nh. Địa Tạng Bồ &ndash; t&aacute;t được biết đến bời lời nguyện cứu độ tất cả ch&uacute;ng sinh trong lục đạo lu&acirc;n hồi v&agrave;o thời kỳ sau khi Phật Th&iacute;ch-ca-M&acirc;u-ni nhập Niết-b&agrave;n cho đến khi Bồ-t&aacute;t Di Lặc hạ sanh v&agrave; nguyện kh&ocirc;ng chứng Phật quả nếu địa ngục chưa trống rỗng. Do đ&oacute;, Địa Tạng thường được xem như l&agrave; vị Bồ t&aacute;t của ch&uacute;ng sanh dưới địa ngục hay l&agrave; gi&aacute;o chủ của c&otilde;i U Minh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Bồ t&aacute;t Địa Tạng tu h&agrave;nh ở n&uacute;i Cửu Hoa Sơn 75 năm. Thọ đến 99 tuổi. Suốt thời gian tu khổ luyện ở đ&acirc;y, Ng&agrave;i kh&ocirc;ng hề trở về nước Đại H&agrave;n. Ng&agrave;i nhập Niết b&agrave;n v&agrave;o ng&agrave;y 30 th&aacute;ng 7 năm Đường Khai Nguy&ecirc;n thứ 26. Ba năm sau khi Ng&agrave;i vi&ecirc;n tịch, th&igrave; tọa quan của Ng&agrave;i tự động mở cửa. V&agrave; trong đ&oacute; thi thể v&agrave; dung mạo của Ng&agrave;i y hệt như người sống. Tay ch&acirc;n vẫn mềm dẻo như c&oacute; thể di chuyển được.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/truc-than-dao\">Trục Thần Đạo Thi&ecirc;n Đường</a></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline&nbsp;</span><strong>0934.524.445</strong><span style=\"font-size:16px\"> hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\">https://nghiatrangthienduong.com.vn/</a>.</span></p>\r\n\r\n<p>&nbsp;</p>', 'true', 'News', 'tuong-dia-tang-vuong-bo-tat', 'tâm linh 8.jpg', 'Tượng Địa Tạng Vương Bồ Tát', 'Tượng Địa Tạng Vương Bồ Tát được xây dựng tại trung tâm đồi Thiên Tâm trong khuôn viên Công viên Nghĩa trang Thiên Đường với chiều cao 19m, được thiết kế tinh xảo, đứng giữa trời xanh như đang phổ độ, bảo vệ các vong linh đang yên nghỉ tại đây. ', '2023-12-20 04:10:27', '2023-12-20 04:20:48'),
(191, 2, 65, 'Trục Thần đạo', 'Trục Thần đạo là con đường tâm linh dài hơn 1 km, hai bên trục thần đạo là 18 bức tượng La Hán trang nghiêm và tôn kính.', NULL, '<p><strong><span style=\"font-size:16px\">Trục Thần đạo l&agrave; con đường t&acirc;m linh d&agrave;i hơn 1 km, hai b&ecirc;n trục thần đạo l&agrave; 18 bức tượng La H&aacute;n trang nghi&ecirc;m v&agrave; t&ocirc;n k&iacute;nh. Trục Thần đạo l&agrave; trục phong thủy ch&iacute;nh của <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</a> nối liền từ <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/chua-bao-an\">Ch&ugrave;a B&aacute;o &Acirc;n</a>, đi qua 18 vị La h&aacute;n, đến cầu Tịnh An v&agrave; kết th&uacute;c tại đồi Thi&ecirc;n An, nơi đặt <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/tuong-phat-thich-ca\">tượng Phật Th&iacute;ch Ca M&acirc;u Ni.</a></span></strong></p>\r\n\r\n<p><span style=\"font-size:16px\">Trục Thần đạo l&agrave; điểm đến, trục h&agrave;nh hương cho c&aacute;c Phật tử, con ch&aacute;u khắp nơi h&agrave;ng năm về h&agrave;nh lễ cầu ph&uacute;c cho gia đ&igrave;nh cũng như người th&acirc;n đang nằm tại v&ugrave;ng đất linh thi&ecirc;n Thi&ecirc;n Đường. Kh&ocirc;ng những vậy, trục Thần đạo như dẫn dắt tất cả c&aacute;c linh hồn tại Thi&ecirc;n Đường đến bờ gi&aacute;c ngộ &ndash; giải tho&aacute;t, hai b&ecirc;n trục Thần đạo l&agrave; 18 vị T&ocirc;n giả uy nghi, mỗi người một vẻ th&aacute;nh thiện ng&agrave;y đ&ecirc;m tr&igrave; tụng &ndash; cứu độ vong linh tới miền cực lạc.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh 10.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Trục Thần Đạo trước ch&ugrave;a&nbsp;</span></em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh 11.jpg\" /><em>&nbsp;</em></span><em><span style=\"font-size:16px\">Trục Thần Đạo&nbsp;sau ch&ugrave;a</span></em></p>\r\n\r\n<p><span style=\"font-size:16px\">Dọc theo trục Thần đạo l&agrave; hai h&agrave;ng c&acirc;y l&ecirc;, lo&agrave;i c&acirc;y đặc trưng của miền đất Tuy&ecirc;n Quang. V&agrave;o m&ugrave;a xu&acirc;n khi c&acirc;y cối đ&acirc;m chồi nảy lộc, vườn l&ecirc; bung nở những đ&oacute;a hoa trắng tinh kh&ocirc;i như những dải m&acirc;y bồng bềnh dẫn lối đến chốn <a href=\"https://nghiatrangthienduong.com.vn/\">Thi&ecirc;n Đường.</a></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline&nbsp;</span><strong>0934.524.445</strong><span style=\"font-size:16px\"> hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\">https://nghiatrangthienduong.com.vn/</a>.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m:&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/canh-quan/cong-trinh-tuong-phat\">C&ocirc;ng tr&igrave;nh tượng Phật</a></span></p>', 'true', 'News', 'truc-than-dao', 'tâm linh 10.jpg', 'Trục Thần đạo', 'Trục Thần đạo là con đường tâm linh dài hơn 1 km, hai bên trục thần đạo là 18 bức tượng La Hán trang nghiêm và tôn kính.', '2023-12-20 04:20:24', '2023-12-20 04:39:11'),
(192, 2, 65, 'Công trình tượng Phật', 'Cách trung tâm thành phố Tuyên Quang 10km, Công viên Nghĩa trang Thiên Đường là địa điểm du lịch tâm linh trang nghiêm với kỳ quan Đại Tượng Phật độc đáo do đôi bàn tay vàng của điêu khắc gia Hồ Văn Đen tạo tác, gửi gắm nhiều thông điệp về \"vĩnh hằng\" tới mọi người.', NULL, '<p><span style=\"font-size:16px\"><strong>C&aacute;ch trung t&acirc;m th&agrave;nh phố Tuy&ecirc;n Quang 10km,&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>&nbsp;l&agrave; địa điểm du lịch t&acirc;m linh trang nghi&ecirc;m với kỳ quan Đại Tượng Phật độc đ&aacute;o do đ&ocirc;i b&agrave;n tay v&agrave;ng của đi&ecirc;u khắc gia Hồ Văn Đen tạo t&aacute;c, gửi gắm nhiều th&ocirc;ng điệp về &quot;vĩnh hằng&quot; tới mọi người</strong>.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Chẳng phải sang tận Ấn Độ, Th&aacute;i Lan, Myanmar, Nhật Bản hay Trung Quốc để ngắm nh&igrave;n c&aacute;c kỳ quan tượng Phật nổi tiếng như: Bức Tượng Sardar Vallabhbhai Patel ở Ấn Độ, Bức Tượng Trung Nguy&ecirc;n Đại Phật (Đại Phật M&ugrave;a Xu&acirc;n) ở H&agrave; Nam - Trung Quốc, Bức Tượng Phật Laykyun Sekkya ở Monywa Myanmar hay Bức tượng Đại Tượng Phật A Di Đ&agrave; Ushiku Daibutsu ở Ibaraki Nhật Bản. Chỉ cần di chuyển hơn 1h đồng hồ theo cao tốc H&agrave; Nội - L&agrave;o Cai đến với mảnh đất Mẫu &ndash; Tuy&ecirc;n Quang, Qu&yacute; kh&aacute;ch sẽ được ngắm c&aacute;c Đại t&ocirc;n tượng Phật độc đ&aacute;o tại C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường.</span></p>\r\n\r\n<h2><strong><span style=\"font-size:18px\">Tượng Phật Th&iacute;ch Ca M&acirc;u Ni</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh 12.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Tượng Phật Th&iacute;ch Ca tại C&ocirc;ng vi&ecirc;n Nghĩa trang&nbsp;Thi&ecirc;n Đường</span></em></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/canh-quan/tuong-phat-thich-ca\">Tượng Đức Phật Th&iacute;ch Ca </a>ngự tr&ecirc;n t&ograve;a sen, ở vị tr&iacute; trung t&acirc;m của đồi Thi&ecirc;n An. Tượng Phật được đ&uacute;c bằng đ&aacute; nguy&ecirc;n khối với độ cao 23m, đường k&iacute;nh t&ograve;a sen rộng 12m. Lưng của tượng Phật tựa v&agrave;o n&uacute;i, mắt nh&igrave;n hướng về s&ocirc;ng. Dưới ch&acirc;n tượng l&agrave; phần diện t&iacute;ch thiết kế cho c&aacute;c phần mộ. &nbsp;</span></p>\r\n\r\n<h2><strong><span style=\"font-size:18px\">Tượng Phật Bồ T&aacute;t Quan Thế &Acirc;m</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh 2.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Tượng Phật Bồ T&aacute;t Quan Thế &Acirc;m tại C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường</span></em></p>\r\n\r\n<p><span style=\"font-size:16px\">Ở giữa trung t&acirc;m đồi&nbsp;Thi&ecirc;n Thọ&nbsp;l&agrave; <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/tuong-phat-quan-the-am-bo-tat\">Tượng Phật Bồ T&aacute;t Quan Thế &Acirc;m</a>. Từ xa, qu&yacute; kh&aacute;ch c&oacute; thể nh&igrave;n thấy bức tượng m&agrave;u trắng xen lẫn vạt v&agrave;ng, đ&oacute; ch&iacute;nh l&agrave; tượng Quan Thế &Acirc;m, với vị thế trang nghi&ecirc;m, vững ch&atilde;i, nổi bật tr&ecirc;n nền xanh thẳm của đồng n&uacute;i Tuy&ecirc;n Quang. D&aacute;ng đứng của Tượng Phật cũng mang nhiều &yacute; nghĩa đặc biệt, đ&ocirc;i mắt nh&igrave;n thẳng ra xa tượng trưng cho l&ograve;ng từ bi, hỉ xả. Tay phải bắt ấn tam muội mang &yacute; nghĩa cứu độ ch&uacute;ng sanh, c&ograve;n tay tr&aacute;i cầm b&igrave;nh nước gi&uacute;p đem lại&nbsp;b&igrave;nh an.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Kh&ocirc;ng chỉ mang &yacute; nghĩa t&acirc;m linh, l&agrave; điểm đến h&agrave;nh hương của du kh&aacute;ch, tượng Phật Bồ T&aacute;t Quan Thế &Acirc;m c&ograve;n l&agrave; c&ocirc;ng tr&igrave;nh sở hữu con số ấn tượng. Tượng c&oacute; chiều cao 20m, đường k&iacute;nh chiều rộng của tượng l&agrave; 10m. Ch&iacute;nh v&igrave; sự to lớn của bức tượng n&agrave;y m&agrave; qu&yacute; kh&aacute;ch chỉ cần đứng c&aacute;ch 500m đ&atilde; c&oacute; thể nhận ra <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường.</a></span></p>\r\n\r\n<h2><strong><span style=\"font-size:18px\">Tượng Phật Địa Tạng Vương Bồ T&aacute;t</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/canh-quan/tuong-dia-tang-vuong-bo-tat\">Địa Tạng Vương Bồ T&aacute;t </a>l&agrave; bức tượng được đặt tại trung t&acirc;m đồi Thi&ecirc;n T&acirc;m. Địa Tạng Vương được xem l&agrave; bậc gi&aacute;o chủ của c&otilde;i U Minh, vị Bồ t&aacute;t của ch&uacute;ng sinh dưới địa Ngục. Địa Tạng Vương Bồ t&aacute;t thường được m&ocirc; tả với h&igrave;nh tượng l&agrave; một vị Bồ t&aacute;t từ bi, đầu đội m&atilde;o tỳ lư, c&oacute; vầng s&aacute;ng h&agrave;o quang tr&ecirc;n đầu, đứng tr&ecirc;n t&ograve;a sen hoặc ngồi tr&ecirc;n t&ograve;a sen do Đề Th&iacute;nh (một con ch&oacute;, linh th&uacute; của Địa Tạng Vương Bồ t&aacute;t) đỡ lấy.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh 13.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Tượng Phật Địa Tạng Vương Bồ T&aacute;t tại C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường</span></em></p>\r\n\r\n<h2><span style=\"font-size:18px\"><strong>Tượng Phật Đại Thế Ch&iacute; Bồ T&aacute;t&nbsp;</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Với chiều cao 19m,<a href=\"https://nghiatrangthienduong.com.vn/canh-quan/tuong-dai-the-chi-bo-tat\"> tượng Đại Thế Ch&iacute; Bồ T&aacute;t</a> nằm ở vị tr&iacute; trung t&acirc;m đồi&nbsp;Thi&ecirc;n Lộc&nbsp;của C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường. Tr&ecirc;n tay ng&agrave;i kh&ocirc;ng cầm ph&aacute;p bảo hay t&ugrave;y kh&iacute; b&ecirc;n ngo&agrave;i m&agrave; l&agrave; một b&ocirc;ng hoa sen xanh. Hoa sen xanh tượng trưng cho sự thanh tịnh, tức l&agrave; đoạn đức. Ng&agrave;i biểu thị cho tinh thần Đại Tr&iacute;, c&ograve;n Qu&aacute;n Thế &Acirc;m Bồ T&aacute;t biểu thị cho tinh thần Đại Bi. Con người hướng theo T&acirc;m Phật lu&ocirc;n cần c&oacute; t&acirc;m từ bi v&agrave; tr&iacute; tuệ để th&ocirc;ng tuệ vạn vật.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh 14.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Tượng Đại Thế Ch&iacute; Bồ T&aacute;t tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</span></em></p>\r\n\r\n<p><span style=\"font-size:16px\">Muốn cứu vớt ch&uacute;ng sanh về c&otilde;i Tịnh độ, trước phải dạy họ dứt sạch phiền n&atilde;o &ocirc; uế. V&igrave; thế,&nbsp;&yacute; nghĩa tượng Đại Thế Ch&iacute; Bồ T&aacute;t tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường với vầng h&agrave;o&nbsp;quang như&nbsp;&aacute;nh s&aacute;ng tr&iacute; tuệ chiếu soi cho ch&uacute;ng sinh thấy r&otilde; những &ocirc; nhiễm của m&igrave;nh, đồng thời cũng gi&uacute;p cho họ c&oacute; sức mạnh đoạn trừ những nhiễm &ocirc; n&agrave;y, để c&oacute; thể đưa họ về c&otilde;i Tịnh độ.</span></p>\r\n\r\n<h2><strong><span style=\"font-size:18px\">Tượng Phật Di Lặc</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/canh-quan/tuong-phat-di-lac\">Tượng Phật Di Lặc</a> tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường được x&acirc;y dựng bằng b&ecirc; t&ocirc;ng cốt th&eacute;p cao 13m, nằm ở vị tr&iacute; trung t&acirc;m đồi Thi&ecirc;n Ph&uacute;c. Tượng được tạc theo h&igrave;nh tướng Bố Đại h&ograve;a thượng, mang th&acirc;n h&igrave;nh mập mạp, hiền h&ograve;a, tự tại, miệng tươi cười&hellip; Đức Phật Di Lặc l&agrave; tượng trưng tuyệt đối của niềm vui v&agrave; hạnh ph&uacute;c. Đ&oacute; l&agrave; l&yacute; do d&acirc;n gian gọi Ng&agrave;i l&agrave; &ldquo;Phật Cười&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh 15.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Tượng Phật Di Lặc tại C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</span></em></p>\r\n\r\n<p><span style=\"font-size:16px\">Theo truyền thuyết, niềm vui lớn nhất của Phật Di Lặc l&agrave; biến những buồn phiền, giận dữ, &aacute;p lực hay căng thẳng của con người th&agrave;nh hạnh ph&uacute;c. Người ta tin rằng nụ cười nội t&acirc;m của Phật Di Lặc mạnh tới mức n&oacute; lu&ocirc;n tỏa s&aacute;ng tr&ecirc;n khu&ocirc;n mặt hiền từ v&agrave; Ng&agrave;i tới đ&acirc;u th&igrave; ở đ&oacute; sẽ c&oacute; hạnh ph&uacute;c.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ngo&agrave;i ra, C&ocirc;ng vi&ecirc;n&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">Nghĩa trang&nbsp;Thi&ecirc;n Đường</a> đang ho&agrave;n thiện Đại Tượng&nbsp;Phật A Di Đ&agrave; tại trung t&acirc;m đồi Thi&ecirc;n Thọ.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Đặc biệt, đi&ecirc;u khắc gia Hồ Văn Đen ch&iacute;nh l&agrave; nghệ nh&acirc;n đ&atilde; tạc n&ecirc;n những tượng Phật kỷ lục quốc gia như tượng Phật Di Lặc tr&ecirc;n n&uacute;i Cấm &ndash; Tịnh Bi&ecirc;n, tượng Phật B&agrave; Quan &Acirc;m &ndash; ch&ugrave;a Linh Ứng, B&atilde;i Bụt, Đ&agrave; Nẵng,..</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Phần lớn c&aacute;c tượng Phật do nghệ nh&acirc;n Hồ Văn Đen tạc đều c&oacute; &ldquo;Nụ cười mỉm từ bi, b&aacute;c &aacute;i&rdquo; của chư Phật khi nh&igrave;n xuống trần gian &ndash; nơi ch&uacute;ng sinh đang c&ograve;n đắm ch&igrave;m trong bể lu&acirc;n hồi. C&ograve;n&hellip;, c&oacute; hay kh&ocirc;ng những c&acirc;u chuyện b&iacute; ẩn về t&acirc;m linh trong từng t&aacute;c phẩm th&igrave; c&oacute; lẽ, chỉ c&oacute; t&aacute;c giả mới giải th&iacute;ch được?.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/kien-truc-canh-quan\">Cảnh quan tại Thi&ecirc;n Đường</a></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xin mượn c&acirc;u chuyện về Phật m&agrave; đi&ecirc;u khắc gia Thụy Lam, thầy của nghệ nh&acirc;n Hồ Văn Đen b&agrave;y tỏ dưới đ&acirc;y để kết th&uacute;c b&agrave;i viết n&agrave;y:</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ldquo;Tinh hoa hội họa nằm ở trong kinh Phật. T&ocirc;i c&ograve;n nhớ một c&acirc;u chuyện về Phật: - C&oacute; lần Đức Phật hỏi một vị Tỳ kheo rằng: Thế n&agrave;o l&agrave; một bức tranh tuyệt t&aacute;c? Vị Tỳ kheo trả lời: &ldquo;Một bức tranh tuyệt t&aacute;c l&agrave; một bức tranh được vẽ bằng t&acirc;m thức.&rdquo; - Đức Phật nh&igrave;n vị Tỳ kheo bằng &aacute;nh mắt từ bi rồi n&oacute;i th&ecirc;m: Nhưng c&aacute;i t&acirc;m c&ograve;n tuyệt t&aacute;c hơn bức tranh đ&oacute; nữa!&rdquo;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline </span><strong>0934.524.445</strong><span style=\"font-size:16px\"> hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\">https://nghiatrangthienduong.com.vn/</a>.</span></p>', 'true', 'News', 'cong-trinh-tuong-phat', '61_thiên đường.jpg', 'Công trình tượng Phật', 'Công viên Nghĩa trang Thiên Đường là địa điểm du lịch tâm linh trang nghiêm với kỳ quan Đại Tượng Phật độc đáo do đôi bàn tay vàng của điêu khắc gia Hồ Văn Đen tạo tác, gửi gắm nhiều thông điệp về \"vĩnh hằng\" tới mọi người.', '2023-12-20 04:35:54', '2023-12-20 04:35:54');
INSERT INTO `posts` (`id`, `user_id`, `category_id`, `name`, `detail`, `info`, `content`, `status`, `sort_by`, `slug`, `img`, `title`, `description`, `created_at`, `updated_at`) VALUES
(193, 2, 68, 'Hỏa táng là gì ? Quy trình hỏa táng tại Việt Nam', 'Hỏa táng là quá trình thiêu thi thể người chết thành tro bằng lửa trong điều kiện nhiệt độ cực cao. Lễ hỏa táng gồm có 2 bước cơ bản: trước và sau quy trình hỏa táng.', NULL, '<h2><span style=\"font-size:16px\"><strong>Hỏa t&aacute;ng l&agrave; g&igrave;?</strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Hỏa t&aacute;ng&nbsp;l&agrave; qu&aacute; tr&igrave;nh&nbsp;thi&ecirc;u thi thể người chết&nbsp;th&agrave;nh tro bằng lửa trong điều kiện nhiệt độ cực cao. Lễ hỏa t&aacute;ng gồm c&oacute; 2 bước cơ bản: trước v&agrave; sau&nbsp;quy tr&igrave;nh hỏa t&aacute;ng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Sau khi thi thể đ&atilde; bị đốt ch&aacute;y, c&aacute;c mảnh xương sẽ được chuyển qua một bộ xử l&yacute; v&ocirc; c&ugrave;ng vi tế để &ldquo;biến&rdquo; ch&uacute;ng th&agrave;nh tro.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Những t&agrave;n tro n&agrave;y ch&iacute;nh l&agrave; h&agrave;i cốt của người mất m&agrave; ta c&oacute; thể nhận được qua buổi lễ hỏa t&aacute;ng n&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/quy-trinh-hoa-tang-3.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><em>H&igrave;nh ảnh hỏa t&aacute;ng quan t&agrave;i v&agrave; thi thể người chết</em></span></p>\r\n\r\n<h2><strong><span style=\"font-size:16px\">V&igrave; sao n&ecirc;n hỏa t&aacute;ng?</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/hoa-tang-la-gi-quy-trinh-hoa-tang-tai-viet-nam\">Quy tr&igrave;nh hỏa t&aacute;ng&nbsp;</a>n&agrave;y đ&atilde; dần thay thế h&igrave;nh thức ch&ocirc;n cất thi thể truyền thống ở nước ta. Điều n&agrave;y kh&ocirc;ng kh&oacute; để l&iacute; giải khi n&oacute; ph&ugrave; hợp với yếu tố thời đại.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Hiện nay, c&aacute;c khu nghĩa trang lu&ocirc;n trong t&igrave;nh trạng qu&aacute; tải. D&acirc;n số kh&ocirc;ng ngừng tăng theo năm th&aacute;ng nhưng đất th&igrave; kh&ocirc;ng &ldquo;nở&rdquo; ra.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Điều n&agrave;y đ&atilde; g&acirc;y sức &eacute;p đến c&aacute;ch thức ch&ocirc;n cất truyền thống. Với h&igrave;nh thức hỏa t&aacute;ng, ta ho&agrave;n to&agrave;n c&oacute; thể l&agrave;m giảm nhẹ đi &aacute;p lực trong mai t&aacute;ng người đ&atilde; khuất.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Sau&nbsp;qu&aacute; tr&igrave;nh hỏa thi&ecirc;u người chết, th&acirc;n x&aacute;c của người đ&atilde; mất chỉ c&ograve;n lại &ldquo;nắm tro cốt&rdquo;. V&agrave; ta c&oacute; thể đựng trong c&aacute;c lọ để lưu lại. Những chiếc lọ n&agrave;y thường c&oacute; k&iacute;ch thước khoảng bằng ấm tr&agrave;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">L&uacute;c n&agrave;y, t&ugrave;y thuộc v&agrave;o mong muốn của gia đ&igrave;nh m&agrave; ta c&oacute; thể đem về nh&agrave;, ch&ugrave;a hay nh&agrave; thờ, nghĩa trang để thờ c&uacute;ng. Như vậy, h&igrave;nh thức hỏa t&aacute;ng c&oacute; thể giải quyết &ldquo;b&agrave;i to&aacute;n kh&ocirc;ng gian&rdquo; m&agrave; ch&uacute;ng ta đang phải đối mặt.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Thứ hai, ai cũng phải c&ocirc;ng nhận một điều rằng: hỏa t&aacute;ng sẽ tiết kiệm chi ph&iacute; cho người ở lại hơn rất nhiều so với c&aacute;c h&igrave;nh thức an t&aacute;ng kh&aacute;c.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Chi ph&iacute; thi&ecirc;u cốt v&agrave; lưu trữ cốt chỉ chiếm ⅕ &ndash; 1/10 gi&aacute; trị mua đất mộ v&agrave; x&acirc;y dựng mộ huyệt. B&agrave;i to&aacute;n kinh tế cũng được ho&agrave;n th&agrave;nh tốt với h&igrave;nh thức hỏa t&aacute;ng n&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Thứ ba, khi thi thể đ&atilde; qua quy tr&igrave;nh hỏa t&aacute;ng ở nhiệt độ rất cao trong l&ograve; thi&ecirc;u th&igrave; những vi khuẩn, nguy cơ, mầm bệnh cũng bị ti&ecirc;u diệt triệt để theo.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Phần tro cốt của thi thể được bảo quản tốt trong hũ s&agrave;nh hoặc đ&aacute; rất vệ sinh. V&igrave; thế, hỏa t&aacute;ng l&agrave; một c&aacute;ch thức an to&agrave;n, kh&ocirc;ng g&acirc;y &ocirc; nhiễm m&ocirc;i trường, rất đ&aacute;ng được lưu t&acirc;m v&agrave; nh&acirc;n rộng hơn nữa.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Thứ tư, cải t&aacute;ng, bốc mộ kh&ocirc;ng bao giờ xảy ra với h&igrave;nh thức&nbsp;hỏa t&aacute;ng&nbsp;n&agrave;y. Đ&ocirc;i khi, ch&uacute;ng ta buộc phải di dời phần mộ của tổ ti&ecirc;n m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Việc n&agrave;y thật sự rất kh&oacute; khăn, mất nhiều thời gian, c&ocirc;ng sức v&agrave; kh&aacute; tốn k&eacute;m. Nhưng với hỏa t&aacute;ng th&igrave; việc di dời hũ tro cốt của người đ&atilde; khuất cũng dễ d&agrave;ng hơn.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Thứ năm, việc chăm s&oacute;c, bảo dưỡng những hũ tro cốt n&agrave;y cũng đơn giản v&agrave; thuận tiện hơn. Nếu bạn đặt ở nh&agrave;, bạn cũng dễ d&agrave;ng lau ch&ugrave;i hằng ng&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Hoặc bạn gửi ở nơi kh&aacute;c như: ch&ugrave;a, nh&agrave; thờ, khu lưu cốt, th&igrave; cũng h&atilde;y y&ecirc;n t&acirc;m rằng: hũ cốt của người đ&atilde; mất lu&ocirc;n được bảo quản tốt.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Mỗi hũ cốt chỉ chiếm một phần nhỏ diện t&iacute;ch n&ecirc;n việc chăm s&oacute;c cũng dễ d&agrave;ng c&ugrave;ng chế độ canh g&aacute;c, bảo vệ nghi&ecirc;m ngặt sẽ đảm bảo cho phần di cốt kh&ocirc;ng bị ph&aacute; hoại.</span><span style=\"font-size:16px\"> </span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/le-tang-truyen-thong-tai-viet-nam-va-cac-van-de-can-luu-y\">Lễ tang truyền thống tại Việt Nam</a></span></p>\r\n\r\n<h2><strong><span style=\"font-size:16px\">Thắc mắc về quy tr&igrave;nh hỏa t&aacute;ng</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\">Qu&aacute; tr&igrave;nh hỏa t&aacute;ng&nbsp;l&agrave; h&igrave;nh thức an t&aacute;ng mới v&agrave; phổ biến gần đ&acirc;y n&ecirc;n hẳn sẽ c&oacute; nhiều điều m&agrave; kh&ocirc;ng phải ai cũng r&otilde;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Liệu hỏa t&aacute;ng c&oacute; cần đến quan t&agrave;i như c&aacute;ch thức ch&ocirc;n cất truyền thống kh&ocirc;ng? C&acirc;u trả lời l&agrave; kh&ocirc;ng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Tuy nhi&ecirc;n, để đảm t&iacute;nh t&ocirc;n nghi&ecirc;m đối với người đ&atilde; mất c&ugrave;ng l&yacute; do vệ sinh, th&igrave; c&aacute;c nh&agrave; hỏa t&aacute;ng sẽ trang bị th&ecirc;m một chiếc h&ograve;m (vật liệu dễ ch&aacute;y).</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Thi thể sẽ được đặt v&agrave;o trong chiếc h&ograve;m đ&atilde; được b&iacute;t k&iacute;n n&agrave;y v&agrave; đưa v&agrave;o l&ograve; thi&ecirc;u.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Như vậy,&nbsp;quan t&agrave;i sẽ bị đốt trong qu&aacute; tr&igrave;nh hỏa t&aacute;ng, c&ugrave;ng với thi thể của người đ&atilde; khuất.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Khi hỏa t&aacute;ng, kh&ocirc;ng y&ecirc;u cầu quan t&agrave;i nhưng vẫn c&oacute; h&ograve;m đặt thi thể&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/hoa-tang-nhu-the-nao.jpg\" /></span></p>\r\n\r\n<h2><strong><span style=\"font-size:16px\">C&aacute;c đ&agrave;i hỏa t&aacute;ng tại H&agrave; Nội</span></strong></h2>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px\">Đ&agrave;i h&oacute;a th&acirc;n Ho&agrave;n Vũ </span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px\">Địa chỉ: nghĩa trang Văn Điển- H&agrave; Nội</span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:16px\">Đ&agrave;i h&oacute;a th&acirc;n Ho&agrave;n Vũ c&ocirc;ng vi&ecirc;n vĩnh hằng Ba V&igrave;</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px\">Địa chỉ: X&atilde; Vật Lại- huyện Ba V&igrave;- H&agrave; Nội</span></p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:16px\">L&ograve; hỏa thi&ecirc;u Ph&ugrave;ng Hưng </span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px\">Địa chỉ: nh&agrave; tang lễ th&agrave;nh phố (125 Ph&ugrave;ng Hưng, phường cửa đ&ocirc;ng, quận Ho&agrave;n Kiếm, H&agrave; Nội.)</span></p>\r\n\r\n<h2><strong><span style=\"font-size:16px\">Quy tr&igrave;nh hỏa t&aacute;ng diễn ra như thế n&agrave;o?</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\">H&ograve;m hỏa t&aacute;ng được đưa v&agrave;o buồng hỏa t&aacute;ng với điều kiện nhiệt độ l&ecirc;n đến 1200- 1400 độ C. Sau 2h- 2h30m, h&ograve;m hỏa t&aacute;ng được chuyển qua giai đoạn đốt ch&aacute;y bằng nhiệt hoặc bốc hơi.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Kết th&uacute;c qu&aacute; tr&igrave;nh trong l&ograve; thi&ecirc;u sẽ c&ograve;n lại những mảnh xương được gọi l&agrave; t&agrave;n dư hỏa t&aacute;ng. C&aacute;c t&agrave;n dư hỏa t&aacute;ng sau khi đ&atilde; được gom lại cẩn thận sẽ đem qua một bộ xử l&yacute;, sau bước n&agrave;y, ta thu được những hạt mịn (tro).</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Như vậy,&nbsp;sau khi hỏa t&aacute;ng,&nbsp;xương của người chết&nbsp;sẽ kh&ocirc;ng c&ograve;n nữa, m&agrave; chỉ c&ograve;n s&oacute;t lại&nbsp;tro xương.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">V&agrave; cuối c&ugrave;ng l&agrave; cho&nbsp;tro cốt sau khi hỏa t&aacute;ng&nbsp;v&agrave;o hũ, b&igrave;nh đựng m&agrave; gia đ&igrave;nh đ&atilde; lựa chọn trước. To&agrave;n bộ qu&aacute; tr&igrave;nh n&agrave;y diễn ra trong khoảng 3- 4 tiếng đồng hồ.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Để đảm bảo danh t&iacute;nh thi thể người mất tr&ugrave;ng khớp với tro cốt, th&igrave; đ&atilde; c&oacute; một hệ thống d&aacute;n nh&atilde;n kiểm so&aacute;t nghi&ecirc;m ngặt trong suốt qu&aacute; tr&igrave;nh hỏa t&aacute;ng.</span></p>\r\n\r\n<h2><strong><span style=\"font-size:16px\">Sau bao l&acirc;u kể từ khi mất l&agrave; c&oacute; thể hỏa t&aacute;ng?</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\">Hỏa t&aacute;ng l&agrave; quy tr&igrave;nh kh&ocirc;ng thể đảo ngược, cũng như mọi dấu vết về nguy&ecirc;n nh&acirc;n c&aacute;i chết đều kh&ocirc;ng thể x&aacute;c định được.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">V&igrave; thế, ở nhiều nơi, bạn cần phải c&oacute; sư chấp thuận từ gi&aacute;m định vi&ecirc;n hoặc điều tra vi&ecirc;n y tế th&igrave; mới c&oacute; thể tiến h&agrave;nh hỏa t&aacute;ng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ở một số nơi kh&aacute;c, c&ograve;n c&oacute; sự gia hạn mức thời gian, những giấy ph&eacute;p n&agrave;y phải được c&ocirc;ng nhận trước 24 giờ khi hỏa t&aacute;ng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/le-an-tang-tai-cong-vien-nghia-trang-thien-duong\">Lễ an t&aacute;ng tại c&ocirc;ng vi&ecirc;n nghĩa trang Thi&ecirc;n Đường</a></span></p>', 'true', 'News', 'hoa-tang-la-gi-quy-trinh-hoa-tang-tai-viet-nam', 'hoa-tang-nhu-the-nao.jpg', 'Hỏa táng là gì ? Quy trình hỏa táng tại Việt Nam', 'Hỏa táng là quá trình thiêu thi thể người chết thành tro bằng lửa trong điều kiện nhiệt độ cực cao. Lễ hỏa táng gồm có 2 bước cơ bản: trước và sau quy trình hỏa táng.', '2023-12-22 12:03:32', '2023-12-22 12:14:16'),
(194, 2, 68, 'Lễ an táng tại Công viên Nghĩa trang Thiên Đường', 'Nhằm phục vụ nhu cầu của KH, công viên tâm linh Thiên Đường đưa ra thông tin cho KH biết về quy trình thực hiện tang lễ tại Thiên Đường.', NULL, '<h2><span style=\"font-size:16px\"><strong>I-&nbsp;QUY TR&Igrave;NH NHẬN TH&Ocirc;NG TIN:</strong> </span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Gia đ&igrave;nh của KH c&oacute; người mất hoặc c&oacute; kế hoạch di chuyển, quy tập c&aacute;c ng&ocirc;i mộ về <a href=\"https://nghiatrangthienduong.com.vn/\">Nghĩa Trang Thi&ecirc;n Đường:</a></span></p>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp;+ Li&ecirc;n hệ với bộ phận CSKH của c&ocirc;ng ty: 091.935.1155 hoặc li&ecirc;n hệ với nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng. </span></p>\r\n\r\n<p><span style=\"font-size:16px\">+ Cung cấp th&ocirc;ng tin: Ng&agrave;y, giờ an t&aacute;ng v&agrave; c&aacute;c y&ecirc;u cầu cần thiết khi thực hiện tang lễ. </span></p>\r\n\r\n<p><span style=\"font-size:16px\">&bull; Ng&agrave;y an t&aacute;ng </span></p>\r\n\r\n<p><span style=\"font-size:16px\">&bull; Thời gian gia đ&igrave;nh dự kiến sẽ c&oacute; mặt </span></p>\r\n\r\n<p><span style=\"font-size:16px\">&bull; Giờ hạ huyệt </span></p>\r\n\r\n<p><span style=\"font-size:16px\">&bull; Vị tr&iacute; hạ huyệt</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp;&bull; C&aacute;c dịch vụ gia đ&igrave;nh sử dụng: </span></p>\r\n\r\n<p><span style=\"font-size:16px\">&bull; Đất, c&aacute;t v&agrave;ng, c&aacute;t đen lấp huyệt: Gia đ&igrave;nh tự chọn hoặc theo thầy chỉ định. </span></p>\r\n\r\n<p><span style=\"font-size:16px\">&bull; Cỏ Mỹ phủ mộ (nếu chưa c&oacute; mộ đ&aacute;)</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp;&bull; M&acirc;m cơm v&agrave; thời gian d&ugrave;ng cơm. Sau khi chốt xong dự to&aacute;n, KH thanh to&aacute;n 100% gi&aacute; trị dự to&aacute;n trước khi thực hiện</span></p>\r\n\r\n<h2><strong><span style=\"font-size:16px\">II-LỄ AN T&Aacute;NG TẠI NGHĨA TRANG THI&Ecirc;N ĐƯỜNG: </span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\">Tổng thời gian thực hiện: Khoảng 2.5h - 3h đồng hồ. </span></p>\r\n\r\n<p><span style=\"font-size:16px\">T&ugrave;y v&agrave;o số lượng kh&aacute;ch tham dự h&ocirc;m đ&oacute; của gia đ&igrave;nh chọn c&aacute;c dịch vụ ph&ugrave; hợp cho buổi lễ (c&oacute; xe dắt đo&agrave;n từ cổng Thi&ecirc;n Đường v&agrave;o khu&ocirc;n vi&ecirc;n, xe điện di chuyển để đi lễ c&aacute;c điểm t&acirc;m linh, c&oacute; người điều phối Tang lễ, đ&oacute;n tiếp kh&aacute;ch gi&uacute;p gia đ&igrave;nh,. )</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp;&bull; Phần lễ: cần c&oacute; tổng cộng 4 lễ (thời gian l&agrave;m lễ: khoảng 30-45p 2 lễ ngọt (tại: Địa tạng vương Bồ T&aacute;t tại ch&ugrave;a B&aacute;o &Acirc;n v&agrave; ban ch&iacute;nh tại ch&ugrave;a) </span></p>\r\n\r\n<p><span style=\"font-size:16px\">&bull; Bao gồm tối thiểu: 1 m&acirc;m ngũ quả, 1 chai nước, 3 cốc nến, 1 b&oacute; hương 2 lễ mặn (tại Đền Tr&igrave;nh v&agrave; khu&ocirc;n vi&ecirc;n hạ huyệt) Bao gồm tối thiểu: G&agrave; trống hoa hoặc khoanh thịt luộc, x&ocirc;i nếp nương, 3 cốc nến, 1 b&oacute; hương, 1 lọ hoa, 1 lễ tiền v&agrave;ng, 1 g&oacute;i ch&egrave; kh&ocirc;, 1 bao thuốc, 1 chai rượu lễ, trầu cau, 1 đĩa gạo, 1 đĩa muối. (Kh&aacute;ch h&agrave;ng c&oacute; thể tự mang ở nh&agrave; hoặc đặt trước tại <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường</a>)</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m:<a href=\"https://nghiatrangthienduong.com.vn/san-pham\"> c&aacute;c sản phẩm tại Thi&ecirc;n Đường</a></span></p>\r\n\r\n<h2><strong><span style=\"font-size:16px\">&nbsp;QUY TR&Igrave;NH TIẾN H&Agrave;NH TANG LỄ </span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\">1.Đ&oacute;n kh&aacute;ch: </span></p>\r\n\r\n<p><span style=\"font-size:16px\">&bull; Xe điện đưa KH đi l&agrave;m lễ</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp;&bull; Lễ t&acirc;n đ&oacute;n kh&aacute;ch tại khu&ocirc;n vi&ecirc;n </span></p>\r\n\r\n<p><span style=\"font-size:16px\">&bull; Đội nghi lễ bưng, b&ecirc; &aacute;o quan tiểu qu&aacute;ch từ xe v&agrave;o huyệt mộ </span></p>\r\n\r\n<p><span style=\"font-size:16px\">2.Bắt đầu hạ huyệt:</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp;&bull; Đến giờ theo y&ecirc;u cầu của gia đ&igrave;nh, đội nghi lễ hạ tiểu qu&aacute;ch xuống bể kim tĩnh. </span></p>\r\n\r\n<p><span style=\"font-size:16px\">&bull; Tiến h&agrave;nh lấp đất/c&aacute;t đen/c&aacute;t v&agrave;ng hoặc đ&aacute; ngũ sắc (gia đ&igrave;nh hỏi thầy chọn 1 trong 4 loại hoặc theo tỷ lệ) </span></p>\r\n\r\n<p><span style=\"font-size:16px\">&bull; Hạ huyệt</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp;&bull; Đại diện gia đ&igrave;nh n&oacute;i lời tạm biệt với người đ&atilde; khuất v&agrave; cảm ơn với người th&acirc;n, kh&aacute;ch mời tham gia tang lễ. </span></p>\r\n\r\n<p><span style=\"font-size:16px\">&bull; Trong qu&aacute; tr&igrave;nh diễn ra tang lễ cty c&oacute; dịch vụ xe điện miễn ph&iacute; để phục vụ gia đ&igrave;nh c&ugrave;ng quan kh&aacute;ch nếu cần di chuyển trong dự &aacute;n.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp;3.Sau khi hạ huyệt xong:</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp;&bull; Gia đ&igrave;nh c&ugrave;ng quan kh&aacute;ch trở về khu vực dịch vụ để nghỉ ngơi, d&ugrave;ng bữa cơm (nếu c&oacute;) v&agrave; gia đ&igrave;nh ho&agrave;n tất thủ tục hồ sơ </span></p>\r\n\r\n<p><span style=\"font-size:16px\">&bull; (Nộp lại giấy tr&iacute;ch lục khai tử bản sao hoặc giấy chuyển mộ)</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp;&bull; Thanh to&aacute;n ph&iacute; dịch vụ ph&aacute;t sinh (nếu c&oacute;) </span></p>\r\n\r\n<p><span style=\"font-size:16px\">&bull; Với trường hợp Kh&aacute;ch h&agrave;ng lắp đặt mộ đ&aacute; ho&agrave;n thiện, Thầy c&uacute;ng sẽ l&agrave;m lễ bồi ho&agrave;n long mạch v&agrave; tạ mộ.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/hoa-tang-la-gi-quy-trinh-hoa-tang-tai-viet-nam\">Quy tr&igrave;nh hỏa t&aacute;ng&nbsp;</a></span></p>\r\n\r\n<p><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/le-tang-truyen-thong-tai-viet-nam-va-cac-van-de-can-luu-y\"><span style=\"font-size:16px\">Lễ tang truyền thống tại Việt Nam&nbsp;</span></a></p>', 'true', 'News', 'le-an-tang-tai-cong-vien-nghia-trang-thien-duong', 'Pic12.jpg', 'Lễ an táng tại Công viên Nghĩa trang Thiên Đường', 'Nhằm phục vụ nhu cầu của KH, công viên tâm linh Thiên Đường đưa ra thông tin cho KH biết về quy trình thực hiện tang lễ tại Thiên Đường.', '2023-12-22 12:13:37', '2023-12-22 12:13:37'),
(195, 2, 68, 'Sự tích ít người biết về Đền Cô Bé Minh Lương', '​​​​​​​Cô Bé Minh Lương là một trong những tiên cô bản đền nổi tiếng linh thiêng, nhân dân xa gần khi về tới đất Tuyên Quang thường ghé đền cô bé Minh Lương để thắp nhang và cầu lộc tài cầu bình an.', NULL, '<p><strong><span style=\"font-size:16px\">C&ocirc; B&eacute; Minh Lương l&agrave; một trong những ti&ecirc;n c&ocirc; bản đền nổi tiếng linh thi&ecirc;ng, nh&acirc;n d&acirc;n xa gần khi về tới đất Tuyên Quang thường ghé đền cô bé Minh Lương để thắp nhang và cầu lộc tài cầu bình an.</span></strong></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\">&ldquo;Ti&ecirc;n c&ocirc; b&eacute; Minh Lương gi&aacute;ng thế</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\">Lời c&ocirc; như nước bể s&aacute;ng soi</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\">D&ugrave; cho vận đổi sao dời</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\">Lời c&ocirc; vẫn tỏ mu&ocirc;n đời chẳng phai&rdquo;</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/co-be-minh-luong.png\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong><span style=\"font-size:16px\">Sự t&iacute;ch C&ocirc; B&eacute; Minh Lương</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/su-tich-it-nguoi-biet-ve-den-co-be-minh-luong\">Sự t&iacute;ch về C&ocirc; B&eacute; Minh Lương</a> kể rằng: V&agrave;o thời nh&agrave; Trần (thế kỷ XV), ở tổng Minh Lương, thuộc x&atilde; Lang Qu&aacute;n, huyện Y&ecirc;n Sơn, tỉnh Tuy&ecirc;n Quang ng&agrave;y nay c&oacute; hai vợ chồng, &ocirc;ng chồng l&agrave; người Dao, b&agrave; vợ l&agrave; người Mường tuổi đ&atilde; cao m&agrave; chưa c&oacute; con.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ng&agrave;y ng&agrave;y &ocirc;ng b&agrave; ra ng&ograve;i Lịch x&uacute;c t&ocirc;m t&eacute;p sống lần hồi. Một h&ocirc;m, &ocirc;ng ở nh&agrave;, b&agrave; đi x&uacute;c t&eacute;p như mọi ng&agrave;y, nhưng x&uacute;c m&atilde;i kh&ocirc;ng được con g&igrave; m&agrave; chỉ được hai quả trứng lạ. Bực m&igrave;nh, b&agrave; xuống hạ nguồn rồi l&ecirc;n tận thượng nguồn ng&ograve;i Lịch x&uacute;c vẫn chỉ được hai quả trứng ấy. B&agrave; đ&agrave;nh mang về thả v&agrave;o chum nước dưới cầu thang. &Iacute;t l&acirc;u sau, b&agrave; mang thai v&agrave; sinh ra một c&ocirc; b&eacute; bụ bẫm.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">C&ugrave;ng l&uacute;c đ&oacute;, hai quả trứng thả trong chum nước dưới cầu thang nở ra hai con rắn. Hai con rắn v&agrave; c&ocirc; b&eacute; Minh Lương c&ugrave;ng lớn l&ecirc;n, quấn qu&yacute;t l&agrave;m bạn với nhau.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp;<img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/co-be-minh-luong-6.png\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Một buổi chiều, &ocirc;ng b&agrave; đi l&agrave;m về v&agrave; nh&igrave;n thấy hai con rắn quấn chết c&ocirc; b&eacute;. Sẵn con dao rựa đeo b&ecirc;n người, &ocirc;ng tức giận r&uacute;t dao vừa ch&eacute;m, vừa n&oacute;i &ldquo;M&agrave;y hại tao &agrave;&rdquo;. Hai con rắn sợ qu&aacute; chạy trốn, nhưng một con chậm hơn đ&atilde; bị ch&eacute;m đứt đu&ocirc;i. &Ocirc;ng đuổi hai con rắn v&agrave; n&oacute;i: &ldquo;Cụt đi hang Mang, Khoang đi hang Đồng&rdquo;. &Ocirc;ng b&agrave; x&oacute;t thương c&ocirc; b&eacute;, kh&ocirc;ng nỡ ch&ocirc;n, n&ecirc;n đặt c&ocirc; nằm ở tr&ecirc;n s&agrave;n. Đến s&aacute;ng đ&atilde; thấy mối đ&ugrave;n l&ecirc;n đắp mộ cho c&ocirc; b&eacute;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">D&acirc;n l&agrave;ng thấy vậy đều cho l&agrave; c&ocirc; đ&atilde; linh ho&aacute; n&ecirc;n lập miếu thờ. Thời kỳ giặc Cờ đen, c&ocirc; b&eacute; Minh Lương đ&atilde; hiển linh gi&uacute;p quan qu&acirc;n triều đ&igrave;nh tho&aacute;t khỏi rừng rậm, sau đ&oacute; dũng m&atilde;nh dẹp sạch giặc Cờ đen. Sau đ&oacute; C&ocirc; c&ograve;n hiển linh bốc thuốc, gi&uacute;p d&acirc;n chữa bệnh tho&aacute;t cơn hiểm ngh&egrave;o.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/truyen-thuyet-mau-thoai-tuyen-quang\">truyền thuyết mẫu Thoải - Tuy&ecirc;n Quang</a></span></p>\r\n\r\n<h2><strong><span style=\"font-size:16px\">Đền thờ C&ocirc; B&eacute; Minh Lương</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\">C&ocirc; B&eacute; Minh Lương được thờ ch&iacute;nh ở ng&ocirc;i đền thuộc địa phận x&atilde; Lăng Qu&aacute;n, Y&ecirc;n Sơn, Tuy&ecirc;n Quang. Người d&acirc;n về tới đất Tuyên Quang thường ghé đền cô bé Minh Lương để thắp nhang và cầu lộc tài bình an. Ng&ocirc;i đền toạ lạc tr&ecirc;n một quả đồi, ba ph&iacute;a được bao bọc bởi 2 d&ograve;ng suối c&oacute; t&ecirc;n gọi l&agrave; ng&ograve;i Lịch v&agrave; ng&ograve;i Cơi. Hai d&ograve;ng suối trong, m&aacute;t n&agrave;y giao nhau trước cửa đền, chảy qua cầu Cơi ở km 10 rồi ho&agrave; v&agrave;o s&ocirc;ng L&ocirc;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/co-be-minh-luong-2.png\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Trải qua nhiều lần tr&ugrave;ng tu, t&ocirc;n tạo, đền Minh Lương nay đ&atilde; khang trang, bề thế, gồm c&aacute;c ban thờ C&ocirc; B&eacute;, thờ Phật, thờ Đức Th&aacute;nh Trần. S&acirc;n đền gồm hệ thống Lầu C&ocirc;, Lầu Cậu, Quan Sơn Thần, ch&acirc;n nhang bản mệnh, Mẫu Cửu Tr&ugrave;ng Thi&ecirc;n; cạnh đền c&oacute; hai gian nh&agrave; sắm lễ. Xung quanh đền được bao bọc bởi rất nhiều c&acirc;y xanh. Ngay trước cửa đền c&oacute; hai c&acirc;y thi&ecirc;n tuế, một c&acirc;y đực, một c&acirc;y c&aacute;i. Điều đặc biệt l&agrave; c&acirc;y c&aacute;i mọc l&ecirc;n 8 nh&aacute;nh v&agrave; c&oacute; nh&aacute;nh phụ tr&ocirc;ng giống hệt b&agrave;n tay v&aacute;i thi&ecirc;n. Một lần, c&aacute;c b&ocirc; l&atilde;o ở l&agrave;ng hoa Ngọc H&agrave; (H&agrave; Nội) l&ecirc;n thăm đền đ&atilde; x&aacute;c định tuổi thọ c&acirc;y đ&atilde; tr&ecirc;n 500 năm v&agrave; khẳng định, đ&acirc;y l&agrave; c&acirc;y thi&ecirc;n tuế độc nhất v&ocirc; nhị. D&acirc;n l&agrave;ng xung quanh kh&ocirc;ng ai dựng nh&agrave; cửa xung quanh g&ograve; đồi.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/co-be-minh-luong-3.png\" />Hiện nay, đền Minh Lương nằm giữa một quần thể du lịch t&acirc;m linh của x&atilde; Thắng Qu&acirc;n, gồm đền Lương Qu&aacute;n v&agrave; Đầm M&acirc;y (nơi c&oacute; lễ hội Đầm M&acirc;y nổi tiếng của huyện Y&ecirc;n Sơn). C&aacute;ch quần thể du lịch t&acirc;m linh kh&ocirc;ng xa l&agrave; những ng&ocirc;i l&agrave;ng nhỏ của đồng b&agrave;o d&acirc;n tộc Dao, T&agrave;y lu&ocirc;n hết l&ograve;ng với kh&aacute;ch, tạo n&ecirc;n một tua du lịch văn ho&aacute;, t&acirc;m linh.</span></p>\r\n\r\n<h2><strong><span style=\"font-size:16px\">Kh&aacute;nh tiệc C&ocirc; B&eacute; Minh Lương</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\">Ng&agrave;y tiệc C&ocirc; B&eacute; Minh Lương diễn ra v&agrave;o ng&agrave;y 24/6 &acirc;m lịch hằng năm tại Đền C&ocirc; B&eacute; Minh Lương (Lang Qu&aacute;n, Y&ecirc;n Sơn, Tuy&ecirc;n Quang).</span></p>\r\n\r\n<h2><strong><span style=\"font-size:16px\">C&aacute;ch di chuyển từ C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường đến Đền C&ocirc; b&eacute; Minh Lương</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường</a> ở vị tr&iacute; v&ocirc; c&ugrave;ng đặc biệt. Địa thế phong thủy Vượng sơn vượng hướng, tựa n&uacute;i nh&igrave;n s&ocirc;ng. Kh&ocirc;ng chỉ c&oacute; vậy, C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường nằm ngay s&aacute;t cạnh Đền c&ocirc; b&eacute; Minh Lương với mong muốn để c&aacute;c linh hồn đ&atilde; mất được an lạc trong tiếng kinh kệ mỗi ng&agrave;y, nhận th&ecirc;m phước b&aacute;u tr&ecirc;n h&agrave;nh tr&igrave;nh về v&atilde;ng sinh về c&otilde;i Tịnh Độ.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m:<a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cong-vien-nghia-trang-thien-duong-dat-sinh-phan-dep-dang-dau-tu\"> C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường- đất sinh phần đẹp đ&aacute;ng đầu tư</a></span></p>\r\n\r\n<p>&nbsp;</p>', 'true', 'News', 'su-tich-it-nguoi-biet-ve-den-co-be-minh-luong', 'co-be-minh-luong.png', 'Sự tích ít người biết về Đền Cô Bé Minh Lương', '​​​​​​​Cô Bé Minh Lương là một trong những tiên cô bản đền nổi tiếng linh thiêng, nhân dân xa gần khi về tới đất Tuyên Quang thường ghé đền cô bé Minh Lương để thắp nhang và cầu lộc tài cầu bình an.', '2023-12-23 03:45:10', '2023-12-23 03:45:57'),
(197, 2, 68, 'Phong Thủy thờ cúng', 'Thờ cúng ông bà tổ tiên là phong tục tập quán của người dân Việt Nam. Nhưng trong thờ cúng ta còn cần chú ý đến phong thủy để tránh những điều không đáng có cho gia đình.', NULL, '<h2><strong><span style=\"font-size:16px\">Đặt b&aacute;t hương theo 3 cấp</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\">Thắp hương l&agrave; để người dương hướng tới c&otilde;i &acirc;m. Lẻ l&agrave; dương l&agrave; con người v&agrave; ta thờ c&uacute;ng l&agrave; &acirc;m, vậy th&igrave; &acirc;m dương giao h&ograve;a, đ&oacute; l&agrave; l&iacute; do v&igrave; sao m&agrave; thắp hương số lẻ.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Tay tr&aacute;i l&agrave; dương, tay phải l&agrave; &acirc;m, khi &acirc;m dương giao h&ograve;a tạo ra ngũ h&agrave;nh Kim &ndash; Mộc &ndash; Thủy &ndash; Hỏa &ndash; Thổ tạo ra to&agrave;n bộ nh&acirc;n loại, &acirc;m dương l&agrave; s&aacute;ng tối, l&agrave; đực c&aacute;i, l&agrave; bố mẹ, l&agrave; ng&agrave;y đ&ecirc;m l&agrave; nhật nguyệt.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Th&agrave;nh t&acirc;m chăm s&oacute;c b&aacute;t hương đ&uacute;ng c&aacute;ch sẽ tăng sự chứng của thần th&aacute;nh cho gia chủ của ch&uacute;ng ta. Nếu b&aacute;t hương để uế tạp hoặc kh&ocirc;ng hợp lẽ th&igrave; sẽ ảnh hưởng rất kh&ocirc;ng tốt đến gia chủ.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Vậy th&igrave; việc thờ c&uacute;ng b&aacute;t hương th&agrave;nh 3 cấp sẽ được sắp xếp như thế n&agrave;o:</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ndash;&nbsp;Thờ phật trước</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ndash; Thờ thần gồm c&oacute; 3 b&aacute;t hương (b&aacute;t hương thờ B&agrave; c&ocirc; tổ b&ecirc;n tr&aacute;i, b&aacute;t hương thờ th&aacute;nh thần to hơn v&agrave; đặt cao hơn 1 ch&uacute;t, b&aacute;t hương thờ gia ti&ecirc;n tiền tổ b&ecirc;n phải). B&agrave; c&ocirc; tổ sẽ kết nối gia ti&ecirc;n với thần th&aacute;nh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/Picture1.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/san-pham\">c&aacute;c sản phẩm của c&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường</a></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong><span style=\"font-size:16px\">Nguy&ecirc;n tắc đặt b&aacute;t hương:</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\">&ndash; Thờ phật tổ l&agrave; b&aacute;t hương được chạm nhật nguyệt hoặc song long b&aacute;t qu&aacute;i</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ndash; Thờ th&aacute;nh thần cao hơn 1 ch&uacute;t ở giữa, b&agrave; c&ocirc; tổ v&agrave; gia ti&ecirc;n tiền tổ ở 2 b&ecirc;n.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ndash; B&aacute;t hương rất quan trọng l&agrave; nơi gi&aacute;ng ngự, l&agrave; tổ ấm, l&agrave; gia đ&igrave;nh l&agrave; nh&agrave; của th&aacute;nh thần, chư phật về. B&aacute;t hương l&agrave; h&igrave;nh tượng hội tụ t&acirc;m thức, sự giao cảm giữa gia chủ với thần linh, với gia ti&ecirc;n tiền tổ v&agrave; mỗi b&aacute;t hương l&agrave; c&aacute;c lực lượng, c&aacute;c thế lực t&acirc;m linh về chứng gi&aacute;m. N&ecirc;n b&aacute;t hương kh&ocirc;ng bao giờ được dịch chuyển.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ndash; H&igrave;nh thức B&aacute;t hương bằng sứ hoặc bằng đồng được chạm l&agrave; mặt nguyệt hoặc song long chầu nhật nguyệt v&agrave; người bốc b&aacute;t hương kh&ocirc;ng phải người thường m&agrave; phải nhờ nh&agrave; sư, v&igrave; nh&agrave; sư tu h&agrave;nh c&oacute; t&acirc;m thiện mới đủ th&acirc;n tịnh để bốc b&aacute;t hương, nh&agrave; sư đọc kinh niệm ch&uacute; để thắp hương linh kh&iacute;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ndash; Rửa b&aacute;t hương th&igrave; d&ugrave;ng nước muối, rượu gừng hoặc nước ng&acirc;m hoa hồng, rửa xong x&ocirc;ng kh&ocirc; bằng trầm hương, nước rửa b&aacute;t hương kh&ocirc;ng được đổ ra cống m&agrave; phải vẩy ra s&acirc;n hoặc đổ ra s&acirc;n để lấy may mắn</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ndash; L&oacute;t dưới b&aacute;t hương một giấy trắng kim ph&ograve;ng khi đồ yểm ch&aacute;y (v&agrave;ng bạc ch&acirc;u b&aacute;u, m&atilde; l&atilde;o &hellip; )ở giữa c&oacute; đồng tiền m&atilde; số 5 (5 l&agrave; ngũ h&agrave;nh tương sinh v&agrave; số 5 l&agrave; số dương), tro bếp d&ugrave;ng rơm nếp (c&oacute; thể mua chổi về h&oacute;a) v&agrave; đặc biệt l&agrave; d&ugrave;ng chấu, chấu l&agrave; bọc gạo, v&agrave; gạo l&agrave; ngọc của trời n&ecirc;n chấu l&agrave; tốt nhất đốt đi cho b&aacute;t hương.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ndash; C&aacute;c nh&agrave; sư đọc kinh hay tr&uacute; mật t&ocirc;ng của nh&agrave; phật để đặt y&ecirc;n vị b&aacute;t hương, khi đọc th&igrave; cắm chữ thọ để c&oacute; thể để hương v&ograve;ng ở tr&ecirc;n, đặt ở ch&iacute;nh giữa th&igrave; tốt. Khi dọn dẹp, sắp xếp b&agrave;n thờ những dịp giỗ chạp &hellip; th&igrave; phải lễ xin ph&eacute;p trước, sau đ&oacute; chỉ dịch chuyển b&igrave;nh hoa, nước, đỉnh đồng&hellip; ri&ecirc;ng b&aacute;t hương tuyệt đối kh&ocirc;ng di chuyển b&aacute;t hương.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ndash; Nếu muốn lau th&igrave; giữ chặt b&aacute;t hương. 23 th&aacute;ng chạp th&igrave; dọn bớt ch&acirc;n hương, tỉa hết ra, sau đ&oacute; cắm lại 12 n&eacute;n ch&acirc;n hương như sau. Đợt 1 cắm 3 ch&acirc;n hương, v&agrave; khấn ni&ecirc;n ni&ecirc;n thị hảo ni&ecirc;n (cả năm đều tốt). 3 n&eacute;n thứ 2 khấn nguyệt nguyệt thị hảo nguyệt (th&aacute;ng n&agrave;o cũng tốt). 3 n&eacute;n thứ 3 khấn nhật nhật thị hảo nhật (ng&agrave;y n&agrave;o cũng tốt). 3 n&eacute;n cuối khấn l&agrave; thời thời thị hảo thời (l&uacute;c n&agrave;o cũng tốt).</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ndash; H&oacute;a ch&acirc;n hương kh&ocirc;ng t&ugrave;y tiện, khi lấy ch&acirc;n hương ra kh&ocirc;ng n&eacute;m xuống đất, cho v&agrave;o tờ giấy tr&ecirc;n b&agrave;n, g&oacute;i lại, mang thả xuống d&ograve;ng s&ocirc;ng đang chảy v&agrave;o giờ t&yacute; (11h đ&ecirc;m &ndash; 1h s&aacute;ng) nếu l&agrave; ch&acirc;n hương hoặc b&aacute;t hương sứt mẻ hoặc b&aacute;t b&agrave;n thờ vong m&agrave; cho n&eacute;m v&agrave;o ao t&ugrave; th&igrave; chắc chắn l&agrave; kh&ocirc;ng tốt.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/le-an-tang-tai-cong-vien-nghia-trang-thien-duong\">Lễ an t&aacute;ng tại c&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường</a></span></p>\r\n\r\n<h2><strong><span style=\"font-size:16px\">Nguy&ecirc;n tắc c&uacute;ng lễ, thắp hương</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\">&ndash; Khi c&uacute;ng tế, th&igrave; phải mở rộng cửa cho s&aacute;ng, bật đ&egrave;n s&aacute;ng l&ecirc;n cho tho&aacute;ng, r&oacute;t nước r&oacute;t rượu mời rồi mới thắp hương, thắp hương số lẻ v&igrave; lẻ thuộc về số dương, &acirc;m dương giao h&ograve;a l&agrave; tốt.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ndash; Tuyệt đối kh&ocirc;ng d&ugrave;ng miệng thổi hương, thắp hương rồi vẩy một c&aacute;i rồi cắm hương ngay ngắn v&agrave;o b&aacute;t hương, đ&oacute; l&agrave; tỏ th&aacute;i độ tr&acirc;n trọng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ndash; Nếu b&aacute;t hương tự ch&aacute;y đ&oacute; l&agrave; 1 điềm b&aacute;o: Ch&aacute;y từ ch&acirc;n l&ecirc;n (điều &acirc;m) mổ mả thờ c&uacute;ng c&oacute; vấn đề. Ch&aacute;y từ tr&ecirc;n xuống (điểm dương) nh&agrave; cửa, cuộc sống c&oacute; vấn đề.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ndash; Đang c&uacute;ng m&agrave; hương tắt th&igrave; để nguy&ecirc;n, r&uacute;t ra l&agrave; mất ch&acirc;n l&agrave; hương thừa. Nếu hương tắt ở phần tr&ecirc;n (Thi&ecirc;n) l&oacute;c nh&agrave; b&agrave;n thờ c&oacute; vấn đề cần xem lại. Tắt ở phần giữa l&agrave; nh&acirc;n: xem lại sự li&ecirc;n quan quan hệ gia đ&igrave;nh anh em, bố mẹ con c&aacute;i, vợ chồng c&oacute; vấn đề g&igrave; kh&ocirc;ng? Hương tắt ở phần cuối (địa) xem lại mồ mả đất c&aacute;t như thế n&agrave;o.</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/R.jpg\" /><span style=\"font-size:16px\">Tr&ecirc;n đ&acirc;y l&agrave; một số vấn đề v&agrave; nguy&ecirc;n tắc thờ c&uacute;ng, nguy&ecirc;n tắc đặt b&aacute;t hương m&agrave; ch&uacute;ng t&ocirc;i sưu tầm được. Hy vọng sẽ c&oacute; &iacute;ch cho qu&yacute; vị v&agrave; c&aacute;c bạn. Cảm ơn qu&yacute; kh&aacute;ch đ&atilde; đọc tin!</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline&nbsp;0934.524.445&nbsp;hoặc truy cập trang web&nbsp;</span><a href=\"https://congvienthienduong.com.vn/\"><span style=\"font-size:16px\">https://nghiatrangthienduong.com.vn</span>/</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', 'true', 'News', 'phong-thuy-tho-cung', 'R.jpg', 'Phong Thủy thờ cúng', 'Thờ cúng ông bà tổ tiên là phong tục tập quán của người dân Việt Nam. Nhưng trong thờ cúng ta còn cần chú ý đến phong thủy để tránh những điều không đáng có cho gia đình.', '2023-12-23 09:11:52', '2023-12-23 09:11:52');
INSERT INTO `posts` (`id`, `user_id`, `category_id`, `name`, `detail`, `info`, `content`, `status`, `sort_by`, `slug`, `img`, `title`, `description`, `created_at`, `updated_at`) VALUES
(198, 2, 68, 'Tìm hiểu các hình thức mai táng phổ biến ở Việt Nam', 'Các hình thức mai táng người chết hay còn được gọi với cái tên khác là phương thức mai táng là một trong những thủ tục tâm linh của người Việt Nam khi chôn cất người đã khuất.', NULL, '<p>C&aacute;c h&igrave;nh thức mai t&aacute;ng&nbsp;người chết hay c&ograve;n được gọi với c&aacute;i t&ecirc;n kh&aacute;c l&agrave; phương thức mai t&aacute;ng l&agrave; một trong những thủ tục t&acirc;m linh của người Việt Nam khi ch&ocirc;n cất người đ&atilde; khuất. Tuy nhi&ecirc;n vẫn c&ograve;n chưa c&oacute; nhiều người hiểu r&otilde; về phương thức n&agrave;y cũng như c&aacute;c h&igrave;nh thức mai t&aacute;ng phổ biến hiện nay. H&atilde;y c&ugrave;ng <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n nghĩa trang Thi&ecirc;n Đường</a> t&igrave;m hiểu th&ocirc;ng tin bổ &iacute;ch qua b&agrave;i viết ngay dưới đ&acirc;y nh&eacute;.</p>\r\n\r\n<p>NỘI DUNG B&Agrave;I VIẾT</p>\r\n\r\n<ul>\r\n	<li><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/tim-hieu-cac-hinh-thuc-mai-tang-pho-bien-o-viet-nam\">I.&nbsp;Mai t&aacute;ng l&agrave; g&igrave;?</a></li>\r\n	<li><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/tim-hieu-cac-hinh-thuc-mai-tang-pho-bien-o-viet-nam\">II. C&aacute;c h&igrave;nh thức mai t&aacute;ng phổ biến</a></li>\r\n	<li><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/tim-hieu-cac-hinh-thuc-mai-tang-pho-bien-o-viet-nam\">III. N&ecirc;n mai t&aacute;ng ở đ&acirc;u tốt nhất?</a></li>\r\n</ul>\r\n\r\n<h2>Mai t&aacute;ng l&agrave; g&igrave;?</h2>\r\n\r\n<p>Mai t&aacute;ng ch&iacute;nh l&agrave; từ ngữ n&oacute;i chung nhằm mi&ecirc;u tả c&ocirc;ng việc thực hiện lưu giữ thi h&agrave;i người đ&atilde; khuất sau khi qua đời. N&oacute; bao gồm nhiều h&igrave;nh thức kh&aacute;c nhau. Tại một số quốc gia kh&aacute;c nhau sẽ c&oacute; nhiều h&igrave;nh thức mai t&aacute;ng kh&aacute;c nhau.</p>\r\n\r\n<p>Th&ocirc;ng thường đa số&nbsp;c&aacute;c h&igrave;nh thức mai t&aacute;ng&nbsp;của con người đều mang yếu tố t&acirc;m linh mạnh mẽ. C&aacute;c h&igrave;nh thức n&agrave;y đều c&oacute; sự li&ecirc;n quan đến ba nguy&ecirc;n tố được s&ugrave;ng b&aacute;i trong t&acirc;m linh ch&iacute;nh l&agrave; đất, nước v&agrave; trời.</p>\r\n\r\n<p>Về nh&acirc;n sinh quan th&igrave; người Việt Nam cũng như c&aacute;c d&acirc;n tộc trong khu vực Đ&ocirc;ng Nam &Aacute; đều quan niệm rằng con người c&oacute; hai phần linh hồn v&agrave; thể x&aacute;c. Trong đ&oacute; phần linh hồn quyết định trực tiếp đến sự sống chết của con người, nếu người chết đi th&igrave; linh hồn sẽ biến th&agrave;nh ma.</p>\r\n\r\n<p>Bởi những quan niệm cũng như tư tưởng tr&ecirc;n m&agrave; m&agrave; từ xa xưa người Việt Nam đ&atilde; c&oacute; tục lệ t&aacute;ng rất nhiều đồ cho người chết để họ tiếp tục cuộc sống ở thế giới b&ecirc;n kia.</p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/12.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Mai t&aacute;ng l&agrave; g&igrave;?</em></p>\r\n\r\n<p>&gt;&gt;&gt; Đọc th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/le-tang-truyen-thong-tai-viet-nam-va-cac-van-de-can-luu-y\">Lễ an t&aacute;ng truyền thống tại Việt Nam v&agrave; c&aacute;c vấn đề cần lưu &yacute;&nbsp;</a></p>\r\n\r\n<h2>C&aacute;c h&igrave;nh thức mai t&aacute;ng phổ biến</h2>\r\n\r\n<p>Mai t&aacute;ng l&agrave; biện ph&aacute;p xử l&yacute; thi h&agrave;i của người chết với rất nhiều những h&igrave;nh thức kh&aacute;c nhau. Hiện nay tr&ecirc;n thế giới c&oacute; rất nhiều&nbsp;c&aacute;c h&igrave;nh thức mai t&aacute;ng&nbsp;c&oacute; thể kể đến như địa t&aacute;ng, hỏa t&aacute;ng, thủy t&aacute;ng, kh&ocirc;ng t&aacute;ng, huyền t&aacute;ng. Tuy nhi&ecirc;n ở Việt Nam tồn tại hai h&igrave;nh thức phổ biến l&agrave; địa t&aacute;ng v&agrave; hỏa t&aacute;ng, c&ograve;n lại những h&igrave;nh thức mai t&aacute;ng kh&aacute;c chỉ tồn tại ở một số &iacute;t c&aacute;c d&acirc;n tộc &iacute;t người tr&ecirc;n v&ugrave;ng cao.</p>\r\n\r\n<h3>Địa t&aacute;ng</h3>\r\n\r\n<p>Địa t&aacute;ng hay c&ograve;n được gọi với c&aacute;i t&ecirc;n kh&aacute;c l&agrave; thổ t&aacute;ng ch&iacute;nh l&agrave; h&igrave;nh thức mai t&aacute;ng phổ biến nhất. Thổ t&aacute;ng sẽ bao gồm hai loại như sau:</p>\r\n\r\n<ul>\r\n	<li>Một loại ch&ocirc;n xuống dưới đất một thời gian nhất định t&ugrave;y theo tập tục được quy định của từng v&ugrave;ng, sau đ&oacute; bắt buộc phải cải t&aacute;ng, lần n&agrave;y mới được ch&ocirc;n vĩnh viễn.</li>\r\n	<li>Một loại ch&ocirc;n cất thi h&agrave;i của người chết ở dưới đất vĩnh viễn, trừ phi trong gia đ&igrave;nh xảy ra sự cố g&igrave; bất trắc th&igrave; người ta mới phải cải t&aacute;ng.</li>\r\n</ul>\r\n\r\n<p>Đ&acirc;y l&agrave; một h&igrave;nh thức c&oacute; ở Việt Nam từ rất l&acirc;u đời với nhiều kiểu quan t&agrave;i kh&aacute;c nhau như quan t&agrave;i h&igrave;nh v&ograve; v&agrave; h&igrave;nh thuyền trong nền văn h&oacute;a cổ Sa Huỳnh.</p>\r\n\r\n<ul>\r\n	<li>Quan t&agrave;i h&igrave;nh v&ograve; được sử dụng d&ugrave;ng để mai t&aacute;ng người đ&atilde; khuất. V&ograve; hay chum được để hở hoặc đậy bằng loại vung đặc biệt hoặc c&aacute;c hiện vật gốm nhưng c&ugrave;ng loại h&igrave;nh.</li>\r\n	<li>Quan t&agrave;i h&igrave;nh thuyền hay quan t&agrave;i th&acirc;n c&acirc;y được kho&eacute;t rỗng l&ograve;ng, chừa hai đầu hay gh&eacute;p th&ecirc;m hai miếng v&aacute;n. Nắp quan t&agrave;i được thiết kế c&oacute; chốt hay mộng để khớp với quan t&agrave;i.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/đền trình/đền trình 1.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>C&aacute;c h&igrave;nh thức mai t&aacute;ng phổ biến&nbsp;</em></p>\r\n\r\n<h3>Hỏa t&aacute;ng</h3>\r\n\r\n<p><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/hoa-tang-la-gi-quy-trinh-hoa-tang-tai-viet-nam\">Hỏa t&aacute;ng</a>&nbsp;hay c&ograve;n được gọi l&agrave; hỏa thi&ecirc;u. Đ&acirc;y l&agrave; phương ph&aacute;p xử l&yacute; h&agrave;i cốt đang phổ biến thời gian gần đ&acirc;y tại Việt Nam. Hỏa t&aacute;ng thi h&agrave;i người chết bằng c&aacute;ch đốt ch&aacute;y th&agrave;nh tro, tro của h&agrave;i cốt t&ugrave;y theo phong tục ri&ecirc;ng của từng cộng đồng m&agrave; cho v&agrave;o b&igrave;nh k&iacute;n để thờ c&uacute;ng trong gia đ&igrave;nh hoặc đặt v&agrave;o nơi thờ tự t&ocirc;n gi&aacute;o như ch&ugrave;a hoặc trải tro cốt ngo&agrave;i biển theo di nguyện của người đ&atilde; khuất.</p>\r\n\r\n<p>H&igrave;nh thức n&agrave;y được đ&aacute;nh gi&aacute; với ưu điểm thuận tiện, sạch gọn, kh&ocirc;ng g&acirc;y ra &ocirc; nhiễm m&ocirc;i trường, kh&ocirc;ng phải cải t&aacute;ng, kh&ocirc;ng tốn diện t&iacute;ch mở rộng đất nghĩa địa. Ngo&agrave;i ra phương thức n&agrave;y c&ograve;n thuận tiện với người chết trong trường hợp ở xa kh&ocirc;ng đem được thi h&agrave;i về nước.</p>\r\n\r\n<p>Tại Việt Nam trước đ&acirc;y người ta kh&ocirc;ng ưa chuộng tục hỏa t&aacute;ng, tuy nhi&ecirc;n hiện nay phương thức n&agrave;y được rất nhiều gia đ&igrave;nh lựa chọn.</p>\r\n\r\n<p>&gt;&gt; Đọc th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/hoa-tang-la-gi-quy-trinh-hoa-tang-tai-viet-nam\">Quy tr&igrave;nh hỏa t&aacute;ng tại Việt Nam</a></p>\r\n\r\n<h3>Huyền t&aacute;ng</h3>\r\n\r\n<p>Huyền t&aacute;ng được gọi với c&aacute;i t&ecirc;n kh&aacute;c l&agrave; tục t&aacute;ng treo. Kiểu ch&ocirc;n người chết n&agrave;y kh&ocirc;ng phổ biến nhiều như địa t&aacute;ng nhưng xuất hiện rất nhiều tại thời xưa. Với phương thức n&agrave;y th&igrave; người ta để&nbsp; thi h&agrave;i của người chết lộ thi&ecirc;n hoặc đặt nằm tr&ecirc;n một tấm ph&ecirc;n. C&oacute; nhiều c&aacute;ch để quan t&agrave;i như:</p>\r\n\r\n<ul>\r\n	<li>Đặt quan t&agrave;i dưới v&ograve;m m&aacute;i đ&aacute; hay trong hang đ&aacute; ở lưng chừng v&aacute;ch n&uacute;i, đ&ocirc;i khi đặt nằm s&acirc;u trong hang động. Những hang động n&agrave;y thường nằm gần s&ocirc;ng, c&oacute; r&agrave;o chắn một c&aacute;ch cẩn thận. Mỗi hang động đều c&oacute; nhiều v&aacute;ch ngăn kh&aacute;c nhau để đựng quan t&agrave;i.</li>\r\n	<li>Sử dụng những đoạn gỗ to ghim s&acirc;u v&agrave;o v&aacute;ch n&uacute;i để l&agrave;m điểm tựa cho quan t&agrave;i hoặc cắm một đầu quan t&agrave;i v&agrave;o những hốc đ&aacute; l&ecirc;n lưng chừng n&uacute;i.</li>\r\n</ul>\r\n\r\n<p>Tuy nhi&ecirc;n hiện nay t&aacute;ng treo kh&ocirc;ng được phổ biến ở Việt Nam bởi tục n&agrave;y g&acirc;y &ocirc; nhiễm m&ocirc;i trường cũng như tiềm t&agrave;ng nguy cơ ph&aacute;t triển bệnh dịch.</p>\r\n\r\n<p>Như vậy ta n&ecirc;n chọn h&igrave;nh thức mai t&aacute;ng n&agrave;o? H&igrave;nh thức phổ biến từ xa xưa đến nay vẫn giữ nguy&ecirc;n được gi&aacute; trị ch&iacute;nh l&agrave; địa t&aacute;ng. Bởi phương ph&aacute;p n&agrave;y l&agrave; hoạt động đưa x&aacute;c người chết đặt v&agrave;o trong quan t&agrave;i v&agrave; ch&ocirc;n xuống dưới l&ograve;ng đất. Sau đ&oacute; lấp đất lại v&agrave; tiến h&agrave;nh ch&ocirc;n cất người đ&atilde; khuất.</p>\r\n\r\n<p>Nghi lễ địa t&aacute;ng được nhiều gia đ&igrave;nh lựa chọn v&igrave; n&oacute; mang đến sự t&ocirc;n trọng của gia đ&igrave;nh đối với người đ&atilde; khuất. Đồng thời c&ugrave;ng với đ&oacute; ngăn chặn được những m&ugrave;i độc hại cũng như thối rữa của x&aacute;c người. Ch&iacute;nh v&igrave; vậy m&agrave; đ&acirc;y trở th&agrave;nh một tập tục v&ocirc; c&ugrave;ng quan trọng đối với những người qu&aacute; cố.</p>\r\n\r\n<h2>N&ecirc;n mai t&aacute;ng ở đ&acirc;u tốt nhất?</h2>\r\n\r\n<p><a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n nghĩa trang Thi&ecirc;n Đường&nbsp;đẹp chuẩn resort 5 sao</a>&nbsp;tọa lạc ở vị tr&iacute; địa l&yacute; v&ocirc; c&ugrave;ng đắc địa, thuận tiện cho những người con khi xa qu&ecirc; hay những doanh nh&acirc;n th&agrave;nh đạt thăm viếng người th&acirc;n trong c&aacute;c dịp lễ Tết. Được quy hoạch tổng thể dự &aacute;n tại x&atilde; Lang Qu&aacute;n, huyện Yễn Sơn, tỉnh Tuy&ecirc;n Quang, dự &aacute;n c&oacute; thế đất tọa sơn hướng thủy.</p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/z4899209284436_cf6578ba7d10c682365160f56b81f110.jpg\" />Cha &ocirc;ng ta lu&ocirc;n quan niệm nơi n&agrave;o c&oacute; n&uacute;i bao v&ograve;ng che chở, d&ograve;ng nước uốn cong l&agrave; nơi tụ kh&iacute; tốt để x&acirc;y dựng nh&agrave; cửa. Phong thủy quan niệm nơi c&oacute; thủy tụ sẽ tụ kh&iacute;, m&agrave; tụ kh&iacute; th&igrave; tụ t&agrave;i, gi&uacute;p cho gia chủ hưng vượng, sức khỏe an khang. Nếu được cả thế đất c&oacute; sơn thủy bao bọc th&igrave; lại c&agrave;ng l&agrave; điềm đại c&aacute;t đại lợi. Những cuộc đất qu&yacute; hiếm n&agrave;y l&agrave; nơi phong t&agrave;ng kh&iacute; tụ, đem lại t&agrave;i lộc thịnh vượng với dấu hiệu dễ d&agrave;ng nhận biết l&agrave; kh&ocirc;ng kh&iacute; tho&aacute;ng đ&atilde;ng, trong l&agrave;nh, cỏ c&acirc;y bốn m&ugrave;a tươi tốt.</p>\r\n\r\n<p>C&ocirc;ng vi&ecirc;n Nghĩa trang <a href=\"https://nghiatrangthienduong.com.vn/\">Thi&ecirc;n Đường</a> nằm tr&ecirc;n khu đất c&oacute; d&aacute;ng h&igrave;nh LINH QUY biểu hiện sự th&ocirc;ng minh,mẫn c&aacute;n v&agrave; trường thọ. Dải đất n&agrave;y c&oacute; mạch chạy theo hướng T&acirc;y Bắc - Đ&ocirc;ng Nam. Thế đất n&agrave;y theo <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/dia-the-phong-thuy\">Phong Thủy</a> l&agrave; Vượng sơn Vượng hướng, c&oacute; địa vận 160 năm, đem lại Ph&uacute;c Lộc đời đời cho&nbsp;con ch&aacute;u.</p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/z4739939583777_ed37970f5389ec145ff423bd67dab831.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>N&ecirc;n mai t&aacute;ng ở đ&acirc;u tốt nhất?</em></p>\r\n\r\n<p>&gt;&gt; Đọc th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/dia-the-phong-thuy\">địa thế phong thủy của Thi&ecirc;n Đường</a></p>\r\n\r\n<p>Vậy tại sao&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n nghĩa trang Thi&ecirc;n Đường</a> lại l&agrave; nơi mai t&aacute;ng tốt nhất d&agrave;nh cho những người th&acirc;n đ&atilde; khuất của gia đ&igrave;nh bạn? H&atilde;y c&ugrave;ng ch&uacute;ng t&ocirc;i t&igrave;m hiểu qua một số dịch vụ v&agrave; ưu điểm đặc biệt m&agrave; bạn sẽ được trải nghiệm tại đ&acirc;y nh&eacute;:</p>\r\n\r\n<p>1. C&oacute; địa thế phong thủy vượng sơn vượng hướng: Nằm tr&ecirc;n khu đất c&oacute; h&igrave;nh d&aacute;ng Linh Quy biểu hiện sự th&ocirc;ng minh, mẫn c&aacute;n v&agrave; trường thọ</p>\r\n\r\n<p>2. Kết nối giao th&ocirc;ng thuận lợi với c&aacute;c tuyến cao tốc ph&iacute;a bắc : Nằm tại vị tr&iacute; giao th&ocirc;ng thuận lợi, kết nối với hệ thống đường cao tốc ph&iacute;a Bắc</p>\r\n\r\n<p>3. Quần thể c&ocirc;ng tr&igrave;nh t&acirc;m linh giữ g&igrave;n văn h&oacute;a truyền thống: C&ocirc;ng tr&igrave;nh bao gồm Ch&ugrave;a B&aacute;o &Acirc;n, đền Tr&igrave;nh, tượng Phật</p>\r\n\r\n<p>4. Sở hữu kh&ocirc;ng gian xanh theo tỷ lệ v&agrave;ng : Hạng mục c&acirc;y xanh, hồ nước chiếm hơn 65% diện t&iacute;ch c&ocirc;ng vi&ecirc;n</p>\r\n\r\n<p>5. Hội tụ 4 d&ograve;ng sản phẩm từ trung cấp đến cao cấp : Khu mộ gia tộc, khu mộ gia đ&igrave;nh v&agrave; khu đơn</p>\r\n\r\n<p>6. Ph&aacute;p l&yacute; minh bạch, sỡ hữu vĩnh viễn : Dự &aacute;n <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường</a> c&oacute; ph&aacute;p l&yacute; minh bạch, hồ sơ đầy đủ</p>\r\n\r\n<p>7. Hệ thống dịch vụ ho&agrave;n hảo, quản l&yacute; vận h&agrave;nh hiện đại chuy&ecirc;n nghiệp: Dđội ngũ nh&acirc;n vi&ecirc;n chuy&ecirc;n nghiệp, được đ&agrave;o tạo b&agrave;i bản</p>\r\n\r\n<p>8.Gia tăng gi&aacute; trị cốt l&otilde;i: Kh&ocirc;ng chỉ l&agrave; một khoản t&iacute;ch lũy m&agrave; c&ograve;n mang lại ph&uacute;c lộc cho con ch&aacute;u đời sau</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/chùa báo ân/chuà báo ân 5.jpg\" /><em><a href=\"https://nghiatrangthienduong.com.vn/canh-quan/chua-bao-an\">Ch&ugrave;a B&aacute;o &Acirc;n tại Thi&ecirc;n Đường</a></em></p>\r\n\r\n<p>&gt;&gt; Đọc th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/phap-ly-so-huu\">Ph&aacute;p l&yacute; v&agrave; sở hữu của Thi&ecirc;n Đường&nbsp;</a></p>\r\n\r\n<p>Th&ocirc;ng qua b&agrave;i viết tr&ecirc;n, ch&uacute;ng t&ocirc;i đ&atilde; gi&uacute;p bạn t&igrave;m hiểu về&nbsp;c&aacute;c h&igrave;nh thức mai t&aacute;ng&nbsp;phổ biến hiện nay. Nếu bạn c&oacute; nhu cầu t&igrave;m hiểu th&ocirc;ng tin về dịch vụ, h&atilde;y li&ecirc;n hệ ngay đến Hoa vi&ecirc;n Thi&ecirc;n Đường để được hỗ trợ cũng như chăm s&oacute;c tận t&igrave;nh nhất.</p>\r\n\r\n<p>Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline&nbsp;0934.524.445 hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\">https://nghiatrangthienduong.com.vn/</a>.</p>\r\n\r\n<p>&nbsp;</p>', 'true', 'News', 'tim-hieu-cac-hinh-thuc-mai-tang-pho-bien-o-viet-nam', '17_thiên đường.jpg', 'Tìm hiểu các hình thức mai táng phổ biến ở Việt Nam', 'Các hình thức mai táng người chết hay còn được gọi với cái tên khác là phương thức mai táng là một trong những thủ tục tâm linh của người Việt Nam khi chôn cất người đã khuất.', '2023-12-25 04:21:36', '2023-12-25 15:30:30'),
(199, 2, 68, 'Cải táng là gì? Những điều cần biết về phong tục cải táng', 'Cải táng là một phương pháp chôn cất mới thay thế cho việc mai táng truyền thống. Nó được sử dụng nhằm giảm thiểu tác động đến môi trường, tôn trọng giá trị tâm linh và tôn giáo cùng những lợi ích kinh tế.', NULL, '<p><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang\">Cải t&aacute;ng</a>&nbsp;l&agrave; một phương ph&aacute;p ch&ocirc;n cất mới thay thế cho việc mai t&aacute;ng truyền thống. N&oacute; được sử dụng nhằm giảm thiểu t&aacute;c động đến m&ocirc;i trường, t&ocirc;n trọng gi&aacute; trị t&acirc;m linh v&agrave; t&ocirc;n gi&aacute;o c&ugrave;ng những lợi &iacute;ch kinh tế. Trong b&agrave;i viết n&agrave;y, ch&uacute;ng t&ocirc;i sẽ cung cấp th&ocirc;ng tin về cải t&aacute;ng, c&aacute;c loại h&igrave;nh cải t&aacute;ng phổ biến v&agrave; lợi &iacute;ch của n&oacute;.</p>\r\n\r\n<p><strong>Nội dung b&agrave;i viết</strong></p>\r\n\r\n<ul>\r\n	<li><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang\">Cải t&aacute;ng l&agrave; g&igrave;?</a></li>\r\n	<li><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang\">Tại sao n&ecirc;n lựa chọn cải t&aacute;ng thay cho mai t&aacute;ng truyền thống?</a></li>\r\n	<li><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang\">Những loại h&igrave;nh cải t&aacute;ng phổ biến hiện nay</a>\r\n	<ul>\r\n		<li><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang\">Cải t&aacute;ng nhanh</a></li>\r\n		<li><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang\">Cải t&aacute;ng sinh học</a></li>\r\n		<li><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang\">Cải t&aacute;ng h&ograve;a nhập với thi&ecirc;n nhi&ecirc;n</a></li>\r\n		<li><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang\">Cải t&aacute;ng theo phong c&aacute;ch trung quốc</a></li>\r\n	</ul>\r\n	</li>\r\n	<li><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang\">Lợi &iacute;ch của cải t&aacute;ng</a></li>\r\n	<li><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang\">Kết luận</a></li>\r\n</ul>\r\n\r\n<h2><strong>Cải t&aacute;ng l&agrave; g&igrave;?</strong></h2>\r\n\r\n<p>Cải t&aacute;ng l&agrave; một phương ph&aacute;p ch&ocirc;n cất mới thay thế cho việc mai t&aacute;ng truyền thống. N&oacute; l&agrave; qu&aacute; tr&igrave;nh gi&uacute;p biến thiểu x&aacute;c chết th&agrave;nh ph&acirc;n b&oacute;n, hoặc ch&ocirc;n cất thi h&agrave;i theo một c&aacute;ch kh&aacute;c m&agrave; kh&ocirc;ng l&agrave;m &ocirc; nhiễm đất đai hay nước ngầm.</p>\r\n\r\n<p>Cải t&aacute;ng được xem l&agrave; một giải ph&aacute;p bảo vệ m&ocirc;i trường v&agrave; giảm thiểu sự t&agrave;n ph&aacute; đất đai, đặc biệt l&agrave; tại c&aacute;c đ&ocirc; thị lớn với những nghĩa trang ng&agrave;y c&agrave;ng trở n&ecirc;n qu&aacute; tải. Ngo&agrave;i ra, cải t&aacute;ng c&ograve;n được sử dụng nhằm t&ocirc;n trọng gi&aacute; trị t&acirc;m linh v&agrave; t&ocirc;n gi&aacute;o của c&aacute;c d&acirc;n tộc.</p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/cải táng.jpg\" /></p>\r\n\r\n<p>Cải t&aacute;ng l&agrave; phương ph&aacute;p ch&ocirc;n cất mới của người Việt</p>\r\n\r\n<p>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/tim-hieu-cac-hinh-thuc-mai-tang-pho-bien-o-viet-nam\">t&igrave;m hiểu những h&igrave;nh thức mai t&aacute;ng phổ biến ở Việt Nam</a></p>\r\n\r\n<h2><strong>Tại sao n&ecirc;n lựa chọn cải t&aacute;ng thay cho mai t&aacute;ng truyền thống?</strong></h2>\r\n\r\n<p><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/tim-hieu-cac-hinh-thuc-mai-tang-pho-bien-o-viet-nam\">Mai t&aacute;ng truyền thống</a> g&acirc;y ra nhiều t&aacute;c động đến m&ocirc;i trường. Khi thi h&agrave;i được ch&ocirc;n cất, ch&uacute;ng kh&ocirc;ng được ph&acirc;n hủy một c&aacute;ch tự nhi&ecirc;n v&agrave; cần mất h&agrave;ng trăm năm mới c&oacute; thể ph&acirc;n hủy ho&agrave;n to&agrave;n. Hơn nữa, qu&aacute; tr&igrave;nh mai t&aacute;ng truyền thống cũng tạo ra nhiều kh&iacute; thải độc hại như kh&iacute; CO2, kh&iacute; độc từ c&aacute;c chất bảo quản, chất phụ gia trong qu&aacute; tr&igrave;nh bảo quản thi h&agrave;i, v&agrave; nước thải độc hại từ qu&aacute; tr&igrave;nh h&oacute;a đơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/cái táng.jpg\" /></p>\r\n\r\n<p>Tại sao cần phải cải t&aacute;ng</p>\r\n\r\n<p>Cải t&aacute;ng cũng gi&uacute;p t&ocirc;n trọng gi&aacute; trị t&acirc;m linh v&agrave; t&ocirc;n gi&aacute;o của c&aacute;c d&acirc;n tộc. Nhiều người tin rằng, ch&ocirc;n cất theo phương ph&aacute;p cải t&aacute;ng sẽ gi&uacute;p cho linh hồn của người chết được giải tho&aacute;t một c&aacute;ch tốt đẹp, cũng như đảm bảo sự an y&ecirc;n v&agrave; t&igrave;nh thần b&igrave;nh định cho gia đ&igrave;nh của họ.</p>\r\n\r\n<p>Xem th&ecirc;m:<a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/phong-thuy-tho-cung\"> phong thủy thờ c&uacute;ng</a></p>\r\n\r\n<h2><strong>Những loại h&igrave;nh <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang\">cải t&aacute;ng</a> phổ biến hiện nay</strong></h2>\r\n\r\n<h3><strong>Cải t&aacute;ng nhanh</strong></h3>\r\n\r\n<p>Cải t&aacute;ng nhanh l&agrave; một trong những loại h&igrave;nh cải t&aacute;ng kh&aacute;c nhau, nơi qu&aacute; tr&igrave;nh ch&ocirc;n cất v&agrave; ph&acirc;n hủy thi h&agrave;i diễn ra nhanh ch&oacute;ng. Phương ph&aacute;p n&agrave;y được sử dụng trong c&aacute;c t&igrave;nh huống khẩn cấp, nơi phải tiến h&agrave;nh mai t&aacute;ng một c&aacute;ch nhanh ch&oacute;ng v&agrave; hiệu quả.</p>\r\n\r\n<p>Theo phương ph&aacute;p cải t&aacute;ng nhanh, thi h&agrave;i sẽ được đặt trong một chiếc quan t&agrave;i đặc biệt được l&agrave;m bằng vật liệu t&aacute;i chế hoặc vật liệu sinh học, nhưng kh&ocirc;ng được trang tr&iacute; hay tốn k&eacute;m. Quan t&agrave;i sẽ được đặt trong một lớp đất mỏng v&agrave; được bao phủ bởi những loại hoa v&agrave; c&acirc;y cối.</p>\r\n\r\n<p>Sau đ&oacute;, người ta sẽ tiến h&agrave;nh đốt quan t&agrave;i v&agrave; thi h&agrave;i bằng nhi&ecirc;n liệu đặc biệt để ph&acirc;n hủy nhanh ch&oacute;ng. Qu&aacute; tr&igrave;nh n&agrave;y chỉ mất v&agrave;i giờ để ho&agrave;n th&agrave;nh. Sau khi ph&acirc;n hủy xong, c&aacute;c tro v&agrave; xương ph&acirc;n hủy sẽ được đặt trong một hộp v&agrave; b&agrave;n giao cho gia đ&igrave;nh của người chết.</p>\r\n\r\n<p>Phương ph&aacute;p cải t&aacute;ng nhanh được sử dụng trong c&aacute;c t&igrave;nh huống khẩn cấp, v&iacute; dụ như khi người chết đ&atilde; chết bởi một căn bệnh truyền nhiễm hay khi người chết phải được ch&ocirc;n cất nhanh ch&oacute;ng để tr&aacute;nh g&acirc;y ra nguy hiểm cho sức khỏe của những người sống s&oacute;t. N&oacute; cũng được sử dụng trong c&aacute;c t&igrave;nh huống khẩn cấp như thảm họa, chiến tranh hoặc khi số lượng người chết qu&aacute; lớn để tiến h&agrave;nh mai t&aacute;ng bằng c&aacute;c phương ph&aacute;p truyền thống.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, phương ph&aacute;p cải t&aacute;ng nhanh kh&ocirc;ng phải l&agrave; một phương ph&aacute;p phổ biến hoặc được sử dụng rộng r&atilde;i trong nền văn h&oacute;a ch&ocirc;n cất của nhiều quốc gia. N&oacute; chỉ được sử dụng trong c&aacute;c t&igrave;nh huống đặc biệt v&agrave; cần thiết.</p>\r\n\r\n<h3><strong>Cải t&aacute;ng sinh học</strong></h3>\r\n\r\n<p>Cải t&aacute;ng sinh học l&agrave; một phương ph&aacute;p ch&ocirc;n cất mới thay thế cho việc mai t&aacute;ng truyền thống. N&oacute; l&agrave; qu&aacute; tr&igrave;nh ph&acirc;n hủy tự nhi&ecirc;n của thi h&agrave;i bằng vi sinh vật v&agrave; kỹ thuật sinh học. Phương ph&aacute;p n&agrave;y được xem l&agrave; một giải ph&aacute;p bảo vệ m&ocirc;i trường v&agrave; bền vững, gi&uacute;p giảm thiểu t&aacute;c động đến đất đai v&agrave; kh&ocirc;ng gian xanh.</p>\r\n\r\n<p>Qu&aacute; tr&igrave;nh cải t&aacute;ng sinh học bắt đầu bằng việc đặt thi h&agrave;i v&agrave;o một chiếc quan t&agrave;i sinh học hoặc t&uacute;i chống thấm. Trong quan t&agrave;i hay t&uacute;i n&agrave;y, người ta sẽ th&ecirc;m v&agrave;o một hỗn hợp vi sinh vật v&agrave; c&aacute;c chất k&iacute;ch th&iacute;ch qu&aacute; tr&igrave;nh ph&acirc;n hủy. Sau đ&oacute;, quan t&agrave;i hoặc t&uacute;i sẽ được đặt trong một khu vực đất đai được đ&agrave;o sẵn.</p>\r\n\r\n<p>Trong qu&aacute; tr&igrave;nh ph&acirc;n hủy, c&aacute;c vi sinh vật sẽ ti&ecirc;u hủy thi h&agrave;i v&agrave; chuyển n&oacute; th&agrave;nh ph&acirc;n b&oacute;n tự nhi&ecirc;n. Qu&aacute; tr&igrave;nh n&agrave;y c&oacute; thể k&eacute;o d&agrave;i từ v&agrave;i th&aacute;ng đến v&agrave;i năm, t&ugrave;y thuộc v&agrave;o c&aacute;c yếu tố như loại đất, nhiệt độ v&agrave; độ ẩm.</p>\r\n\r\n<p>Qu&aacute; tr&igrave;nh cải t&aacute;ng sinh học kh&ocirc;ng chỉ gi&uacute;p giảm thiểu t&aacute;c động đến đất đai v&agrave; kh&ocirc;ng gian xanh, m&agrave; c&ograve;n gi&uacute;p giảm lượng kh&iacute; thải g&acirc;y hiệu ứng nh&agrave; k&iacute;nh v&agrave; c&aacute;c chất độc hại được sinh ra trong qu&aacute; tr&igrave;nh ph&acirc;n hủy của c&aacute;c phương ph&aacute;p ch&ocirc;n cất truyền thống. Đồng thời, phương ph&aacute;p n&agrave;y cũng gi&uacute;p tạo ra ph&acirc;n b&oacute;n tự nhi&ecirc;n gi&uacute;p cải thiện chất lượng đất v&agrave; tăng cường sự sống động của c&aacute;c lo&agrave;i c&acirc;y cối.</p>\r\n\r\n<p>Cải t&aacute;ng sinh học c&ograve;n được xem l&agrave; một giải ph&aacute;p tiết kiệm chi ph&iacute;. Việc kh&ocirc;ng sử dụng quan t&agrave;i v&agrave; lăng mộ gi&uacute;p giảm thiểu chi ph&iacute; v&agrave; gi&uacute;p gia đ&igrave;nh của người chết tiết kiệm được chi ph&iacute; cho mai t&aacute;ng.</p>\r\n\r\n<h3><strong>Cải t&aacute;ng h&ograve;a nhập với thi&ecirc;n nhi&ecirc;n</strong></h3>\r\n\r\n<p>Cải t&aacute;ng h&ograve;a nhập với thi&ecirc;n nhi&ecirc;n l&agrave; một trong những loại h&igrave;nh cải t&aacute;ng mới nhất v&agrave; được ưa chuộng hiện nay. Điểm đặc biệt của phương ph&aacute;p n&agrave;y l&agrave; sự kết hợp giữa việc ch&ocirc;n cất thi h&agrave;i v&agrave; bảo vệ m&ocirc;i trường.</p>\r\n\r\n<p>Theo phương ph&aacute;p cải t&aacute;ng h&ograve;a nhập với thi&ecirc;n nhi&ecirc;n, thi h&agrave;i sẽ được ch&ocirc;n trong một khu vực đất đai được đ&agrave;o sẵn. Tuy nhi&ecirc;n, thay v&igrave; đặt quan t&agrave;i v&agrave; lăng mộ, thi h&agrave;i sẽ được đặt trong một chiếc t&uacute;i bằng vải hoặc vật liệu sinh học, như tre hoặc tre xanh, để ph&acirc;n hủy tự nhi&ecirc;n.</p>\r\n\r\n<p>Sau đ&oacute;, chiếc t&uacute;i sẽ được đặt trong một khu vực đất đai được đ&agrave;o sẵn v&agrave; được bao phủ bởi lớp đất mỏng v&agrave; những loại hoa v&agrave; c&acirc;y cối trang tr&iacute;. Qu&aacute; tr&igrave;nh ph&acirc;n hủy sẽ được gi&aacute;m s&aacute;t v&agrave; kiểm so&aacute;t bởi c&aacute;c chuy&ecirc;n gia để đảm bảo rằng qu&aacute; tr&igrave;nh ph&acirc;n hủy diễn ra một c&aacute;ch an to&agrave;n v&agrave; hợp vệ sinh.</p>\r\n\r\n<p>Phương ph&aacute;p cải t&aacute;ng h&ograve;a nhập với thi&ecirc;n nhi&ecirc;n được xem l&agrave; một giải ph&aacute;p bảo vệ m&ocirc;i trường v&agrave; bền vững. Việc kh&ocirc;ng sử dụng quan t&agrave;i v&agrave; lăng mộ gi&uacute;p giảm thiểu t&aacute;c động đến đất đai v&agrave; kh&ocirc;ng gian xanh, đồng thời gi&uacute;p tạo ra một kh&ocirc;ng gian xanh mới để c&aacute;c lo&agrave;i c&acirc;y v&agrave; động vật sống.</p>\r\n\r\n<p>Ngo&agrave;i ra, phương ph&aacute;p n&agrave;y c&ograve;n được xem l&agrave; một giải ph&aacute;p tiết kiệm chi ph&iacute;, bởi v&igrave; kh&ocirc;ng cần sử dụng quan t&agrave;i v&agrave; lăng mộ. N&oacute; cũng gi&uacute;p cho người th&acirc;n của người chết cảm thấy an y&ecirc;n v&agrave; th&uacute;c đẩy sự tương t&aacute;c giữa con người v&agrave; thi&ecirc;n nhi&ecirc;n.</p>\r\n\r\n<p>Tổng quan, cải t&aacute;ng h&ograve;a nhập với thi&ecirc;n nhi&ecirc;n l&agrave; một giải ph&aacute;p bền vững v&agrave; t&ocirc;n trọng m&ocirc;i trường, gi&uacute;p bảo vệ t&agrave;i nguy&ecirc;n đất đai v&agrave; kh&ocirc;ng gian xanh, đồng thời gi&uacute;p cho người th&acirc;n của người chết cảm thấy y&ecirc;n t&acirc;m v&agrave; an l&ograve;ng.</p>\r\n\r\n<h3><strong>Cải t&aacute;ng theo phong c&aacute;ch trung quốc</strong></h3>\r\n\r\n<p>Cải t&aacute;ng theo phong c&aacute;ch Trung Quốc l&agrave; một trong những loại h&igrave;nh cải t&aacute;ng phổ biến ở Trung Quốc v&agrave; c&aacute;c nước ch&acirc;u &Aacute; kh&aacute;c. Phương ph&aacute;p n&agrave;y thường được sử dụng để t&ocirc;n vinh truyền thống v&agrave; t&acirc;m linh của d&acirc;n tộc.</p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/cải táng 1.jpg\" /></p>\r\n\r\n<p>Quan t&agrave;i n&agrave;y thường được trang tr&iacute; bằng những họa tiết truyền thống, thể hiện sự t&ocirc;n trọng truyền thống v&agrave; t&acirc;m linh của d&acirc;n tộc</p>\r\n\r\n<p>Theo phong tục của người Trung Quốc, khi người chết được cải t&aacute;ng theo phong c&aacute;ch Trung Quốc, thi h&agrave;i sẽ được đặt trong một chiếc quan t&agrave;i đặc biệt được l&agrave;m bằng gỗ. Quan t&agrave;i n&agrave;y thường được trang tr&iacute; bằng những họa tiết truyền thống, đại diện cho sự may mắn v&agrave; t&agrave;i lộc cho người chết trong thế giới b&ecirc;n kia.</p>\r\n\r\n<p>Sau khi quan t&agrave;i được trang tr&iacute; xong, n&oacute; sẽ được đưa đến nơi ch&ocirc;n cất. Người ta sẽ tiến h&agrave;nh một số nghi lễ truyền thống như đốt những c&acirc;y nhang v&agrave; hương thắp s&aacute;ng, cầu nguyện cho linh hồn của người chết được an b&igrave;nh v&agrave; vui vẻ.</p>\r\n\r\n<p>Sau khi nghi lễ được ho&agrave;n tất, quan t&agrave;i sẽ được đưa xuống hầm mộ được đ&agrave;o sẵn. Sau đ&oacute;, người th&acirc;n của người chết sẽ đổ đất l&ecirc;n quan t&agrave;i v&agrave; trang tr&iacute; nơi ch&ocirc;n cất bằng những loại hoa v&agrave; c&acirc;y cối truyền thống.</p>\r\n\r\n<p>Cải t&aacute;ng theo phong c&aacute;ch Trung Quốc được xem l&agrave; một phương ph&aacute;p t&ocirc;n trọng truyền thống v&agrave; t&acirc;m linh của d&acirc;n tộc. Phương ph&aacute;p n&agrave;y c&ograve;n được xem l&agrave; một dịch vụ cao cấp v&agrave; tốn k&eacute;m hơn so với c&aacute;c phương ph&aacute;p cải t&aacute;ng kh&aacute;c.</p>\r\n\r\n<h2><strong>Lợi &iacute;ch của cải t&aacute;ng</strong></h2>\r\n\r\n<p>Bảo vệ m&ocirc;i trường: Cải t&aacute;ng gi&uacute;p giảm thiểu t&aacute;c động đến m&ocirc;i trường v&agrave; giảm thiểu sự t&agrave;n ph&aacute; đất đai. N&oacute; cũng gi&uacute;p giảm thiểu kh&iacute; thải độc hại v&agrave; nước thải độc hại.</p>\r\n\r\n<p>Tiết kiệm chi ph&iacute;: Cải t&aacute;ng gi&uacute;p tiết kiệm chi ph&iacute; so với mai t&aacute;ng truyền thống. Việc sử dụng đất v&agrave; c&aacute;c vật liệu x&acirc;y dựng như quan t&agrave;i v&agrave; lăng mộ khiến chi ph&iacute; mai t&aacute;ng truyền thống cao hơn nhiều so với cải t&aacute;ng.</p>\r\n\r\n<p>T&ocirc;n trọng gi&aacute; trị t&acirc;m linh v&agrave; t&ocirc;n gi&aacute;o: Cải t&aacute;ng gi&uacute;p t&ocirc;n trọng gi&aacute; trị t&acirc;m linh v&agrave; t&ocirc;n gi&aacute;o của c&aacute;c d&acirc;n tộc. Nhiều người tin rằng, ch&ocirc;n cất theo phương ph&aacute;p cải t&aacute;ng sẽ gi&uacute;p cho linh hồn của người chết được giải tho&aacute;t một c&aacute;ch tốt đẹp, cũng như đảm bảo sự an y&ecirc;n v&agrave; t&igrave;nh thần b&igrave;nh định cho gia đ&igrave;nh của họ.</p>\r\n\r\n<p>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/\">Hoa vi&ecirc;n nghĩa trang Thi&ecirc;n Đường</a></p>\r\n\r\n<h2><strong>Kết luận</strong></h2>\r\n\r\n<p><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang\">Cải t&aacute;ng</a> l&agrave; một phương ph&aacute;p ch&ocirc;n cất mới thay thế cho việc mai t&aacute;ng truyền thống. N&oacute; gi&uacute;p giảm thiểu t&aacute;c động đến m&ocirc;i trường, t&ocirc;n trọng gi&aacute; trị t&acirc;m linh v&agrave; t&ocirc;n gi&aacute;o c&ugrave;ng những lợi &iacute;ch kinh tế. C&oacute; nhiều loại h&igrave;nh cải t&aacute;ng phổ biến hiện nay, bao gồm cải t&aacute;ng nhanh, cải t&aacute;ng sinh học v&agrave; cải t&aacute;ng h&ograve;a nhập với thi&ecirc;n nhi&ecirc;n. Việc sử dụng cải t&aacute;ng cũng gi&uacute;p bảo vệ m&ocirc;i trường, tiết kiệm chi ph&iacute; v&agrave; t&ocirc;n trọng gi&aacute; trị t&acirc;m linh v&agrave; t&ocirc;n gi&aacute;o của c&aacute;c d&acirc;n tộc. Việc lựa chọn cải t&aacute;ng thay cho mai t&aacute;ng truyền thống sẽ gi&uacute;p ch&uacute;ng ta g&oacute;p phần v&agrave;o việc bảo vệ m&ocirc;i trường v&agrave; tạo n&ecirc;n một tương lai bền vững cho thế hệ tương lai.</p>\r\n\r\n<p>Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline&nbsp;0934.524.445 hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\">https://nghiatrangthienduong.com.vn/</a>.</p>', 'true', 'News', 'cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang', 'cải táng.jpg', 'Cải táng là gì? Những điều cần biết về phong tục cải táng', 'Cải táng là một phương pháp chôn cất mới thay thế cho việc mai táng truyền thống. Nó được sử dụng nhằm giảm thiểu tác động đến môi trường, tôn trọng giá trị tâm linh và tôn giáo cùng những lợi ích kinh tế.', '2023-12-25 15:29:43', '2023-12-25 15:32:02');
INSERT INTO `posts` (`id`, `user_id`, `category_id`, `name`, `detail`, `info`, `content`, `status`, `sort_by`, `slug`, `img`, `title`, `description`, `created_at`, `updated_at`) VALUES
(200, 2, 68, 'Lưu ý khi bốc mộ', 'Khi bốc mộ sang cát – cải táng mộ phần cần chú ý những điều gì? Hãy cùng tìm hiểu nhé.', NULL, '<h2><strong>Khi <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/luu-y-khi-boc-mo\">bốc mộ</a> sang c&aacute;t &ndash; <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang\">cải t&aacute;ng</a> mộ phần cần ch&uacute; &yacute; những điều g&igrave;?</strong></h2>\r\n\r\n<p>Trong thực tế hiện nay, c&aacute;ch bốc mộ hay sang c&aacute;t mồ mả cũng như c&aacute;c thủ tục <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang\">cải t&aacute;ng</a> mộ phần đang l&agrave; vấn đề băn khoăn v&agrave; đ&aacute;ng lo ngại của nhiều gia đ&igrave;nh. Việc lựa chọn c&aacute;ch thức bốc mả hay cải t&aacute;ng cho ng&ocirc;i mộ phải đ&uacute;ng v&agrave; hợp với phong thủy, c&oacute; như vậy th&igrave; mới tốt. Bởi lẽ, như ch&uacute;ng ta ai cũng biết mộ l&agrave; nơi an nghỉ cuối c&ugrave;ng cho những người đ&atilde; khuất. Cả một đời người d&ugrave; c&oacute; sống trong cảnh gi&agrave;u sang, nhung lụa, nh&agrave; cửa, đất đai v&agrave; v&ocirc; v&agrave;n những t&agrave;i sản c&oacute; gi&aacute; trị kh&aacute;c hay ngh&egrave;o h&egrave;n, vất vả, tay trắng th&igrave; khi mất đi cũng chỉ c&oacute; vỏn vẹn mấy m&eacute;t vu&ocirc;ng nấm mồ để ch&ocirc;n cất thi thể, l&agrave; nơi y&ecirc;n nghỉ khi về với &acirc;m thế. Bởi vậy mới n&oacute;i, tiền bạc, vật chất c&oacute; thể chuyển nhượng, sang t&ecirc;n, mua b&aacute;n nhưng mồ mả của &ocirc;ng b&agrave; tổ ti&ecirc;n th&igrave; vĩnh viễn kh&ocirc;ng thể thay đổi được. Nghi&ecirc;n cứu về mặt t&acirc;m linh, nhiều người cho rằng việc&nbsp;bốc mộ, sang c&aacute;t hay <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang\">cải t&aacute;ng mộ phần</a> c&oacute; ảnh hưởng rất nhiều đến người đ&atilde; khuất cũng như cuộc sống của thế hệ đi sau.</p>\r\n\r\n<p>Vậy, khi gia đ&igrave;nh c&oacute; việc sang c&aacute;t, <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/luu-y-khi-boc-mo\">bốc mộ</a> ch&uacute;ng ta cần phải nắm được những kiến thức quan trọng g&igrave;, qua đ&oacute; cần đặc biệt ch&uacute; &yacute; những điều g&igrave; để c&ocirc;ng việc được diễn ra một c&aacute;ch &ecirc;m xu&ocirc;i, thuận lợi nhất.</p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/cái táng.jpg\" /></p>\r\n\r\n<h3><strong>1. Tiến h&agrave;nh quan s&aacute;t v&agrave; thực hiện c&aacute;c thao t&aacute;c kiểm tra mộ phần</strong></h3>\r\n\r\n<p>Trước khi ch&uacute;ng ta muốn bốc mộ sang c&aacute;t hay <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang\">cải t&aacute;ng mộ phần</a> th&igrave; việc đầu ti&ecirc;n kh&ocirc;ng thể thiếu đ&oacute; ch&iacute;nh l&agrave; tiến h&agrave;nh quan s&aacute;t v&agrave; thực hiện c&aacute;c thao t&aacute;c kiểm tra xem mộ phần đ&oacute; như thế n&agrave;o? C&oacute; nghĩa l&agrave;, phải x&eacute;t xem mộ phần đ&oacute; đ&atilde; đủ thời gian để l&agrave;m cải t&aacute;ng hay chưa. Nếu chưa đủ cần phải đợi đến một thời điểm th&iacute;ch hợp nhất định mới c&oacute; thể bắt đầu tiến h&agrave;nh c&aacute;c thủ tục lễ nghi sang c&aacute;t, thường người ta lấy năm l&agrave;m đơn vị thang đo. Tiếp đ&oacute; cần ch&uacute; &yacute; xem ng&ocirc;i mộ c&oacute; kết hay kh&ocirc;ng, liệu rằng thịt đ&atilde; ti&ecirc;u hết hay chưa?</p>\r\n\r\n<p>Thế n&agrave;o l&agrave;&nbsp;mộ kết? Giải th&iacute;ch một c&aacute;ch đơn giản, mộ kết l&agrave; ng&ocirc;i mộ đ&atilde; hấp thụ được Linh Kh&iacute; của Long Mạch cũng như đ&oacute;n nhận được Ph&uacute;c Trạch của &Acirc;m Phần. Việc t&iacute;ch tụ kh&iacute; tốt l&agrave;nh trong ng&ocirc;i mộ sinh ra kết ph&aacute;t, nhờ vậy m&agrave; đem đến những điều may mắn cho cuộc sống của con ch&aacute;u, cả d&ograve;ng họ được hưởng nhiều ph&uacute;c lộc, gia đ&igrave;nh h&ograve;a thuận, hạnh ph&uacute;c, sức khỏe dồi d&agrave;o, c&ocirc;ng chuyện l&agrave;m ăn thuận lợi, su&ocirc;n sẻ, gia đạo mọi bề đều tốt đẹp, như &yacute;.</p>\r\n\r\n<p>Trong trường hợp m&agrave; chưa ti&ecirc;u thịt, c&oacute; thể do một trong 3 &nbsp;nguy&ecirc;n nh&acirc;n chủ yếu sau đ&acirc;y: phạm tr&ugrave;ng, ch&ocirc;n tại khu đất hiếm kh&iacute; hay người chết do mắc c&aacute;c bệnh nặng phải điều trị v&agrave; sử dụng nhiều thuốc trị liệu v&agrave; h&oacute;a chất n&ecirc;n kh&oacute; ti&ecirc;u. Đối với cả 3 trường hợp tr&ecirc;n n&ecirc;n kh&ocirc;ng dịch chuyển, bốc dời, chuyển đi ngay m&agrave; phải chờ đến khi n&agrave;o ti&ecirc;u hết.</p>\r\n\r\n<p>Muốn kiểm tra xem đ&atilde; đến thời điểm th&iacute;ch hợp để <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang\">tiến h&agrave;nh cải t&aacute;ng</a> v&agrave; quy tập mộ người ta thường sử dụng thuật xem b&oacute;i. Tức hầu hết c&aacute;c gia đ&igrave;nh đều đến nhờ c&aacute;c thầy c&uacute;ng để xem b&oacute;i, t&iacute;nh to&aacute;n ch&iacute;nh x&aacute;c ng&agrave;y giờ tốt l&agrave;nh qua c&aacute;c phương ph&aacute;p: ngoại cảm, cảm xạ, bấm &aacute;m độn, cảm nhận trường kh&iacute;,.. Qua đ&oacute;, người ta sẽ dự đo&aacute;n được mộ phần n&agrave;y đ&atilde; kết ph&aacute;t hay thịt đ&atilde; ti&ecirc;u hết hay chưa.</p>\r\n\r\n<p>Ngo&agrave;i ra, ch&uacute;ng ta c&oacute; thể tự m&igrave;nh kiểm tra bằng một số biện ph&aacute;p như sau: Thực tế cho thấy, ta c&oacute; thể kiểm tra mộ kết th&ocirc;ng qua việc quan s&aacute;t bằng mắt thường. Do được t&iacute;ch tụ Linh Kh&iacute; của Long Mạch cho n&ecirc;n ng&ocirc;i mộ sẽ ng&agrave;y c&agrave;ng nở ra, nh&ocirc; l&ecirc;n tr&ecirc;n khỏi mặt đất trong như những c&aacute;i g&ograve; mấp m&ocirc; hay như c&aacute;c tổ mối đ&ugrave;n. Hiện tượng dư khi của long mạch v&agrave; &acirc;m phần kết ph&aacute;t sẽ gi&uacute;p cho mộ phần th&ecirc;m chắc chắn, ph&aacute;t nở hơn trước khi ch&ocirc;n. Mặt kh&aacute;c, c&oacute; thể dễ d&agrave;ng nhận thấy c&aacute;c ng&ocirc;i mộ kết thường c&oacute; cỏ mọc phủ k&iacute;n xanh tốt.</p>\r\n\r\n<p>Chẳng hạn như, ở H&agrave; Tĩnh c&oacute; một ng&ocirc;i mộ kết, khi con ch&aacute;u đi thăm mộ, l&uacute;c về họ vứt c&aacute;c c&agrave;nh hoa c&uacute;c ra ven mộ, chỉ v&agrave;i ng&agrave;y sau đ&oacute; những đoạn th&acirc;n hoa c&uacute;c n&agrave;y đ&atilde; mọc ra một kh&oacute;m c&uacute;c nở rộ, tươi tốt. Người xưa cũng thường sử dụng phương ph&aacute;p n&agrave;y để x&aacute;c định xem c&oacute; Huyệt kết hay chưa bằng c&aacute;ch l&agrave;m cụ thể như sau: Ta cắm c&aacute;c c&agrave;nh c&acirc;y kh&ocirc; v&agrave;o những v&ugrave;ng đất được nghi l&agrave; c&oacute; mộ kết, nếu như sau một thời gian m&agrave; nhận thấy c&agrave;nh kh&ocirc; n&agrave;y nảy nở mầm xanh v&agrave; ph&aacute;t triển tươi tốt th&igrave; cũng đồng nghĩa với việc nơi n&agrave;y c&oacute; huyệt kết v&agrave; ngược lại.B&ecirc;n cạnh đ&oacute;, một g&oacute;c nh&igrave;n kh&aacute;c, ta c&oacute; thể quan s&aacute;t những vi&ecirc;n đ&aacute;, bia mộ tại huyệt, nếu như những vi&ecirc;n đ&aacute; v&agrave; bia mộ đ&oacute; b&oacute;ng lo&aacute;ng, sạch sẽ như được phủ một lớp dầu b&oacute;ng hay lau ch&ugrave;i thường xuy&ecirc;n th&igrave; chắc chắn rằng mộ n&agrave;y đ&atilde; kết. Đặc biệt nhớ rằng, trong trường hợp mộ kết th&igrave; tuyệt đối kh&ocirc;ng được dịch chuyển, nếu kh&ocirc;ng sẽ g&acirc;y ra những rắc rối lớn, điều dữ cho d&ograve;ng họ l&agrave; kh&oacute; tr&aacute;nh khỏi.</p>\r\n\r\n<p>Nếu như buộc phải di dời v&agrave; chuyển dịch mồ mả v&igrave; l&yacute; do n&agrave;o đ&oacute; kh&ocirc;ng thể kiểm so&aacute;t được, người ta thường sử dụng c&aacute;c phương ph&aacute;p của Huyền M&ocirc;n v&agrave; theo c&aacute;c nguy&ecirc;n tắc trong Phong Thủy. Sau một qu&aacute; tr&igrave;nh thực hiện kh&aacute; phức tạp mới c&oacute; thể tiến h&agrave;nh di chuyển được. Nếu mộ kết thường bắt đầu kết từ ch&acirc;n cho tới đầu, một số ng&ocirc;i mộ do kết cấu mộ hay Long Mạch m&agrave; chiều hướng kết c&oacute; thể ngược lại. Trong thực tế, kết c&oacute; thể ở một trong c&aacute;c dạng sau: kết tơ hồng, kết mạng nhện, kết chu sa hay kết băng,.. Theo đ&oacute;, mỗi dạng kết sẽ c&oacute; c&aacute;c m&agrave;u sắc kh&aacute;c nhau từ x&aacute;m cho đến m&agrave;u trắng, m&agrave;u đỏ hay m&agrave;u hồng. Trong số đ&oacute;, d&acirc;n gian ta cho rằng m&agrave;u đỏ như chu sa l&agrave; loại mạnh nhất.</p>\r\n\r\n<p>Đối với ng&ocirc;i mộ chưa ti&ecirc;u hết thịt đ&atilde; phải bốc đi v&igrave; một l&yacute; do kh&aacute;ch quan n&agrave;o đ&oacute; th&igrave; sẽ phải ch&uacute; &yacute; đến một số điều sau:</p>\r\n\r\n<p>Thứ nhất, nếu ch&oacute;t bốc l&ecirc;n m&agrave; mộ chưa ti&ecirc;u th&igrave; phải lắp lại khi đắp mộ, nhưng chỉ để c&ocirc;ng k&ecirc;nh cho kh&ocirc;ng kh&iacute; v&agrave;o mộ để ti&ecirc;u hết thịt.</p>\r\n\r\n<p>Thứ hai, nếu như kh&ocirc;ng phải kết ph&aacute;t, c&ograve;n nguy&ecirc;n h&igrave;nh th&igrave; khi đắp lại mộ ta n&ecirc;n lắp lại v&agrave; cần để bớt ra một số lỗ th&ocirc;ng tho&aacute;ng từ v&aacute;n thi&ecirc;n tới n&oacute;c phần ngang mộ gi&uacute;p kh&iacute; v&agrave;o mộ. Bằng c&aacute;ch, trước khi lấp mộ cắm một số c&acirc;y tre theo c&aacute;c vị tr&iacute; từ v&aacute;n thi&ecirc;n tới n&oacute;c phần ngang mộ, sau khi đắp xong, r&uacute;t tre ra để cho kh&ocirc;ng kh&iacute; v&agrave;o mộ ti&ecirc;u hết thịt c&ograve;n lại. Việc bốc mộ sẽ tiếp tục để sang năm tới.</p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/OIP.jpg\" /></p>\r\n\r\n<p>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang\">Cải t&aacute;ng l&agrave; g&igrave;? Những điều cần biết về phong tục cải t&aacute;ng&nbsp;</a></p>\r\n\r\n<h3><strong>2. Thời gian th&iacute;ch hợp để <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/luu-y-khi-boc-mo\">bốc mộ</a></strong></h3>\r\n\r\n<p>Việc lựa chọn thời điểm tốt l&agrave;nh để cải t&aacute;ng v&ocirc; c&ugrave;ng quan trọng, ch&iacute;nh v&igrave; vậy phải xem x&eacute;t thật kỹ lưỡng, cẩn thận để chọn ng&agrave;y giờ cho ch&iacute;nh x&aacute;c. Việt Nam ta từ xưa đến nay lu&ocirc;n c&oacute; phong tục người đ&atilde; khuất sau m&atilde;n tang th&igrave; sẽ cải t&aacute;ng, điều n&agrave;y cũng đồng nghĩa rằng hết để tang vong linh. Do đ&oacute;, người ta thường để &iacute;t nhất l&agrave; khoảng 30 th&aacute;ng sau khi ch&ocirc;n cất mới tiến h&agrave;nh l&agrave;m lễ cải t&aacute;ng. Đ&acirc;y l&agrave; thời điểm th&iacute;ch hợp nhất v&igrave; trong khoảng thời gian n&agrave;y mộ kết v&agrave; c&oacute; thể tiến h&agrave;nh bốc mộ.</p>\r\n\r\n<p>Chọn năm để tiến h&agrave;nh cải t&aacute;ng phải hợp với tuổi của vong, tr&aacute;nh xung khắc với bản mệnh cũng như tr&aacute;nh phạm phải c&aacute;c năm xung s&aacute;t. B&ecirc;n cạnh đ&oacute;, người ta c&ograve;n t&iacute;nh năm dựa tr&ecirc;n tuổi của trưởng nam trong gia đ&igrave;nh để dự đo&aacute;n năm ch&iacute;nh x&aacute;c, th&iacute;ch hợp nhất. Bởi lẽ, nhiều người cho rằng khi vong đ&atilde; mất th&igrave; mọi sự may rủi, l&agrave;nh dữ đều g&aacute;nh tr&ecirc;n vai người trưởng nam. Theo đ&oacute;, năm để tiến h&agrave;nh cải t&aacute;ng cũng phải hợp với tuổi v&agrave; bản mệnh của nam trưởng trong nh&agrave;.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, tr&ecirc;n thực tế hiện nay, m&ocirc;i trường địa l&yacute; với thời tiết kh&iacute; hậu lu&ocirc;n lu&ocirc;n c&oacute; sự biến đổi, c&aacute;c h&oacute;a chất, thuốc s&acirc;u độc hại được sử dụng trong sản xuất n&ocirc;ng nghiệp nhiều l&agrave;m &ocirc; nhiễm nguồn đất trầm trọng. Sau khoảng thời gian l&agrave; 30 th&aacute;ng x&aacute;c tử vong chưa được ph&acirc;n h&oacute;a ho&agrave;n to&agrave;n l&agrave; điều đang diễn ra kh&aacute; phổ biến, cho n&ecirc;n, để tr&aacute;nh hiện tượng tr&ecirc;n nhiều gia đ&igrave;nh đ&atilde; thay v&igrave; để trong 3 năm c&oacute; thể l&ecirc;n đến 4 hay 5 năm hoặc l&acirc;u hơn mới tiến h&agrave;nh cải t&aacute;ng.</p>\r\n\r\n<p>Khi đ&atilde; chọn được năm th&iacute;ch hợp, người ta mới t&iacute;nh đến việc lựa chọn th&aacute;ng tốt trong năm. Th&ocirc;ng thường c&aacute;c th&aacute;ng được chọn sẽ thuộc trong phạm vi từ cuối thu đến trước ng&agrave;y Đ&ocirc;ng Ch&iacute; của năm. Người ta hay hạn chế v&agrave; tr&aacute;nh cải t&aacute;ng v&agrave;o sau Đ&ocirc;ng Ch&iacute; cũng như c&aacute;c ng&agrave;y đầu năm. Sau khi đ&atilde; chọn được th&aacute;ng, tiếp đến cần&nbsp;xem ng&agrave;y tốt&nbsp;v&agrave; chọn giờ ho&agrave;ng đạo để bốc mộ v&agrave; sang c&aacute;t tạo huyệt mới. Cần ch&uacute; &yacute; n&ecirc;n chọn ng&agrave;y giờ tr&aacute;nh tuổi của người khuất v&agrave; con trưởng, cũng như c&aacute;c ng&agrave;y giờ xấu, đại kỵ, b&aacute;ch kỵ, c&oacute; nhiều Sao Xấu kh&ocirc;ng tốt.</p>\r\n\r\n<p>Th&ocirc;ng thường ban ng&agrave;y người ta chỉ tiến h&agrave;nh l&agrave;m lễ phạt nấm, tiếp sau đ&oacute; đợi đ&uacute;ng đến giờ tốt mới bắt đầu l&agrave;m c&aacute;c c&ocirc;ng việc v&agrave; mở tấm v&aacute;n Thi&ecirc;n &ndash; tức nắp v&aacute;n tr&ecirc;n n&oacute;c của quan t&agrave;i. Vong hồn kh&ocirc;ng thể tiếp x&uacute;c với &aacute;nh nắng mặt trời, do vậy, việc bốc mộ phải tiến h&agrave;nh v&agrave;o ban đ&ecirc;m khi trời đ&atilde; tối v&agrave; theo khung giờ đ&atilde; định từ trước đ&oacute;. Ngo&agrave;i ra, việc thực hiện bốc mả v&agrave;o ban đ&ecirc;m cũng tr&aacute;nh cho xương cốt sẽ bị đen đi khi c&oacute; &aacute;nh s&aacute;ng chiếu v&agrave;o.</p>\r\n\r\n<h3><strong>3. Lựa chọn vị tr&iacute; tốt để đặt mộ</strong></h3>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/z4739939583777_ed37970f5389ec145ff423bd67dab831.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><a href=\"https://nghiatrangthienduong.com.vn/\"><em>H&igrave;nh ảnh c&ocirc;ng vi&ecirc;n nghĩa trang Thi&ecirc;n Đường</em></a></p>\r\n\r\n<p>Sau khi đ&atilde; chọn lựa xong xu&ocirc;i thời điểm tiến h&agrave;nh th&igrave; việc m&agrave; những người trong gia đ&igrave;nh cần phải l&agrave;m đ&oacute; ch&iacute;nh l&agrave; xem x&eacute;t v&agrave; chọn ra một vị tr&iacute; huyệt đất mới để di dời, chuyển dịch h&agrave;i cốt. Kh&ocirc;ng phải đất n&agrave;o cũng an t&aacute;ng hay lập mộ phần vĩnh cửu được cho n&ecirc;n việc t&igrave;m ra một huyệt đất vừa thuận về thi&ecirc;n hướng lại vừa hợp với mệnh vong l&agrave; điều quan trọng kh&ocirc;ng thể l&agrave;m qua loa cho xong được. Như ch&uacute;ng ta cũng thấy, ng&agrave;y xưa, với điều kiện đất đai rộng r&atilde;i, nhiều diện t&iacute;ch đất kh&ocirc;ng d&ugrave;ng đến n&ecirc;n c&ocirc;ng việc chọn m&ocirc; đất cũng kh&aacute; dễ d&agrave;ng. C&ograve;n trong thời đại ng&agrave;y nay, nh&agrave; cao cửa rộng, cơ sở hạ tầng, đất chật người đ&ocirc;ng, diện t&iacute;ch đất trống bị thu hẹp lại theo đ&oacute; diện t&iacute;ch đất d&agrave;nh để lập mộ cũng hạn chế hơn. Ở c&aacute;c địa phương, mỗi gia đ&igrave;nh sẽ được bố tr&iacute; một huyệt đất mới ở c&ugrave;ng <a href=\"https://nghiatrangthienduong.com.vn/\">NGHĨA TRANG</a>&nbsp;&ndash; nơi tiến h&agrave;nh hung t&aacute;ng. Mặt kh&aacute;c, đối với những khu đất m&agrave; địa phương an b&agrave;i kh&ocirc;ng được tốt th&igrave; cần phải tiến h&agrave;nh dẫn long tụ kh&iacute; để tăng th&ecirc;m nguồn kh&iacute; lợi, loại bỏ bớt mặt xấu của n&oacute;. Trong c&aacute;c gia đ&igrave;nh m&agrave; c&oacute; điều kiện, họ sẽ thu&ecirc; thầy b&oacute;i huyệt để lựa chọn một khu huyệt mộ ưng &yacute;, tốt nhất cho vong nh&acirc;n người đ&atilde; khuất. Hoặc lập thế tụ long tạo trạch đối với v&ugrave;ng đất m&agrave; địa phương đ&atilde; giao cho để x&acirc;y dựng phần mộ.</p>\r\n\r\n<p>C&ograve;n đối với c&aacute;c gia đ&igrave;nh b&igrave;nh thường, kh&ocirc;ng c&oacute; điều kiện lựa chọn, phải bắt buộc ch&ocirc;n trong v&ugrave;ng đất được giao th&igrave; cần ch&uacute; &yacute; một số điều sau đ&acirc;y:</p>\r\n\r\n<p>Thứ nhất,&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">v&ugrave;ng đất</a> được chọn để tiến h&agrave;nh huyệt mộ</p>\r\n\r\n<p>Phải l&agrave; nơi đất mới, chưa từng bị đ&agrave;o xới, bới tung hay chưa từng ch&ocirc;n lấp l&agrave; tốt nhất. Mặt đất bằng phẳng, rắn chắc v&agrave; tươi tốt sẽ gi&uacute;p cho kh&iacute; huyệt dồi d&agrave;o, t&iacute;ch tụ v&agrave; dẫn luồng kh&iacute; tốt. Đối với v&ugrave;ng đồng bằng, đất phải xốp mịn, c&oacute; m&ugrave;i thơm v&agrave; khi đ&agrave;o s&acirc;u từ 6 đến 7 cm th&igrave; đất phải đặc qu&aacute;nh lại, c&oacute; m&agrave;u n&acirc;u đậm hay v&agrave;ng nhạt th&igrave; tốt, nếu kh&ocirc;ng cũng phải c&ugrave;ng m&agrave;u với đất ở khu vực được chọn. C&ograve;n đối với v&ugrave;ng n&uacute;i cao, đất cần c&oacute; độ mịn m&agrave;ng, m&agrave;u mỡ nhất định, kh&ocirc;ng được chọn v&ugrave;ng diện t&iacute;ch đất qu&aacute; kh&ocirc; hay c&oacute; m&agrave;u v&agrave;ng nhạt, như vậy sẽ kh&ocirc;ng tốt.</p>\r\n\r\n<p>Thứ hai,&nbsp;lựa chọn huyệt đ&agrave;o ph&ugrave; hợp.</p>\r\n\r\n<p>Đ&oacute; phải l&agrave; v&ugrave;ng c&oacute; đất tươi xốp, m&agrave;u mỡ, mềm mịn, kh&ocirc;ng qu&aacute; kh&ocirc; cứng, như vậy sẽ tốt cho xương. Trong trường hợp nếu đ&agrave;o l&ecirc;n thấy đ&aacute;y huyệt c&oacute; mạch nước ngầm chảy xiết th&igrave; tuyệt đối cấm kỵ kh&ocirc;ng được đặt mộ v&igrave; rất dễ tr&ocirc;i mất tiểu, mất mộ. Trừ khi x&aacute;c định được d&ograve;ng nước n&agrave;y l&agrave; &ldquo;tụ huyệt long thủy lộ&rdquo; th&igrave; c&oacute; thể chấp nhận được. Nếu đ&agrave;o thấy c&oacute; &iacute;t nước th&igrave; tốt, nhưng cần ch&uacute; &yacute; đến d&ograve;ng chảy của nước kh&ocirc;ng được chảy xiết, hơn nữa, nước phải trong suốt, kh&ocirc;ng đục ng&agrave;u, kh&ocirc;ng c&oacute; m&ugrave;i h&ocirc;i tanh hay m&ugrave;i kh&oacute; ngửi. Đối với v&ugrave;ng đồng bằng, dưới huyệt chỉ n&ecirc;n c&oacute; &iacute;t nước, c&oacute; thể ch&ocirc;n cất ngay b&ecirc;n cạnh nhưng tuyệt đối kh&ocirc;ng ch&ocirc;n cất đ&egrave; l&ecirc;n huyệt cũ.</p>\r\n\r\n<p>Thứ ba,&nbsp;v&ugrave;ng diện t&iacute;ch đất d&agrave;nh cho c&aacute;c khu <a href=\"https://nghiatrangthienduong.com.vn/\">nghĩa trang</a> &ndash; nơi quy lập,</p>\r\n\r\n<p>Ch&ocirc;n cất v&agrave; x&acirc;y dựng c&aacute;c ng&ocirc;i mộ thường xảy ra t&igrave;nh trạng thiếu đất, qu&aacute; tải trầm trọng g&acirc;y n&ecirc;n hiện tượng c&aacute;c mộ chen lấn nhau. Khi chọn huyệt cần tr&aacute;nh c&aacute;c mộ xung quanh đ&atilde; lấn chiếm trước đ&oacute; để tr&aacute;nh t&igrave;nh trạng mộ đ&egrave; n&ecirc;n mộ. Khi lập mộ cần tr&aacute;nh việc c&aacute;c mộ kh&aacute;c chọc v&agrave;o ngay trước phần mộ nơi ch&ocirc;n cất hoặc đ&acirc;m xuy&ecirc;n v&agrave;o hai b&ecirc;n mộ. Tốt nhất n&ecirc;n chọn huyệt sao cho bề trước rộng r&atilde;i, tho&aacute;ng đ&atilde;ng lại c&oacute; hướng nh&igrave;n ra ao hồ, s&ocirc;ng suối để đ&oacute;n kh&ocirc;ng kh&iacute; tươi m&aacute;t, trong l&agrave;nh. Trong trường hợp diện t&iacute;ch đất qu&aacute; hẹp kh&ocirc;ng đủ để ph&iacute;a trước rộng tho&aacute;ng th&igrave; &iacute;t nhất cũng phải chừa ra được một khoảng trống nhỏ trước huyệt.</p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên đơn/đơn 2.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><a href=\"https://nghiatrangthienduong.com.vn/san-pham/khuon-vien-don\"><em>Khu&ocirc;n vi&ecirc;n mộ đơn tại Thi&ecirc;n Đường&nbsp;</em></a></p>\r\n\r\n<p>Thứ tư,&nbsp;cần phải tiến h&agrave;nh quan s&aacute;t cẩn thận v&agrave; kỹ lưỡng to&agrave;n bộ hệ thống đường dẫn xung quanh huyệt mộ.</p>\r\n\r\n<p>Nếu đường đi đ&acirc;m sang hai b&ecirc;n cũng như đ&acirc;m thẳng ch&iacute;nh diện của huyệt tức chủ yếu về ph&aacute; bại, tuyệt đối kh&ocirc;ng d&ugrave;ng. Theo đ&oacute;, đường đi m&agrave; theo s&aacute;t ngay ph&iacute;a sau huyệt mộ cũng kh&ocirc;ng thể sử dụng v&igrave; n&oacute; chủ về tổn hại, ắt sinh điềm bất lợi. Th&ocirc;ng thường, người ta hay chọn huyệt ở những nơi y&ecirc;n tĩnh, th&ocirc;ng tho&aacute;ng, c&oacute; khoảng c&aacute;ch tương đối so với c&aacute;c đường đi, lối dẫn qua lại quanh khu vực mộ phần.</p>\r\n\r\n<p>Thứ năm,&nbsp;ri&ecirc;ng đối với v&ugrave;ng n&uacute;i non cần phải kiểm tra, thẩm định huyệt theo một số c&aacute;c ti&ecirc;u ch&iacute; địa l&yacute; đ&atilde; x&aacute;c định.</p>\r\n\r\n<p>Tốt nhất n&ecirc;n chọn huyệt ở những nơi c&oacute; long hổ hai b&ecirc;n bao bọc, c&oacute; cao sơn che chắn vững chắc ph&iacute;a sau, trước hướng minh đường thủy tụ.</p>\r\n\r\n<p>Tiến h&agrave;nh l&agrave;m lễ v&agrave; lựa chọn ng&agrave;y giờ tốt l&agrave;nh, sau khi chọn được v&ugrave;ng đất c&oacute; vị tr&iacute; th&iacute;ch hợp th&igrave; c&oacute; thể bắt đầu tiến h&agrave;nh x&acirc;y cất cho ng&ocirc;i mộ.</p>\r\n\r\n<p>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/kiem-tien-ty-tu-dau-tu-dat-nghia-trang\">Kiếm tiền tỷ từ đầu tư đất nghĩa trang</a></p>\r\n\r\n<h3><strong>4. Khi cải t&aacute;ng cần phải chuẩn bị những vật liệu g&igrave;?</strong></h3>\r\n\r\n<p>Trước ti&ecirc;n l&agrave; chọn Tiểu v&agrave; Qu&aacute;ch: T&ugrave;y thuộc v&agrave;o từng điều kiện kinh tế mỗi gia đ&igrave;nh m&agrave; c&oacute; thể lựa chọn bằng c&aacute;c vật liệu kh&aacute;c nhau như s&agrave;nh, sứ, đ&aacute;, xi măng, gỗ&hellip; Tiếp đ&oacute; cần phải chuẩn bị một tấm bạt che, d&ugrave;ng đ&egrave;n để chiếu s&aacute;ng, 1 tấm vải đỏ, rượu, một chiếc chậu rửa xương, khăn lau, ni l&ocirc;ng hay tấm b&igrave;a trải để đựng xương, một chiếc rổ to, đun nhiều nước vang (nước ngũ vị hương) v&agrave; một số vật dụng cần thiết kh&aacute;c nữa. Ngo&agrave;i ra, c&ograve;n căn cứ v&agrave;o tục lệ từng địa phương cũng như từng điều kiện ho&agrave;n cảnh để chuẩn bị th&ecirc;m một số vật liệu. Chẳng hạn như, trong trường hợp mộ phần để cũng nhiều năm rồi m&agrave; khi bốc l&ecirc;n vẫn chưa ti&ecirc;u hết th&igrave; c&oacute; thể sử dụng thuốc d&atilde; thịt để tr&aacute;nh phải d&oacute;c thịt ra khỏi xương. C&oacute; thể mua loại thuốc n&agrave;y tại c&aacute;c nh&agrave; tang lễ.</p>\r\n\r\n<p>Một điều quan trọng đặc biệt kh&ocirc;ng thể kh&ocirc;ng thực hiện trước khi tiến h&agrave;nh bốc mộ đ&oacute; l&agrave; th&acirc;n nh&acirc;n phải l&agrave;m nghi lễ tại Gia ti&ecirc;n để tr&igrave;nh b&aacute;o l&ecirc;n Tổ ti&ecirc;n của m&igrave;nh. NGo&agrave;i ra, tại nghĩa trang nơi bốc h&agrave;i cốt gia đ&igrave;nh cũng phải l&agrave;m lễ tr&igrave;nh l&ecirc;n Quan Thần Linh sở tại quản l&yacute; trong khu vực đ&oacute;. C&aacute;c vật liệu được d&ugrave;ng th&ocirc;ng thường sẽ bao gồm: một bộ đồ của Quan Thần LInh ( gồm c&oacute; 1 chiếc mũ, 1 bộ quần &aacute;o v&agrave; 1 đ&ocirc;i ủng), một con ngựa, một ngh&igrave;n v&agrave;ng hoa m&agrave;u đỏ, tiền giấy, trầu cau, v&agrave;ng bạc, rượu, n&eacute;n, gạo v&agrave; muối. NGo&agrave;i ra, nhiều nh&agrave; c&ograve;n c&uacute;ng th&ecirc;m một số thứ kh&aacute;c nữa như &nbsp;g&agrave; trống luộc, x&ocirc;i, gi&ograve;, Tam s&ecirc;n ( bao gồm thịt lợn luộc, trứng vịt luộc v&agrave; t&ocirc;m kh&ocirc; b&oacute;c vỏ),&hellip;</p>\r\n\r\n<p>Sau khi mở v&aacute;n thi&ecirc;n ra (tức mở nắp quan t&agrave;i) người ta sẽ đổ rượu c&oacute; nồng độ cao v&agrave;o b&ecirc;n trong để tẩy rửa &acirc;m kh&iacute;, rồi sau đ&oacute; mới tiến h&agrave;nh lấy cốt. Trong trường hợp m&agrave; h&agrave;i cốt chưa được ph&acirc;n hủy hết, nếu kh&ocirc;ng chuẩn bị thuốc ti&ecirc;u thịt th&igrave; c&oacute; thể lấy xăng đổ v&agrave;o mộ. Ch&acirc;m lửa đốt ch&aacute;y thịt c&ograve;n lại v&agrave; cuối c&ugrave;ng d&ugrave;ng dao d&oacute;c nốt những mảnh thịt c&ograve;n s&oacute;t, đem xương rửa qua nước vang. Khi đ&atilde; nhặt xong to&agrave;n bộ h&agrave;i cốt v&agrave; đem rửa sạch, người ta trải tấm ni l&ocirc;ng xuống dưới, sau đ&oacute; trải tấm vải đỏ l&ecirc;n tr&ecirc;n rồi lần lượt xếp xương theo thứ tự h&igrave;nh dạng ban đầu của người.</p>\r\n\r\n<p>Đầu sọ phải được xếp sao cho mặt hướng l&ecirc;n tr&ecirc;n, c&oacute; thể d&ugrave;ng tr&agrave; hoặc một tấm vải để k&ecirc;. Tất cả c&aacute;c xương v&agrave; khung xương phải kiểm tra cho đủ, kh&ocirc;ng được để thiếu s&oacute;t. D&acirc;n gian c&oacute; một c&aacute;ch thường sử dụng kh&aacute; phổ biến, đ&oacute; l&agrave;: khi đ&atilde; đ&atilde;i cốt xong đ&acirc;u v&agrave;o đấy, cắm một &nbsp;b&oacute; hương to giữa l&ograve;ng đ&aacute;y huyệt, nếu thấy l&agrave;n kh&oacute;i h&ograve;a quyện lại, bay thẳng th&igrave; hết cốt, nếu kh&oacute;i tỏa xuống, lan quanh huyệt tức chưa hết cốt, cần xem lại. &nbsp;Đ&oacute;ng nắp tiểu lại sau khi đ&atilde; ho&agrave;n tất việc lấy cốt.</p>\r\n\r\n<p>Đợi đến giờ tốt, đem h&agrave;i cốt ch&ocirc;n tại khu huyệt mộ mới. Khi đ&atilde; ho&agrave;n th&agrave;nh xong, tiến h&agrave;nh l&agrave;m lễ h&agrave;n long mạch v&agrave; lễ tạ mộ. Nếu khu mộ mới n&agrave;y ở tại một nghĩa trang kh&aacute;c th&igrave; phải l&agrave;m lễ tế xin ph&eacute;p Quan Thần Linh sở tại ở khu vực đ&oacute; th&igrave; mới được hạ huyệt l&agrave;m lễ ch&ocirc;n cất.</p>\r\n\r\n<p>Theo&nbsp;<a href=\"http://vansu.net/khi-boc-mo-sang-cat-cai-tang-mo-phan-can-chu-y-nhung-dieu-gi\" target=\"_blank\">vansu.net</a></p>\r\n\r\n<p>Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline&nbsp;0934.524.445 hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\">https://nghiatrangthienduong.com.vn/</a>.</p>\r\n\r\n<p>&nbsp;</p>', 'true', 'News', 'luu-y-khi-boc-mo', 'cái táng.jpg', 'Lưu ý khi bốc mộ', 'Khi bốc mộ sang cát – cải táng mộ phần cần chú ý những điều gì? Hãy cùng tìm hiểu nhé.', '2023-12-26 12:44:00', '2023-12-26 12:45:49'),
(201, 2, 68, 'Tích Trượng trong Phật Giáo', 'Tích Trượng là một trong 18 pháp khí của nhà tu hành Phật giáo. Với ý nghĩa là để dẹp trừ những chướng ngại vô minh làm trở ngại bước chân của các Ngài. Tích Trượng của Địa Tạng Vương Bồ Tát tại Thiên Đường là một trong những quyền trượng lớn nhất Việt Nam.', NULL, '<p><strong><span style=\"font-size:16px\">T&iacute;ch Trượng l&agrave; một trong 18 ph&aacute;p kh&iacute; của nh&agrave; tu h&agrave;nh Phật gi&aacute;o. Thời xưa, chư Phật v&agrave; đệ tử đi khất thực, thuyết ph&aacute;p thường mang theo c&acirc;y t&iacute;ch trượng. Với &yacute; nghĩa l&agrave; để dẹp trừ những chướng ngại v&ocirc; minh l&agrave;m trở ngại bước ch&acirc;n của c&aacute;c Ng&agrave;i.&nbsp;T&iacute;ch Trượng của Địa Tạng Vương Bồ T&aacute;t tại <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n nghĩa trang Thi&ecirc;n Đường</a> c&oacute; thể n&oacute;i l&agrave; một trong những quyền trượng được đ&uacute;c bằng đồng lớn nhất Việt Nam.</span></strong></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/R (2).jpg\" /></span></p>\r\n\r\n<p><em>H&igrave;nh ảnh t&iacute;ch trượng</em></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/tich-truong-trong-phat-giao\">Tích trượng</a> ti&ecirc;́ng Phạn là Khakhara (Khi&ecirc;́t khí la), Khích Khí La, cũng dịch là &ldquo;Thanh trượng&rdquo;, &ldquo;Minh trượng&rdquo;. &ldquo;Trí trượng&rdquo;, &ldquo;Đức trượng&rdquo;, &ldquo;Kim tích trượng&rdquo; là m&ocirc;̣t trong những pháp khí của Ph&acirc;̣t giáo. Khi c&ograve;n tại thế, Đức Phật giải th&iacute;ch T&iacute;ch trượng: &ldquo;T&iacute;ch c&oacute; nghĩa l&agrave; Khinh, c&oacute; nghĩa l&agrave; nhờ chiếc gậy đức hạnh v&agrave; tr&iacute; tuệ nầy m&agrave; phiền n&atilde;o được nhẹ đi v&agrave; sớm ra khỏi cảnh sinh tử lu&acirc;n hồi. T&iacute;ch cũng c&oacute; nghĩa l&agrave; Minh, nghĩa l&agrave; được tr&iacute; huệ s&aacute;ng suốt, l&agrave; hết khổ n&atilde;o&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Tr&ecirc;n đầu t&iacute;ch trượng sở dĩ c&oacute; 12 v&ograve;ng khoan l&agrave; biểu trưng cho Thập nhị nh&acirc;n duy&ecirc;n m&agrave; Đức Phật gi&aacute;c ngộ được để chứng th&agrave;nh đạo quả. Từ đ&oacute;, Ng&agrave;i đem Thập nhị nh&acirc;n duy&ecirc;n để gi&aacute;o h&oacute;a ch&uacute;ng sinh v&igrave; muốn mọi người đều được gi&aacute;c ngộ đạo l&yacute; như Ng&agrave;i.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"12 vòng khoan trên đầu Tích trượng biểu trưng cho Thập nhị nhân duyên mà Đức Phật giác ngộ được để chứng thành đạo quả\" src=\"https://i.ex-cdn.com/phatgiao.org.vn/files/content/2019/08/19/cay-tich-truong-2-1505.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">12 v&ograve;ng khoan tr&ecirc;n đầu T&iacute;ch trượng biểu trưng cho Thập nhị nh&acirc;n duy&ecirc;n m&agrave; Đức Phật gi&aacute;c ngộ được để chứng th&agrave;nh đạo quả</span></em></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/canh-quan/tuong-dia-tang-vuong-bo-tat\">Địa Tạng Vương Bồ T&aacute;t</a> tay phải cầm T&iacute;ch Trượng để biểu dương sức mạnh của Ch&aacute;nh Ph&aacute;p. Năng lực của T&iacute;ch Trượng l&agrave; t&acirc;m đại từ đại bi của Bồ T&aacute;t, d&ugrave;ng ch&aacute;nh ph&aacute;p chuyển h&oacute;a t&acirc;m địa con người đầy tham lam, hận th&ugrave;, đố kỵ, si m&ecirc; m&ugrave; qu&aacute;ng c&oacute; thể thức tỉnh kịp thời. C&ograve;n tay tr&aacute;i Ng&agrave;i nắm hạt minh ch&acirc;u l&agrave; tượng trưng cho tr&iacute; tuệ. V&igrave; muốn ph&aacute; v&ocirc; minh cần phải c&oacute; tr&iacute; tuệ, một khi c&oacute; tr&iacute; tuệ chiếu s&aacute;ng th&igrave; v&ocirc; minh kh&ocirc;ng c&ograve;n. Đầu mối của Thập nhị nh&acirc;n duy&ecirc;n l&agrave; v&ocirc; minh n&ecirc;n 12 v&ograve;ng khoan l&agrave; tượng trưng cho v&ocirc; minh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh 13.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Địa Tạng Vương Bồ T&aacute;t tay phải cầm T&iacute;ch Trượng để biểu dương sức mạnh của Ch&aacute;nh Ph&aacute;p</span></em></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <em><a href=\"https://nghiatrangthienduong.com.vn/canh-quan/tuong-quan-the-am-bo-tat\">Tượng&nbsp;Qu&aacute;n Thế &Acirc;m Bồ T&aacute;t&nbsp;</a></em></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ng&agrave;i <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/https://nghiatrangthienduong.com.vn/canh-quan/tuong-dia-tang-vuong-bo-tat\">Địa Tạng Vương Bồ T&aacute;t</a> đ&atilde; d&ugrave;ng t&iacute;ch trượng để đi v&agrave;o địa ngục hướng dẫn cho thập loại ch&uacute;ng sinh đang bị đọa trong địa ngục được hiểu r&otilde; ch&aacute;nh ph&aacute;p v&agrave; được giải tho&aacute;t l&ecirc;n c&aacute;c cảnh giới sung sướng hơn hay trực chỉ đến cảnh gi&aacute;c ngộ th&agrave;nh Phật. Mỗi khi ng&agrave;i v&agrave;o trong địa ngục, ng&agrave;i d&ugrave;ng t&iacute;ch trượng g&otilde; xuống đất th&igrave; c&aacute;c cửa ngục được mở ra, cho n&ecirc;n trong kinh c&oacute; day: Chấn khai địa ngục chi m&ocirc;n.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/tich-truong-trong-phat-giao\">T&iacute;ch trượng</a> c&oacute; h&igrave;nh d&aacute;ng của b&ocirc;ng hoa sen nở thơm tho thanh khiết, kết tụ của sự gi&aacute;c ngộ giải tho&aacute;t, hoa sen đều từ gốc b&ugrave;n tanh. Mục đ&iacute;ch của người tu l&agrave; phải c&oacute; ch&aacute;nh ph&aacute;p, hiểu tận tường thấu đ&aacute;o, thực h&agrave;nh đ&uacute;ng theo lời Phật dạy.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"Tích trượng có hình dáng của bông hoa sen nở thơm tho thanh khiết, kết tụ của sự giác ngộ giải thoát, hoa sen đều từ gốc bùn tanh.\" src=\"https://i.ex-cdn.com/phatgiao.org.vn/files/content/2019/08/19/cay-tich-truong-4-1505.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">T&iacute;ch trượng c&oacute; h&igrave;nh d&aacute;ng của b&ocirc;ng hoa sen nở thơm tho thanh khiết, kết tụ của sự gi&aacute;c ngộ giải tho&aacute;t, hoa sen đều từ gốc b&ugrave;n tanh.</span></em></p>\r\n\r\n<p><span style=\"font-size:16px\">Nh&agrave; tu h&agrave;nh tay cầm t&iacute;ch trượng du h&oacute;a khắp nơi, gọi l&agrave; Phi trượng, Tu&acirc;n trượng. Dựng trụ ở một nơi n&agrave;o th&igrave; gọi l&agrave; Lưu t&iacute;ch, Quải t&iacute;ch.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ng&agrave;y nay trong c&aacute;c lễ tang lớn nhỏ, trường hợp tang chủ c&oacute; thỉnh chư Tăng quang l&acirc;m chứng minh, chư Tăng s&aacute;m chủ thuyết minh sinh, n&oacute;i lại lịch sử, lai lịch của người đ&atilde; mất, tống t&aacute;ng đưa linh, qu&yacute; Ng&agrave;i c&oacute; sử dụng t&iacute;ch trượng để &ldquo;Dẫn vong&rdquo; trước khi động quan. &ETH;&acirc;y l&agrave; thuộc ph&aacute;p sự đạo tr&agrave;ng, rất c&oacute; &yacute; nghĩa trong Nghi lễ Phật gi&aacute;o; đồng thời cũng gi&uacute;p cho tang chủ c&oacute; niềm tin Phật ph&aacute;p m&agrave; v&agrave;o đạo, quy y Tam bảo. H&igrave;nh ảnh vị s&aacute;m chủ cầm t&iacute;ch trượng &ldquo;Dẫn vong&rdquo; tiếp dẫn vong hồn si&ecirc;u sinh an lạc quốc trở th&agrave;nh truyền thống v&agrave; l&agrave;m cho Phật gi&aacute;o rất gần gũi với x&atilde; hội.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Đọc th&ecirc;m: <em><a href=\"https://nghiatrangthienduong.com.vn/canh-quan/tuong-phat-di-lac\">tượng Phật Di Lạc</a></em></span></p>\r\n\r\n<h2><a href=\"https://nghiatrangthienduong.com.vn/canh-quan/tuong-dia-tang-vuong-bo-tat\"><strong><span style=\"font-size:16px\">T&iacute;ch Trượng của tượng phật Địa Tạng Vương Bồ T&aacute;t tại C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường</span></strong></a></h2>\r\n\r\n<p><span style=\"font-size:16px\">C&oacute; thể n&oacute;i t&iacute;ch trượng m&agrave; Địa Tạng Vương Bồ T&aacute;t tại nghĩa trang <a href=\"https://nghiatrangthienduong.com.vn/\">Thi&ecirc;n Đường</a> cầm tr&ecirc;n tay l&agrave; một trong những c&acirc;y quyền trượng lớn nhất tại Việt Nam được đ&uacute;c bằng đồng đỏ l&agrave;m m&agrave;u. </span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh 8.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Được thực hiện bởi đơn vị đ&uacute;c đồng Dương Quang H&agrave;, c&acirc;y trượng c&oacute; chiều d&agrave;i 15.2m, khối lượng l&ecirc;n đến 1 tấn 1, được thi c&ocirc;ng đ&uacute;c bằng đồng đỏ l&agrave;m m&agrave;u, v&agrave; được thực hiện b&agrave;n giao lắp đặt cho c&ocirc;ng tr&igrave;nh C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường tại tỉnh Tuy&ecirc;n Quang, Với nhiều năm kinh nghiệm đ&uacute;c những đại c&ocirc;ng tr&igrave;nh v&agrave; đội ngũ chuy&ecirc;n nghiệp c&oacute; tay nghề cao, th&ecirc;m v&agrave;o đ&oacute; l&agrave; sự trợ gi&uacute;p từ m&aacute;y m&oacute;c c&ocirc;ng nghệ cao đ&atilde; gi&uacute;p cho tiến tr&igrave;nh lắp đặt được diễn ra su&ocirc;n sẻ. Bức tượng Địa Tạng Vương Bồ T&aacute;t được tọa lạc tr&ecirc;n đồi Thi&ecirc;n T&acirc;m với chiều cao 19m, được thiết kế tinh xảo, đứng giữa trời xanh như đang độ phổ, bảo vệ c&aacute;c vong linh đang y&ecirc;n ngủ tại đ&acirc;y.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Đọc th&ecirc;m: <em><a href=\"https://nghiatrangthienduong.com.vn/canh-quan/tuong-dia-tang-vuong-bo-tat\">Tượng Địa Tạng Vương Bồ T&aacute;t</a></em></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline&nbsp;0934.524.445 hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\">https://nghiatrangthienduong.com.vn/</a>.</span></p>\r\n\r\n<p>&nbsp;</p>', 'true', 'News', 'tich-truong-trong-phat-giao', '6_cay-tich-truong-1.webp', 'Tích trượng trong Phật Giáo', 'Tích Trượng là một trong 18 pháp khí của nhà tu hành Phật giáo. Tích Trượng của Địa Tạng Vương Bồ Tát tại Thiên Đường là một trong những quyền trượng lớn nhất Việt Nam.', '2023-12-27 03:57:41', '2023-12-27 03:59:22');
INSERT INTO `posts` (`id`, `user_id`, `category_id`, `name`, `detail`, `info`, `content`, `status`, `sort_by`, `slug`, `img`, `title`, `description`, `created_at`, `updated_at`) VALUES
(202, 2, 68, 'Long Lân Quy Phụng là gì? Ý nghĩa của Tứ Linh trong phong thủy.', 'Long Lân Quy Phụng hay Long Ly Quy Phượng là 4 linh vật thuộc bộ tứ linh nổi tiếng trong văn hóa tâm linh. Bốn linh vật này có sức mạnh phi thường bắt nguồn từ bộ tứ linh thần quyền lực tượng trưng cho trời đất.', NULL, '<p><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/long-lan-quy-phung-la-gi-y-nghia-cua-tu-linh-trong-phong-thuy\">Long L&acirc;n Quy Phụng</a> hay Long Ly Quy Phượng l&agrave; 4 linh vật thuộc bộ tứ linh nổi tiếng trong văn h&oacute;a t&acirc;m linh. Bốn linh vật n&agrave;y c&oacute; sức mạnh phi thường bắt nguồn từ bộ tứ linh thần quyền lực tượng trưng cho trời đất. H&igrave;nh ảnh tứ linh được sử dụng v&agrave; khắc họa nhiều trong nghệ thuật <a href=\"https://nghiatrangthienduong.com.vn/canh-quan\">kiến tr&uacute;c t&acirc;m linh</a> tại Việt Nam từ nh&agrave; ri&ecirc;ng, đền ch&ugrave;a, c&ocirc;ng tr&igrave;nh t&acirc;m linh lớn như kinh đ&ocirc;.</p>\r\n\r\n<p>Long L&acirc;n Quy Phụng hay c&ograve;n được gọi l&agrave; Long Ly Quy Phụng l&agrave; h&igrave;nh ảnh quen thuộc kh&ocirc;ng thể thiếu trong văn h&oacute;a phương Đ&ocirc;ng. Kh&ocirc;ng chỉ l&agrave; một lo&agrave;i vật m&agrave; đ&acirc;y thuộc tứ linh được xem l&agrave; biểu tượng của sự may mắn, th&agrave;nh đạt trong sự nghiệp. C&ugrave;ng giải m&atilde; về Long Ly Quy Phụng v&agrave; &yacute; nghĩa tứ linh trong <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/dia-the-phong-thuy\">phong thủy.</a></p>\r\n\r\n<h2>1: Tứ linh l&agrave; những con vật n&agrave;o?</h2>\r\n\r\n<p><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/long-lan-quy-phung-la-gi-y-nghia-cua-tu-linh-trong-phong-thuy\">Tứ linh</a> l&agrave; bốn lo&agrave;i linh vật gồm Long, Ly, Quy, Phượng. Tứ linh c&oacute; mặt trong n&ecirc;n văn h&oacute;a t&acirc;m linh của nhiều nước tr&ecirc;n thế giới nhất l&agrave; Trung Quốc v&agrave; Việt Nam rất coi trọng 4 linh vật n&agrave;y.</p>\r\n\r\n<p>Bốn con vật trong bộ tứ linh c&oacute; nguồn gốc từ c&aacute;c linh thần như Thanh Long, Bạch Hổ, Huyền Vũ, Chu Tước. Những linh thần n&agrave;y đều được cha &ocirc;ng đời trước s&aacute;ng tạo dựa tr&ecirc;n c&aacute;c ch&ograve;m sao v&agrave; đ&acirc;y l&agrave; những nguy&ecirc;n tố cấu th&agrave;nh n&ecirc;n trời đất gồm c&aacute;c yếu tố đất, nước, lửa, gi&oacute;.</p>\r\n\r\n<p><img alt=\"Cù lao Long - Lân - Quy - Phụng - Four Holy Beasts Islands: Long ...\" src=\"https://tranhlinh.com/wp-content/uploads/2021/04/top-10-mau-tranh-long-lan-quy-phung-5.jpg\" /></p>\r\n\r\n<h2>2: Giải m&atilde; Long L&acirc;n Quy Phụng hay Long Ly Quy Phượng.</h2>\r\n\r\n<p>Long L&acirc;n Quy Phụng hay Long Ly Quy Phượng l&agrave; 4 linh vật thuộc bộ tứ linh nổi tiếng trong văn h&oacute;a t&acirc;m linh. Bốn linh vật n&agrave;y c&oacute; sức mạnh phi thường bắt nguồn từ bộ tứ linh thần quyền lực tượng trưng cho trời đất.</p>\r\n\r\n<p>H&igrave;nh ảnh <a href=\"https://hoavienthienduong.vn/giai-ma-long-ly-quy-phung-y-nghia-tu-linh-trong-phong-thuy/\">tứ linh</a> được sử dụng v&agrave; khắc họa nhiều trong nghệ thuật kiến tr&uacute;c t&acirc;m linh tại Việt Nam từ nh&agrave; ri&ecirc;ng, đền ch&ugrave;a, c&ocirc;ng tr&igrave;nh t&acirc;m linh lớn như kinh đ&ocirc;. Điều n&agrave;y c&oacute; nghĩa l&agrave; h&igrave;nh ảnh của 4 linh vật trong bộ tứ linh lu&ocirc;n sống m&atilde;i trong tiềm thức của người Việt Nam.</p>\r\n\r\n<p>C&aacute;c vật phẩm phong thủy khắc họa h&igrave;nh ảnh Long Ly Quy Phụng đều đ&ograve;i hỏi độ tinh xảo v&agrave; tay nghề cao do đ&ograve;i hỏi phải khắc họa cả 4 h&igrave;nh tượng tr&ecirc;n c&ugrave;ng 1 kh&ocirc;ng gian. Kh&ocirc;ng phải khắc họa kiểu tượng trưng m&agrave; c&oacute; những chi tiết c&ograve;n đ&ograve;i hỏi độ ch&iacute;nh x&aacute;c cao n&ecirc;n thời gian thực hiện cũng l&acirc;u hơn n&ecirc;n gi&aacute; trị thẩm mỹ cao v&agrave; chi ph&iacute; bỏ ra cũng kh&ocirc;ng hề nhỏ.</p>\r\n\r\n<p>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/dia-the-phong-thuy\">Địa thế phong thủy của c&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường</a></p>\r\n\r\n<h2>3: &Yacute; nghĩa của tứ linh trong phong thủy l&agrave; g&igrave;?</h2>\r\n\r\n<p><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/long-lan-quy-phung-la-gi-y-nghia-cua-tu-linh-trong-phong-thuy\">Tứ linh</a> trong phong thủy c&oacute; &yacute; nghĩa rất quan trọng v&agrave; mỗi loại linh vật trong đ&oacute; đều mang một &yacute; nghĩa kh&aacute;c nhau để tạo n&ecirc;n bộ tứ linh ho&agrave;n hảo.</p>\r\n\r\n<p>&Yacute; nghĩa Long L&acirc;n Quy Phượng</p>\r\n\r\n<ul>\r\n	<li>\r\n	<h3>Long</h3>\r\n	</li>\r\n</ul>\r\n\r\n<p>Đứng đầu trong bộ tứ linh ch&iacute;nh l&agrave; Long (Rồng) biểu tượng cho sức mạnh, quyền lực v&agrave; tr&iacute; tuệ n&ecirc;n mang &yacute; nghĩa t&agrave;i lộc v&agrave; c&ocirc;ng danh. Rồng nắm giữ sức mạnh vượt trội của mu&ocirc;n lo&agrave;i, c&oacute; quyền uy vượt trội nhất. Trong văn h&oacute;a nghệ thuật phương Đ&ocirc;ng lo&agrave;i Rồng thường xuất hiện để biểu tượng sức sống m&atilde;nh liệt v&agrave; sự cao qu&yacute;.</p>\r\n\r\n<p><img alt=\"Tượng Rồng Đá Phong Thủy - Ý nghĩa, Cách đặt và Bảng giá tại Xưởng\" src=\"https://www.phanthien.com/tin-tuc/images/tuong-rong-da/tuong-rong-da-tu-nhien-ttgt-001.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>H&igrave;nh ảnh Long (Rồng) được sử dụng nhiều trong phong thủy tại Việt Nam</em></p>\r\n\r\n<p>Trong phong thủy&nbsp;Rồng&nbsp;c&ograve;n được coi l&agrave; vị thần gi&uacute;p m&ugrave;a m&agrave;ng tốt tươi n&ecirc;n người d&acirc;n tin rằng nếu thờ phụng Rồng sẽ mang đến một năm mưa gi&oacute; thuận h&ograve;a, m&ugrave;a m&agrave;ng tốt tươi. Do đ&oacute;, trong t&iacute;n ngưỡng văn h&oacute;a người &Aacute; Ch&acirc;u mỗi khi hạn h&aacute;n mất m&ugrave;a vua thường đại diện cho to&agrave;n bộ người d&acirc;n l&agrave;m lễ cầu mưa tại miếu Long Vương.</p>\r\n\r\n<p>D&acirc;n gian đều c&oacute; niềm tin rằng Rồng v&agrave; Phượng đều l&agrave; biểu tượng tốt l&agrave;nh, đủ đầy v&agrave; cuộc sống vi&ecirc;n m&atilde;n. Đặc biệt, nếu kết hợp Rồng v&agrave; Phượng sẽ mang đến sự may mắn trong t&igrave;nh duy&ecirc;n đ&ocirc;i lứa.</p>\r\n\r\n<p>Rồng được mệnh danh l&agrave; linh vật mang nhiều nguồn sức mạnh sinh kh&iacute; dồi d&agrave;o, l&agrave; linh vật hội tụ đầy đủ quyền uy của vũ trụ. Nếu x&acirc;y nh&agrave; cửa, nh&agrave; thờ họ hoặc&nbsp;lăng mộ đ&aacute;&nbsp;t&acirc;m linh tại những v&ugrave;ng đất c&oacute; long mạch vượng kh&iacute; tốt sẽ đem đến những ph&uacute;c lợi to lớn cho con ch&aacute;u hậu thế.</p>\r\n\r\n<p>Ngo&agrave;i ra, nếu ai muốn thăng tiến trong c&ocirc;ng việc, sự nghiệp đều sử dụng Rồng phong thủy l&agrave; biểu tượng của sự quyền uy. Tượng rồng ph&ugrave; hợp với những người muốn củng cố địa vị trong sự nghiệp, những người l&agrave;m ch&iacute;nh trị v&agrave; l&agrave;m h&agrave;nh ch&iacute;nh.</p>\r\n\r\n<ul>\r\n	<li>\r\n	<h3>L&acirc;n</h3>\r\n	</li>\r\n</ul>\r\n\r\n<p><img alt=\"Marble Resin Garden Large Pair of Temple Foo Dogs Statue Sculpture ...\" src=\"https://s-media-cache-ak0.pinimg.com/736x/4c/dc/55/4cdc55074832f0c4ee7fc6a9178ca75b.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>L&acirc;n phong thủy</em></p>\r\n\r\n<p>Lo&agrave;i linh vật thứ hai sau Long ch&iacute;nh l&agrave; L&acirc;n biểu tượng của tr&iacute; tuệ. L&acirc;n c&ograve;n được gọi l&agrave; Ly, sự xuất hiện của Kỳ L&acirc;n thường b&aacute;o hiệu một năm th&aacute;i b&igrave;nh thịnh trị v&agrave; mang lại điềm l&agrave;nh. Trong phong thủy, L&acirc;n d&ugrave;ng để trấn tr&aacute;ch gi&uacute;p h&oacute;a hung th&agrave;nh c&aacute;t, h&oacute;a giải những v&ugrave;ng đất xấu kh&ocirc;ng thịnh. Ch&uacute;ng ta thường thấy h&igrave;nh ảnh đ&ocirc;i L&acirc;n miệng h&aacute; to trấn &aacute;p mọi hung kh&iacute; v&agrave;o nh&agrave; v&agrave; đứng canh giữ cửa nh&agrave; để gi&uacute;p mang lại b&igrave;nh an cho gia chủ.</p>\r\n\r\n<p>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/phong-thuy-tho-cung\">Phong thủy thờ c&uacute;ng</a></p>\r\n\r\n<p>Trong d&acirc;n gian, Kỳ L&acirc;n l&agrave; lo&agrave;i linh vật c&oacute; h&igrave;nh d&aacute;ng kỳ dị với đầu rồng th&acirc;n th&uacute; thuộc d&ograve;ng họ nh&agrave; nai, m&igrave;nh vằn v&agrave; đu&ocirc;i giống đu&ocirc;i tr&acirc;u, tr&ecirc;n đầu c&oacute; 1 sừng. Con L&acirc;n c&oacute; th&acirc;n h&igrave;nh giống Hươu khắp người c&oacute; vẩy v&agrave; n&oacute; ăn thức ăn chủ yếu l&agrave; cỏ.</p>\r\n\r\n<p>L&acirc;n c&oacute; thần th&aacute;i v&ocirc; c&ugrave;ng sinh động n&ecirc;n trong d&acirc;n gian n&oacute; l&agrave; lo&agrave;i linh th&uacute; chuy&ecirc;n gi&uacute;p đỡ đỡ v&agrave; cứu những người ngh&egrave;o n&ecirc;n sự xuất hiện của Kỳ L&acirc;n như một th&aacute;nh nh&acirc;n gi&uacute;p đời.</p>\r\n\r\n<ul>\r\n	<li>\r\n	<h3>Quy</h3>\r\n	</li>\r\n</ul>\r\n\r\n<p><img alt=\"Rùa phong thủy là gì? Ý nghĩa, cách đặt và những kiêng ky ra sao?\" src=\"https://cdn.shortpixel.ai/client/q_glossy,ret_img,w_700/https://hoangminhdecor.com/wp-content/uploads/2020/04/R%C3%B9a-phong-th%E1%BB%A7y.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>R&ugrave;a phong thủy</em></p>\r\n\r\n<p><a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/dia-the-phong-thuy\">Quy</a> c&ograve;n được gọi l&agrave; R&ugrave;a, l&agrave; biểu tượng của sức khỏe dồi d&agrave;o v&agrave; sống trường thọ. Trong bộ tứ linh th&igrave; đ&acirc;y l&agrave; con vật c&oacute; thật duy nhất trong tự nhi&ecirc;n. Đ&acirc;y l&agrave; lo&agrave;i b&ograve; s&aacute;t lưỡng cư c&oacute; tuổi thọ c&agrave;o v&agrave; ch&uacute;ng c&oacute; khả năng sống m&atilde;nh liệt ngay cả khi kh&ocirc;ng c&oacute; thức ăn.</p>\r\n\r\n<p>&Yacute; nghĩa phong thủy của R&ugrave;a l&agrave; tinh thần thanh cao v&agrave; sự tho&aacute;t tục n&ecirc;n từ xa xưa R&ugrave;a đ&atilde; c&oacute; mặt trong rất nhiều truyền thuyết. Nổi tiếng nhất l&agrave; c&acirc;u truyện Thần Kim Quy gi&uacute;p vua An Dương Vương x&acirc;y dựng v&agrave; bảo vệ th&agrave;nh Cổ Loa.</p>\r\n\r\n<p>R&ugrave;a l&agrave; biểu tượng cho sự trường tồn bất diệt được xem như sự hội tụ trời đất, &acirc;m dương. Bụng r&ugrave;a tượng trưng cho mặt đất v&agrave; mai r&ugrave;a h&igrave;nh v&ograve;m tượng trưng cho v&ograve;m trời n&ecirc;n trong phong thủy R&ugrave;a thường được kết hợp với Rắn hoặc r&ugrave;a đầu rồng để tạo n&ecirc;n một linh vật thi&ecirc;ng li&ecirc;ng.</p>\r\n\r\n<p>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang\">giải m&atilde; cải t&aacute;ng v&agrave; những điều cần biết về cải t&aacute;ng</a></p>\r\n\r\n<ul>\r\n	<li>\r\n	<h3>Phụng</h3>\r\n	</li>\r\n</ul>\r\n\r\n<p><img alt=\"#Long Ly Quy Phụng Là Gì? Ý Nghĩa Tứ Linh Trong Phong Thủy\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/phụng.jpg\" /></p>\r\n\r\n<p><em>Phượng Ho&agrave;ng biểu tượng của sự bất diệt.</em></p>\r\n\r\n<p>Phụng hay c&ograve;n gọi l&agrave; Phượng, đ&acirc;y l&agrave; một trong bốn tứ linh v&agrave; l&agrave; vua của c&aacute;c lo&agrave;i chim. Phượng c&oacute; những đặc điểm nổi bật như đầu g&agrave;, cổ cao như chim hạc, mỏ d&agrave;i như diều h&acirc;u, t&oacute;c như chim trĩ, đu&ocirc;i rực rỡ như chim c&ocirc;ng v&agrave; vảy của c&aacute; ch&eacute;p với th&acirc;n h&igrave;nh cao s&aacute;u thước, mắt s&aacute;ng như ngọn lửa.</p>\r\n\r\n<p>Với những đặc điểm như vậy Phượng Ho&agrave;ng được xem l&agrave; con vật bất tử, c&oacute; v&ograve;ng đời kh&ocirc;ng bao giờ kết th&uacute;c, ch&uacute;ng sẽ tự t&aacute;i sinh khi cảm thấy gi&agrave; nua. Qua mỗi lần t&aacute;i sinh, Phượng Ho&agrave;ng sẽ đẹp hơn, mạnh mẽ v&agrave; rực rỡ hơn.</p>\r\n\r\n<p>&Yacute; nghĩa phong thủy của Phượng Ho&agrave;ng được xem l&agrave; biểu tượng của sự duy&ecirc;n d&aacute;ng, đức hạnh &nbsp;v&agrave; biểu tượng cho sự h&ograve;a hợp &acirc;m dương. Trong truyền thuyết kể rằng, Phượng Ho&agrave;ng chỉ xuất hiện trong thời kỳ thịnh vượng.</p>\r\n\r\n<p>Rồng biểu tượng của vua ch&uacute;a, c&ograve;n Phượng Ho&agrave;ng biểu tượng của ho&agrave;ng hậu n&ecirc;n Phượng Ho&agrave;ng kết hợp c&ugrave;ng Rồng được coi l&agrave; cặp đ&ocirc;i đại diện cho hạnh ph&uacute;c, may mắn, sức khỏe d&agrave;i l&acirc;u.</p>\r\n\r\n<p>H&igrave;nh ảnh rồng phượng thường ứng với vua ch&uacute;a v&agrave; ho&agrave;ng hậu ng&agrave;y xưa v&igrave; thế&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/san-pham\">mộ đ&aacute;</a>&nbsp;của những bậc n&agrave;y thường được chạm khắc h&igrave;nh rồng phượng để thể hiện sự uy nghi&ecirc;m v&agrave; t&ocirc;n quyền.</p>\r\n\r\n<p>Phượng Ho&agrave;ng l&agrave; linh vật linh thi&ecirc;ng xuất hiện trong nhiều t&ocirc;n gi&aacute;o kh&aacute;c nhau n&ecirc;n n&oacute; c&ograve;n đại diện cho h&agrave;nh h&oacute;a. Đặc biệt, mọi người thường đặt h&igrave;nh ảnh Phượng Ho&agrave;ng tại cung t&agrave;i hoặc cung danh vọng để đem đến nhiều may mắn, thịnh vượng cho gia chủ. Cho n&ecirc;n Phượng c&ograve;n l&agrave; biểu tượng của sự th&aacute;nh nh&acirc;n v&agrave; hạnh ph&uacute;c bền l&acirc;u.</p>\r\n\r\n<p>V&igrave; vậy, bộ <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/long-lan-quy-phung-la-gi-y-nghia-cua-tu-linh-trong-phong-thuy\">Tứ linh</a> mang đậm t&iacute;nh chất t&acirc;m linh v&agrave; phong thuỷ n&ecirc;n việc trưng b&agrave;y tượng c&aacute;c con trong Tứ linh trong nh&agrave; kh&ocirc;ng chỉ đem đến sự sang trọng v&agrave; ấm &aacute;p cho ng&ocirc;i nh&agrave; m&agrave; c&ograve;n mang đến may mắn cho gia chủ. Theo quan niệm phong thuỷ, đặt tứ linh đ&uacute;ng chuẩn, hợp tuổi sẽ gi&uacute;p ngăn chặn t&agrave; kh&iacute; x&acirc;m nhập, trấn trạch v&agrave; mang đến may mắn, b&igrave;nh an cho gia đ&igrave;nh.</p>\r\n\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave; những th&ocirc;ng tin cụ thể về bộ tứ linh, những &yacute; nghĩa trong phong thủy m&agrave; c&aacute;c bạn n&ecirc;n hiểu kỹ trước khi trưng b&agrave;i c&aacute;c linh vật n&agrave;y trong nh&agrave;. Do đ&oacute;, sau khi nắm được nguồn gốc v&agrave; &yacute; nghĩa phong thủy của bộ tứ linh sẽ gi&uacute;p c&aacute;c bạn lựa chọn được loại tượng ph&ugrave; hợp với phong thủy v&agrave; y&ecirc;u cầu của m&igrave;nh.</p>\r\n\r\n<p>Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>, thứ 7 &amp; chủ nhật h&agrave;ng tuần, Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline 0934.524.445 hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\" rel=\"noopener\" target=\"_blank\">https://nghiatrangthienduong.com.vn/</a>.</p>', 'true', 'News', 'long-lan-quy-phung-la-gi-y-nghia-cua-tu-linh-trong-phong-thuy', 'top-10-mau-tranh-long-lan-quy-phung-5.jpg', 'Long Lân Quy Phụng là gì? Ý nghĩa của Tứ Linh trong phong thủy.', 'Long Lân Quy Phụng hay Long Ly Quy Phượng là 4 linh vật thuộc bộ tứ linh nổi tiếng trong văn hóa tâm linh. Bốn linh vật này có sức mạnh phi thường bắt nguồn từ bộ tứ linh thần quyền lực tượng trưng cho trời đất.', '2023-12-28 08:42:02', '2023-12-28 09:01:08'),
(203, 2, 68, 'Phong Thuỷ Âm Trạch Mộ Phần Cải Táng', '​​​​​​​Phong Thuỷ Âm Trạch Mộ Phần Cải Táng là việc lớn quan trọng trong cuộc đời mỗi cá nhân dù xét cho người mất hay cho những người còn sống hiện tại và tương lai của con cháu dài xa hơn. ', NULL, '<p><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/phong-thuy-am-trach-mo-phan-cai-tang\">Phong Thuỷ &Acirc;m Trạch Mộ Phần Cải T&aacute;ng</a>&nbsp;l&agrave; việc lớn quan trọng trong cuộc&nbsp;đời mỗi c&aacute; nh&acirc;n d&ugrave; x&eacute;t cho người mất hay cho những người c&ograve;n sống hiện tại v&agrave; tương lai của con ch&aacute;u d&agrave;i xa hơn.&nbsp;N&ecirc;n ta mới x&eacute;t thấy, sinh ra anh t&agrave;i, tuấn kiệt, hay sinh ra hung đồ&nbsp;&aacute;c tặc lại sinh ra trong gia&nbsp;đ&igrave;nh n&agrave;y, d&ograve;ng họ n&agrave;y, v&agrave;o thời điểm n&agrave;y. Theo phật gi&aacute;o&nbsp;đ&oacute; l&agrave; lẽ nh&acirc;n duy&ecirc;n m&agrave; th&agrave;nh, nh&acirc;n quả m&agrave; gặp, hay theo lẽ&nbsp;đạo trời đất, thi&ecirc;n thời ,&nbsp;địa lợi, sinh nh&acirc;n ho&agrave;&nbsp;ắt c&oacute; th&aacute;nh hiền, b&acirc;c trưởng giả t&ocirc;n qu&yacute;.&nbsp;Phong Thuỷ &Acirc;m Trạch Mộ Phần <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang\">Cải T&aacute;ng</a>&nbsp;gặp&nbsp;đủ Thi&ecirc;n &ndash;&nbsp;Địa &ndash; Nh&acirc;n&nbsp; ắt&nbsp;đắc&nbsp;Đại&nbsp;Đại C&aacute;t Tường.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/z4739939583777_ed37970f5389ec145ff423bd67dab831.jpg\" /></span></p>\r\n\r\n<p><em><a href=\"https://nghiatrangthienduong.com.vn/\">H&igrave;nh ảnh Thi&ecirc;n Đường&nbsp;</a></em></p>\r\n\r\n<p><span style=\"font-size:16px\">Về mệnh l&yacute; c&aacute; nh&acirc;n m&agrave;&nbsp;đ&aacute;nh gi&aacute;: Ph&uacute;c&nbsp;đức cung l&agrave; gốc rễ của 1 c&aacute; nh&acirc;n, mệnh l&agrave; th&acirc;n c&acirc;y, th&acirc;n l&agrave; c&agrave;nh l&aacute;, thời vận l&agrave; hoa quả.&nbsp;Cung Ph&uacute;c&nbsp;đức&nbsp;đ&acirc;y c&oacute; 3 yếu tố m&agrave; hội đến (ph&uacute;c đức của c&aacute; nh&acirc;n&nbsp;&oacute; tạo từ kiếp trước &ndash; 3 kiếp gần nhất, Ph&uacute;c&nbsp;đức&nbsp;&Acirc;m Phần của d&ograve;ng họ m&agrave; người n&agrave;y được sinh v&agrave;o, v&agrave; ph&uacute;c&nbsp;đức của ngươi đ&oacute; tạo ra&nbsp;ở hiện tại). Ph&uacute;c&nbsp;đức cung sẽ&nbsp;đ&aacute;nh gi&aacute; mức&nbsp;độ tốt xấu, tăng tốt, giảm xấu hoặc ngược lại khi người&nbsp;đ&oacute; gặp vận, hạn&nbsp;để&nbsp;định sự việc. Ph&uacute;c&nbsp;đức cung đối xung cung t&agrave;i ch&aacute;nh, kiếm tiền ra sao, gi&agrave;u c&oacute; thế n&agrave;o, mưu sinh ra sao l&agrave; ở 1 phần cung ph&uacute;c đức m&agrave; ra. Cung Ph&uacute;c Đức&nbsp;&nbsp;thế tam hợp cung Phu/ Th&ecirc; (vợ chồng &ndash; h&ocirc;n nh&acirc;n) v&agrave; cung Thi&ecirc;n Di (cơ hội, giao tế,&nbsp;đi lại, vận may).</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Vậy cung ph&uacute;c&nbsp;đức c&aacute; nh&acirc;n x&eacute;t về&nbsp;phần c&oacute; sự t&aacute;c động qua lại rất lớn đến cuộc đời mỗi con người. Với nữ giới lại quan trọng hơn, nữ l&agrave;&nbsp;&acirc;m, nữ giới c&oacute; mức ảnh hưởng Gene cao gấp 3 lần nam giới khi truyền qua c&aacute;c thế hệ sau c&aacute;c cụ c&oacute; c&acirc;u :&rdquo; Vợ dại hại ba đời- đời m&igrave;nh, đời con v&agrave; đời ch&aacute;u &rdquo;&nbsp;. Quay trở lại&nbsp; vấn&nbsp;đề tr&ecirc;n ta thấy t&iacute;nh&nbsp;&Acirc;m:&nbsp;&acirc;m&nbsp;đức, &acirc;m phần&nbsp;c&oacute; sức ảnh hưởng rất lớn&nbsp;đến c&aacute; nh&acirc;n.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/z4899209231613_e19414e9fe51d27622c363511d07fb12.jpg\" /></span></p>\r\n\r\n<p>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cai-tang-la-gi-nhung-dieu-can-biet-ve-phong-tuc-cai-tang\">Cải t&aacute;ng l&agrave; g&igrave; ?</a>&nbsp;</p>\r\n\r\n<p><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/phong-thuy-am-trach-mo-phan-cai-tang\"><span style=\"font-size:16px\">Phong Thuỷ &Acirc;m Trạch Mộ Phần Cải T&aacute;ng</span></a></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px\">Tổng quan mọi g&oacute;c cạnh Huyền Học dựa tr&ecirc;n Thuyết tam t&agrave;i: Thi&ecirc;n &ndash; Địa &ndash; Nh&acirc;n.</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px\">Đinh vấn đề về&nbsp;Phong Thuỷ &Acirc;m Trạch Mộ Phần Cải T&aacute;ng&nbsp;&nbsp;cho 1 c&aacute; nh&acirc;n hay d&ograve;ng tộc được gắn kết chặt chẽ bởi 3 yếu tố tr&ecirc;n.<br />\r\n1 Yếu tố Thi&ecirc;n thời: thời giờ định việc vu&ocirc;ng tr&ograve;n, Năm &ndash; Th&aacute;ng &ndash; Ng&agrave;y _ Giờ cụ thể, kh&iacute; tiết ra sao, tốt cho ai, tuổi n&agrave;o, xấu cho ai ứng với tuổi n&agrave;o, c&ograve;n tuỳ thuộc v&agrave;o c&aacute; nh&acirc;n người thực hiện việc tr&ecirc;n.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">2 Yếu tố địa lợi: tức cục đất t&aacute;ng thế n&agrave;o, vị tr&iacute; phong thuỷ ra sao, rất tốt, tốt, kh&aacute;, tương đối kh&aacute;, vừa, xấu, rất xấu tuỳ thuộc v&agrave;o c&aacute;ch cục rồi đến hướng, đại gia đ&igrave;nh mong cầu điều g&igrave; để khởi tạo động thổ, đặt mộ phần, ph&acirc;n kim sơn hướng chi tiết cụ thể, thế đất c&oacute; hợp với thời vận hay kh&ocirc;ng&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3. Yếu tố Nh&acirc;n ( Con người: người mất, người sống đứng ra thực hiện sự việc): Người mất l&acirc;m chung v&agrave;o Năm &ndash; Th&aacute;ng &ndash; Ng&agrave;y &ndash; Giờ n&agrave;o ?, khi sống ra sao ?. Người thực hiện ( người th&acirc;n trong gia đ&igrave;nh) chọn người c&oacute; ph&uacute;c đức tốt, t&acirc;m từ tốt, c&oacute; Năm _ th&aacute;ng- Ng&agrave;y _ giờ Sinh cụ thể, ng&agrave;y giờ thực hiện việc đại sự c&oacute; hợp với người n&agrave;y kh&ocirc;ng&hellip;. Gia quyến mong cầu điều g&igrave; cho gia đ&igrave;nh, d&ograve;ng tộc, hay mong cầu điều g&igrave; cho người đ&atilde; khuất để ta định cho to&agrave;n c&aacute;ch cục ( Thi&ecirc;n _ Địa _ Nh&acirc;n ứng hợp, v&agrave; ph&aacute;t vượng)</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Tổng kết Ba yếu tốt tr&ecirc;n l&agrave; tổng ho&agrave; của vấn đề&nbsp;Phong Thuỷ &Acirc;m Trạch Mộ Phần Cải T&aacute;ng&nbsp;đưa ra c&aacute;c mức độ để đ&aacute;nh gi&aacute;:</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\">&ndash; Được cả 3 yếu tốt Thi&ecirc;n thời &ndash; Địa lợi &ndash; Nh&acirc;n ho&agrave; đạt ( điều n&agrave;y kh&oacute;) để ph&acirc;n c&aacute;c mức độ Đại c&aacute;t _ trung c&aacute;t &ndash; tiểu c&aacute;t.</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\">&ndash; Được 2 yếu tố trong đ&oacute; Nh&acirc;n l&agrave; chủ yếu (&nbsp;Nh&acirc;n định thắng thi&ecirc;n&nbsp;hay &yacute; chữ t&acirc;m bằng 3 chữ t&agrave;i ( t&agrave;i đ&acirc;y l&agrave; t&agrave;i hoa hay t&agrave;i vậy theo t&ocirc;i l&agrave; T&agrave;i đ&acirc;y l&agrave; Tam T&agrave;i mới nẩy được &yacute; Nh&acirc;n định thắng thi&ecirc;n ở chỗ đ&oacute;). Th&igrave; Được thượng trung c&aacute;t &ndash; trung c&aacute;t &ndash; tiểu c&aacute;t.</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\">&ndash; Được 1 yếu tố Thi&ecirc;n hoặc Địa hoặc nh&acirc;n th&igrave; tuỳ v&agrave;o mức độ ri&ecirc;ng m&agrave; được, hoặc được ở trong 1 giai đoạn nhất định n&agrave;o đ&oacute; tuỳ v&agrave;o ho&agrave;n cảnh để ph&acirc;n: Trung c&aacute;t &ndash; thượng tiểu c&aacute;t &ndash; tiểu c&aacute;t.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên đơn/đơn 5.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Vậy nhiều trường hợp chẳng xem g&igrave; cả,, t&ocirc;i chỉ biết điều cần l&agrave;m n&ecirc;n l&agrave;m chang cần&nbsp;Phong Thuỷ &Acirc;m Trạch Mộ Phần Cải T&aacute;ng,&nbsp;lắm thầy nhiều ma.&nbsp;cứ đặt ng&agrave;y n&agrave;o tra google ra thấy đẹp ? th&ocirc;ng thường l&agrave; theo c&aacute;ch n&agrave;y. V&agrave; điểm n&agrave;y c&oacute;&nbsp;&yacute;&nbsp;đung&nbsp;&yacute; sai. Mỗi người một c&aacute;ch thức hiện kh&aacute;c nhau, mỗi thầy c&oacute; kiến giải ri&ecirc;ng chẳng ai giống ai, Nhưng theo t&ocirc;i bao tr&ugrave;m nhất vẫn l&agrave; 3&nbsp;điều kiện tr&ecirc;n th&igrave; tốt. Nay ta thử ph&acirc;n t&iacute;ch trường hợp th&ocirc;ng dụng hiện nay, l&agrave; chẳng xem g&igrave; cả th&igrave; sao ?</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Nếu chẳng xem g&igrave; cả cứ thấy việc cần l&agrave;m n&ecirc;n l&agrave;m, tức kh&ocirc;ng cần x&eacute;t kỹ 3 yếu tố thuyết tam t&agrave;i tr&ecirc;n, sẽ c&oacute; c&aacute;c vấn đề sự kiện sẩy ra: tốt l&ecirc;n, b&igrave;nh thường,kh&ocirc;ng c&oacute; việc g&igrave; nghi&ecirc;m trọng sẩy ra, c&oacute; v&agrave;i sự kiện nhỏ, sự kiện xấu l&agrave;m hao t&agrave;i tốn của c&oacute; người th&acirc;n mất, chết sau khi cải t&aacute;ng mộ phần&hellip;.&nbsp;&nbsp;Xin thưa đi theo nghiệp lực v&agrave; theo d&ograve;ng chảy sự kiện của&nbsp;mỗi c&aacute; nh&acirc;n hay gọi l&agrave; số phận&nbsp;m&agrave; c&oacute; hiện tượng tốt xấu như tr&ecirc;n, hoặc rất hiếm, hạn hữu lắm mới 3 yếu tố kể tr&ecirc;n xấu cả 3: Thời xấu &ndash;&nbsp;đất xấu &ndash; ng&agrave;y giờ người mất v&agrave; khi t&aacute;ng ch&ocirc;n kh&ocirc;ng tốt lại&nbsp;cung số&nbsp;ứng hợp với 1 v&agrave;i ngươi th&acirc;n th&igrave; sảy ra hiện tượng tr&ecirc;n. Vậy cứ theo t&acirc;m m&igrave;nh&nbsp;m&agrave; l&agrave;m th&igrave; tự khắc theo số mệnh c&aacute; nh&acirc;n con ch&aacute;u&nbsp;đ&atilde;&nbsp;định sẵn, cộng ph&uacute;c&nbsp;&acirc;m phần c&oacute; cộng trừ đi ch&uacute;t &iacute;t.&nbsp;&nbsp;V&iacute; dụ 1 ngươi Con hay ch&aacute;u n&agrave;o đ&oacute; của người mất khi th&igrave; xem năm th&aacute;ng ng&agrave;y giờ sinh, cuộc đời người n&agrave;y&nbsp;&nbsp;tương đối tốt vậy cứ thế thẳng tiến đến đ&iacute;ch.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/hoa-tang-la-gi-quy-trinh-hoa-tang-tai-viet-nam\">Hỏa t&aacute;ng l&agrave; g&igrave;?&nbsp;</a></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Muốn l&ecirc;n nữa cũng kh&oacute; v&igrave; ph&uacute;c &ndash; phần họ định sẵn, l&agrave;m b&agrave;i được 8 điểm, c&oacute; th&ecirc;m 1 ch&uacute;t Ph&uacute;c đưc &Acirc;m phần d&ograve;ng họ&nbsp;th&igrave; được 8,5 vẫn xếp loại giỏi. Nhưng 8 điểm m&agrave; được cộng 1,5 điểm từ Ph&uacute;c Đức &Acirc;m Phần của mồ mả, d&ograve;ng họ th&igrave;&nbsp;th&agrave;nh 9,5 hay 2 điểm th&agrave;nh 10 l&agrave; vấn đề ho&agrave;n to&agrave;n kh&aacute;c- đ&oacute; l&agrave; Xuất Sắc. Đ&oacute; l&agrave; chuyện về người dương v&agrave; &acirc;m phần l&agrave; vậy.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/z4899209322914_de3313afea283e6545eacd1b6874303e.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">B. C&aacute;c vấn đề cần c&oacute; khi xem&nbsp;Phong Thuỷ &Acirc;m Trạch Mộ Phần Cải T&aacute;ng ng&agrave;y giờ tốt đặt mộ, chọn hướng đặt v&agrave; thủ tục kh&aacute;c:</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Vậy theo quan điểm c&aacute; nh&acirc;n t&ocirc;i &ndash; M&atilde; Đắc Khoa xin đưa ra c&aacute;c điều để hỗ trợ c&aacute;c qu&yacute; vị, c&aacute;c Anh/ chị xin cung cấp c&aacute;c th&ocirc;ng tin sau ( xin li&ecirc;n hệ trực tiếp, t&ocirc;i sẽ giải đ&aacute;p).<br />\r\nTh&ocirc;ng tin cần c&oacute; để định việc:</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\">1. Năm &ndash; th&aacute;ng &ndash; ng&agrave;y &ndash; GIỜ MẤT của người đ&atilde; khuất.(th&ocirc;ng tin ng&agrave;y giờ tr&ecirc;n l&agrave; &acirc;m hay dương lịch)</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\">2. Năm &ndash; th&aacute;ng &ndash; ng&agrave;y &ndash; GIỜ SINH của người thực hiện ch&iacute;nh trong việc bốc mộ.(th&ocirc;ng tin ng&agrave;y giờ tr&ecirc;n l&agrave; &acirc;m hay dương lịch)</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\">3. Năm sinh của c&aacute;c người th&acirc;n sẽ tiến h&agrave;nh việc tr&ecirc;n ( v&igrave; nếu người tr&ecirc;n ko hợp tuổi c&ograve;n c&oacute; phương &aacute;n thay thế)</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\">4. Vị tr&iacute; đặt ở đ&acirc;u v&agrave; hướng mặt nh&igrave;n ra hướng n&agrave;o ( chưa x&eacute;t đến yếu tố 2 như b&agrave;n ở tr&ecirc;n)</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px\">5. CHỌN NG&Agrave;Y GIỜ tốt th&igrave; gia đ&igrave;nh mong cầu điều g&igrave; để an định ( danh tiếng, học vấn, ph&uacute;c đức, hay t&agrave;i ch&iacute;nh, hay cho người &acirc;m si&ecirc;u tho&aacute;t, thăng l&ecirc;n c&aacute;c tầng cao hơn trong c&otilde;i v&ocirc; h&igrave;nh&hellip;) </span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/phong-thuy-am-trach-mo-phan-cai-tang\">Phong Thuỷ &Acirc;m Trạch Mộ Phần Cải T&aacute;ng</a> ng&agrave;y giờ tốt đặt mộ,chọn hướng đặt qu&aacute;ch tiểu v&agrave; thủ tục ch&ocirc;n cất, Xem ng&agrave;y giờ&nbsp;đẹp, Ph&ograve;ng thuỷ&nbsp;&acirc;m phần, Phong thuỷ&nbsp;&acirc;m trạch, xem ng&agrave;y giờ&nbsp;đặt mộ, sửa mộ, ch&ocirc;n cất cải t&aacute;ng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline&nbsp;0934.524.445 hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\">https://nghiatrangthienduong.com.vn/</a>.</span></p>', 'true', 'News', 'phong-thuy-am-trach-mo-phan-cai-tang', '57_thiên đường.jpg', 'Phong Thuỷ Âm Trạch Mộ Phần Cải Táng', '​​​​​​​Phong Thuỷ Âm Trạch Mộ Phần Cải Táng là việc lớn quan trọng trong cuộc đời mỗi cá nhân dù xét cho người mất hay cho những người còn sống hiện tại và tương lai của con cháu dài xa hơn. ', '2023-12-29 17:03:45', '2023-12-29 17:07:04');
INSERT INTO `posts` (`id`, `user_id`, `category_id`, `name`, `detail`, `info`, `content`, `status`, `sort_by`, `slug`, `img`, `title`, `description`, `created_at`, `updated_at`) VALUES
(204, 2, 68, 'Những Mẫu Văn Khấn Tạ Mộ Cuối Năm Đầy Đủ Nhất', '​​​​​​​Sử dụng văn khấn tạ mộ cuối năm cũng chính là thể hiện lòng thành tâm của con cháu dâng đến những người thân đã khuất và thổ thân cai quản khu vực linh thiêng.', NULL, '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Sử dụng văn khấn <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/nhung-mau-van-khan-ta-mo-cuoi-nam-day-du-nhat\">tạ mộ cuối năm</a> cũng ch&iacute;nh l&agrave; thể hiện l&ograve;ng th&agrave;nh t&acirc;m của con ch&aacute;u d&acirc;ng đến những người th&acirc;n đ&atilde; khuất v&agrave; thổ th&acirc;n cai quản khu vực linh thi&ecirc;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Lễ tạ mộ cuối năm l&agrave; một n&eacute;t đẹp văn h&oacute;a của người Việt Nam, đ&acirc;y ch&iacute;nh l&agrave; dịp con ch&aacute;u đến để tỏ l&ograve;ng biết ơn tổ ti&ecirc;n cũng như cảm tạ những thổ địa chốn ấy đ&atilde; bảo vệ cho linh hồn người th&acirc;n của m&igrave;nh được an nghỉ. Bởi vấn đề li&ecirc;n quan đến <a href=\"https://nghiatrangthienduong.com.vn/\">t&acirc;m linh</a>, việc sử dụng&nbsp;văn khấn tạ mộ cuối năm&nbsp;cũng ch&iacute;nh l&agrave; thể hiện sự trang trọng v&agrave; th&agrave;nh t&acirc;m của con ch&aacute;u d&acirc;ng đến những người khuất mặt khuất m&agrave;y.</span></p>\r\n\r\n<h2 style=\"text-align:justify\"><strong><span style=\"font-size:16px\">1. &Yacute; nghĩa s&acirc;u sắc về ng&agrave;y lễ tạ mộ cuối năm</span></strong></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">V&agrave;o dịp lễ<a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/nhung-mau-van-khan-ta-mo-cuoi-nam-day-du-nhat\"> tạ mộ</a> cuối năm con ch&aacute;u sẽ đến viếng thăm, dọn dẹp, nhổ cỏ, chăm s&oacute;c c&aacute;c loại c&acirc;y cảnh, hoa quanh khu vực mộ phần. Sau khi c&aacute;c ng&ocirc;i mộ đ&atilde; được dọn dẹp phong quang sạch sẽ, ch&uacute;ng ta c&oacute; thể b&agrave;y đồ lễ, k&iacute;nh mời Thần linh thổ địa T&ocirc;n thần cai quản khu mộ v&agrave; hương linh người th&acirc;n đ&atilde; khuất về thụ hưởng. Chờ <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/phong-thuy-tho-cung\">hương ch&aacute;y</a> được một nửa, c&oacute; thể h&oacute;a tiền v&agrave;ng, hạ lễ. Đặc biệt, con ch&aacute;u thường th&agrave;nh k&iacute;nh mời hương linh gia ti&ecirc;n tiền tổ về ăn tết c&ugrave;ng gia đ&igrave;nh qua b&agrave;i văn khấn tạ mộ cuối năm.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia tộc/gia tộc 4.jpg\" /></span><a href=\"https://nghiatrangthienduong.com.vn/san-pham/khuon-vien-gia-toc\"><em><span style=\"font-size:16px\">Văn khấn tạ mộ cuối năm tỏ l&ograve;ng th&agrave;nh k&iacute;nh đến c&aacute;c thần linh thổ địa</span></em></a></p>\r\n\r\n<h2 style=\"text-align:justify\"><strong><span style=\"font-size:16px\">2. Lễ vật d&acirc;ng c&uacute;ng tạ mộ cuối năm</span></strong></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">T&ugrave;y v&agrave;o phong tục sống của một số địa phương, lễ vật c&uacute;ng <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/nhung-mau-van-khan-ta-mo-cuoi-nam-day-du-nhat\">tạ mộ</a> c&ograve;n bao gồm c&aacute;c vật phẩm kh&aacute;c như &aacute;o, gi&agrave;y, t&uacute;i x&aacute;ch, hộp đựng mỹ phẩm hoặc c&aacute;c vật dụng c&aacute; nh&acirc;n kh&aacute;c, nhưng những vật c&uacute;ng cơ bản phải c&oacute; những lễ vật dưới đ&acirc;y:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/le-cung-ta-mo-cuoi-nam-moi-ong-ba-to-tien-ve-an-tet-can-chuan-bi-gi-1-e1518060832725.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Văn khấn tạ mộ cuối năm phải được đọc bằng tất cả l&ograve;ng th&agrave;nh</span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">X&ocirc;i v&agrave; g&agrave; luộc</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">Tr&aacute;i c&acirc;y v&agrave; hoa</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">Rượu trắng</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">Ch&egrave; (tr&agrave;)</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">Trầu cau</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">V&agrave;ng m&atilde;</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Lưu &yacute;: Khi d&acirc;ng lễ vật c&uacute;ng phải th&agrave;nh t&acirc;m tỏ l&ograve;ng th&agrave;nh k&iacute;nh, lễ vật c&uacute;ng tạ mộ thường được đặt tại nơi mộ phần hoặc tại nh&agrave; thờ, đền, ch&ugrave;a,&hellip; Khi tạ mộ, người tham dự&nbsp;lễ tạ mộ&nbsp;sẽ c&uacute;i đầu v&agrave; cầu nguyện cho người đ&atilde; mất.</span></p>\r\n\r\n<p style=\"text-align:justify\"><em><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/tim-hieu-cac-hinh-thuc-mai-tang-pho-bien-o-viet-nam\">T&igrave;m hiểu c&aacute;c h&igrave;nh thức mai t&aacute;ng phổ biến tại Việt Nam</a></span></em></p>\r\n\r\n<h2 style=\"text-align:justify\"><strong><span style=\"font-size:16px\">3. Những mẫu văn khấn tạ mộ cuối năm chuẩn nhất</span></strong></h2>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">3.1 Văn khấn lễ tạ mộ cuối năm mẫu 1</span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">(Tạ mộ để xin ph&eacute;p Thổ thần Thổ địa nơi đ&oacute; cho &Ocirc;ng b&agrave; về ăn Tết)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">K&iacute;nh lạy:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&ndash; Ng&agrave;i Kim ni&ecirc;n Đương cai Th&aacute;i tuế Ch&iacute; đức T&ocirc;n thần, Kim ni&ecirc;n h&agrave;nh binh, C&ocirc;ng t&agrave;o ph&aacute;n quan.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&ndash; Ng&agrave;i bản cảnh Th&agrave;nh ho&agrave;ng Chư vị Đại vương.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&ndash; Ng&agrave;i bản xứ thần linh Thổ địa t&ocirc;n thần.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&ndash; C&aacute;c ng&agrave;i Ngũ phương, Ngũ thổ long mạch T&ocirc;n thần, Tiền Chu tước, Hậu Huyền vũ, Tả Thanh long, Hữu Bạch hổ c&ugrave;ng liệt vị T&ocirc;n Thần cai quản ở trong xứ n&agrave;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ch&uacute;ng con (Họ t&ecirc;n vợ, chồng)............................................................</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Địa chỉ.............................................................................................</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Sắm sanh phẩm vật, hương hoa ph&ugrave; tửu lễ nghi, tr&igrave;nh c&aacute;o T&ocirc;n thần, k&iacute;nh viếng vong linh l&agrave;:....................................</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">(T&ecirc;n của &Ocirc;ng, b&agrave;, cha mẹ v.v&hellip;): Tuổi...............................</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tạ thế ng&agrave;y....................................................................</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Phần mộ k&yacute; t&aacute;ng tại.......................................................</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nay nh&acirc;n ng&agrave;y (V&iacute; dụ: Cuối năm, hoặc Thanh minh, hoặc thăm mộ) con xin c&uacute;i lạy Thần linh đất n&agrave;y, Th&agrave;nh ho&agrave;ng bản thổ nơi đ&acirc;y, đất l&agrave;nh chim đậu, đức d&agrave;y thanh cao, giữ l&agrave;nh c&ocirc;ng lao, c&oacute; kết c&oacute; ph&aacute;t nhờ v&agrave;o thần quan, t&ocirc;n thần long mạch cao sang, nhị thập tứ hướng nhị thập tứ sơn quanh v&ugrave;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chọn đ&acirc;y an t&aacute;ng mộ phần, thỏa y&ecirc;n mu&ocirc;n thủa, hồng &acirc;n đời đời, gia &acirc;n m&atilde;i m&atilde;i kh&ocirc;ng th&ocirc;i, ch&uacute;ng con xin c&oacute; v&agrave;i lời cầu xin: B&aacute;i tạ thủ mộ thần quan, cho ch&acirc;n linh dưỡng cho h&agrave;i cốt nguy&ecirc;n vẹn to&agrave;n, ph&ugrave; hộ con ch&aacute;u trần gian, an khang mạnh khỏe ăn l&agrave;m gặp may. m dương c&aacute;ch trở, b&aacute;t nước n&eacute;n hương, biểu tấm l&ograve;ng th&agrave;nh, c&uacute;i xin chứng gi&aacute;m.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Phục duy cẩn c&aacute;o!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">(Khấn 3 lần rồi đốt v&agrave;ng tiền).</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Văn khấn lễ tạ mộ cuối năm mẫu 1</span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">3.2 Văn khấn tạ mộ cuối năm mẫu 2</span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">(Tạ mộ để xin ph&eacute;p Thổ thần Thổ địa nơi đ&oacute; cho &Ocirc;ng b&agrave; về ăn Tết)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">K&iacute;nh lạy:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&ndash; Ng&agrave;i Kim ni&ecirc;n Đương cai Th&aacute;i tuế Ch&iacute; đức T&ocirc;n thần, Kim ni&ecirc;n h&agrave;nh binh, C&ocirc;ng t&agrave;o ph&aacute;n quan.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&ndash; Ng&agrave;i bản cảnh Th&agrave;nh ho&agrave;ng Chư vị Đại vương.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&ndash; Ng&agrave;i bản xứ thần linh Thổ địa t&ocirc;n thần.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&ndash; C&aacute;c ng&agrave;i Ngũ phương, Ngũ thổ long mạch T&ocirc;n thần, Tiền Chu tước, Hậu Huyền vũ, Tả Thanh long, Hữu Bạch hổ c&ugrave;ng liệt vị T&ocirc;n Thần cai quản ở trong xứ n&agrave;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ch&uacute;ng con (Họ t&ecirc;n vợ, chồng)............................................................</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Địa chỉ.............................................................................................</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Sắm sanh phẩm vật, hương hoa ph&ugrave; tửu lễ nghi, tr&igrave;nh c&aacute;o T&ocirc;n thần, k&iacute;nh viếng vong linh l&agrave;:....................................</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">(T&ecirc;n của &Ocirc;ng, b&agrave;, cha mẹ v.v&hellip;): Tuổi...............................</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tạ thế ng&agrave;y....................................................................</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Phần mộ k&yacute; t&aacute;ng tại.......................................................</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nay nh&acirc;n ng&agrave;y (V&iacute; dụ: Cuối năm, hoặc Thanh minh, hoặc thăm mộ) con xin c&uacute;i lạy Thần linh đất n&agrave;y, Th&agrave;nh ho&agrave;ng bản thổ nơi đ&acirc;y, đất l&agrave;nh chim đậu, đức d&agrave;y thanh cao, giữ l&agrave;nh c&ocirc;ng lao, c&oacute; kết c&oacute; ph&aacute;t nhờ v&agrave;o thần quan, t&ocirc;n thần long mạch cao sang, nhị thập tứ hướng nhị thập tứ sơn quanh v&ugrave;ng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chọn đ&acirc;y an t&aacute;ng mộ phần, thỏa y&ecirc;n mu&ocirc;n thủa, hồng &acirc;n đời đời, gia &acirc;n m&atilde;i m&atilde;i kh&ocirc;ng th&ocirc;i, ch&uacute;ng con xin c&oacute; v&agrave;i lời cầu xin: B&aacute;i tạ thủ mộ thần quan, cho ch&acirc;n linh dưỡng cho h&agrave;i cốt nguy&ecirc;n vẹn to&agrave;n, ph&ugrave; hộ con ch&aacute;u trần gian, an khang mạnh khỏe ăn l&agrave;m gặp may. m dương c&aacute;ch trở, b&aacute;t nước n&eacute;n hương, biểu tấm l&ograve;ng th&agrave;nh, c&uacute;i xin chứng gi&aacute;m.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Phục duy cẩn c&aacute;o!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">(Khấn 3 lần rồi đốt v&agrave;ng tiền).</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Văn khấn lễ c&uacute;ng tạ mộ cuối năm mẫu 2</span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">3.3 Văn khấn c&uacute;ng tạ mộ cuối năm mẫu 3</span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&ldquo;Nam m&ocirc; a di đ&agrave; Phật!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nam m&ocirc; a di đ&agrave; Phật!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nam m&ocirc; a di đ&agrave; Phật!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Con lạy ch&iacute;n phương Trời, mười phương Chư Phật, Chư Phật mười phương</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Con k&iacute;nh lạy ng&agrave;i Địa tạng vương bồ t&aacute;t.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Con k&iacute;nh lạy ng&agrave;i Kim Ni&ecirc;n Đương cai Th&aacute;i Tuế Ch&iacute; đức T&ocirc;n thần, Kim ni&ecirc;n h&agrave;nh binh, C&ocirc;ng t&agrave;o Ph&aacute;n quan.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Con k&iacute;nh lạy ng&agrave;y Bản cảnh Th&agrave;nh Ho&agrave;ng Chư vị Đại Vương quản cai nơi nghĩa trang.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Con k&iacute;nh lạy Ng&agrave;i Bản xứ Thần linh Thổ địa T&ocirc;n thần.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Con k&iacute;nh lạy c&aacute;c ng&agrave;i Ngũ phương, Ngũ thổ Long Mạch T&ocirc;n thần, c&aacute;c ng&agrave;i Tiền Chu tước, Hậu Huyền Vũ, Tả Thanh Long, Hữu Bạch Hổ c&ugrave;ng liệt vị T&ocirc;n Thần cai quản trong nơi nghĩa trang n&agrave;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- Con k&iacute;nh lạy hương linh cụ:&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;..</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">H&ocirc;m nay l&agrave; ng&agrave;y&hellip;..... th&aacute;ng Chạp, năm cũ sắp qua, năm mới sắp đến.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">T&iacute;n chủ (ch&uacute;ng) con l&agrave;:&hellip;&hellip;&hellip;&hellip;..</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ngụ tại:&hellip;&hellip;&hellip;&hellip;..</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ch&uacute;ng con sắm sanh phẩm vật, hương hoa tr&agrave; quả,kim ng&acirc;n t&agrave;i m&atilde;, d&acirc;ng hiến tr&igrave;nh c&aacute;o T&ocirc;n thần.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">K&iacute;nh rước vong linh bản gia ti&ecirc;n tổ ch&uacute;ng con l&agrave;:...............</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Kỵ nhật l&agrave;&hellip;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">C&oacute; phần mộ t&aacute;ng tại&hellip;&hellip;&hellip;&hellip;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Được về với gia đ&igrave;nh đ&oacute;n mừng năm mới, để cho ch&aacute;u con được phụng sự trong tiết xu&acirc;n thi&ecirc;n, b&aacute;o đ&aacute;p &acirc;n th&acirc;m, tỏ l&ograve;ng hiếu k&iacute;nh. C&uacute;i xin T&ocirc;n thần, Phủ th&ugrave;y do&atilde;n hứa.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&acirc;m dương c&aacute;ch trở</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">B&aacute;t nước n&eacute;n hương.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Th&agrave;nh t&acirc;m k&iacute;nh lễ</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">C&uacute;i xin chứng gi&aacute;m</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ph&ugrave; hộ độ tr&igrave;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nam m&ocirc; a di đ&agrave; Phật!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nam m&ocirc; a di đ&agrave; Phật!</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nam m&ocirc; a di đ&agrave; Phật!&rdquo;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Văn khấn tạ mộ cuối năm mẫu 3</span></p>\r\n\r\n<p style=\"text-align:justify\"><em><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/phong-thuy-tho-cung\">Phong thủy thờ c&uacute;ng</a>&nbsp;</span></em></p>\r\n\r\n<h2 style=\"text-align:justify\"><strong><span style=\"font-size:16px\">4. Lưu &yacute; khi đi <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/nhung-mau-van-khan-ta-mo-cuoi-nam-day-du-nhat\">tạ mộ</a> ng&agrave;y cuối năm</span></strong></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nghĩa trang l&agrave; nơi linh thi&ecirc;ng n&ecirc;n khi đến nơi đ&acirc;y để tr&aacute;nh phạm phải những giới răn khi&ecirc;n những linh hồn đang y&ecirc;n nghỉ ở đ&acirc;y tức giận, người đến thăm viếng tạ mộ cho người th&acirc;n cần lưu &yacute; một v&agrave;i điều dưới đ&acirc;y nh&eacute;:</span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">Cần sắp lễ vật ở miếu thần linh. Bởi ở nghĩa trang thường c&oacute; nơi thờ thần linh, thổ địa ri&ecirc;ng.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">Khi đi tạ mộ, mọi người cần lưu t&acirc;m đến tất cả c&aacute;c cụ trong d&ograve;ng họ chứ kh&ocirc;ng n&ecirc;n chỉ quan t&acirc;m đến c&aacute;c cụ gần đời m&igrave;nh như cha mẹ, &ocirc;ng b&agrave;&hellip; Ngo&agrave;i ra, mọi người n&ecirc;n thắp hương c&aacute;c ng&ocirc;i mộ ở b&ecirc;n cạnh những ng&ocirc;i mộ người th&acirc;n của m&igrave;nh, ngay cả những ng&ocirc;i mộ v&ocirc; chủ cũng n&ecirc;n thắp cho họ n&eacute;n hương.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">Khi đi tạ mộ kh&ocirc;ng n&ecirc;n dẫm đạp l&ecirc;n mộ của nh&agrave; kh&aacute;c hoặc đ&aacute; v&agrave;o đồ c&uacute;ng tr&ecirc;n mộ của người kh&aacute;c.</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/z4746121049250_d179dcce3a6ef7a4e2e4e0a75324501b.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Những điều lưu &yacute; khi đi tạ mộ ng&agrave;y cuối năm</span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">Sau khi đi tạ mộ về, mọi người n&ecirc;n hơ lửa hoặc tắm nước gừng để thanh lọc c&aacute;c kh&iacute; v&agrave; &acirc;m kh&iacute; b&aacute;m v&agrave;o người v&agrave; quần &aacute;o.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">Một số người n&ecirc;n hạn chế đi tạ mộ v&igrave; nghĩa trang l&agrave; nơi nhiều kh&iacute; lạnh, vi khuẩn sinh s&ocirc;i. Đặc biệt l&agrave; người đang ốm yếu, trẻ nhỏ dưới 3 tuổi, phụ nữ mang thai, kh&ocirc;ng n&ecirc;n đi v&igrave; những đối tượng n&agrave;y rất dễ bị nhiễm &acirc;m kh&iacute;, phong h&agrave;n.</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/long-lan-quy-phung-la-gi-y-nghia-cua-tu-linh-trong-phong-thuy\">Giải m&atilde; Long Ly Quy Phụng-tứ linh trong văn h&oacute;a t&acirc;m linh</a></p>\r\n\r\n<p style=\"text-align:justify\">Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline&nbsp;0934.524.445&nbsp;hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\">https://nghiatrangthienduong.com.vn/</a></p>\r\n\r\n<p>&nbsp;</p>', 'true', 'News', 'nhung-mau-van-khan-ta-mo-cuoi-nam-day-du-nhat', 'le-cung-ta-mo-cuoi-nam-moi-ong-ba-to-tien-ve-an-tet-can-chuan-bi-gi-1-e1518060832725.jpg', 'Mẫu Văn Khấn Tạ Mộ Cuối Năm Đầy Đủ Chuẩn Nhất', '​​​​​​​Sử dụng văn khấn tạ mộ cuối năm cũng chính là thể hiện lòng thành tâm của con cháu dâng đến những người thân đã khuất và thổ thân cai quản khu vực linh thiêng.', '2023-12-30 10:24:51', '2023-12-30 10:58:12'),
(205, 2, 68, 'Top 5 hoa viên nghĩa trang đẹp nhất miền bắc', '​​​​​​​Xu hướng lựa chọn công viên nghĩa trang ở khu vực miền Bắc hiện nay đang ngày càng nhiều. Đây không chỉ ngoài là nơi mang đến cho người đã khuất một nơi yên nghỉ vĩnh hằng mà còn mang đến sự bình an, thịnh vượng cho con cháu.', NULL, '<p style=\"text-align:justify\"><strong><span style=\"font-size:16px\">Xu hướng lựa chọn c&ocirc;ng vi&ecirc;n nghĩa trang ở khu vực miền Bắc hiện nay đang ng&agrave;y c&agrave;ng nhiều. Đ&acirc;y kh&ocirc;ng chỉ ngo&agrave;i l&agrave; nơi mang đến cho người đ&atilde; khuất một nơi y&ecirc;n nghỉ vĩnh hằng m&agrave; c&ograve;n mang đến sự b&igrave;nh an, thịnh vượng cho con ch&aacute;u. Với vị tr&iacute; đẹp, phong thủy tốt c&ugrave;ng chất lượng cao cấp, c&aacute;c khu nghĩa trang n&agrave;y lu&ocirc;n nhận được sự ch&uacute; &yacute; rất lớn của thị trường.</span></strong></p>\r\n\r\n<h2 style=\"text-align:justify\"><strong><span style=\"font-size:16px\">5 C&ocirc;ng Vi&ecirc;n Nghĩa Trang Đẹp Nhất Miền Bắc</span></strong></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Cuộc sống x&atilde; hội ng&agrave;y c&agrave;ng ph&aacute;t triển, con người đ&atilde; kh&ocirc;ng c&ograve;n quan niệm việc t&igrave;m nơi x&acirc;y cất mộ phần l&agrave; một điều xui xẻo như trước nữa. Thậm ch&iacute;, kh&ocirc;ng &iacute;t người lu&ocirc;n mong muốn t&igrave;m nơi an nghỉ cho người th&acirc;n đ&atilde; khuất của m&igrave;nh thật đẹp, thật cao cấp. Đ&oacute; cũng ch&iacute;nh l&agrave; l&agrave; l&yacute; do khiến c&aacute;c&nbsp;c&ocirc;ng vi&ecirc;n nghĩa trang&nbsp;ở thị trường &ldquo;bất động sản t&acirc;m linh&rdquo; ph&iacute;a Bắc xuất hiện ng&agrave;y c&agrave;ng nhiều. Mỗi khu nghĩa trang c&oacute; điểm độc đ&aacute;o kh&aacute;c biệt của ri&ecirc;ng m&igrave;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Hiện nay, ở khu vực ph&iacute;a Bắc c&oacute; rất nhiều c&ocirc;ng vi&ecirc;n,&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\" target=\"_blank\">hoa vi&ecirc;n nghĩa trang</a>&nbsp;được x&acirc;y dựng để đ&aacute;p ứng nhu cầu ng&agrave;y c&agrave;ng cao của thị trường. C&oacute; thể kể đến với những &ldquo;ứng vi&ecirc;n ti&ecirc;u biểu&rdquo; dưới đ&acirc;y.</span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">&nbsp;<strong>C&Ocirc;NG VI&Ecirc;N NGHĨA TRANG THI&Ecirc;N ĐƯỜNG- TUY&Ecirc;N QUANG</strong> </span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><strong><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/z4739939583777_ed37970f5389ec145ff423bd67dab831.jpg\" /></strong>Tọa lạc tr&ecirc;n mảnh đất linh thi&ecirc;ng Tuy&ecirc;n Quang, c&ocirc;ng vi&ecirc;n nghĩa trang <a href=\"https://nghiatrangthienduong.com.vn/\">Thi&ecirc;n Đường</a> được biết đến l&agrave; một dự &aacute;n chuẩn resort 5 sao đẹp bậc nhất VN. Dự &aacute;n được khởi c&ocirc;ng x&acirc;y dựng từ năm 2021 với tổng quy m&ocirc; giai đoạn 1 l&ecirc;n đến 24,56 hecta. </span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/chùa báo ân/chuà báo ân 5.jpg\" /></span><a href=\"https://nghiatrangthienduong.com.vn/canh-quan/chua-bao-an\"><em><span style=\"font-size:16px\">Ch&ugrave;a B&aacute;o &Acirc;n tại c&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường</span></em></a></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Với thực trạng hiện nay khi quỹ đất nghĩa trang đang dần eo hẹp, c&ugrave;ng với bất cập trong quy hoạch v&agrave; trong chất lượng c&ocirc;ng tr&igrave;nh, sự thiếu chuy&ecirc;n nghiệp trong dịch vụ, c&ugrave;ng nguy cơ bị di dời, dỡ bỏ bất cứ l&uacute;c n&agrave;o&hellip; v&agrave; đồng thời nhu cầu t&igrave;m kiếm một nơi &aacute;n t&aacute;ng văn minh, hiện đại để b&aacute;o hiếu với cha mẹ, &ocirc;ng b&agrave;, tổ ti&ecirc;n ng&agrave;y c&agrave;ng tăng th&ecirc;m v&agrave;o đ&oacute; thị trường bds t&acirc;m linh l&agrave; một thị trường mới, nhiều tiềm năng, trong khi đ&oacute; c&aacute;c sản phẩm &ldquo;cao cấp&rdquo; mang t&iacute;nh đồng bộ cao rất &iacute;t c&aacute;c chủ đầu tư triển khai được th&igrave; dự &aacute;n c&ocirc;ng vi&ecirc;n thi&ecirc;n đường đ&atilde; được x&acirc;y dựng&nbsp; nhằm mục đ&iacute;ch tạo ra một c&ocirc;ng vi&ecirc;n nghĩa trang vừa thanh thịnh, t&ocirc;n nghi&ecirc;m nhưng lại mang quy hoạch b&agrave;i bản, t&iacute;nh thẩm mỹ cao, sản phẩm đa dạng, độc đ&aacute;o v&agrave; cao cấp.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh 11.jpg\" /></span><a href=\"https://nghiatrangthienduong.com.vn/canh-quan/truc-than-dao\"><em><span style=\"font-size:16px\">Trục Thần Đạo </span></em></a></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang Thi&ecirc;n Đường</a>&nbsp;c&oacute; thế đất tọa sơn hướng thủy được đặt tại x&atilde; Lang Qu&aacute;n, huyện Y&ecirc;n Sơn, tỉnh Tuy&ecirc;n Quang. Nơi đ&acirc;y được bao bọc bởi d&atilde;y n&uacute;i L&agrave; &ndash; một d&atilde;y n&uacute;i nổi tiếng của qu&ecirc; hương c&aacute;ch mạng. D&atilde;y n&uacute;i n&agrave;y gồm c&aacute;c ngọn n&uacute;i l&ecirc;n xuống tr&ugrave;ng tr&ugrave;ng, uốn lượn c&aacute; nhảy chim bay, độ dốc thoải, địa thế đất lớn v&agrave; rất vững ch&atilde;i. Khi quan s&aacute;t, c&oacute; thể thấy rằng c&aacute;c v&ugrave;ng đất được ngọn n&uacute;i n&agrave;y bao quanh lu&ocirc;n c&oacute; một trường sinh lực mạnh mẽ v&agrave; thế tụ kh&iacute; rất r&otilde; r&agrave;ng. C&ocirc;ng vi&ecirc;n Thi&ecirc;n Đường nằm tr&ecirc;n khu đất c&oacute; d&aacute;ng h&igrave;nh LINH QUY biểu hiện sự th&ocirc;ng minh,mẫn c&aacute;n v&agrave; trường thọ. Dải đất n&agrave;y c&oacute; mạch chạy theo hướng T&acirc;y Bắc - Đ&ocirc;ng Nam. Thế đất n&agrave;y theo Phong Thủy l&agrave; Vượng sơn Vượng hướng, c&oacute; địa vận 160 năm, đem lại Ph&uacute;c Lộc đời đời cho&nbsp;con ch&aacute;u.&nbsp;kết hợp với vị tr&iacute; giao th&ocirc;ng thuận lợi khi nằm tr&ecirc;n trục cao tốc Tuy&ecirc;n Quang- Ph&uacute; Thọ đ&atilde; gi&uacute;p khu nghĩa trang cao cấp n&agrave;y trở th&agrave;nh sự lựa chọn h&agrave;ng đầu của người d&acirc;n khu vực miền Bắc n&oacute;i ri&ecirc;ng v&agrave; cả nước n&oacute;i chung. </span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Khuôn viên gia đình/gia đình.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:center\"><a href=\"https://nghiatrangthienduong.com.vn/san-pham/khuon-vien-gia-dinh\"><span style=\"font-size:16px\"><em>Khu&ocirc;n vi&ecirc;n mộ gia đ&igrave;nh tại Thi&ecirc;n Đường</em></span></a></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thiết kế của <a href=\"https://nghiatrangthienduong.com.vn/\">nghĩa trang Thi&ecirc;n Đường</a> l&agrave; một quần thể kiến tr&uacute;c t&acirc;m linh rộng lớn v&agrave; rất h&agrave;i h&ograve;a về mặt thẩm mỹ. Dự &aacute;n chia th&agrave;nh khu đất x&acirc;y dựng mộ phần, khu c&acirc;y xanh, hồ nước v&agrave; c&aacute;c kiến tr&uacute;c nghệ thuật độc đ&aacute;o. Tất cả đ&atilde; biến v&ugrave;ng đất d&agrave;nh cho người chết trở n&ecirc;n sống động v&agrave; c&oacute; &ldquo;hồn&rdquo; hơn rất nhiều.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đọc th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n nghĩa trang Thi&ecirc;n Đường</a></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><strong><span style=\"font-size:16px\">C&ocirc;ng Vi&ecirc;n Nghĩa Trang Vĩnh Hằng &ndash; Ba V&igrave; &ndash; H&agrave; Nội</span></strong></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">C&ocirc;ng vi&ecirc;n nghĩa trang Vĩnh Hằng&nbsp;ch&iacute;nh thức đi v&agrave;o hoạt động từ năm 2004 với diện t&iacute;ch x&acirc;y dựng hơn 39ha. Sau hơn 15 năm hoạt động, nghĩa trang đang chuẩn bị mở rộng th&ecirc;m hơn 38ha để đ&aacute;p ứng nhu cầu &ldquo;x&acirc;y nh&agrave;&rdquo; cho người đ&atilde; khuất ở đ&acirc;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/vĩnh hằng ba vì.png\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><em><span style=\"font-size:16px\">To&agrave;n cảnh nghĩa trang Vĩnh Hằng Ba V&igrave;</span></em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">C&ocirc;ng vi&ecirc;n nghĩa trang&nbsp;n&agrave;y được c&aacute;c chuy&ecirc;n gia phong thủy đ&aacute;nh gi&aacute; l&agrave; c&oacute; thế &ldquo;Lưng tựa sơn, ch&acirc;n đạp thủy&rdquo; rất hiếm thấy. Xung quanh nghĩa trang l&agrave; d&ograve;ng s&ocirc;ng Đ&agrave; v&agrave; d&ograve;ng s&ocirc;ng T&iacute;ch bao bọc, tạo n&ecirc;n yếu tố phong thủy rất đắc địa. N&oacute; gi&uacute;p cho người c&otilde;i &acirc;m được y&ecirc;n ổn m&agrave; người c&otilde;i dương cũng được thịnh vượng, b&igrave;nh an.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/vĩnh hằng ba vì 1.png\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><em><span style=\"font-size:16px\">Khu mộ gia tộc, gia đ&igrave;nh tại nghĩa trang Vĩnh Hằng Ba V&igrave;</span></em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Mặc d&ugrave; l&agrave; khu nghĩa trang nhưng c&ocirc;ng vi&ecirc;n Vĩnh Hằng lại giống như một quần thể kiến tr&uacute;c rất ho&agrave;n chỉnh. Từ thiết kế đến c&aacute;c tiện &iacute;ch ở đ&acirc;y đều đảm bảo được t&iacute;nh thẩm mỹ, tiện &iacute;ch v&agrave; t&iacute;nh t&acirc;m linh rất cao. D&ugrave; l&agrave; người theo t&iacute;n ngưỡng n&agrave;o đều c&oacute; thể lựa chọn&nbsp;nghĩa trang lớn nhất H&agrave; Nội&nbsp;n&agrave;y để x&acirc;y dựng mộ phần cho người qu&aacute; cố.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đọc th&ecirc;m:&nbsp;<a href=\"https://wikihoavien.vn/cong-vien-vinh-hang-ba-vi-noi-yen-nghi-hoan-hao-van-minh/\" target=\"_blank\">C&ocirc;ng Vi&ecirc;n Vĩnh Hằng Ba V&igrave; &ndash; Nơi Y&ecirc;n Nghỉ Ho&agrave;n Hảo, Văn Minh</a></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><strong><span style=\"font-size:16px\">C&ocirc;ng Vi&ecirc;n Nghĩa Trang Thi&ecirc;n Đức Vĩnh Hằng Vi&ecirc;n</span></strong></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Người d&acirc;n Ph&uacute; Thọ lu&ocirc;n tự h&agrave;o khi tr&ecirc;n địa b&agrave;n của m&igrave;nh sở hữu một&nbsp;nghĩa trang tư nh&acirc;n&nbsp;c&oacute; chất lượng h&agrave;ng đầu tr&ecirc;n thị trường hiện nay.&nbsp;C&ocirc;ng vi&ecirc;n nghĩa trang Thi&ecirc;n Đức Vĩnh Hằng&nbsp;lu&ocirc;n mang đến cho bất cứ ai khi đến thăm viếng ở đ&acirc;y cảm gi&aacute;c nhẹ nh&agrave;ng, thanh tĩnh khi sở hữu một kh&ocirc;ng gian sinh th&aacute;i h&agrave;i h&ograve;a, tươi m&aacute;t.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/thiene đức.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><em><span style=\"font-size:16px\">Thi&ecirc;n Đức Vĩnh Hằng Vi&ecirc;n</span></em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Sở hữu vị tr&iacute; thuận lợi khi chỉ c&aacute;ch thủ đ&ocirc; H&agrave; Nội khoảng 1 giờ chạy xe, khu nghĩa trang n&agrave;y sẽ l&agrave; lựa chọn rất tốt để gi&uacute;p người th&acirc;n c&oacute; thể đến thăm viếng mộ phần thường xuy&ecirc;n v&agrave; đơn giản nhất. Điểm đặc biệt kh&ocirc;ng thể kh&ocirc;ng nhắc đến ở nghĩa trang Thi&ecirc;n Đức Vĩnh Hằng ch&iacute;nh l&agrave; t&iacute;nh phong thủy tốt đẹp. Tọa lạc tr&ecirc;n 9 quả đồi cao với thổ nhưỡng c&oacute; m&agrave;u ngũ sắc được xem l&agrave; rất th&iacute;ch hợp để người c&otilde;i &acirc;m y&ecirc;n nghỉ giấc ng&agrave;n thu. T&iacute;nh phong thủy t&acirc;m linh ở nghĩa trang được c&aacute;c chuy&ecirc;n gia đ&aacute;nh gi&aacute; rất cao.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/thiên đức 1.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><em><span style=\"font-size:16px\">Ch&ugrave;a Thi&ecirc;n Long</span></em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ở đ&acirc;y, c&aacute;c c&ocirc;ng tr&igrave;nh nghệ thuật t&acirc;m linh được x&acirc;y dựng để mang đến một kh&ocirc;ng gian thanh tịnh cho người đ&atilde; khuất. C&oacute; thể kể đến như: Đền Tr&igrave;nh, Tượng phật A- di &ndash; đ&agrave;, hồ Lục Thủy&hellip;Nghĩa trang 4 m&agrave;u sẽ mang những cảnh sắc ri&ecirc;ng rất đặc biệt để thu h&uacute;t sự ch&uacute; &yacute; của du kh&aacute;ch viếng thăm một c&aacute;ch hiệu quả nhất.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/thiên đức 2.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><em><span style=\"font-size:16px\">Tượng phật b&ecirc;n trong c&ocirc;ng vi&ecirc;n nghĩa trang Thi&ecirc;n Đức Vĩnh Hằng Vi&ecirc;n</span></em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đọc th&ecirc;m:&nbsp;<a href=\"https://wikihoavien.vn/cong-vien-tuong-niem-thien-duc-vinh-hang-vien/\" target=\"_blank\">C&ocirc;ng Vi&ecirc;n Tưởng Niệm Thi&ecirc;n Đức Vĩnh Hằng Vi&ecirc;n</a></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><strong><span style=\"font-size:16px\">C&ocirc;ng Vi&ecirc;n Nghĩa Trang Lạc Hồng Vi&ecirc;n</span></strong></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">N&oacute;i đến thị trường&nbsp;nghĩa trang miền Bắc&nbsp;th&igrave; Lạc Hồng Vi&ecirc;n sẽ lu&ocirc;n nằm trong danh s&aacute;ch được nhắc đến nhiều nhất. Dự &aacute;n được x&acirc;y dựng ở huyện Kỳ Sơn, tỉnh H&ograve;a B&igrave;nh. Với diện t&iacute;ch l&ecirc;n tới 98ha, khu nghĩa trang n&agrave;y c&oacute; thể đ&aacute;p ứng được nhu cầu t&igrave;m kiếm &ldquo;nơi an nghỉ cuối c&ugrave;ng&rdquo; cho người đ&atilde; khuất ở địa b&agrave;n tỉnh H&ograve;a B&igrave;nh v&agrave; c&aacute;c khu vực l&acirc;n cận.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/lac hồng viên 1.png\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><em><span style=\"font-size:16px\">Một thiết kế mộ gia đ&igrave;nh tại c&ocirc;ng vi&ecirc;n nghĩa trang Lạc Hồng Vi&ecirc;n</span></em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Điểm ấn tượng nhất của khu&nbsp;c&ocirc;ng vi&ecirc;n nghĩa trang&nbsp;n&agrave;y ch&iacute;nh l&agrave; c&oacute; vị tr&iacute; rất đắc địa. Tọa lạc tr&ecirc;n 9 quả đồi cao với sinh kh&iacute; tốt, nghĩa trang được đ&aacute;nh gi&aacute; l&agrave; hội tụ đủ 4 yếu tố phong thủy độc t&ocirc;n gồm: Long &ndash; Ly &ndash; Quy &ndash; Phượng. Kh&ocirc;ng dừng lại ở đ&oacute;, nghĩa trang được chủ đầu tư đặt nhiều t&acirc;m huyết để mang đến sự to&agrave;n diện trong cả thiết kế kiến tr&uacute;c v&agrave; yếu tố t&acirc;m linh. Ch&iacute;nh v&igrave; thế, n&oacute; vừa l&agrave; một nơi an nghỉ vĩnh hằng cho người đ&atilde; khuất v&agrave; vừa trở th&agrave;nh một lựa chọn ho&agrave;n hảo để người c&ograve;n sống thấy nhẹ l&ograve;ng khi đến viếng mộ ở đ&acirc;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/lajc hồng viên.jpg\" />Hiện nay,&nbsp;C&ocirc;ng vi&ecirc;n nghĩa trang Lạc Hồng Vi&ecirc;n&nbsp;đang cung cấp rất nhiều dịch vụ để hỗ trợ c&aacute;c kh&aacute;ch h&agrave;ng đặt mua đất x&acirc;y dựng mộ phần ở đ&acirc;y. Từ việc h&ograve;a thi&ecirc;u, mai t&aacute;ng cho đến l&agrave;m lễ hay thắp nhang h&agrave;ng ng&agrave;y&hellip;Tất cả đều được phục vụ chu đ&aacute;o từ A-Z. Đ&oacute; l&agrave; l&yacute; do &ldquo;tiếng thơm&rdquo; của nghĩa trang ng&agrave;y c&agrave;ng lan xa tr&ecirc;n thị trường &ldquo;địa ốc d&agrave;nh cho người đ&atilde; khuất&rdquo;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đọc th&ecirc;m:&nbsp;<a href=\"https://wikihoavien.vn/cong-vien-nghia-trang-lac-hong-vien/\" target=\"_blank\">C&ocirc;ng Vi&ecirc;n Nghĩa Trang Lạc Hồng Vi&ecirc;n</a></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><strong><span style=\"font-size:16px\">C&ocirc;ng Vi&ecirc;n Nghĩa Trang An Lạc &ndash; Quảng Ninh</span></strong></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trong c&aacute;c&nbsp;c&ocirc;ng vi&ecirc;n nghĩa trang miền Bắc&nbsp;th&igrave; nghĩa trang An Lạc được mệnh danh l&agrave; &ldquo;v&ugrave;ng đất linh cho người qu&aacute; cố&rdquo;. Nghĩa trang được x&acirc;y dựng tr&ecirc;n địa phận 2 x&atilde; Ho&agrave; B&igrave;nh v&agrave; Vũ Oai thuộc huyện Ho&agrave;nh Bồ, tỉnh Quảng Ninh. Vị tr&iacute; n&agrave;y gi&uacute;p việc di chuyển đến nghĩa trang thuận lợi v&agrave; đơn giản hơn rất nhiều. Bởi khu nghĩa trang chỉ c&aacute;ch trung t&acirc;m th&agrave;nh phố Hạ Long khoảng 17km, khoảng 20km để đến trung t&acirc;m huyện Ho&agrave;nh Bồ 20km v&agrave; 15km nếu muốn đến trung t&acirc;m th&agrave;nh phố Cẩm Phả.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/an lạc.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Được x&acirc;y dựng v&agrave; thiết kế với những h&agrave;ng c&acirc;y xanh mướt,&nbsp;c&ocirc;ng vi&ecirc;n nghĩa trang An Lạc&nbsp;sở hữu kh&ocirc;ng gian tho&aacute;ng đ&atilde;ng v&agrave; c&oacute; t&iacute;nh thẩm mỹ rất cao. N&oacute; lu&ocirc;n khiến người th&acirc;n của c&aacute;c vong linh đ&atilde; khuất thấy y&ecirc;n t&acirc;m khi lựa chọn nơi đ&acirc;y để &ldquo;x&acirc;y nh&agrave;&rdquo; cho người đ&atilde; khuất.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tổng diện t&iacute;ch đất của nghĩa trang An Lạc l&ecirc;n đến 631ha. Trong đ&oacute;, hơn 216ha được chủ đầu tư sử dụng để x&acirc;y dựng c&aacute;c khu mộ. Khoảng 415ha c&ograve;n lại được d&ugrave;ng để x&acirc;y dựng h&agrave;nh lang c&acirc;y xanh v&agrave; c&aacute;c c&ocirc;ng tr&igrave;nh nghệ thuật độc đ&aacute;o. N&oacute; xứng đ&aacute;ng với danh hiệu &ldquo;nghĩa trang lớn nhất miền Bắc&nbsp;m&agrave; thị trường đ&aacute;nh gi&aacute;. Khu nghĩa trang n&agrave;y c&oacute; khả năng đ&aacute;p ứng nhu cầu mai t&aacute;ng cho người d&acirc;n ở trong v&agrave; ngo&agrave;i tỉnh Quảng Ninh trong v&ograve;ng 50 năm.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/ân lạc.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Một điều ấn tượng ở&nbsp;c&ocirc;ng vi&ecirc;n nghĩa trang&nbsp;n&agrave;y ch&iacute;nh l&agrave; từ vị tr&iacute; địa l&yacute; cho đến c&aacute;c thiết kế trong c&ocirc;ng vi&ecirc;n lu&ocirc;n mang đến t&iacute;nh phong thủy rất đắc địa.Theo nhận định của Đại sư Phong thủy Đ&agrave;i Loan L&yacute; Diệu Hồng th&igrave;: &ldquo;Đ&acirc;y quả thực l&agrave; nơi long huyệt bảo địa, vượng kh&iacute; tụ t&agrave;i, kim b&iacute;ch huy ho&agrave;ng; mộ phần an t&aacute;ng tại nơi đ&acirc;y th&igrave; con ch&aacute;u sẽ được hưởng ph&uacute;c lợi v&ocirc; tận&rdquo;. C&oacute; thể n&oacute;i, nghĩa trang c&oacute; được lợi thế rất độc đ&aacute;o khi c&oacute; vị tr&iacute; &nbsp; giao h&ograve;a của Trời &ndash; Đất &ndash; M&acirc;y &ndash; N&uacute;i &ndash; S&ocirc;ng &ndash; Nước. Đ&acirc;y chắc chắn sẽ l&agrave; một lựa chọn c&oacute; thể gi&uacute;p y&ecirc;n l&ograve;ng người chết v&agrave;&nbsp; mang lại ph&uacute;c lộc cho con ch&aacute;u mu&ocirc;n đời.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Điểm nổi bật trong thiết kế của&nbsp;nghĩa trang An Lạc&nbsp;ch&iacute;nh l&agrave; n&oacute; m&ocirc; phỏng m&ocirc; h&igrave;nh c&ocirc;ng vi&ecirc;n hiện đại nhưng vẫn giữ được n&eacute;t truyền thống của người &Aacute; Đ&ocirc;ng. Sự xuất hiện của c&aacute;c c&ocirc;ng tr&igrave;nh nghệ thuật t&acirc;m linh ở trong c&ocirc;ng vi&ecirc;n như: Quảng trường An Lạc,&nbsp; khu vườn 12 con Gi&aacute;p, Vườn La H&aacute;n, Đại Phật A Di Đ&agrave;, Đại lộ An Lạctượng Di Đ&agrave; Tiếp dẫn, Điện Địa tạng, tượng Địa Tạng Vương Bồ T&aacute;t, tượng Quan Thế &Acirc;m Bồ T&aacute;t cưỡi rồng&hellip;Tất cả nhằm gi&uacute;p người đ&atilde; khuất c&oacute; được một nơi an nhi&ecirc;n miền đất Phật.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/an lạc 1.jpg\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ngo&agrave;i ra, trong nghĩa trang được chia th&agrave;nh 6 khu: An Ph&uacute;, An Thịnh; An Trường; An Thọ; An Vi&ecirc;n. Mỗi khu sẽ được thiết kế với n&eacute;t độc đ&aacute;o ri&ecirc;ng để tạo ấn tượng trong l&ograve;ng c&aacute;c vị kh&aacute;ch đến thăm mộ hay tham quan ở đ&acirc;y. V&agrave;o năm 2018, nghĩa trang đ&atilde; được c&ocirc;ng nhận l&agrave; C&ocirc;ng vi&ecirc;n nghĩa trang tr&ecirc;n n&uacute;i lớn nhất Việt Nam do Tổ chức kỷ lục Việt Nam ghi nhận v&agrave; trao bằng,</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đọc th&ecirc;m:&nbsp;<a href=\"https://wikihoavien.vn/cong-vien-nghia-trang-an-lac-quang-ninh/\" target=\"_blank\">C&ocirc;ng Vi&ecirc;n Nghĩa Trang An Lạc &ndash; Quảng Ninh</a></span></p>\r\n\r\n<p style=\"text-align:justify\">Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline&nbsp;0934.524.445 hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\">https://nghiatrangthienduong.com.vn/</a>.</p>', 'true', 'News', 'top-5-hoa-vien-nghia-trang-dep-nhat-mien-bac', '111.jpg', 'Top 5 hoa viên nghĩa trang đẹp nhất miền bắc', '​​​​​​​Xu hướng lựa chọn công viên nghĩa trang ở khu vực miền Bắc hiện nay đang ngày càng nhiều. Đây không chỉ ngoài là nơi mang đến cho người đã khuất một nơi yên nghỉ vĩnh hằng mà còn mang đến sự bình an, thịnh vượng cho con cháu.', '2023-12-30 10:54:17', '2023-12-30 10:57:52');
INSERT INTO `posts` (`id`, `user_id`, `category_id`, `name`, `detail`, `info`, `content`, `status`, `sort_by`, `slug`, `img`, `title`, `description`, `created_at`, `updated_at`) VALUES
(206, 2, 68, 'Truyền thuyết và ý nghĩa của hoa sen trong Phật giáo', '​​​​​​​Hoa sen tám cánh như một biểu tượng của sự hài hòa trong vũ trụ, gắn liền với nhiều hoạt động và hình ảnh của Phật giáo...', NULL, '<p><strong><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/truyen-thuyet-va-y-nghia-cua-hoa-sen-trong-phat-giao\">Hoa sen</a> t&aacute;m c&aacute;nh như một biểu tượng của sự h&agrave;i h&ograve;a trong vũ trụ, gắn liền với nhiều hoạt động v&agrave; h&igrave;nh ảnh của Phật gi&aacute;o. &Yacute; nghĩa của hoa sen trong đạo Phật tượng trưng cho những đức t&iacute;nh tốt đẹp m&agrave; mọi người hướng đến, sở dĩ như thế, v&igrave; thoa sen sở hữu vẻ đẹp tinh khiết &ldquo;gần b&ugrave;n m&agrave; chẳng h&ocirc;i tanh m&ugrave;i b&ugrave;n.&rdquo;</span></strong></p>\r\n\r\n<p><span style=\"font-size:16px\">Hoa sen như một biểu tượng của sự h&agrave;i h&ograve;a trong vũ trụ, l&agrave; minh chứng cho thuyết lu&acirc;n hồi của <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/cong-trinh-tuong-phat\">Phật gi&aacute;o</a></span><a href=\"https://nghiatrangthienduong.com.vn/canh-quan/cong-trinh-tuong-phat\">.</a></p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/.1.jpg\" /></p>\r\n\r\n<p><em>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/long-lan-quy-phung-la-gi-y-nghia-cua-tu-linh-trong-phong-thuy\">Long L&acirc;n Quy Phụng l&agrave; g&igrave;?</a></em></p>\r\n\r\n<p><span style=\"font-size:16px\">Nhắc đến hoa sen, người ta liền nghĩ ngay đến sự thanh tịnh của chốn t&acirc;m linh. Đ&acirc;y l&agrave; lo&agrave;i hoa của <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/cong-trinh-tuong-phat\">Phật</a>, thường được d&ugrave;ng trong việc thờ c&uacute;ng v&agrave; d&acirc;ng l&ecirc;n c&aacute;c Đấng thần linh. <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/truyen-thuyet-va-y-nghia-cua-hoa-sen-trong-phat-giao\">Hoa sen</a> c&ograve;n l&agrave; biểu tượng của t&igrave;nh y&ecirc;u thương v&agrave; sự cao cả. V&igrave; sống với Phật n&ecirc;n m&agrave;u sắc của hoa được v&iacute; như m&agrave;u của đức hạnh, từ bi, tr&iacute; tuệ hay c&ograve;n l&agrave; m&agrave;u của sự thanh khiết. Đ&acirc;y l&agrave; nơi t&acirc;m hồn thể hiện những phẩm chất th&aacute;nh thiện, tinh kh&ocirc;i nhất, xuất ph&aacute;t từ l&ograve;ng y&ecirc;u thương, khoan dung, nh&acirc;n &aacute;i v&agrave; cao thượng của kiếp người.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp;<img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/.2.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Nhắc đến Sen l&agrave; nhắc đến sự thanh tịnh chốn t&acirc;m linh, mang đức t&iacute;nh khi&ecirc;m tốn, kh&ocirc;ng khoe t&agrave;i, đua sắc với đời</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Nhắc đến Sen l&agrave; nhắc đến sự thanh tịnh chốn t&acirc;m linh, mang đức t&iacute;nh khi&ecirc;m tốn, kh&ocirc;ng khoe t&agrave;i, đua sắc với đời</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Từ hơn 5000 năm trước, <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/truyen-thuyet-va-y-nghia-cua-hoa-sen-trong-phat-giao\">hoa sen</a> đ&atilde; xuất hiện trong hầu hết c&aacute;c nghi thức tế lễ với sự s&ugrave;ng k&iacute;nh của người Ai Cập cổ đại. Tại Ấn Độ - c&aacute;i n&ocirc;i của Phật gi&aacute;o - hoa sen l&agrave; biểu tượng vươn l&ecirc;n giữa đầm lầy, tr&agrave;n đầy sức sống, biểu trưng cho sức mạnh tinh thần v&agrave; gi&aacute; trị đạo đức ở mỗi con người.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ở Việt Nam, ch&uacute;ng ta thường thấy hoa sen xuất hiện nhiều trong c&aacute;c ch&ugrave;a chiền, thiện viện gắn liền với c&aacute;c vị Phật, Bồ t&aacute;t. Ch&iacute;nh sự vươn l&ecirc;n mạnh mẽ v&agrave; tỏa ra hương sắc vốn c&oacute; của lo&agrave;i hoa n&agrave;y được v&iacute; như c&aacute;i t&acirc;m thanh tịnh, l&agrave; sự tu dưỡng tốt nhất trong kiếp nh&acirc;n sinh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp;<em>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/cong-trinh-tuong-phat\">C&aacute;c c&ocirc;ng tr&igrave;nh tượng Phật</a></em></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/.3.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">H&igrave;nh ảnh hoa sen ẩn dưới b&ugrave;n lầy gọi l&agrave; &ldquo;u vi&rdquo;, giống như cuộc đời của người tu h&agrave;nh lu&ocirc;n tr&aacute;nh xa những điều trần tục, cố gắng tu tập, ho&agrave;n thiện bản th&acirc;n, vươn đến sự giải tho&aacute;t. Sen l&agrave; lo&agrave;i hoa khi&ecirc;m tốn kh&ocirc;ng khoe t&agrave;i đua sắc với c&aacute;c lo&agrave;i hoa kh&aacute;c gọi l&agrave; &ldquo;ẩn vi&rdquo;, điều n&agrave;y được v&iacute; như đức t&iacute;nh khi&ecirc;m nhường của người <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/cong-trinh-tuong-phat\">Phật tử</a>. Họ kh&ocirc;ng ham danh lợi hay của cải vật chất chỉ, v&igrave; đ&oacute; chỉ l&agrave; ph&ugrave; du. L&aacute; sen to tr&ograve;n, ngửa rộng l&ecirc;n trời với những thớ mạch tựa như n&eacute;t vẽ của thi&ecirc;n nhi&ecirc;n gọi l&agrave; &ldquo;tế vi&rdquo;, mặt phải của l&aacute; đều hướng l&ecirc;n tr&ecirc;n tượng trưng cho sự th&agrave;nh t&acirc;m, cung k&iacute;nh.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Trong đạo Phật, hoa sen đại diện cho 8 đặc t&iacute;nh của tu sĩ, gồm: Trừng thanh - Thanh lương - Kh&ocirc;ng nhiễm - Ki&ecirc;n nhẫn - Ngẫu kh&ocirc;ng -&nbsp; Vi&ecirc;n dung - H&agrave;nh trực - Bồng thực.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ldquo;Trừng thanh&rdquo; c&oacute; nghĩa l&agrave; trong suốt, với h&agrave;m &yacute; ở đ&acirc;u c&oacute; Phật th&igrave; nơi đ&oacute; c&oacute; cuộc sống b&igrave;nh y&ecirc;n, hạnh ph&uacute;c.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ldquo;Thanh lương&rdquo; thể hiện tinh thần vượt kh&oacute;, cũng giống như hoa sen kh&ocirc;ng nở v&agrave;o m&ugrave;a xu&acirc;n ấm &aacute;p m&agrave; sinh trưởng v&agrave; nở rộ v&agrave;o ng&agrave;y h&egrave; nắng n&oacute;ng, khắc nghiệt. Mang đến cho mọi người những gi&aacute; trị tốt đẹp, l&agrave; nguồn động vi&ecirc;n, tưới m&aacute;t những t&acirc;m hồn.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ldquo;Kh&ocirc;ng nhiễm&rdquo; tức l&agrave; kh&ocirc;ng bị ảnh hưởng bởi m&ocirc;i trường b&ecirc;n ngo&agrave;i m&agrave; thay đổi những đặc t&iacute;nh vốn c&oacute; hay trở n&ecirc;n xấu đi, cũng như c&acirc;u &quot;gần b&ugrave;n m&agrave; chẳng h&ocirc;i tanh m&ugrave;i b&ugrave;n&quot;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ldquo;Ki&ecirc;n nhẫn&rdquo; l&agrave; chất liệu cho mọi sự th&agrave;nh c&ocirc;ng. Đ&acirc;y được xem l&agrave; đức t&iacute;nh quan trọng nhất của nh&agrave; Phật gắn liền với hoa sen, bởi qu&aacute; tr&igrave;nh sinh trưởng từ b&ugrave;n lầy, gặp kh&ocirc;ng &iacute;t kh&oacute; khăn, trắc trở nhưng ch&uacute;ng vẫn vươn l&ecirc;n tr&ecirc;n mặt nước v&agrave; tỏa ng&aacute;t hương thơm bằng &yacute; ch&iacute; ki&ecirc;n cường v&agrave; mạnh mẽ.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ldquo;Ngẫu kh&ocirc;ng&rdquo; nghĩa l&agrave; kh&ocirc;ng chấp nhặt, để bụng chuyện đời, trong gi&aacute;o l&yacute; của Đức Phật gọi l&agrave; đức t&iacute;nh &quot;hỷ, xả&quot;. Hoa sen mặc d&ugrave; thẳng tắp nhưng b&ecirc;n trong lại rỗng, ch&iacute;nh l&agrave; rũ bỏ mọi toan t&iacute;nh, đau khổ, chỉ giữ th&acirc;n t&acirc;m lu&ocirc;n an l&agrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ldquo;Vi&ecirc;n dung&rdquo; c&oacute; nghĩa l&agrave; kh&ocirc;ng v&igrave; tư lợi trước mắt m&agrave; bỏ đi l&ograve;ng từ bi, thiện l&agrave;nh. Đ&acirc;y l&agrave; đức t&iacute;nh cần c&oacute; ở mỗi người, giống như hoa sen từ l&uacute;c mới bắt đầu chớm nở đến l&uacute;c phai t&agrave;n lu&ocirc;n như vậy, kh&ocirc;ng thay đổi.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ldquo;H&agrave;nh trực&rdquo; tức n&oacute;i đến sự ngay thẳng trong đức t&iacute;nh của nh&agrave; Phật, m&agrave; hoa sen l&agrave; h&igrave;nh ảnh biểu trưng vươn l&ecirc;n vững v&agrave;ng, thẳng tắp trước đầu ngọn gi&oacute;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ldquo;Bồng thực&rdquo; l&agrave; đặc điểm duy nhất chỉ c&oacute; ở hoa sen, bởi, c&oacute; cả hoa v&agrave; quả c&ugrave;ng xuất hiện giống như &ldquo;gieo nh&acirc;n n&agrave;o, gặp quả nấy&rdquo; trong <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/cong-trinh-tuong-phat\">đạo Phật.</a>&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/.4.jpg\" /></span></p>\r\n\r\n<p><em>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/top-5-hoa-vien-nghia-trang-dep-nhat-mien-bac\">Top 5 hoa vi&ecirc;n nghĩa trang đẹp nhất miền Bắc&nbsp;</a></em></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/truyen-thuyet-va-y-nghia-cua-hoa-sen-trong-phat-giao\">Hoa sen</a> c&oacute; nhiều m&agrave;u sắc kh&aacute;c nhau, mỗi m&agrave;u ẩn chứa một &yacute; nghĩa ri&ecirc;ng biệt</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Sen đỏ: tượng trưng cho tr&aacute;i tim, t&igrave;nh y&ecirc;u, đam m&ecirc; v&agrave; sự năng động.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Sen xanh: l&agrave; biểu tượng của kiến thức v&agrave; tr&iacute; tuệ. Đ&acirc;y l&agrave; hiện th&acirc;n của Văn Th&ugrave; Sư Lợi Bồ T&aacute;t tr&iacute; tuệ vi&ecirc;n th&agrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Sen hồng: l&agrave; đo&aacute; sen của những vị tu theo gi&aacute;o ph&aacute;i nguy&ecirc;n thủy, tượng trưng cho sự tối thượng, c&oacute; &yacute; nghĩa t&ocirc;n nghi&ecirc;m, cao qu&yacute;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Sen t&iacute;m: l&agrave; m&agrave;u của sự huyền diệu, b&iacute; ẩn, c&oacute; thể thấy dưới h&igrave;nh thức b&uacute;p hoa hoặc hoa nở.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Sen trắng: l&agrave; biểu tượng của sự thuần khiết, của tr&iacute; tuệ vẹn nguy&ecirc;n, li&ecirc;n quan tới sự tịnh t&acirc;m trong mỗi con người.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ba c&aacute;nh hoa dưới tượng trưng cho Tam bảo Phật - Ph&aacute;p - Tăng. Năm c&aacute;nh hoa ph&iacute;a tr&ecirc;n l&agrave; năm đức hạnh theo gi&aacute;o l&yacute; của Đức Phật: tr&iacute; tuệ, hỉ xả, tinh tấn, thanh tịnh, từ bi.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Hoa sen l&agrave; lo&agrave;i hoa mang trong m&igrave;nh biểu trưng của Phật gi&aacute;o v&agrave; được lựa chọn để trở th&agrave;nh quốc hoa của Việt Nam.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline&nbsp;0934.524.445 hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\">https://nghiatrangthienduong.com.vn/</a>.</span></p>\r\n\r\n<p>&nbsp;</p>', 'true', 'News', 'truyen-thuyet-va-y-nghia-cua-hoa-sen-trong-phat-giao', '.1.jpg', 'Truyền thuyết và ý nghĩa của hoa sen trong Phật giáo', '​​​​​​​Hoa sen tám cánh như một biểu tượng của sự hài hòa trong vũ trụ, gắn liền với nhiều hoạt động và hình ảnh của Phật giáo...', '2023-12-31 10:38:31', '2023-12-31 10:40:49'),
(207, 2, 68, 'Đặc sắc lễ hội rước mẫu tại Tuyên Quang', 'Mẫu đền Hạ, đền Thượng, đền Ỷ La. Sau 3 năm gián đoạn không tổ chức do dịch bệnh Covid-19, năm nay, Lễ hội đã thu hút hàng vạn du khách thập phương trong và ngoài tỉnh tới tham gia.', NULL, '<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/.5.jpg\" /></p>\r\n\r\n<p>Mẫu đền Hạ, đền Thượng, đền Ỷ La. Sau 3 năm gi&aacute;n đoạn kh&ocirc;ng tổ chức do dịch bệnh Covid-19, năm nay, <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/dac-sac-le-hoi-ruoc-mau-tai-tuyen-quang\">Lễ hội</a> đ&atilde; thu h&uacute;t h&agrave;ng vạn du kh&aacute;ch thập phương trong v&agrave; ngo&agrave;i tỉnh tới tham gia.</p>\r\n\r\n<p>Đền Hạ, đền Thượng, đền Ỷ La l&agrave; những di t&iacute;ch văn h&oacute;a mang sự linh thi&ecirc;ng của t&iacute;n ngưỡng <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/truyen-thuyet-mau-thoai-tuyen-quang\">thờ Mẫu,</a> tạo n&ecirc;n một kh&ocirc;ng gian văn h&oacute;a độc đ&aacute;o, gi&agrave;u bản sắc tại th&agrave;nh phố Tuy&ecirc;n Quang. Năm 2018, Lễ hội đ&atilde; được Bộ Văn h&oacute;a, Thể thao v&agrave; Du lịch c&ocirc;ng nhận l&agrave; Di sản Văn h&oacute;a phi vật thể cấp quốc gia.</p>\r\n\r\n<p><em>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/truyen-thuyet-mau-thoai-tuyen-quang\">Truyền thuyết mẫu Thoải- Tuy&ecirc;n Quang</a></em></p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/.6.jpg\" /></p>\r\n\r\n<p><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/dac-sac-le-hoi-ruoc-mau-tai-tuyen-quang\">Lễ hội rước mẫu</a> đền Hạ, đền Thượng, đền Ỷ La được diễn ra ở <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/dia-the-phong-thuy\">3 ng&ocirc;i đền</a>: đền Hạ, phường T&acirc;n Quang; đền Thượng, x&atilde; Tr&agrave;ng Đ&agrave; v&agrave; đền Ỷ La, phường Ỷ La thuộc th&agrave;nh phố Tuy&ecirc;n Quang.</p>\r\n\r\n<p>Lễ hội bắt đầu từ huyền thoại về c&aacute;c <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/truyen-thuyet-mau-thoai-tuyen-quang\">Th&aacute;nh Mẫu</a> vốn l&agrave; những n&agrave;ng c&ocirc;ng ch&uacute;a con vua du ngoạn thắng cảnh s&ocirc;ng L&ocirc; m&agrave; hiến h&oacute;a về trời. Nh&acirc;n d&acirc;n lấy chỗ c&aacute;c n&agrave;ng hiến h&oacute;a lập đền Tam Kỳ (nay l&agrave; đền Hạ) thờ chị v&agrave; đền Thượng l&agrave; nơi thờ em. Đền Ỷ La được lập do c&oacute; sự thay đổi về vị tr&iacute; thờ của người chị. Hằng năm, họ gặp nhau 2 lần v&agrave;o trung tuần th&aacute;ng 2 v&agrave; th&aacute;ng 7 (&acirc;m lịch). Khi rước b&agrave;i vị của Mẫu từ đền Thượng v&agrave; đền Ỷ La về đền Hạ l&agrave; biểu hiện sự gặp gỡ đo&agrave;n tụ, sum họp gia đ&igrave;nh của 2 chị em Ngọc L&acirc;n v&agrave; Phương Dung c&ocirc;ng ch&uacute;a (Th&aacute;nh Mẫu) được thờ ở trong 3 ng&ocirc;i đền.</p>\r\n\r\n<p>B&agrave; Nguyễn Thị Bi&ecirc;n, ở quận Hai B&agrave; Trưng, th&agrave;nh phố H&agrave; Nội cho biết, đ&acirc;y l&agrave; lễ hội rất đặc sắc v&agrave; mang truyền thống bản sắc d&acirc;n tộc; b&agrave; đ&atilde; đi lễ hội n&agrave;y nhiều lần v&agrave; cảm thấy rất vui vẻ, x&uacute;c động.</p>\r\n\r\n<p>C&ograve;n b&agrave; Kiều B&iacute;ch V&acirc;n, đến từ th&agrave;nh phố Hải Ph&ograve;ng cho biết, c&aacute;c đền Mẫu ở Tuy&ecirc;n Quang gồm c&oacute; đền Mẫu Tam Cờ, đền Mẫu D&ugrave;m v&agrave; đền Mẫu Ỷ La. Đ&acirc;y đều l&agrave; c&aacute;c ng&ocirc;i đền thờ c&aacute;c con Vua H&ugrave;ng thứ 17 được gi&aacute;ng về v&ugrave;ng đất Tuy&ecirc;n Quang. Do biết được sử s&aacute;ch như thế n&ecirc;n cứ mỗi khi lễ hội diễn ra l&agrave; b&agrave; đều về đ&acirc;y để rước Mẫu.</p>\r\n\r\n<p>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/truyen-thuyet-va-y-nghia-cua-hoa-sen-trong-phat-giao\">Truyền thuyết v&agrave; &yacute; nghĩa của hoa sen trong Phật Gi&aacute;o</a></p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/tin tức/.7.jpg\" /></p>\r\n\r\n<p>Th&agrave;nh phố Tuy&ecirc;n Quang hiện c&oacute; 14 ng&ocirc;i đền, trong đ&oacute; c&oacute; 12 đền thờ Mẫu. Tuy&ecirc;n Quang c&ograve;n được coi l&agrave; nơi thờ ch&iacute;nh của <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/truyen-thuyet-mau-thoai-tuyen-quang\">Mẫu Thoải - Mẹ Nước</a> lớn nhất cả nước. C&aacute;c đền thờ Mẫu tại Tuy&ecirc;n Quang được h&igrave;nh th&agrave;nh sớm v&agrave; nổi tiếng linh thi&ecirc;ng trong t&iacute;n ngưỡng thờ Mẫu. Đ&acirc;y l&agrave; những điều kiện thuận lợi để Tuy&ecirc;n Quang đẩy mạnh ph&aacute;t triển du lịch t&acirc;m linh thu h&uacute;t kh&aacute;ch tham quan.</p>\r\n\r\n<p>&Ocirc;ng Nguyễn Xu&acirc;n Nho, Ph&oacute; Chủ tịch Ủy ban nh&acirc;n d&acirc;n th&agrave;nh phố Tuy&ecirc;n Quang cho biết, lễ hội rước Mẫu đền Hạ, đền Thượng v&agrave; đền Ỷ La l&agrave; lễ hội truyền thống kh&ocirc;ng chỉ ri&ecirc;ng của người d&acirc;n sinh sống tr&ecirc;n địa b&agrave;n th&agrave;nh phố Tuy&ecirc;n Quang m&agrave; c&ograve;n l&agrave; ng&agrave;y hội của nh&acirc;n d&acirc;n c&aacute;c d&acirc;n tộc tỉnh Tuy&ecirc;n Quang, được người d&acirc;n bảo tồn v&agrave; ph&aacute;t triển li&ecirc;n tục trong suốt chiều d&agrave;i lịch sử của th&agrave;nh phố.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; dịp để nh&acirc;n d&acirc;n c&aacute;c d&acirc;n tộc th&agrave;nh phố Tuy&ecirc;n Quang th&agrave;nh k&iacute;nh, tỏ l&ograve;ng tri &acirc;n c&ocirc;ng đức Th&aacute;nh Mẫu đối với cộng đồng c&aacute;c d&acirc;n tộc sinh sống tr&ecirc;n địa b&agrave;n. Đồng thời, cũng l&agrave; dịp để người d&acirc;n b&agrave;y tỏ kh&aacute;t vọng của m&igrave;nh, cầu cho cuộc sống ấm no, hạnh ph&uacute;c, nh&acirc;n cường, vật thịnh, mưa thuận, gi&oacute; h&ograve;a, m&ugrave;a m&agrave;ng tươi tốt bội thu, nh&agrave; nh&agrave; an khang, thịnh vượng.</p>\r\n\r\n<p><a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/dac-sac-le-hoi-ruoc-mau-tai-tuyen-quang\">Lễ hội</a> đền Hạ, đền Thượng, đền Ỷ La năm 2023 được tổ chức từ ng&agrave;y 2 đến 7/3 (tức từ 11 đến 16 th&aacute;ng 2 &acirc;m lịch) với c&aacute;c hoạt động gắn liền t&iacute;n ngưỡng thờ Mẫu Tam Phủ như: lễ rước kiệu từ đền Thượng, đền Ỷ La về đền Hạ, lễ rước nước v&agrave; thả hoa đăng.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, phần hội sẽ mang đến cho người d&acirc;n v&agrave; du kh&aacute;ch thập phương một kh&ocirc;ng gian văn h&oacute;a đa dạng với nhiều hoạt động như: li&ecirc;n hoan tr&igrave;nh diễn thực h&agrave;nh nghi lễ t&iacute;n ngưỡng thờ mẫu Tam phủ, c&aacute;c hoạt động văn h&oacute;a, văn nghệ, c&aacute;c tr&ograve; chơi d&acirc;n gian.</p>\r\n\r\n<p>Đ&acirc;y cũng l&agrave; dịp giới thiệu, quảng b&aacute;, t&ocirc;n vinh, ph&aacute;t huy c&aacute;c gi&aacute; trị văn h&oacute;a, c&aacute;c di t&iacute;ch cấp quốc gia của th&agrave;nh phố Tuy&ecirc;n Quang với bạn b&egrave;, du kh&aacute;ch trong nước v&agrave; quốc tế, g&oacute;p phần v&agrave;o sự ph&aacute;t triển chung của ng&agrave;nh du lịch Tuy&ecirc;n Quang.</p>\r\n\r\n<p><a href=\"https://nghiatrangthienduong.com.vn/\">Tỉnh Tuy&ecirc;n Quang</a> phấn đấu đến năm 2025, đ&oacute;n hơn 2,6 triệu lượt kh&aacute;ch du lịch. Tổng thu từ du lịch hơn 4.000 tỷ đồng, đ&oacute;ng g&oacute;p cho GRDP khoảng 5%, tạo việc l&agrave;m cho khoảng 20.000 lao động.</p>\r\n\r\n<p>&nbsp;Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline&nbsp;0934.524.445 hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\">https://nghiatrangthienduong.com.vn/</a>.</p>\r\n\r\n<p>&nbsp;</p>', 'true', 'News', 'dac-sac-le-hoi-ruoc-mau-tai-tuyen-quang', '.5.jpg', 'Đặc sắc lễ hội rước mẫu tại Tuyên Quang', 'Mẫu đền Hạ, đền Thượng, đền Ỷ La. Sau 3 năm gián đoạn không tổ chức do dịch bệnh Covid-19, năm nay, Lễ hội đã thu hút hàng vạn du khách thập phương trong và ngoài tỉnh tới tham gia.', '2023-12-31 11:02:19', '2023-12-31 11:04:36'),
(208, 2, 68, 'VẺ ĐẸP CỦA CÔNG VIÊN NGHĨA TRANG THIÊN ĐƯỜNG', 'Bước chân vào công viên du khách sẽ bị choáng ngợp ngay bởi vẻ đẹp tự nhiên của công viên.', NULL, '<p><span style=\"font-size:16px\">Nhắc đến <a href=\"https://nghiatrangthienduong.com.vn/\">xứ Tuy&ecirc;n </a>ngo&agrave;i việc được mệnh danh l&agrave; v&ugrave;ng đất Phật nơi c&oacute; nhiều ch&ugrave;a chiền v&agrave; quần thể đền Mẫu linh thi&ecirc;ng m&agrave; c&ograve;n phải nhắc đến cảnh sắc n&uacute;i non hữu t&igrave;nh, kh&ocirc;ng kh&iacute; trong l&agrave;nh tho&aacute;ng đ&atilde;ng của nơi đ&acirc;y. Địa thế v&ugrave;ng đất ở Tuy&ecirc;n Quang l&agrave; đất cao, sở hữu loại đất phong thủy bậc nhất, đất c&oacute; độ thẩm thấu tốt, đẹp, kh&ocirc;ng bị ngập &uacute;ng v&agrave; hội tụ nhiều sinh kh&iacute;. </span></p>\r\n\r\n<p><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/z4899209375565_36cb4f76e8a4d97ff727969405e11d48.jpg\" /></p>\r\n\r\n<p><span style=\"font-size:16px\">Ch&iacute;nh v&igrave; l&iacute; do đấy m&agrave; nh&agrave; đầu tư ch&uacute;ng t&ocirc;i đ&atilde; lựa chọn Tuy&ecirc;n Quang l&agrave; v&ugrave;ng đất để gửi gắm t&acirc;m nguyện x&acirc;y dựng n&ecirc;n một khu nghĩa trang hiện đại, th&acirc;n thiện với m&ocirc;i trường, quy hoạch hạ tầng, c&acirc;y xanh, kiến tr&uacute;c chuẩn mực c&ocirc;ng vi&ecirc;n văn h&oacute;a t&acirc;m linh. C&ocirc;ng ty Cổ phần Đầu tư X&acirc;y dựng v&agrave; Thương mại An Ph&aacute;t Vinh đ&atilde; khởi c&ocirc;ng x&acirc;y dựng dự &aacute;n C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường tọa lạc tại x&atilde; Lang Qu&aacute;n, huyện Y&ecirc;n Sơn, tỉnh Tuy&ecirc;n Quang v&agrave;o th&aacute;ng 7/2021.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Bước ch&acirc;n v&agrave;o c&ocirc;ng vi&ecirc;n du kh&aacute;ch sẽ bị cho&aacute;ng ngợp ngay bởi vẻ đẹp tự nhi&ecirc;n của c&ocirc;ng vi&ecirc;n. M&agrave;u xanh mướt mắt tại&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>&nbsp;lu&ocirc;n l&agrave; điểm nhấn khiến cho bất k&igrave; ai gh&eacute; qua đều cảm thấy dễ chịu, thong dong, thanh thản. L&agrave;m n&ecirc;n m&agrave;u xanh mướt ấy kh&ocirc;ng thể kh&ocirc;ng kể đến những thảm cỏ trải d&agrave;i từ đồi Thi&ecirc;n T&acirc;m đến đồi Thi&ecirc;n Lộc.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/z4899209548766_8a96a36678f530a268c56452fa202a57.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Tại C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">Thi&ecirc;n Đường</a>, ch&uacute;ng t&ocirc;i sử dụng cỏ Zoysia Toyco, một giống cỏ được nghi&ecirc;n cứu v&agrave; ph&aacute;t triển bởi Đại học California, Mỹ. Đ&acirc;y l&agrave; loại cỏ được sử dụng phổ biến hiện nay tại c&aacute;c c&ocirc;ng tr&igrave;nh c&acirc;y xanh lớn như c&ocirc;ng vi&ecirc;n, s&acirc;n Golf với t&aacute;c dụng phủ xanh mặt đất, hạn chế cỏ dại v&agrave; tạo mảng xanh cho c&ocirc;ng tr&igrave;nh, s&acirc;n nh&agrave;.&nbsp;Cỏ Zoysia Toyco c&oacute; sức sống tốt, l&aacute; c&acirc;y m&agrave;u xanh nhạt kh&ocirc;ng xanh đậm. Th&acirc;n cỏ nhỏ, ngắn, b&ograve; s&aacute;t mặt đất. Phiến l&aacute; cứng, ngắn, c&oacute; chiều d&agrave;i khoảng 5cm, l&aacute; h&igrave;nh kim. L&aacute; cỏ Zoysia Toyco mọc so le nhau, xếp th&agrave;nh 2 d&atilde;y theo th&acirc;n d&agrave;i khoảng 10cm. Loại cỏ n&agrave;y thường mọc theo phương ngang, c&oacute; độ che phủ cao, kh&ocirc;ng bị ngả m&agrave;u rạ ch&aacute;y như cỏ l&aacute; gừng m&agrave; thường&nbsp;xanh mướt quanh năm.</span></p>\r\n\r\n<p><em><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/kien-truc-canh-quan\">Kiến tr&uacute;c cảnh quan tại Thi&ecirc;n Đường</a></span></em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\">Điểm xuyến cho vẻ đẹp xanh tươi đấy của c&ocirc;ng vi&ecirc;n kh&ocirc;ng chỉ c&oacute; mỗi cỏ m&agrave; c&ograve;n phải kể đến cả những h&agrave;ng l&ecirc; trắng trải d&agrave;i khắp c&aacute;c lối đi trong c&ocirc;ng vi&ecirc;n v&agrave; những c&acirc;y đại thụ vững ch&atilde;i. Việc lựa chọn h&agrave;ng trăm c&acirc;y l&ecirc; trắng để trồng trong c&ocirc;ng vi&ecirc;n cũng được ch&uacute;ng t&ocirc;i suy nghĩ kỹ c&agrave;ng, vẻ đẹp của hoa l&ecirc; trắng mang lại sức h&uacute;t hấp dẫn với người chi&ecirc;m ngưỡng, mang trong m&igrave;nh m&agrave;u trắng tinh kh&ocirc;i, thuần khiết, lo&agrave;i hoa n&agrave;y c&agrave;ng l&agrave;m cho người nh&igrave;n say đắm trước vẻ đẹp của n&oacute;, mang lại cho người tham quan cảm gi&aacute;c y&ecirc;n b&igrave;nh, nhẹ l&ograve;ng hơn khi đặt tr&ecirc;n tới đ&acirc;y. </span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/Ảnh Thiên Đường/thien_duong_19.05_4.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><a href=\"https://nghiatrangthienduong.com.vn/gioi-thieu/kien-truc-canh-quan\"><em><span style=\"font-size:16px\">Trục đường l&ecirc; trắng tại Thi&ecirc;n Đường</span></em></a></p>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp;Những c&acirc;y đại thụ c&oacute; tuổi đời l&ecirc;n đến 200 300 tuổi cũng được chủ đầu tư lựa chọn kỹ lưỡng để đem về. Những giống c&acirc;y n&agrave;y th&igrave; c&oacute; ưu điểm l&agrave; l&acirc;u năm, &iacute;t rụng l&agrave; v&agrave; xanh mướt quanh năm, đảm bảo cho c&ocirc;ng vi&ecirc;n nghĩa trang lu&ocirc;n giữ được vẻ xanh m&aacute;t nhất.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ngo&agrave;i vẻ đẹp về thi&ecirc;n nhi&ecirc;n, c&acirc;y cối, nước non hữu t&igrave;nh v&igrave; c&oacute; thế phong thủy tựa sơn hướng thủy, được thi&ecirc;n nhi&ecirc;n bảo bọc, che chở th&igrave; <a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n nghĩa trang</a> Thi&ecirc;n Đường c&ograve;n ấn tượng bởi vẻ đẹp tho&aacute;t tục của n&oacute;. Đến thăm c&ocirc;ng vi&ecirc;n h&igrave;nh ảnh đầu ti&ecirc;n du kh&aacute;ch c&oacute; thể bắt gặp được ch&iacute;nh l&agrave; ch&ugrave;a B&aacute;o &Acirc;n. Ng&ocirc;i ch&ugrave;a mang đậm kiến tr&uacute;c văn h&oacute;a Bắc Bộ xưa. </span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/chùa báo ân/chuà báo ân 5.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/canh-quan/chua-bao-an\">Ch&ugrave;a B&aacute;o &Acirc;n</a> - C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</span></em></p>\r\n\r\n<p><span style=\"font-size:16px\">Mang trong m&igrave;nh vẻ đẹp cổ k&iacute;nh, ch&ugrave;a B&aacute;o &Acirc;n đ&atilde; tạo cho cảnh quan nơi đ&acirc;y c&oacute; phần n&ecirc;n thơ. Với lối thiết kế m&aacute;i ng&oacute;i v&ograve;m cong v&agrave; trụ cột được đi&ecirc;u khắc họa tiết rồng phượng v&ocirc; c&ugrave;ng tinh xảo đ&atilde; l&agrave;m cho ng&ocirc;i ch&ugrave;a c&agrave;ng th&ecirc;m ấm c&uacute;ng v&agrave; gần gũi với người d&acirc;n Việt Nam. Khu&ocirc;n vi&ecirc;n của ch&ugrave;a c&ograve;n được l&aacute;t gạch nung c&agrave;ng khiến cho du kh&aacute;ch c&oacute; cảm gi&aacute;c th&acirc;n thuộc. </span></p>\r\n\r\n<p><em><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/canh-quan\">Ch&ugrave;a B&aacute;o &Acirc;n</a></span></em></p>\r\n\r\n<p><span style=\"font-size:16px\">Từ ch&ugrave;a B&aacute;o &Acirc;n, ta ph&ograve;ng tầm mắt ra xa hơn l&agrave; sẽ thấy được những pho tượng Phật h&ugrave;ng vĩ tại đ&acirc;y. C&oacute; thể kể đến như tượng Đức Phật Th&iacute;ch Ca, tượng Phật Quan Thế &Acirc;m Bồ T&aacute;t, tượng Phật Địa Tạng Vương Bồ T&aacute;t, tượng Phật Đại thế Ch&iacute; Bồ T&aacute;t, tượng Phật Di Lặc, v&agrave; cả trục Thần Đạo</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh 5.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Tượng Phật Th&iacute;ch Ca M&acirc;u Ni - C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</span></em></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/canh-quan/tuong-phat-thich-ca\">Tượng Đức Phật Th&iacute;ch Ca</a> ngự tr&ecirc;n t&ograve;a sen, ở vị tri trung t&acirc;m của đồi Thi&ecirc;n An. Tượng Phật được đ&uacute;c bằng đ&aacute; nguy&ecirc;n khối với độ cao 27m. Lưng của tượng Phật tựa v&agrave;o n&uacute;i, mắt nh&igrave;n hướng về s&ocirc;ng. Dưới ch&acirc;n tượng l&agrave; phần diện t&iacute;ch thiết kế cho c&aacute;c phần mộ. </span></p>\r\n\r\n<p><em><span style=\"font-size:16px\">Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/cong-trinh-tuong-phat\">C&ocirc;ng tr&igrave;nh t&acirc;m linh của Thi&ecirc;n Đường</a></span></em></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"https://nghiatrangthienduong.com.vn/uploads/photos/2/công trình tâm linh/tâm linh 2.jpg\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em><span style=\"font-size:16px\">Tượng Phật Quan Thế &Acirc;m Bồ T&aacute;t - C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</span></em></p>\r\n\r\n<p><span style=\"font-size:16px\"><a href=\"https://nghiatrangthienduong.com.vn/canh-quan/tuong-quan-the-am-bo-tat\">Tượng Phật Quan Thế &Acirc;m</a> được tọa lạc tr&ecirc;n đồi Thi&ecirc;n Đức, được đ&uacute;c bằng b&ecirc; t&ocirc;ng cốt th&eacute;p, cao 19m, trọng lượng gần 100 tấn, được c&aacute;c nghệ nh&acirc;n đi&ecirc;u khắc k&igrave; c&ocirc;ng trong khoảng 8 th&aacute;ng. Tr&ocirc;ng từ xa sẽ thấy h&igrave;nh ảnh Bồ T&aacute;t nổi bật tr&ecirc;n đ&agrave;i sen, ph&iacute;a sau l&agrave; khung trời bao la, n&uacute;i rừng h&ugrave;ng vĩ&hellip;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&nbsp;Tất cả những h&igrave;nh ảnh ấy đ&atilde; c&ugrave;ng nhau vẽ n&ecirc;n bức tranh thanh khiết, tho&aacute;t tục cho c&ocirc;ng vi&ecirc;n. Được x&acirc;y dựng l&agrave; một quần thể t&acirc;m linh đặc sắc mang n&eacute;t y&ecirc;n b&igrave;nh, thanh tịnh, c&ocirc;ng vi&ecirc;n nghĩa trang Thi&ecirc;n Đường đ&atilde; thiết kế rất tốt để c&oacute; cảnh quan đẹp đẽ như vậy. Đến đ&acirc;y, người th&acirc;n gia đ&igrave;nh sẽ lu&ocirc;n cam thấy được vỗ vễ, thanh thản. </span></p>\r\n\r\n<p>Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về C&ocirc;ng vi&ecirc;n&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline&nbsp;<strong>0934.524.445</strong>&nbsp;hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\">https://nghiatrangthienduong.com.vn/</a>.</p>', 'true', 'News', 've-dep-cua-cong-vien-nghia-trang-thien-duong', '.0.jpg', 'VẺ ĐẸP CỦA CÔNG VIÊN NGHĨA TRANG THIÊN ĐƯỜNG', 'Bước chân vào công viên du khách sẽ bị choáng ngợp ngay bởi vẻ đẹp tự nhiên của công viên.', '2024-01-04 08:39:41', '2024-01-04 08:39:41'),
(209, 2, 68, 'Những ngày lễ tâm linh đặc biệt trong năm của người Việt Nam', 'Dưới đây là tổng hợp những ngày lễ tâm linh đặc biệt trong năm của người Việt Nam.', NULL, '<h2>Những ng&agrave;y lễ t&acirc;m linh đặc biệt trong năm của người Việt Nam</h2>\r\n\r\n<h3>Ng&agrave;y 01 th&aacute;ng 01: Tết Nguy&ecirc;n Đ&aacute;n</h3>\r\n\r\n<p>Tết Nguy&ecirc;n Đ&aacute;n, c&ograve;n gọi l&agrave; Tết Cả, Tết Ta, Tết &Acirc;m lịch, Tết Cổ truyền l&agrave; dịp lễ quan trọng nhất của người Việt Nam, theo ảnh hưởng văn h&oacute;a của Tết &Acirc;m lịch Trung Hoa v&agrave; V&ograve;ng văn h&oacute;a Đ&ocirc;ng &Aacute;. Tết t&iacute;nh theo &Acirc;m lịch l&agrave; lịch theo chu kỳ vận h&agrave;nh của Mặt Trăng n&ecirc;n Tết Nguy&ecirc;n Đ&aacute;n muộn hơn Tết Dương lịch. Do quy luật 3 năm nhuận một th&aacute;ng của &Acirc;m lịch n&ecirc;n ng&agrave;y đầu năm của dịp Tết Nguy&ecirc;n Đ&aacute;n kh&ocirc;ng bao giờ trước ng&agrave;y 21 th&aacute;ng 1 Dương lịch v&agrave; sau ng&agrave;y 19 th&aacute;ng 2 Dương lịch m&agrave; thường v&agrave;o khoảng cuối th&aacute;ng 1đến giữa th&aacute;ng 2 Dương lịch. H&agrave;ng năm, Tết được tổ chức v&agrave;o ng&agrave;y mồng 1 th&aacute;ng 1 theo &acirc;m lịch. Trong những ng&agrave;y Tết, c&aacute;c gia đ&igrave;nh sum họp b&ecirc;n nhau, thờ c&uacute;ng tổ ti&ecirc;n, thăm hỏi người th&acirc;n, mừng tuổi, h&aacute;i lộc,&hellip;</p>\r\n\r\n<p><img alt=\"Nguồn gốc, ý nghĩa của tên gọi Tết Nguyên đán không phải ai cũng biết\" src=\"https://giadinh.mediacdn.vn/296230595582509056/2023/1/10/tet-nguyen-dan-1673318634018292874670.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em>Gia đ&igrave;nh xum họp trong ng&agrave;y&nbsp;Tết Nguy&ecirc;n Đ&aacute;n.</em></p>\r\n\r\n<h3>Ng&agrave;y 15 th&aacute;ng 01: Tết Nguy&ecirc;n Ti&ecirc;u</h3>\r\n\r\n<p>Ng&agrave;y Rằm th&aacute;ng Gi&ecirc;ng, ng&agrave;y trăng tr&ograve;n đầu ti&ecirc;n của năm mới, theo tục xưa gọi l&agrave;: Tết Nguy&ecirc;n Ti&ecirc;u. L&agrave; một trong những ng&agrave;y rất quan trọng theo lịch &acirc;m của người ch&acirc;u &Aacute;. C&oacute; nơi thậm ch&iacute; c&ograve;n quan niệm &ldquo;C&uacute;ng cả năm kh&ocirc;ng bằng rằm th&aacute;ng Gi&ecirc;ng&rdquo;. Trong ng&agrave;y lễ n&agrave;y, mỗi gia đ&igrave;nh người Việt c&oacute; thể c&uacute;ng cơm chay, hương đ&egrave;n, hoa quả hoặc m&acirc;m lễ mặn, x&ocirc;i g&agrave;, cơm canh th&agrave;nh k&iacute;nh d&acirc;ng l&ecirc;n tổ ti&ecirc;n, v&agrave; đi lễ Ch&ugrave;a, lễ Phật để cầu mong cho sự b&igrave;nh y&ecirc;n, khoẻ mạnh quanh năm.</p>\r\n\r\n<p><img alt=\"Special Dishes for Tet holidays - vietnam-online\" src=\"https://th.bing.com/th/id/R.675aa56f3502ec990d4923411556d9db?rik=Um76X3mZf7uJwA&amp;riu=http%3a%2f%2fvietnam-online.org%2fwp-content%2fuploads%2f2018%2f02%2fmam-co-tet-4.jpg&amp;ehk=1KIN7DEqkd9A8jnNjp33TZ6oFweqjttscYx3KcQ08ZI%3d&amp;risl=&amp;pid=ImgRaw&amp;r=0\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em>M&acirc;m cỗ c&uacute;ng ng&agrave;y tết Nguy&ecirc;n Ti&ecirc;u</em></p>\r\n\r\n<p><em>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/phong-thuy-tho-cung\">Phong thủy thờ c&uacute;ng</a></em></p>\r\n\r\n<h3>Ng&agrave;y 3 th&aacute;ng 3: Tết H&agrave;n Thực</h3>\r\n\r\n<p>Theo nghĩa chữ H&aacute;n &ldquo;H&agrave;n&rdquo; l&agrave; lạnh, &ldquo;thực &ldquo; l&agrave; ăn; &ldquo; Tết H&agrave;n thực&rdquo; l&agrave; tết ăn đồ lạnh. Phong tục cổ truyền n&agrave;y c&oacute; nguồn gốc từ Trung Quốc theo một c&acirc;u chuyện ly kỳ truyền tụng nhiều đời về hiền sỹ Giới Tử Th&ocirc;i. Do giao lưu văn ho&aacute; l&acirc;u đời với Trung Hoa n&ecirc;n người Việt ảnh hưởng tết H&agrave;n thực. Nhưng ở nước ta tết H&agrave;n thực kh&ocirc;ng phải để tưởng nhớ đến Tử Th&ocirc;i m&agrave; mang &yacute; nghĩa d&acirc;n tộc s&acirc;u sắc. V&agrave;o ng&agrave;y tết, c&aacute;c gia đ&igrave;nh l&agrave;m b&aacute;nh tr&ocirc;i b&aacute;nh chay c&uacute;ng &ocirc;ng b&agrave;, tổ ti&ecirc;n v&agrave; kh&ocirc;ng ki&ecirc;ng đốt lửa. Cũng trong dịp n&agrave;y nhiều nơi l&agrave;m b&aacute;nh tr&ocirc;i b&aacute;nh chay c&uacute;ng thần ho&agrave;ng. Ng&agrave;y nay, cứ mỗi dịp tết H&agrave;n thực về, người d&acirc;n mọi v&ugrave;ng qu&ecirc; đều l&agrave;m b&aacute;nh tr&ocirc;i, b&aacute;nh chay. C&ograve;n ở thị th&agrave;nh, ng&agrave;y xu&acirc;n đi du ngoạn, kh&aacute;ch cũng được hưởng hương vị b&aacute;nh tr&ocirc;i, b&aacute;nh chay từ c&aacute;c qu&aacute;n h&agrave;ng.</p>\r\n\r\n<p><img alt=\"Nguồn gốc, ý nghĩa bánh trôi nước ngày Tết Hàn Thực 3/3 âm lịch là gì\" src=\"https://useful.vn/wp-content/uploads/2020/05/2_1.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em>B&aacute;nh tr&ocirc;i c&uacute;ng &ocirc;ng, b&agrave;, tổ ti&ecirc;n trong ng&agrave;y Tết H&agrave;n Thực.</em></p>\r\n\r\n<p><em>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/dac-sac-le-hoi-ruoc-mau-tai-tuyen-quang\">Đặc sắc lễ hội rước mẫu tại Tuy&ecirc;n Quang</a></em></p>\r\n\r\n<h3>&nbsp;Ng&agrave;y 10 th&aacute;ng 3: Giỗ Tổ H&ugrave;ng Vương</h3>\r\n\r\n<p>Ng&agrave;y Giỗ tổ H&ugrave;ng Vương hay Lễ hội Đền H&ugrave;ng l&agrave; một ng&agrave;y lễ của Việt Nam. Ng&agrave;y giỗ H&ugrave;ng Vương từ l&acirc;u đ&atilde; được c&ocirc;ng nhận l&agrave; một trong những ng&agrave;y quốc lễ của Việt Nam. Từ thời xưa, c&aacute;c triều đại qu&acirc;n chủ v&agrave; phong kiến Việt Nam đ&atilde; quản l&yacute; Đền H&ugrave;ng theo c&aacute;ch giao thẳng cho d&acirc;n sở tại tr&ocirc;ng nom, sửa chữa, c&uacute;ng b&aacute;i, l&agrave;m Giỗ Tổ v&agrave;o ng&agrave;y 10 th&aacute;ng 3 &acirc;m lịch; đổi lại d&acirc;n địa phương được triều đ&igrave;nh miễn cho những khoản thuế ruộng c&ugrave;ng sưu dịch v&agrave; sung v&agrave;o l&iacute;nh. Đ&acirc;y l&agrave; ng&agrave;y hội truyền thống của d&acirc;n tộc tưởng nhớ c&ocirc;ng lao dựng nước của H&ugrave;ng Vương. Nghi lễ truyền thống được tổ chức h&agrave;ng năm v&agrave;o mồng 10 th&aacute;ng 3 &acirc;m lịch tại Đền H&ugrave;ng, th&agrave;nh phố Việt Tr&igrave;, tỉnh Ph&uacute; Thọ nhưng được d&acirc;n Việt trong cũng như ngo&agrave;i nước đều kỷ niệm.</p>\r\n\r\n<p><img alt=\"Các nghi lễ chính thức trong ngày giỗ tổ Hùng Vương | Phụ Nữ &amp; Gia Đình\" src=\"https://s.meta.com.vn/img/thumb.ashx/Data/image/2021/03/15/gio-to-hung-vuong-6.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em>Nghi lễ trong ng&agrave;y Giỗ tổ H&ugrave;ng Vương.</em></p>\r\n\r\n<h3>Ng&agrave;y 4;5 th&aacute;ng 4: Tết Thanh Minh</h3>\r\n\r\n<p>Tiết Thanh Minh l&agrave; khoảng thời gian bắt đầu từ ng&agrave;y 4 hay 5 th&aacute;ng 4 khi hết tiết xu&acirc;n ph&acirc;n v&agrave; kết th&uacute;c v&agrave;o khoảng ng&agrave;y 20 hay 21 th&aacute;ng 4 trong lịch Gregory theo c&aacute;c m&uacute;i giờ Đ&ocirc;ng &Aacute; khi tiết cốc vũ bắt đầu.</p>\r\n\r\n<p>Với đạo l&yacute; uống nước nhớ nguồn, từ xa xưa Tết Thanh minh đ&atilde; trở th&agrave;nh lễ hội quan trọng, thi&ecirc;ng li&ecirc;ng, đi s&acirc;u v&agrave;o tiềm thức của biết bao người Việt Nam trong v&agrave; ngo&agrave;i nước. Tết Thanh Minh gắn liền với đạo đức, bổn phận người Việt Nam &ndash; bổn phận của con ch&aacute;u tưởng nhớ c&ocirc;ng lao của tổ phụ, của những người đi trước.</p>\r\n\r\n<p>&nbsp;<img alt=\"Nguồn gốc và ý nghĩa của Tết Thanh Minh – Hoa viên vĩnh hằng Kiên Giang\" src=\"https://th.bing.com/th/id/R.45890c431eac2b2ad81e5a615030143d?rik=rCU0IDFMYLOIqg&amp;riu=http%3a%2f%2fhoavienvinhhang.com%2fwp-content%2fuploads%2f2020%2f10%2fNguon-goc-va-y-nghia-cua-Tet-Thanh-Minh.jpg&amp;ehk=Olq%2bFF1OmQKECINFPkmqhU7kOyFli7YyJ8vBhrZ%2b2Rc%3d&amp;risl=&amp;pid=ImgRaw&amp;r=0\" /></p>\r\n\r\n<p><em>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/truyen-thuyet-va-y-nghia-cua-hoa-sen-trong-phat-giao\">Truyền thuyết v&agrave; &yacute; nghĩa của hoa sen trong Phật Gi&aacute;o</a></em></p>\r\n\r\n<h3>Ng&agrave;y 15 th&aacute;ng 4: Lễ Phật Đản</h3>\r\n\r\n<p><a href=\"https://nghiatrangthienduong.com.vn/canh-quan/tuong-phat-thich-ca\">Đức Phật Th&iacute;ch Ca M&acirc;u Ni</a> xuất th&acirc;n l&agrave; một th&aacute;i tử t&ecirc;n Tất Đạt Đa, d&ograve;ng họ Cồ Đ&agrave;m, Vương tộc Th&iacute;ch Ca. Ng&agrave;i được cho l&agrave; sinh v&agrave;o ng&agrave;y Rằm th&aacute;ng tư năm 624 trước t&acirc;y lịch (theo Nam t&ocirc;ng); m&ugrave;ng 8/4 (theo Bắc t&ocirc;ng) tại vườn L&acirc;m Tỳ Ni &ndash; nơi&nbsp; nằm giữa Ca Tỳ La Vệ v&agrave; Devadaha ở Nepal.</p>\r\n\r\n<p>Theo th&ocirc;ng lệ, hằng năm cứ đến ng&agrave;y Rằm th&aacute;ng tư, hầu hết những nước c&oacute; Phật gi&aacute;o v&agrave; c&aacute;c phật tử long trọng tổ chức kỷ niệm ng&agrave;y Đức Phật ra đời. Từ năm 1999, lễ Phật đản v&agrave;o 15/4 đ&atilde; được Li&ecirc;n Hiệp Quốc c&ocirc;ng nhận l&agrave; ng&agrave;y lễ hội văn h&oacute;a t&acirc;m linh thế giới. Lễ Phật đản l&agrave; một trong ba lễ cấu th&agrave;nh Lễ Tam hợp m&agrave; Li&ecirc;n Hiệp Quốc gọi l&agrave; Vesak (lễ Phật đản sinh, lễ Phật th&agrave;nh đạo v&agrave; lễ Phật nhập Niết b&agrave;n). V&agrave;o ng&agrave;y Phật Đản, c&aacute;c Phật tử kh&ocirc;ng s&aacute;t sinh, giết g&agrave;, vịt&hellip; Ng&agrave;y đ&oacute;, tất cả mọi người đều ăn chay, người b&aacute;n h&agrave;ng ở chợ cũng chỉ b&aacute;n đồ chay. Ngo&agrave;i ra, nhiều người c&ograve;n thả chim, thả c&aacute; tạo niềm vui v&agrave; hiến d&acirc;ng sự sống cho mu&ocirc;n lo&agrave;i&hellip; Tại c&aacute;c ch&ugrave;a, Phật tử thường dựng l&ecirc;n lễ đ&agrave;i lớn, trang tr&iacute; c&aacute;c xe hoa. Tuy nhi&ecirc;n, tất cả những việc n&agrave;y đều được thực hiện sao cho kh&ocirc;ng g&acirc;y tốn k&eacute;m nhiều, kh&ocirc;ng phung ph&iacute;, vốn l&agrave; đạo l&yacute; nh&agrave; Phật.</p>\r\n\r\n<p><img alt=\"Phó Thủ tướng Thường trực chúc mừng Đại lễ Phật đản Phật lịch 2566 ...\" src=\"https://bcp.cdnchinhphu.vn/334894974524682240/2022/5/11/c14a7477-1652239192267839320235.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:right\"><em>Đại lễ Phật Đản diễn ra v&agrave;o ng&agrave;y rằm th&aacute;ng tư h&agrave;ng năm.&nbsp;</em></p>\r\n\r\n<p><em>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/canh-quan/cong-trinh-tuong-phat\">C&ocirc;ng tr&igrave;nh t&acirc;m linh tại Thi&ecirc;n Đường</a></em></p>\r\n\r\n<h3>Ng&agrave;y 05 th&aacute;ng 5: Tết Đoan Ngọ</h3>\r\n\r\n<p>Theo s&aacute;ch Phong Thổ k&yacute; th&igrave; Tết Đoan Ngọ c&ograve;n gọi l&agrave; Tết Đoan Dương. Đoan nghĩa l&agrave; mở đầu, Ngọ l&agrave; giữa trưa, Đoan Ngọ l&agrave; bắt đầu l&uacute;c giữa trưa; c&ograve;n Dương l&agrave; mặt trời, l&agrave; kh&iacute; dương Đoan Dương nghĩa l&agrave; bắt đầu l&uacute;c kh&iacute; dương đang thịnh. Tết mồng 5 th&aacute;ng Năm, c&ograve;n được ta gọi l&agrave; Tết Giết s&acirc;u bọ, v&igrave; trong ng&agrave;y h&ocirc;m ấy ta c&oacute; tục giết s&acirc;u bọ. Theo quan niệm của ta xưa, trong người, nhất l&agrave; trong bộ phận ti&ecirc;u h&oacute;a, thường c&oacute; s&acirc;u bọ. S&acirc;u bọ n&agrave;y nếu kh&ocirc;ng bị trừ đi sẽ sinh sản ng&agrave;y c&agrave;ng nhiều v&agrave; g&acirc;y tại hại cho người. Điều đặc biệt l&agrave; giết s&acirc;u bọ ch&iacute;nh bằng những thức ăn, nhất l&agrave; bằng rượu nếp v&agrave; hoa quả.</p>\r\n\r\n<p><img alt=\"Mâm cúng tết Đoan Ngọ Năm Tân Sửu 2021 gồm những gì?\" src=\"https://th.bing.com/th/id/OIP.BcQOrTQQWAkYC9mdcrZJfAHaFr?w=1344&amp;h=1030&amp;rs=1&amp;pid=ImgDetMain\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em>Giết s&acirc;u bọ bằng đồ ăn trong ng&agrave;y&nbsp;Tết Đoan Ngọ</em></p>\r\n\r\n<h3>Ng&agrave;y 15 th&aacute;ng 7: Tết Vu Lan</h3>\r\n\r\n<p>Vu lan c&ograve;n được hiểu l&agrave; lễ b&aacute;o hiếu, l&agrave; một trong những ng&agrave;y lễ ch&iacute;nh của Phật gi&aacute;o (Đại thừa Bắc t&ocirc;ng) v&agrave; phong tục Trung Hoa. Lễ n&agrave;y tr&ugrave;ng với Tết Trung nguy&ecirc;n của người H&aacute;n, v&agrave; cũng tr&ugrave;ng hợp với ng&agrave;y Rằm th&aacute;ng 7 X&aacute; tội vong nh&acirc;n của phong tục &Aacute; Đ&ocirc;ng. Theo t&iacute;n ngưỡng d&acirc;n gian, l&agrave; ng&agrave;y mở cửa ngục, &acirc;n x&aacute; cho vong nh&acirc;n n&ecirc;n c&oacute; lễ c&uacute;ng C&ocirc; Hồn (v&agrave;o buổi chiều) cho c&aacute;c vong linh kh&ocirc;ng nh&agrave; cửa kh&ocirc;ng nơi nương tựa, kh&ocirc;ng c&oacute; th&acirc;n nh&acirc;n tr&ecirc;n Dương thế, v&agrave; l&agrave; ng&agrave;y mọi t&ugrave; nh&acirc;n ở Địa ngục c&oacute; cơ hội được x&aacute; tội, được tho&aacute;t sanh về cảnh giới an l&agrave;nh. Tại Việt Nam, v&agrave;o ng&agrave;y n&agrave;y, mọi gia đ&igrave;nh đều c&uacute;ng hai m&acirc;m: c&uacute;ng tổ ti&ecirc;n tại b&agrave;n thờ tổ ti&ecirc;n v&agrave; c&uacute;ng ch&uacute;ng sinh (c&uacute;ng th&iacute; thực hay c&uacute;ng c&ocirc; hồn) ở s&acirc;n trước nh&agrave; hoặc tr&ecirc;n vỉa h&egrave;, đến ch&ugrave;a cầu mong cha mẹ sức khỏe v&agrave; niềm vui, ăn chay để t&iacute;ch đức, d&agrave;nh tặng cha mẹ những lời ch&uacute;c v&agrave; m&oacute;n qu&agrave; &yacute; nghĩa&hellip;</p>\r\n\r\n<p><img alt=\"Object moved\" src=\"https://s.meta.com.vn/img/thumb.ashx/Data/image/2021/07/06/stt-vu-lan-bao-hieu-1.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em>Tết Vu Lan&nbsp;</em></p>\r\n\r\n<h3>Ng&agrave;y 15 th&aacute;ng 8: Tết Trung Thu</h3>\r\n\r\n<p>Phong tục của người Việt, ng&agrave;y Tết Trung thu được tổ chức v&agrave;o rằm th&aacute;ng t&aacute;m, đ&acirc;y l&agrave; dịp c&aacute;c gia đ&igrave;nh l&agrave;m cỗ c&uacute;ng gia ti&ecirc;n, b&agrave;y b&aacute;n tr&aacute;i c&acirc;y c&uacute;ng mặt trăng. Trẻ em thưởng thức b&aacute;nh, kẹo trong đ&ecirc;m Trung thu được gọi l&agrave; &ldquo;ph&aacute; cỗ&rdquo;. Ng&agrave;y nay, Tết Trung Thu đ&atilde; trở th&agrave;nh ng&agrave;y tết của trẻ em (Tết Thiếu nhi), c&ograve;n được gọi l&agrave; Tết tr&ocirc;ng Trăng hay Tết Đo&agrave;n Vi&ecirc;n. Trẻ em rất mong đợi được đ&oacute;n tết n&agrave;y v&igrave; thường được người lớn tặng đồ chơi, thường l&agrave; đ&egrave;n &ocirc;ng sao, mặt nạ, đ&egrave;n k&eacute;o qu&acirc;n, s&uacute;ng phun nước&hellip; rồi b&aacute;nh nướng, b&aacute;nh dẻo. Thời điểm trăng l&ecirc;n cao, trẻ em sẽ vừa m&uacute;a h&aacute;t vừa ngắm trăng ph&aacute; cỗ. Ở một số nơi c&ograve;n tổ chức m&uacute;a l&acirc;n, m&uacute;a sư tử, m&uacute;a rồng để c&aacute;c em vui chơi thoả th&iacute;ch.</p>\r\n\r\n<p><img alt=\"Tết trung thu ở các nước trên thế giới\" src=\"https://lichvannien365.com/upload/images/2020/tet-trung-thu-viet-nam-lichvannien365.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em>Trẻ em rước đ&egrave;n &ocirc;ng sao trong&nbsp;Tết Trung Thu</em></p>\r\n\r\n<p><em>Xem th&ecirc;m: <a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/top-5-hoa-vien-nghia-trang-dep-nhat-mien-bac\">Top 5 hoa vi&ecirc;n đẹp nhất miền Bắc&nbsp;</a></em></p>\r\n\r\n<h3>Ng&agrave;y 23 th&aacute;ng 12: &Ocirc;ng T&aacute;o chầu trời</h3>\r\n\r\n<p>T&aacute;o Qu&acirc;n hay &Ocirc;ng T&aacute;o trong t&iacute;n ngưỡng d&acirc;n gian Việt Nam&nbsp; được xem l&agrave; vị thần cai quản việc bếp n&uacute;c trong mỗi nh&agrave; gồm ba vị thần: Thổ C&ocirc;ng, Thổ Địa, Thổ Kỳ. Người Việt quan niệm ba vị Thần T&aacute;o định đoạt phước đức cho gia đ&igrave;nh, phước đức n&agrave;y do việc l&agrave;m đ&uacute;ng đạo l&yacute; của gia chủ v&agrave; những người trong nh&agrave;. H&agrave;ng năm, đ&uacute;ng v&agrave;o ng&agrave;y 23 th&aacute;ng Chạp l&agrave; ng&agrave;y T&aacute;o C&ocirc;ng l&ecirc;n chầu trời n&ecirc;n c&oacute; nơi gọi ng&agrave;y n&agrave;y l&agrave; &ldquo;Tết &ocirc;ng C&ocirc;ng&rdquo;, lễ c&uacute;ng c&oacute; c&aacute; ch&eacute;p v&igrave; người Việt cho rằng, &ocirc;ng T&aacute;o cưỡi c&aacute; ch&eacute;p l&ecirc;n trời.</p>\r\n\r\n<p>Ngo&agrave;i ra người Việt Nam c&ograve;n quan niệm T&aacute;o Qu&acirc;n sẽ l&ecirc;n trời v&agrave; thưa với Ngọc Ho&agrave;ng Thượng Đế những sự kiện xảy ra trong năm vừa qua ở dưới trần gian. V&igrave; thế người Việt Nam l&agrave;m lễ tiễn &ocirc;ng C&ocirc;ng &ocirc;ng T&aacute;o rất thịnh soạn với mong muốn những điều tốt đẹp nhất sẽ được thưa với Ngọc Ho&agrave;ng, v&agrave; những điều kh&ocirc;ng may mắn hoặc kh&ocirc;ng tốt sẽ được b&aacute;o c&aacute;o nhẹ đi, việc l&agrave;m n&agrave;y c&oacute; thể l&agrave; do Văn h&oacute;a v&agrave; th&oacute;i quen từ xa xưa truyền lại.</p>\r\n\r\n<p><img alt=\"Gợi ý thực đơn mâm cỗ cúng ông Công ông Táo đầy đủ, đẹp mắt nhất\" src=\"https://danviet.mediacdn.vn/296231569849192448/2022/1/24/14506207335781918423017566890860291380464389n-16429853797431230552432.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><em>Mẫm cỗ ng&agrave;y c&uacute;ng &Ocirc;ng C&ocirc;ng &ndash; &Ocirc;ng T&aacute;o</em></p>\r\n\r\n<p>Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline&nbsp;<strong>0934.524.445</strong>&nbsp;hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\">https://nghiatrangthienduong.com.vn/</a>.</p>\r\n\r\n<p>&nbsp;</p>', 'true', 'News', 'nhung-ngay-le-tam-linh-dac-biet-trong-nam-cua-nguoi-viet-nam', 'tết.jpg', 'Những ngày lễ tâm linh đặc biệt trong năm của người Việt Nam', 'Dưới đây là tổng hợp những ngày lễ tâm linh đặc biệt trong năm của người Việt Nam.', '2024-01-04 09:04:30', '2024-01-04 09:04:30');
INSERT INTO `posts` (`id`, `user_id`, `category_id`, `name`, `detail`, `info`, `content`, `status`, `sort_by`, `slug`, `img`, `title`, `description`, `created_at`, `updated_at`) VALUES
(210, 2, 68, ' GIÁ TRỊ TÂM LINH CỦA ĐẤT NGŨ SẮC TRONG XÂY DỰNG MỘ PHẦN', 'Đất Ngũ Sắc là một trong những yếu tố tiên quyết phải khi lựa chọn nơi đặt mộ bởi những giá trị cả về mặt tâm linh lẫn thực tế mà loại đất này mang lại.', NULL, '<p><strong><span style=\"font-size:16px\">Theo kinh nghiệm qua ng&agrave;n đời truyền lại, c&oacute; rất nhiều yếu tố quan trọng khi chọn nơi an nghỉ cho người th&acirc;n của m&igrave;nh nhằm đảm bảo mồ y&ecirc;n, mả đẹp đồng thời mang đến ph&uacute;c đức cho con ch&aacute;u đời sau. Đất Ngũ Sắc l&agrave; một trong những yếu tố ti&ecirc;n quyết phải khi lựa chọn nơi đặt mộ bởi những gi&aacute; trị cả về mặt t&acirc;m linh lẫn thực tế m&agrave; loại đất n&agrave;y mang lại. B&agrave;i viết n&agrave;y sẽ l&yacute; giải l&yacute; do tại sao đất ngũ sắc lại được đ&aacute;nh gi&aacute; cao bởi nhiều gia đ&igrave;nh v&agrave; c&aacute;c nh&agrave; phong thủy.</span></strong></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"http://hoavienthienduong.vn/wp-content/uploads/2024/01/thien-duong-1.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m:&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/luu-y-khi-boc-mo/\">Lưu &yacute; khi bốc mộ</a></span></p>\r\n\r\n<h2><span style=\"font-size:16px\">Đất ngũ sắc l&agrave; g&igrave;?</span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Đất ngũ sắc l&agrave; loại đất thi&ecirc;ng, chất đất c&oacute; độ tơi, kh&ocirc;, ẩm nhẹ v&agrave; c&oacute; tụ thủy với năm m&agrave;u đặc trưng: V&agrave;ng son, N&acirc;u đậm, V&agrave;ng &aacute;nh kim, Hồng c&oacute; v&acirc;n, Gan g&agrave;. Đất ngũ sắc c&ograve;n được gọi l&agrave; đất &ldquo;Th&aacute;i Cực Bi&ecirc;n Hu&acirc;n&rdquo; ph&ugrave; hợp trong việc&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/cac-hinh-thuc-mai-tang-pho-bien-o-viet-nam/\">mai t&aacute;ng, an t&aacute;ng.</a></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Đất ngũ sắc được coi l&agrave; biểu tượng của sự c&acirc;n bằng, sự ph&aacute;t triển v&agrave; sự h&ograve;a hợp trong tự nhi&ecirc;n v&agrave; x&atilde; hội. Đối với một số d&acirc;n tộc v&agrave; cộng đồng, đất ngũ sắc c&ograve;n đại diện cho sự kết nối với tổ ti&ecirc;n v&agrave; tương t&aacute;c với&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/canh-quan/\">thế giới t&acirc;m linh.</a></span></p>\r\n\r\n<h2><span style=\"font-size:16px\"><img alt=\"\" src=\"http://hoavienthienduong.vn/wp-content/uploads/2024/01/thien-duong.jpg\" /></span></h2>\r\n\r\n<p><span style=\"font-size:16px\">Xem th&ecirc;m:&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/phong-thuy-tho-cung\">Phong thủy thờ c&uacute;ng</a></span></p>\r\n\r\n<h2><span style=\"font-size:16px\">Tại sao lại cần chọn đất ngũ sắc khi hạ huyệt, cải t&aacute;ng:</span></h2>\r\n\r\n<p><span style=\"font-size:16px\">1. Dấu hiệu đất &ldquo;sinh kh&iacute;&rdquo;- Đại Ph&aacute;t, Đại Lộc:&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/tin-tuc/luu-y-khi-boc-mo/\">Huyệt mộ</a>&nbsp;l&agrave; khu đất d&ugrave;ng để ch&ocirc;n cất người đ&atilde; khuất. Khi đ&agrave;o huyệt nếu thấy đất ngũ sắc hạ thổ (thường c&oacute; m&agrave;u trắng &ndash; đỏ &ndash; v&agrave;ng) th&igrave; đ&oacute; l&agrave; một trong những dấu hiệu đất tốt, sinh kh&iacute;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">2. Đảm bảo phần &ldquo;an&rdquo; cho mộ phần: Đất ngũ sắc tơi, xốp nhưng khi gặp mạch thủy dưới l&ograve;ng đất sẽ dẻo v&agrave; qu&aacute;nh lại nhờ vậy m&agrave; huyệt mộ, tiểu qu&aacute;ch sẽ lu&ocirc;n được giữ đ&uacute;ng vị tr&iacute;&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/dia-the-phong-thuy/\">phong thủy</a>, kh&ocirc;ng x&ocirc;, kh&ocirc;ng x&ecirc; dịch, kh&ocirc;ng động.&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/san-pham/\">Phần mộ</a>&nbsp;của người mất sẽ được y&ecirc;n ả l&agrave; điều kiện ti&ecirc;n quyết để kết ph&aacute;t, linh thi&ecirc;ng, ph&ugrave; hộ cho con ch&aacute;u đời sau.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3. Bảo vệ &ldquo;<a href=\"https://nghiatrangthienduong.com.vn/san-pham/\">ng&ocirc;i nh&agrave; vĩnh hằng</a>&rdquo; của người th&acirc;n: Đất ngũ sắc l&agrave; loại đất chịu được mọi điều kiện nhiệt độ, kh&ocirc;ng bị phai mọt, hư hỏng theo thời gian. Đất bao trọn v&agrave; bảo vệ quan t&agrave;i, tiểu qu&aacute;ch gi&uacute;p &ldquo;ng&ocirc;i nh&agrave; vĩnh hằng&rdquo; của người mất kh&ocirc;ng bị hư hại.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">4. Gi&aacute; trị t&acirc;m linh c&acirc;n bằng đất trời: Đất ngũ sắc được coi l&agrave; biểu tượng của sự c&acirc;n bằng, sự ph&aacute;t triển v&agrave; sự h&ograve;a hợp trong tự nhi&ecirc;n v&agrave; x&atilde; hội. Đối với một số d&acirc;n tộc v&agrave; cộng đồng, đất ngũ sắc c&oacute; thể đại diện cho sự kết nối với tổ ti&ecirc;n v&agrave; tương t&aacute;c với thế giới t&acirc;m linh.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Để gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; những trải nghiệm tốt nhất về&nbsp;<a href=\"https://nghiatrangthienduong.com.vn/\">C&ocirc;ng vi&ecirc;n&nbsp;Nghĩa trang&nbsp;Thi&ecirc;n Đường</a>,&nbsp;thứ 7 &amp; chủ nhật h&agrave;ng tuần,&nbsp;Thi&ecirc;n Đường tổ chức đưa đ&oacute;n tham quan&nbsp;MIỄN PH&Iacute;, tất cả chi ph&iacute; di chuyển đều do Thi&ecirc;n Đường h&acirc;n hạnh đ&agrave;i thọ. Qu&yacute; kh&aacute;ch quan t&acirc;m v&agrave; đăng k&yacute; tham quan vui l&ograve;ng li&ecirc;n hệ Hotline&nbsp;0934.524.445&nbsp;hoặc truy cập trang web&nbsp;<a href=\"https://congvienthienduong.com.vn/\" rel=\"noopener\" target=\"_blank\">https://nghiatrangthienduong.com.vn/</a></span></p>', 'true', 'News', 'gia-tri-tam-linh-cua-dat-ngu-sac-trong-xay-dung-mo-phan', 'thien duong.jpg', ' GIÁ TRỊ TÂM LINH CỦA ĐẤT NGŨ SẮC TRONG XÂY DỰNG MỘ PHẦN', 'Đất Ngũ Sắc là một trong những yếu tố tiên quyết phải khi lựa chọn nơi đặt mộ bởi những giá trị cả về mặt tâm linh lẫn thực tế mà loại đất này mang lại.', '2024-01-04 09:14:31', '2024-01-04 09:16:30');

